Table_Dialog_Quest2 =
{ 
	[526557] = { id = 526557, Voice = '', Text = '是不是很厉害，我曾经有个梦想，就是把自己变成一株魔法风藤。总之现在情况紧急，先不说这些。', Speaker = 5201, Option = '风藤在哪？', Action = _EmptyTable,}, 
	[101662] = { id = 101662, Voice = '', Text = '冒险者，让我们一起观看水晶开启的盛大场景吧。', Speaker = 5949, Option = '', Action = _EmptyTable,}, 
	[101545] = { id = 101545, Voice = '', Text = '可能你还有其他想法吧，等你想好了再来找我吧~', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[101544] = { id = 101544, Voice = '', Text = '没想到你这么快就收集齐了，把这二十片破碎遗迹交给我吧~', Speaker = 5944, Option = '{愿意,7},{不愿意,8}', Action = _EmptyTable,}, 
	[101543] = { id = 101543, Voice = '', Text = '这是我收集到的破碎遗迹。', Speaker = 5765, Option = '', Action = _EmptyTable,}, 
	[101542] = { id = 101542, Voice = '', Text = '谢谢你对我们任务的配合！', Speaker = 5765, Option = '', Action = _EmptyTable,}, 
	[101388] = { id = 101388, Voice = '', Text = '感谢你，异国来的冒险者，海姆达水晶的守护之力与你同在。', Speaker = 5716, Option = '', Action = _EmptyTable,}, 
	[101386] = { id = 101386, Voice = '', Text = '我将对海姆达水晶进行第二次修复！', Speaker = 5949, Option = '', Action = _EmptyTable,}, 
	[101385] = { id = 101385, Voice = '', Text = '他将对朱诺的海姆达水晶进行第二次修复！', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[101384] = { id = 101384, Voice = '', Text = '把这个完整的齿轮交给贤者学院的水晶修复者帕德拉！', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[101383] = { id = 101383, Voice = '', Text = '去塞壬的曙光卫队员那里接取“修复水晶”任务，能破碎遗迹。', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[101382] = { id = 101382, Voice = '', Text = '你的破碎遗迹不足十片，是无法唤醒水晶的。', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[101381] = { id = 101381, Voice = '', Text = '你已经收集到了十个破碎遗迹了吗？', Speaker = 5944, Option = '{是的,3},{没有,4}', Action = _EmptyTable,}, 
	[101380] = { id = 101380, Voice = '', Text = '来组成修复水晶的能量齿轮。', Speaker = 5944, Option = '我这就去！', Action = _EmptyTable,}, 
	[101379] = { id = 101379, Voice = '', Text = '需要再次集齐十个破碎遗迹，', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[101378] = { id = 101378, Voice = '', Text = '完成任务将得到由曙光队员收集的“破碎遗迹”。', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[101377] = { id = 101377, Voice = '', Text = '是什么', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[101376] = { id = 101376, Voice = '', Text = '修复水晶的任务每天只发布五个。', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[101375] = { id = 101375, Voice = '', Text = '你还是会与塞壬的曙光卫队员共同负责修复水晶。', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[101374] = { id = 101374, Voice = '', Text = '要完全开启海姆达水晶，需要再进行四次修复。', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[101373] = { id = 101373, Voice = '', Text = '事实上，水晶还没有被完全开启。', Speaker = 5944, Option = '哦？', Action = _EmptyTable,}, 
	[101372] = { id = 101372, Voice = '', Text = '感谢你，异国来的冒险者，海姆达水晶的守护之力与你同在。', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[101370] = { id = 101370, Voice = '', Text = '你做的很好，现在我将唤醒你面前的这座水晶。', Speaker = 5948, Option = '', Action = _EmptyTable,}, 
	[101368] = { id = 101368, Voice = '', Text = '把这个完整的齿轮交给贤者学院的水晶修复者拉佩斯！', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[101367] = { id = 101367, Voice = '', Text = '这二十片破碎遗迹将组成一个完整的能量齿轮。', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[101366] = { id = 101366, Voice = '', Text = '去塞壬的曙光卫队员那里接取“修复水晶”任务，能获得破碎遗迹。', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[101365] = { id = 101365, Voice = '', Text = '破碎遗迹不足二十片是无法唤醒水晶的。', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[101364] = { id = 101364, Voice = '', Text = '你已经收集到了二十个破碎遗迹了吗？', Speaker = 5944, Option = '{是的,3},{没有,4}', Action = _EmptyTable,}, 
	[101363] = { id = 101363, Voice = '', Text = '来唤醒沉睡的海姆达水晶！', Speaker = 5944, Option = '我这就去！', Action = _EmptyTable,}, 
	[101362] = { id = 101362, Voice = '', Text = '就能修复水晶塔末端的破损部分。', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[101361] = { id = 101361, Voice = '', Text = '首先需要集齐二十个破碎遗迹，', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[101360] = { id = 101360, Voice = '', Text = '完成任务将得到由曙光队员收集的“破碎遗迹”。', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[101358] = { id = 101358, Voice = '', Text = '修复水晶的任务每天只发布五个。', Speaker = 5944, Option = '是什么？', Action = _EmptyTable,}, 
	[101357] = { id = 101357, Voice = '', Text = '你将会与塞壬的曙光卫队员共同负责修复水晶。', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[101356] = { id = 101356, Voice = '', Text = '沉睡了百年的海姆达水晶在等待它苏醒的那一刻！', Speaker = 5944, Option = '是', Action = _EmptyTable,}, 
	[101355] = { id = 101355, Voice = '', Text = '冒险者！不要让这朱诺之光熄灭！', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[101354] = { id = 101354, Voice = '', Text = '请你收下我找到的破碎遗迹。', Speaker = 5763, Option = '', Action = _EmptyTable,}, 
	[101353] = { id = 101353, Voice = '', Text = '完成得不错，冒险者。', Speaker = 5763, Option = '', Action = _EmptyTable,}, 
	[101352] = { id = 101352, Voice = '', Text = '请采一些来给我，相信这对你来说并不难。', Speaker = 5763, Option = '', Action = _EmptyTable,}, 
	[101351] = { id = 101351, Voice = '', Text = '原野上盛开的白色卢栗花对伤痛有奇效。', Speaker = 5763, Option = '', Action = _EmptyTable,}, 
	[101350] = { id = 101350, Voice = '', Text = '里面多少还有些伤员。', Speaker = 5763, Option = '', Action = _EmptyTable,}, 
	[101349] = { id = 101349, Voice = '', Text = '可能是之前被那个袭击水晶的大怪物抓走的冒险者们！', Speaker = 5763, Option = '', Action = _EmptyTable,}, 
	[101348] = { id = 101348, Voice = '', Text = '还发现了一些失踪的冒险者。', Speaker = 5763, Option = '', Action = _EmptyTable,}, 
	[101347] = { id = 101347, Voice = '', Text = '我们在寻找遗迹的同时，', Speaker = 5763, Option = '', Action = _EmptyTable,}, 
	[101346] = { id = 101346, Voice = '', Text = '我们的任务将轻松不少。', Speaker = 5765, Option = '', Action = _EmptyTable,}, 
	[101345] = { id = 101345, Voice = '', Text = '如果有人能帮着修理那些棘手的魔物。', Speaker = 5765, Option = '', Action = _EmptyTable,}, 
	[101344] = { id = 101344, Voice = '', Text = '增加了很大难度。', Speaker = 5765, Option = '', Action = _EmptyTable,}, 
	[101343] = { id = 101343, Voice = '', Text = '这给曙光队的队员们寻找修复水晶的遗迹。', Speaker = 5765, Option = '', Action = _EmptyTable,}, 
	[101342] = { id = 101342, Voice = '', Text = '原野废土上到处都是碍事的邪恶向日葵！', Speaker = 5765, Option = '', Action = _EmptyTable,}, 
	[101341] = { id = 101341, Voice = '', Text = '请收好我收集到的破碎遗迹。', Speaker = 5764, Option = '', Action = _EmptyTable,}, 
	[101340] = { id = 101340, Voice = '', Text = '谢谢你对我们任务的配合！', Speaker = 5764, Option = '', Action = _EmptyTable,}, 
	[101339] = { id = 101339, Voice = '', Text = '所以冒险者，请为我们收集一些鸟人哈比的羽毛。', Speaker = 5764, Option = '', Action = _EmptyTable,}, 
	[101338] = { id = 101338, Voice = '', Text = '鸟人哈比的羽毛做出的防护衣，能抵挡工业废土污染对人体的侵蚀。', Speaker = 5764, Option = '', Action = _EmptyTable,}, 
	[101337] = { id = 101337, Voice = '', Text = '十分需要强韧的防护衣物。', Speaker = 5764, Option = '', Action = _EmptyTable,}, 
	[101336] = { id = 101336, Voice = '', Text = '我们的队员在被污染的废土寻找遗迹。', Speaker = 5764, Option = '', Action = _EmptyTable,}, 
	[101335] = { id = 101335, Voice = '', Text = '这是我收集到的破碎遗迹。', Speaker = 5759, Option = '', Action = _EmptyTable,}, 
	[101334] = { id = 101334, Voice = '', Text = '谢谢你，冒险者。', Speaker = 5759, Option = '', Action = _EmptyTable,}, 
	[101333] = { id = 101333, Voice = '', Text = '希望父亲不要爽约才好~', Speaker = 5762, Option = '', Action = _EmptyTable,}, 
	[101332] = { id = 101332, Voice = '', Text = '这是真的吗？舞鞋我要粉红色的！', Speaker = 5761, Option = '', Action = _EmptyTable,}, 
	[101331] = { id = 101331, Voice = '', Text = '我只希望父亲能多花点时间陪陪我！', Speaker = 5760, Option = '', Action = _EmptyTable,}, 
	[101330] = { id = 101330, Voice = '', Text = '告诉她们，这次回来就一定给她们买她们梦寐以求的芭蕾舞鞋。', Speaker = 5759, Option = '', Action = _EmptyTable,}, 
	[101329] = { id = 101329, Voice = '', Text = '如果可以的话，请帮我带信给我的三个小女儿乔娜，苏和莫斯。', Speaker = 5759, Option = '', Action = _EmptyTable,}, 
	[101328] = { id = 101328, Voice = '', Text = '但为了我们家园的安全，我愿意做出牺牲。', Speaker = 5759, Option = '', Action = _EmptyTable,}, 
	[101327] = { id = 101327, Voice = '', Text = '我们已经有许多队员有去无回了…', Speaker = 5759, Option = '', Action = _EmptyTable,}, 
	[101326] = { id = 101326, Voice = '', Text = '去探索和收集遗迹，是件十分危险的事。', Speaker = 5759, Option = '', Action = _EmptyTable,}, 
	[101325] = { id = 101325, Voice = '', Text = '这是我收集到的破碎遗迹，请拿好。', Speaker = 5758, Option = '', Action = _EmptyTable,}, 
	[101324] = { id = 101324, Voice = '', Text = '干的不错！', Speaker = 5758, Option = '', Action = _EmptyTable,}, 
	[101323] = { id = 101323, Voice = '', Text = '幼年火焰鸟飞扑时会掉落这种粉尘，我想你知道该怎么做了。', Speaker = 5758, Option = '', Action = _EmptyTable,}, 
	[101322] = { id = 101322, Voice = '', Text = '需要炙热的魔法粉尘，来擦拭水晶塔的塔身。', Speaker = 5758, Option = '', Action = _EmptyTable,}, 
	[101321] = { id = 101321, Voice = '', Text = '海姆达水晶因为长时间的沉睡，周身缠绕着许多青苔与植被。', Speaker = 5758, Option = '', Action = _EmptyTable,}, 
	[100523] = { id = 100523, Voice = '', Text = '可恶！！这魔物居然又一次在我们眼皮子底下逃跑了！', Speaker = 5713, Option = '', Action = _EmptyTable,}, 
	[100051] = { id = 100051, Voice = '', Text = '没能激活水晶，到底发生了什么事情……', Speaker = 5716, Option = '', Action = _EmptyTable,}, 
	[100022] = { id = 100022, Voice = '', Text = '这件事疑点太多了，我需要好好想一想。', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[100021] = { id = 100021, Voice = '', Text = '这太诡异了，那些神秘研究员口中所说的大人又是谁？', Speaker = 5702, Option = '对啊', Action = _EmptyTable,}, 
	[89805] = { id = 89805, Voice = '', Text = '救命啊！！', Speaker = 6606, Option = '', Action = _EmptyTable,}, 
	[89804] = { id = 89804, Voice = '', Text = '这时，突然传来一个熟悉的声音在呼救。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89793] = { id = 89793, Voice = '', Text = '感谢你特地跑一趟，冒险者。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89792] = { id = 89792, Voice = '', Text = '找到了！就在这里！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89791] = { id = 89791, Voice = '', Text = '血迹越来越明显了…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89790] = { id = 89790, Voice = '', Text = '地上有血迹…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89767] = { id = 89767, Voice = '', Text = '你的协助真的对艾尔帕兰城意义重大。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89766] = { id = 89766, Voice = '', Text = '谢谢你，冒险者。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89765] = { id = 89765, Voice = '', Text = '不管了，当下之急是营救诺曼的弟弟瑞德！', Speaker = 6461, Option = '没错', Action = _EmptyTable,}, 
	[89764] = { id = 89764, Voice = '', Text = '一定是霍兰和那些叛徒走漏的消息。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89763] = { id = 89763, Voice = '', Text = '他好像很了解我和诺曼，我们整个时钟守卫者的情况他也很清楚。', Speaker = 6461, Option = '好像是', Action = _EmptyTable,}, 
	[89762] = { id = 89762, Voice = '', Text = '听他的语气，我总觉得…', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89761] = { id = 89761, Voice = '', Text = '不过这条蛇…', Speaker = 6461, Option = '怎么？', Action = _EmptyTable,}, 
	[89760] = { id = 89760, Voice = '', Text = '我们一定要把他救出来。', Speaker = 6461, Option = '嗯', Action = _EmptyTable,}, 
	[89759] = { id = 89759, Voice = '', Text = '狡猾的耶梦加得！居然抓走了诺曼的弟弟瑞德。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89758] = { id = 89758, Voice = '', Text = '否则不光是艾尔帕兰，整个大陆都要遭殃。', Speaker = 6461, Option = '同意', Action = _EmptyTable,}, 
	[89757] = { id = 89757, Voice = '', Text = '我们决不能让他用黄金时钟的力量释放他那魔狼哥哥。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89756] = { id = 89756, Voice = '', Text = '这条阴险毒辣的蛇！！葬送了多少无辜市民的性命。', Speaker = 6461, Option = '是啊', Action = _EmptyTable,}, 
	[89716] = { id = 89716, Voice = '', Text = '动作快，毒液炸弹随时有可能爆炸！！', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89715] = { id = 89715, Voice = '', Text = '你快同诺曼一起将防护面罩发放给他们！！', Speaker = 6461, Option = '好', Action = _EmptyTable,}, 
	[89714] = { id = 89714, Voice = '', Text = '什么！！', Speaker = 6461, Option = '', Emoji = 1, Action = _EmptyTable,}, 
	[89713] = { id = 89713, Voice = '', Text = '还有在这钟楼附近的城民没有及时的出去避难！', Speaker = 6625, Option = '', Action = _EmptyTable,}, 
	[89712] = { id = 89712, Voice = '', Text = '大事不好！', Speaker = 6625, Option = '', Action = _EmptyTable,}, 
	[89711] = { id = 89711, Voice = '', Text = '不过我还是会让守护者们带好防护面罩！', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89710] = { id = 89710, Voice = '', Text = '毒液炸弹对有能力的时钟守卫者来说伤害并不是很大。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89709] = { id = 89709, Voice = '', Text = '太好了！赶上了！！', Speaker = 6461, Option = '是啊', Action = _EmptyTable,}, 
	[89708] = { id = 89708, Voice = '', Text = '形成了一个巨大的结界将钟楼周边整个包了起来。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89707] = { id = 89707, Voice = '', Text = '在空中的光点发散出无数道光束与刚才布置的时间之轮连接在一起。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89706] = { id = 89706, Voice = '', Text = '只见从默斯曼身上发出一道通透的光直射天空。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89705] = { id = 89705, Voice = '', Text = '看我的！', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89704] = { id = 89704, Voice = '', Text = '冒险者，你动作非常快！很好！', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89703] = { id = 89703, Voice = '', Text = '现在，将这些时间之轮布在钟楼旁的四个桥前！！', Speaker = 6461, Option = '这就去', Action = _EmptyTable,}, 
	[89702] = { id = 89702, Voice = '', Text = '我们正在和时间赛跑！', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89701] = { id = 89701, Voice = '', Text = '干的好！', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89700] = { id = 89700, Voice = '', Text = '动作要快，我们无法拿整个艾尔帕兰城民众的性命开玩笑。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89699] = { id = 89699, Voice = '', Text = '那是布下结界坐标的重要物品！', Speaker = 6461, Option = '明白', Action = _EmptyTable,}, 
	[89698] = { id = 89698, Voice = '', Text = '需要你去钟楼内收集古钟魔的时间之轮。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89697] = { id = 89697, Voice = '', Text = '我现在要布下一个时钟结界，把整个钟楼附近都包起来。', Speaker = 6461, Option = '需要什么？', Action = _EmptyTable,}, 
	[89696] = { id = 89696, Voice = '', Text = '冒险者！时间紧迫！！！', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89676] = { id = 89676, Voice = '', Text = '这是我们守护者的勋章，请拿好。', Speaker = 6470, Option = '', Action = _EmptyTable,}, 
	[89675] = { id = 89675, Voice = '', Text = '完成得不错，冒险者。', Speaker = 6470, Option = '', Action = _EmptyTable,}, 
	[89674] = { id = 89674, Voice = '', Text = '帮我把这些药品带给那些受伤的战士吧。', Speaker = 6470, Option = '', Action = _EmptyTable,}, 
	[89673] = { id = 89673, Voice = '', Text = '我们许多战士都受了伤，好在我们储备了足够的药品。', Speaker = 6470, Option = '', Action = _EmptyTable,}, 
	[89672] = { id = 89672, Voice = '', Text = '这次我们在耶梦加得的偷袭下损伤惨重。', Speaker = 6470, Option = '', Action = _EmptyTable,}, 
	[89671] = { id = 89671, Voice = '', Text = '这是我们守护者的勋章，请拿好。', Speaker = 6469, Option = '', Action = _EmptyTable,}, 
	[89670] = { id = 89670, Voice = '', Text = '完成得不错，冒险者。', Speaker = 6469, Option = '', Action = _EmptyTable,}, 
	[89669] = { id = 89669, Voice = '', Text = '我们现在人手不够，冒险者请帮我们清理一些朽魔吧。', Speaker = 6469, Option = '', Action = _EmptyTable,}, 
	[89668] = { id = 89668, Voice = '', Text = '我觉得他们都快冲出钟塔了！', Speaker = 6469, Option = '', Action = _EmptyTable,}, 
	[89667] = { id = 89667, Voice = '', Text = '自从那次灾难过后，钟楼的魔物都开始暴动了。', Speaker = 6469, Option = '', Action = _EmptyTable,}, 
	[89666] = { id = 89666, Voice = '', Text = '这是我们守护者的勋章，请拿好。', Speaker = 6468, Option = '', Action = _EmptyTable,}, 
	[89665] = { id = 89665, Voice = '', Text = '完成得不错，冒险者。', Speaker = 6468, Option = '', Action = _EmptyTable,}, 
	[89664] = { id = 89664, Voice = '', Text = '你可以去普隆德拉北部寻找这些木材。', Speaker = 6468, Option = '', Action = _EmptyTable,}, 
	[89663] = { id = 89663, Voice = '', Text = '现在我们急需大量木材。', Speaker = 6468, Option = '', Action = _EmptyTable,}, 
	[89662] = { id = 89662, Voice = '', Text = '这场灾难十分突然，我们没有充足的准备。', Speaker = 6468, Option = '', Action = _EmptyTable,}, 
	[89661] = { id = 89661, Voice = '', Text = '你可以去我们守护者成员那里接取“守卫家园”任务获取勋章哦。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89660] = { id = 89660, Voice = '', Text = '冒险者，你并没有收集到足够的勋章。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
