	[89449] = { id = 89449, Voice = '', Text = '你逃不掉的！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89448] = { id = 89448, Voice = '', Text = '我看你能追多久！', Speaker = 6604, Option = '', Action = _EmptyTable,}, 
	[89447] = { id = 89447, Voice = '', Text = '在这里！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89439] = { id = 89439, Voice = '', Text = '看来就是这里了。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89438] = { id = 89438, Voice = '', Text = '为了防止《秘法典籍》丢失，请你前去汉纳姆家中看看。', Speaker = 6461, Option = '没问题', Action = _EmptyTable,}, 
	[89437] = { id = 89437, Voice = '', Text = '《秘法典籍》的重要性我想你也能明白。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89436] = { id = 89436, Voice = '', Text = '我甚至把记载大贤者古代符文与咒语的《秘法典籍》都交由他保管。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89435] = { id = 89435, Voice = '', Text = '汉纳姆是我重要的助手，我对他非常信任。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89434] = { id = 89434, Voice = '', Text = '等我们妥善处理好牺牲的战士，我们再商量对策！', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89433] = { id = 89433, Voice = '', Text = '我早就知道这个预言，但是我却没想到这一天来得这么快…', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89432] = { id = 89432, Voice = '', Text = '是先知口中的那头沉睡的巨蛇吗…', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89431] = { id = 89431, Voice = '', Text = '那个神秘人究竟是什么人…竟然这么强大。', Speaker = 6461, Option = '耶梦加得', Action = _EmptyTable,}, 
	[89430] = { id = 89430, Voice = '', Text = '多谢你的帮助冒险者。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89429] = { id = 89429, Voice = '', Text = '先去问问默斯曼大人的看法吧。', Speaker = 6598, Option = '', Action = _EmptyTable,}, 
	[89428] = { id = 89428, Voice = '', Text = '不，因为魔物聚集，钟楼现在无比危险。我怎么能让你冒险。', Speaker = 6598, Option = '', Action = _EmptyTable,}, 
	[89427] = { id = 89427, Voice = '', Text = '也不知道他现在怎么样了…', Speaker = 6598, Option = '我帮你找', Action = _EmptyTable,}, 
	[89426] = { id = 89426, Voice = '', Text = '我唯一担心的就是我的弟弟，我们在钟楼走散了。', Speaker = 6598, Option = '', Action = _EmptyTable,}, 
	[89425] = { id = 89425, Voice = '', Text = '我们一定会想办法阻止这个邪恶的家伙的。', Speaker = 6598, Option = '', Action = _EmptyTable,}, 
	[89424] = { id = 89424, Voice = '', Text = '数名同僚牺牲了，为了黄金时钟，他们的死光荣而神圣。', Speaker = 6598, Option = '', Action = _EmptyTable,}, 
	[89410] = { id = 89410, Voice = '', Text = '如果你现在前往钟楼应该还来得及救下他们…', Speaker = 6550, Option = '我这就去！', Action = _EmptyTable,}, 
	[89409] = { id = 89409, Voice = '', Text = '在他们的未来里，我看到了一片血红。', Speaker = 6550, Option = '', Action = _EmptyTable,}, 
	[89408] = { id = 89408, Voice = '', Text = '现在我可以尝试看清他们的未来。稍等，冒险者。', Speaker = 6550, Option = '好的', Action = _EmptyTable,}, 
	[89407] = { id = 89407, Voice = '', Text = '但是我可以看到时钟守护者的命运。', Speaker = 6550, Option = '', Action = _EmptyTable,}, 
	[89406] = { id = 89406, Voice = '', Text = '我的预知能力有限，无法得知耶梦加得的下落。', Speaker = 6550, Option = '那怎么办', Action = _EmptyTable,}, 
	[89405] = { id = 89405, Voice = '', Text = '耶梦加得加快了夺取“黄金时钟”的步伐。', Speaker = 6550, Option = '原来是这样', Action = _EmptyTable,}, 
	[89404] = { id = 89404, Voice = '', Text = '而现在，命运的车轮开始转动。', Speaker = 6550, Option = '', Action = _EmptyTable,}, 
	[89403] = { id = 89403, Voice = '', Text = '那就是时钟守护者们一直守护的“黄金时钟”。', Speaker = 6550, Option = '黄金时钟！', Action = _EmptyTable,}, 
	[89402] = { id = 89402, Voice = '', Text = '没错，他在艾尔帕兰终于发现了能够前往月亮的办法。', Speaker = 6550, Option = '', Action = _EmptyTable,}, 
	[89401] = { id = 89401, Voice = '', Text = '他开始游历大陆，期望能找到某个上古遗迹能通往月亮。', Speaker = 6550, Option = '莫非', Action = _EmptyTable,}, 
	[89400] = { id = 89400, Voice = '', Text = '耶梦加得的力量流逝使他无法前往月亮，救回自己的大哥芬里尔。', Speaker = 6550, Option = '弟弟救哥哥', Action = _EmptyTable,}, 
	[89399] = { id = 89399, Voice = '', Text = '而他的弟弟经过几百年的沉睡，勉强治愈好了自己的致命伤势。', Speaker = 6550, Option = '', Action = _EmptyTable,}, 
	[89398] = { id = 89398, Voice = '', Text = '他的弟弟也失去了踪迹，只剩下他孤寂的困锁在月亮之上。', Speaker = 6550, Option = '', Action = _EmptyTable,}, 
	[89397] = { id = 89397, Voice = '', Text = '直到某天诸神的黄昏来临，芬里尔因为被锁在月亮上而逃过一劫。', Speaker = 6550, Option = '', Action = _EmptyTable,}, 
	[89396] = { id = 89396, Voice = '', Text = '他的弟弟耶梦加得看到遍体鳞伤的芬里尔，决定一定要把他从月亮上救出来。', Speaker = 6550, Option = '耶梦加得', Action = _EmptyTable,}, 
	[89395] = { id = 89395, Voice = '', Text = '直到有一天芬里尔的灾祸触怒了诸神，诸神用侏儒们研究出的锁链锁住了芬里尔。', Speaker = 6550, Option = '锁住了', Action = _EmptyTable,}, 
	[89394] = { id = 89394, Voice = '', Text = '无论出现在哪里，芬里尔身边的灾祸总会给人们带来死亡。', Speaker = 6550, Option = '', Action = _EmptyTable,}, 
	[89393] = { id = 89393, Voice = '', Text = '先听我讲一个故事吧。月狼芬里尔生来伴随着无数灾祸。', Speaker = 6550, Option = '月狼？', Action = _EmptyTable,}, 
	[89392] = { id = 89392, Voice = '', Text = '谢谢你冒险者。', Speaker = 6550, Option = '', Action = _EmptyTable,}, 
	[89391] = { id = 89391, Voice = '', Text = '你回来了。我想你应该成功拿到生命心核了。', Speaker = 6550, Option = '', Action = _EmptyTable,}, 
	[89390] = { id = 89390, Voice = '', Text = '未来不会欺骗我…咳咳，钟怪接受了时间的力量，他们身上拥有生命心核。', Speaker = 6550, Option = '我这就去', Action = _EmptyTable,}, 
	[89389] = { id = 89389, Voice = '', Text = '除非我能够拿到一些生命心核补充生命力。', Speaker = 6550, Option = '我来帮你', Action = _EmptyTable,}, 
	[89388] = { id = 89388, Voice = '', Text = '而现在，我的时间已经不多了。', Speaker = 6550, Option = '', Action = _EmptyTable,}, 
	[89387] = { id = 89387, Voice = '', Text = '时间是公平的，预知了未来，时间也会从我身上取走一些时间。', Speaker = 6550, Option = '', Action = _EmptyTable,}, 
	[89386] = { id = 89386, Voice = '', Text = '但是很遗憾，我已经老了，我已经没有足够的力量来告诉你这一切。', Speaker = 6550, Option = '为什么', Action = _EmptyTable,}, 
	[89385] = { id = 89385, Voice = '', Text = '你不需要怀疑，我知道你来的目的。你是为婚礼上的混乱而来。', Speaker = 6550, Option = '是的', Action = _EmptyTable,}, 
	[89384] = { id = 89384, Voice = '', Text = '你将在灾难中大放光彩。', Speaker = 6550, Option = '是吗', Action = _EmptyTable,}, 
	[89383] = { id = 89383, Voice = '', Text = '我在未来的一角碎片中看到了你活跃的身影。', Speaker = 6550, Option = '', Action = _EmptyTable,}, 
	[89382] = { id = 89382, Voice = '', Text = '年轻人，我等你很久了。', Speaker = 6550, Option = '等我？', Action = _EmptyTable,}, 
	[89381] = { id = 89381, Voice = '', Text = '大先知会给你些有用的信息。', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89380] = { id = 89380, Voice = '', Text = '虽然不知道幕后黑手是谁，不过我也冒着危险调查了很久。', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89379] = { id = 89379, Voice = '', Text = '直到刚才我才明白，婚礼上的灾难是个预谋已久的阴谋。', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89378] = { id = 89378, Voice = '', Text = '也就是不久前你刚刚看到的那个资料。', Speaker = 6549, Option = '原来如此', Action = _EmptyTable,}, 
	[89377] = { id = 89377, Voice = '', Text = '我曾经从大先知那里得到过灾难的启示。', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89376] = { id = 89376, Voice = '', Text = '罗曼斯基能够看到一些未来的时间碎片。', Speaker = 6549, Option = '看到未来？', Action = _EmptyTable,}, 
	[89375] = { id = 89375, Voice = '', Text = '在艾尔帕兰我想你一定听过时间先知的大名。', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89374] = { id = 89374, Voice = '', Text = '而汉纳姆的死，可能与大先知所说的那些资料有关！', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89373] = { id = 89373, Voice = '', Text = '婚礼上的暴动将会是灾难的开端！', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89372] = { id = 89372, Voice = '', Text = '果然是这样吗…', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89371] = { id = 89371, Voice = '', Text = '到处都找不到妮薇儿的踪影…她能去哪？', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89370] = { id = 89370, Voice = '', Text = '地上似乎有些毒液…怎么会在这种地方？', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89369] = { id = 89369, Voice = '', Text = '妮薇儿不见了…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89368] = { id = 89368, Voice = '', Text = '你现在回去找找妮薇儿。如果我的猜测没错的话…', Speaker = 6549, Option = '好的', Action = _EmptyTable,}, 
	[89367] = { id = 89367, Voice = '', Text = '听着，冒险者，这次的事件背后隐藏着巨大的阴谋，我要准备起来了。', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89366] = { id = 89366, Voice = '', Text = '是他动的手吗？如果是他的话，那…', Speaker = 6549, Option = '你在说什么？', Action = _EmptyTable,}, 
	[89365] = { id = 89365, Voice = '', Text = '淡黄色的腥臭剧毒粘液，这已经是很明显的蛇毒特征了。', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89364] = { id = 89364, Voice = '', Text = '蛇毒…原来是他，他终于忍不住了…', Speaker = 6549, Option = '蛇毒？', Action = _EmptyTable,}, 
	[89363] = { id = 89363, Voice = '', Text = '难道是！', Speaker = 6549, Option = '是什么？', Emoji = 1, Action = _EmptyTable,}, 
	[89362] = { id = 89362, Voice = '', Text = '淡黄色，腥臭粘稠的毒液…', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89361] = { id = 89361, Voice = '', Text = '现在，让我看看这剧毒的真面目…', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89360] = { id = 89360, Voice = '', Text = '森灵是大自然的宠儿，它的血液有净化的功效…', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89359] = { id = 89359, Voice = '', Text = '看样子是顺利拿到血液了呢，给我吧。', Speaker = 6549, Option = '拿去', Action = _EmptyTable,}, 
	[89358] = { id = 89358, Voice = '', Text = '安息吧，汉纳姆…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89357] = { id = 89357, Voice = '', Text = '不好，他们要毁灭遗体！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89356] = { id = 89356, Voice = '', Text = '动手吧。一点痕迹都不要留下！', Speaker = 6591, Option = '', Action = _EmptyTable,}, 
	[89355] = { id = 89355, Voice = '', Text = '拜托你了，希望你能为我、为我丈夫报仇。', Speaker = 6588, Option = '明白', Action = _EmptyTable,}, 
	[89354] = { id = 89354, Voice = '', Text = '你查到害死我丈夫的凶手了吗？', Speaker = 6588, Option = '还没有', Action = _EmptyTable,}, 
	[89353] = { id = 89353, Voice = '', Text = '我只能满足他的愿望。', Speaker = 6588, Option = '', Action = _EmptyTable,}, 
	[89352] = { id = 89352, Voice = '', Text = '汉纳姆曾经表示誓死都要守卫黄金时钟。', Speaker = 6588, Option = '', Action = _EmptyTable,}, 
	[89351] = { id = 89351, Voice = '', Text = '汉纳姆…在主教做完祷告后，就被运往钟楼了。', Speaker = 6588, Option = '', Emoji = 14, Action = _EmptyTable,}, 
	[89350] = { id = 89350, Voice = '', Text = '他们现在应该还在教堂，你去那里看看吧。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89349] = { id = 89349, Voice = '', Text = '我答应了这个可怜人的请求。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89348] = { id = 89348, Voice = '', Text = '妮薇儿想在汉纳姆下葬之前再好好看看她的丈夫。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89347] = { id = 89347, Voice = '', Text = '只要你帮我找到森灵的血液并且带来汉纳姆的血液，我就可以尝试进行分析。', Speaker = 6549, Option = '等着', Action = _EmptyTable,}, 
	[89346] = { id = 89346, Voice = '', Text = '坦白说，我对那种毒也非常感兴趣…', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89345] = { id = 89345, Voice = '', Text = '就算能接近，我手中也缺少研究道具，无法给出准确判断。', Speaker = 6549, Option = '帮我调查', Action = _EmptyTable,}, 
	[89344] = { id = 89344, Voice = '', Text = '可惜混乱过后我再也找不到机会接近汉纳姆的尸体了，更别说调查是什么毒了。', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89343] = { id = 89343, Voice = '', Text = '嘴唇泛紫，四肢臃肿，这是很典型的中毒症状。', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89342] = { id = 89342, Voice = '', Text = '那可是有所研究的。', Speaker = 6549, Option = '怎么说', Action = _EmptyTable,}, 
	[89341] = { id = 89341, Voice = '', Text = '你知道，我是一个研究员，对于这些非自然死亡的原因…', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89340] = { id = 89340, Voice = '', Text = '汉纳姆的婚礼时候，我可就在附近…', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89339] = { id = 89339, Voice = '', Text = '只是没想到他会被毒杀…', Speaker = 6549, Option = '等等，毒杀？', Action = _EmptyTable,}, 
	[89338] = { id = 89338, Voice = '', Text = '我知道，这次混乱就是在汉纳姆的婚礼上出现的吧。', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89337] = { id = 89337, Voice = '', Text = '你说的那些突然出现的魔物，跟我们雷根贝勒一点关系都没有！', Speaker = 6549, Option = '汉纳姆的死', Action = _EmptyTable,}, 
	[89336] = { id = 89336, Voice = '', Text = '我们雷根贝勒公司一直都是做着正常的生意。', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89335] = { id = 89335, Voice = '', Text = '真的吗！！你可要说到做到…', Speaker = 6549, Option = '真的', Action = _EmptyTable,}, 
	[89334] = { id = 89334, Voice = '', Text = '我不敢说多，怕惹来杀身之祸！', Speaker = 6549, Option = '我保护你', Action = _EmptyTable,}, 
	[89333] = { id = 89333, Voice = '', Text = '但我结合汉纳姆的调查，发现这之中可能会有个惊天大阴谋！', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89332] = { id = 89332, Voice = '', Text = '是汉纳姆让我查这些的，对于这模糊不清的文字我也是一知半解。', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89331] = { id = 89331, Voice = '', Text = '灾难…黄金时钟…异时空…', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89330] = { id = 89330, Voice = '', Text = '沉眠的巨蛇…令诸神畏惧的巨狼…时钟塔…', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89329] = { id = 89329, Voice = '', Text = '调查过的研究资料都在这里，不要杀我！', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89328] = { id = 89328, Voice = '', Text = '虽然他生前确实有拜托我调查过一些事情。', Speaker = 6549, Option = '调查？', Action = _EmptyTable,}, 
	[89327] = { id = 89327, Voice = '', Text = '对于他的死我表示很遗憾，但那和我没关系…', Speaker = 6549, Option = '', Action = _EmptyTable,}, 
	[89326] = { id = 89326, Voice = '', Text = '你有什么目的？我没有钱！', Speaker = 6549, Option = '汉纳姆', Emoji = 10, Action = _EmptyTable,}, 
	[89325] = { id = 89325, Voice = '', Text = '谁！你怎么进来的？卫兵！卫兵！', Speaker = 6549, Option = '别叫了', Action = _EmptyTable,}, 
	[89324] = { id = 89324, Voice = '', Text = '进去看看。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89321] = { id = 89321, Voice = '', Text = '请一定要帮我抓住害死我丈夫的凶手。', Speaker = 6588, Option = '放心吧', Action = _EmptyTable,}, 
	[89320] = { id = 89320, Voice = '', Text = '我丈夫的死因一定与雷根贝勒的詹金斯脱不了干系。', Speaker = 6588, Option = '', Action = _EmptyTable,}, 
	[89319] = { id = 89319, Voice = '', Text = '但听我丈夫说，雷根贝勒的研究员詹金斯正在研究什么不可告人的东西。', Speaker = 6588, Option = '', Action = _EmptyTable,}, 
	[89318] = { id = 89318, Voice = '', Text = '不清楚…', Speaker = 6588, Option = '', Action = _EmptyTable,}, 
	[89317] = { id = 89317, Voice = '', Text = '在婚礼前夜，我丈夫曾经进入雷根贝勒公司调查什么东西。', Speaker = 6588, Option = '什么东西', Action = _EmptyTable,}, 
	[89316] = { id = 89316, Voice = '', Text = '哦！对了。', Speaker = 6588, Option = '什么？', Action = _EmptyTable,}, 
	[89315] = { id = 89315, Voice = '', Text = '我只记得当时婚礼现场太过混乱，我忙于仪式，实在是没有感觉到奇怪的地方。', Speaker = 6588, Option = '', Action = _EmptyTable,}, 
	[89314] = { id = 89314, Voice = '', Text = '可你问我线索…', Speaker = 6588, Option = '', Action = _EmptyTable,}, 
	[89313] = { id = 89313, Voice = '', Text = '嗯…我不会哭，我要毒害我丈夫的人付出代价！', Speaker = 6588, Option = '', Action = _EmptyTable,}, 
	[89312] = { id = 89312, Voice = '', Text = '在婚礼之前，我从未感觉到生命是如此的脆弱。', Speaker = 6588, Option = '别难过', Emoji = 24, Action = _EmptyTable,}, 
	[89311] = { id = 89311, Voice = '', Text = '她就在婚礼的教堂附近。', Speaker = 6461, Option = '明白', Action = _EmptyTable,}, 
	[89310] = { id = 89310, Voice = '', Text = '虽然她应该还在悲痛之中，但我们需要她的线索和证言。', Speaker = 6461, Option = '了解', Action = _EmptyTable,}, 
	[89309] = { id = 89309, Voice = '', Text = '去问问汉纳姆的新娘妮薇儿。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89308] = { id = 89308, Voice = '', Text = '我们没有足够的人手调查汉纳姆的死因，冒险者，我们需要你的协助。', Speaker = 6461, Option = '义不容辞', Action = _EmptyTable,}, 
	[89307] = { id = 89307, Voice = '', Text = '为了安抚民众和追击那些魔物，守护者的成员们大部分都派遣出去了。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89306] = { id = 89306, Voice = '', Text = '已经引起了普通市民的恐慌。', Speaker = 6461, Option = '对', Action = _EmptyTable,}, 
	[89305] = { id = 89305, Voice = '', Text = '不过在婚礼现场突然出现的大批钟楼魔物…', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89304] = { id = 89304, Voice = '', Text = '我是不会让我重要的成员白白牺牲的，一定要查清此事。', Speaker = 6461, Option = '是啊', Action = _EmptyTable,}, 
	[89303] = { id = 89303, Voice = '', Text = '真没想到发生这样的惨剧…', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89283] = { id = 89283, Voice = '', Text = '我们【时钟守护者】一定会彻查此事的。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89282] = { id = 89282, Voice = '', Text = '这背后一定还存在着什么更大的阴谋。', Speaker = 6461, Option = '同感', Action = _EmptyTable,}, 
	[89281] = { id = 89281, Voice = '', Text = '和这片红色的鳞片应该有密不可分的联系。', Speaker = 6461, Option = '对', Action = _EmptyTable,}, 
	[89280] = { id = 89280, Voice = '', Text = '至于伯纳德嘴里提到的人……', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89279] = { id = 89279, Voice = '', Text = '这里面也有你不可或缺的功劳，再次感谢你。', Speaker = 6461, Option = '这没什么', Action = _EmptyTable,}, 
	[89278] = { id = 89278, Voice = '', Text = '告诉你一个好消息，河水已经全部被净化完毕了。', Speaker = 6461, Option = '太好了！', Action = _EmptyTable,}, 
	[89277] = { id = 89277, Voice = '', Text = '谢谢你与我们一同守卫了艾尔帕兰的秩序。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89276] = { id = 89276, Voice = '', Text = '不过你在这场危机当中的表现实在是太勇敢了。', Speaker = 6461, Option = '过奖', Action = _EmptyTable,}, 
	[89275] = { id = 89275, Voice = '', Text = '伯纳德居然叛变了……真是让我心痛。', Speaker = 6461, Option = '遗憾', Action = _EmptyTable,}, 
	[89274] = { id = 89274, Voice = '', Text = '我还需要留在此处善后，万事就拜托你了。', Speaker = 6582, Option = '嗯', Action = _EmptyTable,}, 
	[89273] = { id = 89273, Voice = '', Text = '此事有必要向岁月大人汇报一下。', Speaker = 6582, Option = '同意', Action = _EmptyTable,}, 
	[89272] = { id = 89272, Voice = '', Text = '果然，事情似乎没那么简单。', Speaker = 6582, Option = '', Action = _EmptyTable,}, 
	[89271] = { id = 89271, Voice = '', Text = '地上居然有一片红色的闪亮鳞片，这会是线索吗？', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89270] = { id = 89270, Voice = '', Text = '依旧是黏稠充满怪味的液体，密室周边的空气似乎比其他地方潮湿许多。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89269] = { id = 89269, Voice = '', Text = '如果能收集到什么重要线索就更好了。', Speaker = 6582, Option = '好', Action = _EmptyTable,}, 
	[89268] = { id = 89268, Voice = '', Text = '我们分头再调查一下。', Speaker = 6582, Option = '', Action = _EmptyTable,}, 
	[89267] = { id = 89267, Voice = '', Text = '来的时候我看密室周围的样子总觉得有些不对劲。', Speaker = 6582, Option = '哦？', Action = _EmptyTable,}, 
	[89266] = { id = 89266, Voice = '', Text = '好像此事并没有结束一样。', Speaker = 6582, Option = '怎么说？', Action = _EmptyTable,}, 
	[89265] = { id = 89265, Voice = '', Text = '我心里还是有些毛毛的。', Speaker = 6582, Option = '', Action = _EmptyTable,}, 
	[89264] = { id = 89264, Voice = '', Text = '刚才那个叛徒说到什么大人……', Speaker = 6582, Option = '', Action = _EmptyTable,}, 
	[89249] = { id = 89249, Voice = '', Text = '是否要进入密室？', Speaker = 6585, Option = '{是,2},{稍后,3}', Action = _EmptyTable,}, 
	[89248] = { id = 89248, Voice = '', Text = '事不宜迟，我们进去看看。', Speaker = 6582, Option = '', Action = _EmptyTable,}, 
	[89247] = { id = 89247, Voice = '', Text = '时间守卫者的密室已经被打开。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89246] = { id = 89246, Voice = '', Text = '最珍贵却最被忽略……', Speaker = 6582, Option = '', Action = _EmptyTable,}, 
	[89245] = { id = 89245, Voice = '', Text = '最伟大而又最渺小……', Speaker = 6582, Option = '', Action = _EmptyTable,}, 
	[89244] = { id = 89244, Voice = '', Text = '最广也最被分割……', Speaker = 6582, Option = '', Action = _EmptyTable,}, 
	[89243] = { id = 89243, Voice = '', Text = '是最长也最短……', Speaker = 6582, Option = '', Action = _EmptyTable,}, 
	[89242] = { id = 89242, Voice = '', Text = '现在，我将吟诵打开密室的魔法。', Speaker = 6582, Option = '', Action = _EmptyTable,}, 
	[89241] = { id = 89241, Voice = '', Text = '是岁月大人派我来协助你调查的。', Speaker = 6582, Option = '嗯', Action = _EmptyTable,}, 
	[89240] = { id = 89240, Voice = '', Text = '很高兴见到你，冒险者。', Speaker = 6582, Option = '', Action = _EmptyTable,}, 
	[89239] = { id = 89239, Voice = '', Text = '是否取走钥匙？', Speaker = 6581, Option = '{是,2},{否,3}', Action = _EmptyTable,}, 
	[89238] = { id = 89238, Voice = '', Text = '记住，先找到箱子。就在神圣的石像旁。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89237] = { id = 89237, Voice = '', Text = '我会派另一名守卫者狄娜与你一同前去，我已经告知了她。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89236] = { id = 89236, Voice = '', Text = '将箱子里的钥匙放在你之前调查的那个位置，然后默念魔法。', Speaker = 6461, Option = '什么魔法？', Action = _EmptyTable,}, 
	[89235] = { id = 89235, Voice = '', Text = '我在钟楼地下藏了一个存放密室钥匙的小箱子。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89234] = { id = 89234, Voice = '', Text = '我就告诉你开启密室的方法吧。', Speaker = 6461, Option = '好', Action = _EmptyTable,}, 
	[89233] = { id = 89233, Voice = '', Text = '年轻人，你的确不简单。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[89232] = { id = 89232, Voice = '', Text = '但冒险者你的胆量和实力实在让我佩服，我相信岁月大人会给你这个权限。', Speaker = 6469, Option = '', Action = _EmptyTable,}, 
	[89231] = { id = 89231, Voice = '', Text = '确实有必要去调查一下那间密室了，我是没有权限进入的。', Speaker = 6469, Option = '', Action = _EmptyTable,}, 
	[89230] = { id = 89230, Voice = '', Text = '眼里还冒着诡异的……黄绿色的光。', Speaker = 6469, Option = '嗯…', Action = _EmptyTable,}, 
	[89229] = { id = 89229, Voice = '', Text = '我最后一次看见他，他的神情慌慌张张的。', Speaker = 6469, Option = '', Action = _EmptyTable,}, 
	[89228] = { id = 89228, Voice = '', Text = '可是伯纳德却在上个月神秘失踪了，没有人知道他去了哪里。', Speaker = 6469, Option = '奇怪…', Action = _EmptyTable,}, 
	[89227] = { id = 89227, Voice = '', Text = '还有一个是岁月大人的亲信伯纳德·阿诺特。', Speaker = 6469, Option = '', Action = _EmptyTable,}, 
	[89226] = { id = 89226, Voice = '', Text = '一个是我们伟大的领袖默斯曼·岁月！', Speaker = 6469, Option = '还有一位？', Action = _EmptyTable,}, 
	[89225] = { id = 89225, Voice = '', Text = '是的，蹊跷的是，这间密室只有两个人知道如何入内。', Speaker = 6469, Option = '谁？', Action = _EmptyTable,}, 
	[89224] = { id = 89224, Voice = '', Text = '那里有个时钟守护者曾经建立的集会密室。', Speaker = 6469, Option = '是嘛！', Action = _EmptyTable,}, 
	[89223] = { id = 89223, Voice = '', Text = '你说的那个位置，可就蹊跷了。', Speaker = 6469, Option = '蹊跷？', Action = _EmptyTable,}, 
	[89222] = { id = 89222, Voice = '', Text = '你确定是在钟楼的二楼发现了有异味的地方？', Speaker = 6469, Option = '是的', Action = _EmptyTable,}, 
	[89221] = { id = 89221, Voice = '', Text = '辛苦了！', Speaker = 6469, Option = '', Action = _EmptyTable,}, 
	[89220] = { id = 89220, Voice = '', Text = '地上不知为何有些黏黏滑滑的液体，还散发着和之前河水一样淡淡的怪味。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89219] = { id = 89219, Voice = '', Text = '齿轮转动的声音似乎有些刺耳不和谐。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89218] = { id = 89218, Voice = '', Text = '有些时钟的零件散落在地，除此之外似乎没有什么异常。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89217] = { id = 89217, Voice = '', Text = '但还是请你去钟楼里调查一下。', Speaker = 6469, Option = '交给我吧', Action = _EmptyTable,}, 
	[89216] = { id = 89216, Voice = '', Text = '很好，虽然目前我们还没查出影响黄金时钟的具体原因。', Speaker = 6469, Option = '', Action = _EmptyTable,}, 
	[89215] = { id = 89215, Voice = '', Text = '你有实力与我们【时钟守护者】一同前去调查此事吗？', Speaker = 6469, Option = '有！', Action = _EmptyTable,}, 
	[89214] = { id = 89214, Voice = '', Text = '我绝不允许有任何情况影响到黄金时钟的力量。', Speaker = 6469, Option = '', Action = _EmptyTable,}, 
	[89213] = { id = 89213, Voice = '', Text = '我们的使命就是守护这座黄金时钟。', Speaker = 6469, Option = '是这样', Action = _EmptyTable,}, 
	[89212] = { id = 89212, Voice = '', Text = '所以黄金时钟是稳固钟楼和整个艾尔帕兰城的核心，至关重要。', Speaker = 6469, Option = '', Action = _EmptyTable,}, 
	[89211] = { id = 89211, Voice = '', Text = '是大贤者巴特蒙德的出现，他调试了黄金时钟，才好不容易平息了钟楼内混乱的时间规则。', Speaker = 6469, Option = '原来如此', Action = _EmptyTable,}, 
	[89210] = { id = 89210, Voice = '', Text = '很多年以前钟楼内发生过两次异变，使空间变得扭曲可怕。', Speaker = 6469, Option = '', Action = _EmptyTable,}, 
	[89209] = { id = 89209, Voice = '', Text = '是的，这座黄金时钟是带给艾尔帕兰人们幸福的时钟。', Speaker = 6469, Option = '怎么说？', Action = _EmptyTable,}, 
	[89208] = { id = 89208, Voice = '', Text = '这件事，让我想到了最近黄金时钟的力量非常不稳定！', Speaker = 6469, Option = '黄金时钟？', Action = _EmptyTable,}, 
	[89207] = { id = 89207, Voice = '', Text = '但这并不是问题的源头。', Speaker = 6469, Option = '哦？', Action = _EmptyTable,}, 
	[89206] = { id = 89206, Voice = '', Text = '我们已经派人去净化河水了，别担心。', Speaker = 6469, Option = '这就好', Action = _EmptyTable,}, 
	[89205] = { id = 89205, Voice = '', Text = '冒险者！谢谢你及时来找我说明状况！', Speaker = 6469, Option = '', Action = _EmptyTable,}, 
	[89204] = { id = 89204, Voice = '', Text = '那就拜托你了。', Speaker = 6580, Option = '', Action = _EmptyTable,}, 
	[89203] = { id = 89203, Voice = '', Text = '我认为应该尽快让辛迪知道这件事。', Speaker = 6580, Option = '是的', Action = _EmptyTable,}, 
	[89202] = { id = 89202, Voice = '', Text = '这样，我的好友辛迪是【时钟守护者】教团的一员。', Speaker = 6580, Option = '', Action = _EmptyTable,}, 
	[89201] = { id = 89201, Voice = '', Text = '这件事事关重大。', Speaker = 6580, Option = '', Action = _EmptyTable,}, 
	[89200] = { id = 89200, Voice = '', Text = '这方面我不是专家，但我肯定，是钟楼里的魔物搞得鬼！！', Speaker = 6580, Option = '那怎么办？', Action = _EmptyTable,}, 
	[89199] = { id = 89199, Voice = '', Text = '那块可是有钟楼的排水口，我就说这事情不简单！', Speaker = 6580, Option = '的确', Action = _EmptyTable,}, 
	[89198] = { id = 89198, Voice = '', Text = '我刚才喝了西边取的水！', Speaker = 6580, Option = '这样', Action = _EmptyTable,}, 
	[89197] = { id = 89197, Voice = '', Text = '什么，我刚才中毒差点失控？', Speaker = 6580, Option = '是的', Action = _EmptyTable,}, 
	[89196] = { id = 89196, Voice = '', Text = '刚才发生了什么事！！', Speaker = 6580, Option = '', Action = _EmptyTable,}, 
	[89195] = { id = 89195, Voice = '', Text = '只见塔哈在尝了些河水后，两眼发直，神志开始有些不清晰。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89194] = { id = 89194, Voice = '', Text = '嘭！！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89193] = { id = 89193, Voice = '', Text = '让我看看。', Speaker = 6580, Option = '', Action = _EmptyTable,}, 
	[89192] = { id = 89192, Voice = '', Text = '你可真是神速，这城市可是不小啊。', Speaker = 6580, Option = '', Action = _EmptyTable,}, 
	[89191] = { id = 89191, Voice = '', Text = '此处取的水似乎散发着一股淡淡的怪味。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[89190] = { id = 89190, Voice = '', Text = '是的，你能帮我绕着城市取点水的样本来吗？我好调查调查。', Speaker = 6580, Option = '没问题', Action = _EmptyTable,}, 
	[89189] = { id = 89189, Voice = '', Text = '于是我们猜测，是城里的运河水出了问题。', Speaker = 6580, Option = '运河水？', Action = _EmptyTable,}, 
	[89188] = { id = 89188, Voice = '', Text = '我和几个餐馆的老板交流过，他们店里也出现了同样的问题。', Speaker = 6580, Option = '哦？', Action = _EmptyTable,}, 
	[89187] = { id = 89187, Voice = '', Text = '后来我发现事情并没有这么简单。', Speaker = 6580, Option = '', Action = _EmptyTable,}, 
	[89186] = { id = 89186, Voice = '', Text = '一开始，我以为是自己的技术出了问题。', Speaker = 6580, Option = '后来呢？', Action = _EmptyTable,}, 
	[89185] = { id = 89185, Voice = '', Text = '最近我做的料理客人总是吃坏肚子……', Speaker = 6580, Option = '', Emoji = 18, Action = _EmptyTable,}, 
	[89184] = { id = 89184, Voice = '', Text = '太好了兄弟，虽然我不认识你，但我感受到了你如料理一般的热情。', Speaker = 6580, Option = '', Action = _EmptyTable,}, 
	[89183] = { id = 89183, Voice = '', Text = '别这么冷淡嘛，这事儿可事关全城。', Speaker = 6580, Option = '', Action = _EmptyTable,}, 
	[89182] = { id = 89182, Voice = '', Text = '救星！你愿意听我说嘛？', Speaker = 6580, Option = '{愿意,2},{不愿意,3}', Action = _EmptyTable,}, 
	[89181] = { id = 89181, Voice = '', Text = '真是太愁人了。', Speaker = 6580, Option = '发生什么事？', Action = _EmptyTable,}, 
	[88235] = { id = 88235, Voice = '', Text = '接下来按顺序使用木灵、水灵和土灵，相信就能见到消失已久的森灵王了。', Speaker = 6526, Option = '', Action = _EmptyTable,}, 
	[88234] = { id = 88234, Voice = '', Text = '做得不错，看来你已经得到了守护者们的认可。', Speaker = 6526, Option = '', Action = _EmptyTable,}, 
	[88203] = { id = 88203, Voice = '', Text = '这“森灵之心”你就带着去冒险吧，也许在冒险的途中，你会找到你想要的答案。', Speaker = 6526, Option = '', Action = _EmptyTable,}, 
	[88202] = { id = 88202, Voice = '', Text = '总之谢谢你的帮助。我调查出了北门异变的原因，扎克雷这次可没法跟我比了！', Speaker = 6526, Option = '', Action = _EmptyTable,}, 
	[88201] = { id = 88201, Voice = '', Text = '也许某天森灵一族也不再需要“森灵之心”来维持这片土地的生态平衡了…', Speaker = 6526, Option = '', Action = _EmptyTable,}, 
	[88200] = { id = 88200, Voice = '', Text = '不用担心这里，我相信北门在国王陛下的命令之下会越来越好。', Speaker = 6526, Option = '', Action = _EmptyTable,}, 
	[88199] = { id = 88199, Voice = '', Text = '我会向国王陛下禀告，要求治理北门区域的污染，但是关于“森灵之心”，我实在是帮不上什么忙…', Speaker = 6526, Option = '', Action = _EmptyTable,}, 
	[88198] = { id = 88198, Voice = '', Text = '原来是这样…一切的一切，都是我们人类咎由自取…', Speaker = 6526, Option = '', Action = _EmptyTable,}, 
	[88183] = { id = 88183, Voice = '', Text = '是否进入秘境？', Speaker = 6535, Option = '{开始传送,2},{再等等,3}', Action = _EmptyTable,}, 
	[88182] = { id = 88182, Voice = '', Text = '一个光芒闪耀的传送阵出现了……', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[88181] = { id = 88181, Voice = '', Text = '能不能拿到水灵和木灵就看你的本事了。好了人类，你可以离开这里了。', Speaker = 6528, Option = '', Action = _EmptyTable,}, 
	[88180] = { id = 88180, Voice = '', Text = '把这块土灵带走吧，你要知道，想要见我王，光有土灵是不够的。', Speaker = 6528, Option = '', Action = _EmptyTable,}, 
	[88179] = { id = 88179, Voice = '', Text = '既然你找到了真正的土灵，那么就给你一个见我王的机会。', Speaker = 6528, Option = '', Action = _EmptyTable,}, 
	[88176] = { id = 88176, Voice = '', Text = '所以，你准备好了吗？', Speaker = 6528, Option = '{准备好了,4},{再等等,5}', Action = _EmptyTable,}, 
	[88175] = { id = 88175, Voice = '', Text = '也许里面会存在很多土灵，而你要找出其中真正的土灵，并且把它带出来。', Speaker = 6528, Option = '', Action = _EmptyTable,}, 
	[88174] = { id = 88174, Voice = '', Text = '我会把你送到土之秘境，去寻找土灵。需要提醒你的是，土之秘境现在已经大不一样了。', Speaker = 6528, Option = '', Action = _EmptyTable,}, 
	[88173] = { id = 88173, Voice = '', Text = '看来你们还没有清醒地意识到你们到底做了什么…不过没关系，我王会解答你的问题。', Speaker = 6528, Option = '', Action = _EmptyTable,}, 
	[88172] = { id = 88172, Voice = '', Text = '这片土地已经成为了现在这幅模样，再这么下去…人类？你们终于意识到你们做了什么了吗？', Speaker = 6528, Option = '森灵王', Action = _EmptyTable,}, 
	[88171] = { id = 88171, Voice = '', Text = '浓郁的土之气息散发，在土堆上闪烁着氤氲光辉……', Speaker = 6533, Option = '{捡起,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[88170] = { id = 88170, Voice = '', Text = '拿着它去见我们的王吧，事情总要解决的。', Speaker = 6529, Option = '', Action = _EmptyTable,}, 
	[88169] = { id = 88169, Voice = '', Text = '这是我守护的水灵，如今它已经黯淡无光，再也不复往日的活力了。', Speaker = 6529, Option = '', Action = _EmptyTable,}, 
	[88168] = { id = 88168, Voice = '', Text = '不过现在知道也不算晚，我真想看看，你要怎么面对我王的诘问。', Speaker = 6529, Option = '', Action = _EmptyTable,}, 
	[88167] = { id = 88167, Voice = '', Text = '可能你们还不清楚，这水质问题就是你们人类导致的吧。', Speaker = 6529, Option = '', Action = _EmptyTable,}, 
	[88166] = { id = 88166, Voice = '', Text = '看来你已经明白了什么。', Speaker = 6529, Option = '水质问题', Action = _EmptyTable,}, 
	[88165] = { id = 88165, Voice = '', Text = '水中的芦苇干枯发黄，河水中难道有些什么？', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[88164] = { id = 88164, Voice = '', Text = '河水虽然浑浊，但是还是能够看清，令人疑惑的是，为什么河里没有鱼呢？', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[88163] = { id = 88163, Voice = '', Text = '河水有些浑浊，但是似乎没有什么异常……', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[88162] = { id = 88162, Voice = '', Text = '这样，在我王诘问你的时候，你还不至于一无所知。', Speaker = 6529, Option = '', Action = _EmptyTable,}, 
	[88161] = { id = 88161, Voice = '', Text = '你想见我们的王？没问题，但是你得先去河边看看，看看那些纯净的河水如今变成了什么样子。', Speaker = 6529, Option = '', Action = _EmptyTable,}, 
	[88160] = { id = 88160, Voice = '', Text = '人类？虽然我不讨厌人类，但是你们的所作所为，我也谈不上喜欢。没事的话，不要来打扰我！', Speaker = 6529, Option = '森灵王', Action = _EmptyTable,}, 
	[88159] = { id = 88159, Voice = '', Text = '浓郁的水之气息散发，在河边上闪烁着氤氲光辉……', Speaker = 6532, Option = '{捡起,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[88158] = { id = 88158, Voice = '', Text = '那就是木灵、水灵和土灵，木灵我可以给你，但是其他的只能你自己想办法了！', Speaker = 6527, Option = '', Action = _EmptyTable,}, 
	[88157] = { id = 88157, Voice = '', Text = '嗯？没想到你真的能取回木灵。想见我们的王，你必须拥有三个物品。', Speaker = 6527, Option = '', Action = _EmptyTable,}, 
	[88153] = { id = 88153, Voice = '', Text = '发生了什么？好，既然你想知道，就让你看看你们人类所做的好事！你确定要看？', Speaker = 6527, Option = '{确定,4},{再等等,5}', Action = _EmptyTable,}, 
	[88152] = { id = 88152, Voice = '', Text = '想寻找我们的王？我们的王是不会见你们人类的！我们曾经天真地以为，人类与我们森灵能够和平共处，但是我们错了！', Speaker = 6527, Option = '发生了什么？', Action = _EmptyTable,}, 
	[88151] = { id = 88151, Voice = '', Text = '肮脏的人类！你竟然还敢来这里，不怕那些异变的魔物把你们撕碎吗？', Speaker = 6527, Option = '森灵王', Action = _EmptyTable,}, 
	[88150] = { id = 88150, Voice = '', Text = '浓郁的木之气息散发，在草地上闪烁着氤氲光辉……', Speaker = 6531, Option = '{捡起,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[88149] = { id = 88149, Voice = '', Text = '土灵守护者则是喜欢呆在阴暗的角落，你可以去这里找到他。', Speaker = 6526, Option = '', Action = _EmptyTable,}, 
	[88148] = { id = 88148, Voice = '', Text = '水灵守护者喜欢水，他应该在北部河流沿岸。', Speaker = 6526, Option = '', Action = _EmptyTable,}, 
	[88147] = { id = 88147, Voice = '', Text = '木灵守护者喜欢树木密集的地方，你可以去树林中找到木灵守护者。', Speaker = 6526, Option = '', Action = _EmptyTable,}, 
	[88146] = { id = 88146, Voice = '', Text = '要解决问题一定要找到他们，可惜我不是那些凶狠魔物的对手…只能寄希望于你们勇敢的冒险者了！', Speaker = 6526, Option = '', Action = _EmptyTable,}, 
	[88145] = { id = 88145, Voice = '', Text = '不仅仅是森灵王，就连木灵水灵土灵三位守护者都见不到了。', Speaker = 6526, Option = '', Action = _EmptyTable,}, 
	[88144] = { id = 88144, Voice = '', Text = '根据我的研究，这次事件恐怕与森灵一族有关。早年，我曾经见过他们的王，但是现在，我已经失去森灵王的消息很久了。', Speaker = 6526, Option = '', Action = _EmptyTable,}, 
	[88143] = { id = 88143, Voice = '', Text = '但是这样下去不是办法，国王陛下派我研究这里的生态，就是为了解决这个问题。', Speaker = 6526, Option = '', Action = _EmptyTable,}, 
	[88142] = { id = 88142, Voice = '', Text = '自从北边的蝗虫和苍蝇异变之后，我们只能龟缩在这片区域里——因为有城卫军的守护。', Speaker = 6526, Option = '', Action = _EmptyTable,}, 
	[88141] = { id = 88141, Voice = '', Text = '我研究普隆德拉北部生态已经很久了，这里很久以前还是一片平静的区域。', Speaker = 6526, Option = '', Action = _EmptyTable,}, 
	[88118] = { id = 88118, Voice = '', Text = '你的身上并没有变傻光线枪，不要浪费我的时间，快去找一把。', Speaker = 6485, Option = '', Action = _EmptyTable,}, 
	[88117] = { id = 88117, Voice = '', Text = '似乎选错了，你还是再试一次吧！', Speaker = 6484, Option = '', Action = _EmptyTable,}, 
	[88092] = { id = 88092, Voice = '', Text = '有什么需要找我们帮忙的，准备好本枯节就可以了喵~~', Speaker = 6492, Option = '', Action = _EmptyTable,}, 
	[88091] = { id = 88091, Voice = '', Text = '喵喵，我们两就先行告退了~~', Speaker = 6492, Option = '', Action = _EmptyTable,}, 
	[88090] = { id = 88090, Voice = '', Text = '好了，说好的本枯节呢。冒险者，你可不许赖皮喵~', Speaker = 6484, Option = '', Emoji = 28, Action = _EmptyTable,}, 
	[88089] = { id = 88089, Voice = '', Text = '我的雇主芙莎肯定担心死了喵。', Speaker = 6492, Option = '是的', Action = _EmptyTable,}, 
	[88088] = { id = 88088, Voice = '', Text = '该死的喵喵大盗，抢走了我所有的东西还把我关在这种鬼地方喵！！', Speaker = 6492, Option = '是啊', Action = _EmptyTable,}, 
	[88087] = { id = 88087, Voice = '', Text = '真是太感谢你了喵！！', Speaker = 6492, Option = '没什么', Action = _EmptyTable,}, 
	[88086] = { id = 88086, Voice = '', Text = '一个毛茸茸的身影从密室中蹦了出来。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[88085] = { id = 88085, Voice = '', Text = '密室入口发出耀眼的红光，裂开了一道缝隙。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[88084] = { id = 88084, Voice = '', Text = '是否打开密室？', Speaker = 6491, Option = '打开', Action = _EmptyTable,}, 
	[88071] = { id = 88071, Voice = '', Text = '放轻松，我体内的表盘上也会提示哪里损坏的。准备好了吗？', Speaker = 6485, Option = '准备好了', Action = _EmptyTable,}, 
	[88070] = { id = 88070, Voice = '', Text = '我也不清楚具体哪里损坏了，还得靠你的聪明才智自己发现并维修。', Speaker = 6485, Option = '明白了', Action = _EmptyTable,}, 
	[88069] = { id = 88069, Voice = '', Text = '我体内有个大钟盘，请按照对的顺序在对的位置放置那些齿轮。', Speaker = 6485, Option = '这么困难', Action = _EmptyTable,}, 
	[88068] = { id = 88068, Voice = '', Text = '接下来我要将你变小，你将进入到我体内修理我破损的零件。', Speaker = 6485, Option = '怎么修？', Action = _EmptyTable,}, 
	[88067] = { id = 88067, Voice = '', Text = '那就弄一把过来，动作快！', Speaker = 6485, Option = '', Action = _EmptyTable,}, 
	[88066] = { id = 88066, Voice = '', Text = '你身上有带变傻光线枪吗？', Speaker = 6485, Option = '{给你,2},{没有,3}', Action = _EmptyTable,}, 
	[88065] = { id = 88065, Voice = '', Text = '看你的样子也不像是个钟表匠。你就试试吧。', Speaker = 6485, Option = '', Action = _EmptyTable,}, 
	[88064] = { id = 88064, Voice = '', Text = '嗯，有了这些，我应该能被修好。', Speaker = 6485, Option = '应该？', Action = _EmptyTable,}, 
	[88063] = { id = 88063, Voice = '', Text = '太好了，这可恶的喵喵大盗也该有人给他点颜色看看。', Speaker = 6485, Option = '', Action = _EmptyTable,}, 
	[88062] = { id = 88062, Voice = '', Text = '对，我相信你一定能识破他的障眼法！', Speaker = 6484, Option = '', Action = _EmptyTable,}, 
	[88061] = { id = 88061, Voice = '', Text = '所以他常年拖着条丑陋的狐狸尾巴！！喵哈哈哈', Speaker = 6484, Option = '狐狸尾巴？', Action = _EmptyTable,}, 
	[88060] = { id = 88060, Voice = '', Text = '别慌喵，我听说喵喵大盗是与我们不共戴天的狐狸变的！', Speaker = 6484, Option = '哦？', Action = _EmptyTable,}, 
	[88059] = { id = 88059, Voice = '', Text = '我刚才在这钟楼之中就看到三四个带着草笠的刺客！！', Speaker = 6484, Option = '那怎么办？', Action = _EmptyTable,}, 
	[88058] = { id = 88058, Voice = '', Text = '这家伙狡猾得很！能使用分身来分散敌人的注意力喵。', Speaker = 6484, Option = '是嘛', Action = _EmptyTable,}, 
	[88057] = { id = 88057, Voice = '', Text = '他一定是看罗杰呆呆的喵，比较好骗。', Speaker = 6484, Option = '也许', Action = _EmptyTable,}, 
	[88056] = { id = 88056, Voice = '', Text = '可恶的家伙，专找我们佣兵猫下手喵！', Speaker = 6484, Option = '呃', Action = _EmptyTable,}, 
	[88055] = { id = 88055, Voice = '', Text = '喵！！我想起来了，这家伙很有可能是艾尔帕兰城有名的喵喵大盗。', Speaker = 6484, Option = '喵喵大盗？', Action = _EmptyTable,}, 
	[88054] = { id = 88054, Voice = '', Text = '啊！我见到的也是那个人。', Speaker = 6484, Option = '', Action = _EmptyTable,}, 
	[88053] = { id = 88053, Voice = '', Text = '我只记得他是名戴着顶草笠的刺客。', Speaker = 6485, Option = '', Action = _EmptyTable,}, 
	[88052] = { id = 88052, Voice = '', Text = '被那个该死的人类拿去了。', Speaker = 6485, Option = '那个人类？', Action = _EmptyTable,}, 
	[88051] = { id = 88051, Voice = '', Text = '是我的太阳机械之心！', Speaker = 6485, Option = '在哪里？', Action = _EmptyTable,}, 
	[88050] = { id = 88050, Voice = '', Text = '还不够，还差最后一样关键物品。', Speaker = 6485, Option = '是什么？', Action = _EmptyTable,}, 
	[88049] = { id = 88049, Voice = '', Text = '喵，我也找了一些，这样应该就够了。', Speaker = 6484, Option = '', Action = _EmptyTable,}, 
	[88048] = { id = 88048, Voice = '', Text = '看来我们得分头帮太阳时钟找回被拆掉的零件喵。', Speaker = 6484, Option = '', Action = _EmptyTable,}, 
	[88047] = { id = 88047, Voice = '', Text = '咳，这可在钟楼的钟怪身上多的是。', Speaker = 6485, Option = '明白了', Action = _EmptyTable,}, 
	[88046] = { id = 88046, Voice = '', Text = '你可以在钟塔里弄些时钟零件给我。', Speaker = 6485, Option = '如何弄？', Action = _EmptyTable,}, 
	[88045] = { id = 88045, Voice = '', Text = '他拿走了我身上的零件，所以我也打不开密室门。', Speaker = 6485, Option = '那怎么办？', Action = _EmptyTable,}, 
	[88044] = { id = 88044, Voice = '', Text = '但是我被那个该死的家伙做了手脚，运转不起来了。', Speaker = 6485, Option = '', Action = _EmptyTable,}, 
	[88043] = { id = 88043, Voice = '', Text = '相信我孩子，我很想帮你。', Speaker = 6485, Option = '', Action = _EmptyTable,}, 
	[88042] = { id = 88042, Voice = '', Text = '看来我们得拜托太阳时钟把密室门打开。', Speaker = 6484, Option = '', Action = _EmptyTable,}, 
	[88041] = { id = 88041, Voice = '', Text = '没错！', Speaker = 6485, Option = '', Action = _EmptyTable,}, 
	[88040] = { id = 88040, Voice = '', Text = '那只猫！！！是不是只白色的佣兵猫！？', Speaker = 6484, Option = '', Action = _EmptyTable,}, 
	[88039] = { id = 88039, Voice = '', Text = '有个该死的人类，关了一只猫在我的密室里！', Speaker = 6485, Option = '', Action = _EmptyTable,}, 
	[88038] = { id = 88038, Voice = '', Text = '咳咳咳，我是太阳密室的主人，太阳时钟！', Speaker = 6485, Option = '太阳密室？', Action = _EmptyTable,}, 
	[88037] = { id = 88037, Voice = '', Text = '喵喵，这口钟又是怎么回事？', Speaker = 6484, Option = '', Action = _EmptyTable,}, 
	[88036] = { id = 88036, Voice = '', Text = '太阳钟秒针：指向', Speaker = 6483, Option = '{Ⅲ,0},{Ⅵ,0},{Ⅸ,0},{Ⅻ,5}', Action = _EmptyTable,}, 
	[88035] = { id = 88035, Voice = '', Text = '太阳钟秒针：指向', Speaker = 6483, Option = '{Ⅲ,0},{Ⅵ,0},{Ⅸ,0},{Ⅻ,0}', Action = _EmptyTable,}, 
	[88034] = { id = 88034, Voice = '', Text = '太阳钟分针：指向', Speaker = 6482, Option = '{Ⅲ,0},{Ⅵ,4},{Ⅸ,0},{Ⅻ,0}', Action = _EmptyTable,}, 
	[88033] = { id = 88033, Voice = '', Text = '太阳钟秒针：指向', Speaker = 6483, Option = '{Ⅲ,0},{Ⅵ,0},{Ⅸ,0},{Ⅻ,0}', Action = _EmptyTable,}, 
	[88032] = { id = 88032, Voice = '', Text = '太阳钟分针：指向', Speaker = 6482, Option = '{Ⅲ,0},{Ⅵ,0},{Ⅸ,0},{Ⅻ,0}', Action = _EmptyTable,}, 
	[88031] = { id = 88031, Voice = '', Text = '太阳钟时针：指向', Speaker = 6481, Option = '{Ⅲ,0},{Ⅵ,3},{Ⅸ,0},{Ⅻ,0}', Action = _EmptyTable,}, 
	[88030] = { id = 88030, Voice = '', Text = '小钻话音刚落，从小钻背后升起三个古怪的机关。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[88029] = { id = 88029, Voice = '', Text = '这附近一定还有什么机关喵。', Speaker = 6484, Option = '', Action = _EmptyTable,}, 
	[88027] = { id = 88027, Voice = '', Text = '喵呜，那可就说明罗杰就在附近。', Speaker = 6484, Option = '', Action = _EmptyTable,}, 
	[88026] = { id = 88026, Voice = '', Text = '这是罗杰的羽毛帽！！', Speaker = 6484, Option = '', Action = _EmptyTable,}, 
	[88025] = { id = 88025, Voice = '', Text = '这不是芙莎提到的罗杰中意的羽毛帽嘛！', Speaker = 6480, Option = '{查看,2},{放弃,3}', Action = _EmptyTable,}, 
	[88024] = { id = 88024, Voice = '', Text = '我可以帮你一块找找那个什么太阳时钟在哪里。', Speaker = 6479, Option = '', Action = _EmptyTable,}, 
	[88023] = { id = 88023, Voice = '', Text = '我转告完罗杰，就去忙自己的事了。', Speaker = 6479, Option = '', Action = _EmptyTable,}, 
	[88022] = { id = 88022, Voice = '', Text = '我也不知道那是什么意思喵，可能…二层有个时钟叫太阳时钟？', Speaker = 6479, Option = '好吧', Action = _EmptyTable,}, 
	[88021] = { id = 88021, Voice = '', Text = '他说有工作要交给罗杰，让我转告罗杰下午六点三十分的时候在钟楼二层的太阳时钟下见面。', Speaker = 6479, Option = '太阳时钟？', Action = _EmptyTable,}, 
	[88020] = { id = 88020, Voice = '', Text = '三天前的早上我进塔前，我遇到一位先生喵。', Speaker = 6479, Option = '', Action = _EmptyTable,}, 
	[88019] = { id = 88019, Voice = '', Text = '我的确认识那家伙，在喵喵岛的时候那家伙可没有我做事勤快。', Speaker = 6479, Option = '说重点！', Action = _EmptyTable,}, 
	[88018] = { id = 88018, Voice = '', Text = '太好了！！喵喵！！', Speaker = 6479, Option = '快说', Action = _EmptyTable,}, 
	[88017] = { id = 88017, Voice = '', Text = '真的吗！？只要我帮你找到罗杰，你就把这些本枯节给我喵！？', Speaker = 6479, Option = '是的', Action = _EmptyTable,}, 
	[88016] = { id = 88016, Voice = '', Text = '嗯~好香的本枯节！！', Speaker = 6479, Option = '', Action = _EmptyTable,}, 
	[88015] = { id = 88015, Voice = '', Text = '要特别注意的是，本枯节也是钟楼里的一些魔物非常喜欢的食物，所以要多加小心。', Speaker = 6478, Option = '', Action = _EmptyTable,}, 
	[88014] = { id = 88014, Voice = '', Text = '那就拜托你去钟楼探查一下了，说不定罗杰闻到鱼的味道就会自己跑出来。', Speaker = 6478, Option = '好', Action = _EmptyTable,}, 
	[88013] = { id = 88013, Voice = '', Text = '它平时最喜欢吃的就是本枯节，带上这篮本枯节。', Speaker = 6478, Option = '接过', Action = _EmptyTable,}, 
	[88012] = { id = 88012, Voice = '', Text = '罗杰一直戴着一顶它中意的羽毛帽，所以非常好辨认。', Speaker = 6478, Option = '', Action = _EmptyTable,}, 
	[88011] = { id = 88011, Voice = '', Text = '接下来只要找到罗杰就好。', Speaker = 6478, Option = '是啊', Action = _EmptyTable,}, 
	[88010] = { id = 88010, Voice = '', Text = '太好了，这下总算能给公司一个交代。', Speaker = 6478, Option = '', Action = _EmptyTable,}, 
	[88009] = { id = 88009, Voice = '', Text = '那些钟楼里的邪灵魔导士，可是带着硼砂水片刻不离。', Speaker = 6478, Option = '', Action = _EmptyTable,}, 
	[88008] = { id = 88008, Voice = '', Text = '是的，公司需要这些硼砂水来研究炼金术，当然报酬少不了您的。', Speaker = 6478, Option = '', Action = _EmptyTable,}, 
	[88007] = { id = 88007, Voice = '', Text = '你能代替罗杰进钟楼收集些硼砂水吗？', Speaker = 6478, Option = '硼砂水？', Action = _EmptyTable,}, 
	[88006] = { id = 88006, Voice = '', Text = '现在公司的物品也没收集，罗杰也不见了，我实在是没办法向上面交差。', Speaker = 6478, Option = '可怜', Action = _EmptyTable,}, 
	[88005] = { id = 88005, Voice = '', Text = '这只佣兵猫叫作罗杰，非常温顺。', Speaker = 6478, Option = '原来如此', Action = _EmptyTable,}, 
	[88004] = { id = 88004, Voice = '', Text = '之后就再也没见过它的踪迹。', Speaker = 6478, Option = '这样啊', Action = _EmptyTable,}, 
	[88003] = { id = 88003, Voice = '', Text = '是的，三天前我拜托它去钟楼内寻找公司所需的物品。', Speaker = 6478, Option = '', Action = _EmptyTable,}, 
	[88002] = { id = 88002, Voice = '', Text = '我是卡普拉的工作人员，最近公司雇佣的一只佣兵猫失踪了。', Speaker = 6478, Option = '失踪？', Action = _EmptyTable,}, 
	[88001] = { id = 88001, Voice = '', Text = '现在的人心真是淡薄了许多啊。', Speaker = 6478, Option = '', Action = _EmptyTable,}, 
	[88000] = { id = 88000, Voice = '', Text = '冒险者，愿意听我这可怜的弱女子说几句话吗？', Speaker = 6478, Option = '{愿意,2},{没有时间,3}', Action = _EmptyTable,}, 
	[87827] = { id = 87827, Voice = '', Text = '很抱歉，表芯炼化炉无法正常启动。', Speaker = 6431, Option = '', Action = _EmptyTable,}, 
	[87826] = { id = 87826, Voice = '', Text = '七七姐姐说过我们还会再见面的，我会等她回来！', Speaker = 6418, Option = '', Action = _EmptyTable,}, 
	[87744] = { id = 87744, Voice = '', Text = '会的，在不久的将来，小鬼。', Speaker = 6417, Option = '', Emoji = 17, Action = _EmptyTable,}, 
	[87743] = { id = 87743, Voice = '', Text = '七七露出了孩子一般的微笑。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[87742] = { id = 87742, Voice = '', Text = '七七姐姐，我们还会再相见吗？', Speaker = 6418, Option = '', Action = _EmptyTable,}, 
	[87741] = { id = 87741, Voice = '', Text = '七七的身上发出耀眼的光芒，身体也在渐渐变透明，看来她就要回到未来去了。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[87740] = { id = 87740, Voice = '', Text = '你不用懂，反正现在魔杖也没了，我倒是觉得一身轻松。', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
	[87739] = { id = 87739, Voice = '', Text = '怎么是你谢我？不是应该我谢你？我被你搞糊涂了……', Speaker = 6418, Option = '', Emoji = 19, Action = _EmptyTable,}, 
	[87738] = { id = 87738, Voice = '', Text = '呵呵，小怪物，谢谢你。', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
	[87737] = { id = 87737, Voice = '', Text = '可是我舍不得你。', Speaker = 6418, Option = '', Action = _EmptyTable,}, 
	[87736] = { id = 87736, Voice = '', Text = '可是什么，臭小鬼。', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
	[87735] = { id = 87735, Voice = '', Text = '可是…', Speaker = 6418, Option = '', Action = _EmptyTable,}, 
	[87734] = { id = 87734, Voice = '', Text = '是时候说再见了。', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
	[87733] = { id = 87733, Voice = '', Text = '对，不是和你们说我是从十年后的未来到这里的吗。', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
	[87732] = { id = 87732, Voice = '', Text = '我的魔杖彻底销毁了，我也不得不回到我本来的时空去了。', Speaker = 6417, Option = '本来时空？', Action = _EmptyTable,}, 
	[87731] = { id = 87731, Voice = '', Text = '好了，裂隙合上了。', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
	[87730] = { id = 87730, Voice = '', Text = '快，站到我身边来。', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
	[87729] = { id = 87729, Voice = '', Text = '不说废话，我也需要借助你的力量。', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
	[87728] = { id = 87728, Voice = '', Text = '没错，魔杖会因为修复这个裂缝而被彻底摧毁。', Speaker = 6417, Option = '什么！', Action = _EmptyTable,}, 
	[87727] = { id = 87727, Voice = '', Text = '但这个裂口现在太大了，修复这个裂缝就意味这要注入时空魔杖的全部力量。', Speaker = 6417, Option = '这就意味着', Action = _EmptyTable,}, 
	[87726] = { id = 87726, Voice = '', Text = '我之前说过，这是时空魔杖制造出来的裂隙，普通方法是修复不好的，得有时空魔杖才能完全修复。', Speaker = 6417, Option = '是的', Action = _EmptyTable,}, 
	[87725] = { id = 87725, Voice = '', Text = '别拍我马屁，我们还有正事没做完。', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
	[87724] = { id = 87724, Voice = '', Text = '大家都说，魔女七七可是个坏魔女。我不这么认为，我觉得她是天底下最好的魔女。', Speaker = 6418, Option = '', Action = _EmptyTable,}, 
	[87723] = { id = 87723, Voice = '', Text = '七七姐姐还是来救我了。', Speaker = 6418, Option = '是啊', Action = _EmptyTable,}, 
