	[87709] = { id = 87709, Voice = '', Text = '我不会去救他的，要去救你救吧，再见了哈哈哈哈！', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
	[87708] = { id = 87708, Voice = '', Text = '所以异时空的魔物将他抓走，正好帮我省事了。', Speaker = 6417, Option = '', Emoji = 5, Action = _EmptyTable,}, 
	[87707] = { id = 87707, Voice = '', Text = '随便你怎么说，我本来就是穿越时空回来想当他还是个孩子的时候，除掉这个后患。', Speaker = 6417, Option = '再次谴责', Action = _EmptyTable,}, 
	[87706] = { id = 87706, Voice = '', Text = '这讨厌的家伙将会成为打败我和惩治我的人！', Speaker = 6417, Option = '你活该', Action = _EmptyTable,}, 
	[87705] = { id = 87705, Voice = '', Text = '你也看到了他从小就表现出来的魔法天赋,。', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
