	[87594] = { id = 87594, Voice = '', Text = '噢，来嘛，你这坏姐姐，来吧，漂亮姐姐，来握个手。', Speaker = 6418, Option = '', Emoji = 9, Action = _EmptyTable,}, 
	[87593] = { id = 87593, Voice = '', Text = '我不是你姐姐。', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
	[87592] = { id = 87592, Voice = '', Text = '来吧，怎么样，姐姐。', Speaker = 6418, Option = '', Emoji = 9, Action = _EmptyTable,}, 
	[87591] = { id = 87591, Voice = '', Text = '我们现在可是一条船上的人了。', Speaker = 6418, Option = '', Action = _EmptyTable,}, 
	[87590] = { id = 87590, Voice = '', Text = '你想让我...帮你？', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
	[87589] = { id = 87589, Voice = '', Text = '你们两帮我修好我爷爷设计的古钟，一旦钟箱重新运转，就能把卡住的魔杖弹出来。', Speaker = 6418, Option = '', Action = _EmptyTable,}, 
	[87588] = { id = 87588, Voice = '', Text = '你看这样行不行。', Speaker = 6418, Option = '如何', Action = _EmptyTable,}, 
	[87587] = { id = 87587, Voice = '', Text = '七七姐姐你想要你的魔杖，对吧？而我想修好我爷爷的钟。', Speaker = 6418, Option = '', Action = _EmptyTable,}, 
	[87586] = { id = 87586, Voice = '', Text = '好了好了，我会负责，我知道我搞砸了。我只是想修好我爷爷的时钟…', Speaker = 6418, Option = '', Action = _EmptyTable,}, 
	[87585] = { id = 87585, Voice = '', Text = '说到底，这一切都是这个小贼偷了我的魔杖造成的！你不觉得你该负点责任吗？你这个肮脏的，可怜的…', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
	[87584] = { id = 87584, Voice = '', Text = '别做梦了，这可是时空魔杖制造出来的裂隙，普通方法是修复不好的，得有时空魔杖才能完全修复。', Speaker = 6417, Option = '那怎么办', Action = _EmptyTable,}, 
	[87583] = { id = 87583, Voice = '', Text = '更何况现在时空魔杖撞击产生的裂隙还没有被修复，只是一时的合上了。', Speaker = 6417, Option = '我能修', Action = _EmptyTable,}, 
	[87582] = { id = 87582, Voice = '', Text = '闭嘴，我不需要你伪善的安慰！', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
	[87581] = { id = 87581, Voice = '', Text = '这下完了，我的魔杖牢牢地插进了这口破钟里。', Speaker = 6417, Option = '别灰心', Action = _EmptyTable,}, 
	[87580] = { id = 87580, Voice = '', Text = '你看这个小怪物干的好事！！他未来也跟现在一样讨厌，一点都没变化。', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
	[87578] = { id = 87578, Voice = '', Text = '由于魔杖与古钟激烈的摩擦撞出一道道火花，居然打开了时空裂隙。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[87577] = { id = 87577, Voice = '', Text = '“咻”的一声魔杖弹了出去，插在了古钟的钟箱上。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[87576] = { id = 87576, Voice = '', Text = '魔杖在空中震动得厉害，发出了嗖嗖的声音。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[87575] = { id = 87575, Voice = '', Text = '魔法似乎已经无法停下来了。 ', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[87574] = { id = 87574, Voice = '', Text = '不好！！', Speaker = 6418, Option = '', Emoji = 18, Action = _EmptyTable,}, 
	[87573] = { id = 87573, Voice = '', Text = '你！！！！这个小贼，立刻把我的魔杖还给我！！！', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
	[87572] = { id = 87572, Voice = '', Text = '看来我的咒语正在生效。', Speaker = 6418, Option = '', Action = _EmptyTable,}, 
	[87571] = { id = 87571, Voice = '', Text = '只见小马蒂默念了些什么，魔杖便开始发出奇异的光辉。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[87570] = { id = 87570, Voice = '', Text = '趁那可怕的姐姐追来之前！我要集中注意力！！', Speaker = 6418, Option = '', Action = _EmptyTable,}, 
	[87569] = { id = 87569, Voice = '', Text = '总之我得抓紧时间，我爷爷的时间可不等人。', Speaker = 6418, Option = '', Action = _EmptyTable,}, 
	[87568] = { id = 87568, Voice = '', Text = '别怪我偷了那个可怕姐姐的东西，她来找我的时候也根本就不友善，好像和我有深仇大恨一样！！', Speaker = 6418, Option = '怎么回事', Action = _EmptyTable,}, 
	[87567] = { id = 87567, Voice = '', Text = '修钟啊！这可是魔女的魔杖，我一定能修好并告诉年迈躺在病床上的爷爷这个好消息。', Speaker = 6418, Option = '指责', Action = _EmptyTable,}, 
	[87566] = { id = 87566, Voice = '', Text = '那我可得抓紧时间了。', Speaker = 6418, Option = '做什么', Action = _EmptyTable,}, 
	[87565] = { id = 87565, Voice = '', Text = '什么！！你说那个魔女在找我。', Speaker = 6418, Option = '是啊', Emoji = 18, Action = _EmptyTable,}, 
	[87564] = { id = 87564, Voice = '', Text = '别打扰我！！我正在修我爷爷设计的古钟。', Speaker = 6418, Option = '', Action = _EmptyTable,}, 
	[87563] = { id = 87563, Voice = '', Text = '你还愣在这里干什么，还不快去找？想我再把你变成盗虫吗！！', Speaker = 6417, Option = '我这就去', Action = _EmptyTable,}, 
	[87562] = { id = 87562, Voice = '', Text = '你这废物，我看他往钟楼的方向跑了，不过一个八九岁的小男孩跑进这么危险的地方应该也活不久了，呵呵。', Speaker = 6417, Option = '什么！？', Action = _EmptyTable,}, 
	[87561] = { id = 87561, Voice = '', Text = '所以你现在去把这臭小鬼给我找到！！', Speaker = 6417, Option = '怎么找', Action = _EmptyTable,}, 
	[87560] = { id = 87560, Voice = '', Text = '小怪物！！这个肮脏的小贼，他居然偷了我的时间魔杖，害得我无法再重返未来。', Speaker = 6417, Option = '原来如此', Action = _EmptyTable,}, 
	[87559] = { id = 87559, Voice = '', Text = '让我都替他感到难过。', Speaker = 6417, Option = '这样', Action = _EmptyTable,}, 
	[87558] = { id = 87558, Voice = '', Text = '呵呵没想到穿越回十年前，这小鬼还是和未来一样丑，完全没有一点可爱之处。', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
	[87557] = { id = 87557, Voice = '', Text = '总之你不需要知道这么多。', Speaker = 6417, Option = '好吧', Action = _EmptyTable,}, 
	[87556] = { id = 87556, Voice = '', Text = '一个狂妄无比的魔法师！这卑鄙的家伙在未来处处与我作对…', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
	[87555] = { id = 87555, Voice = '', Text = '我从十年后的未来穿越时空到这里，来找一个仇家算账。', Speaker = 6417, Option = '什么仇家', Action = _EmptyTable,}, 
	[87554] = { id = 87554, Voice = '', Text = '好吧，变回来帮我做事吧。', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
	[87553] = { id = 87553, Voice = '', Text = '再说一遍。', Speaker = 6417, Option = '求您', Action = _EmptyTable,}, 
	[87552] = { id = 87552, Voice = '', Text = '非常好，我喜欢听别人求我。', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
	[87551] = { id = 87551, Voice = '', Text = '愚蠢的冒险者，简直不知天高地厚。我要把你变成一只肮脏的盗虫！！', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
	[87550] = { id = 87550, Voice = '', Text = '非常好，我喜欢别人服从我的样子。', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
	[87549] = { id = 87549, Voice = '', Text = '不然我就诅咒你直到时间的尽头。', Speaker = 6417, Option = '{帮她做事,2},{拒绝,3}', Action = _EmptyTable,}, 
	[87548] = { id = 87548, Voice = '', Text = '哼，倒霉的小蠢蛋。我给你两条路，跟我搭话就代表着得帮我做事。', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
	[87547] = { id = 87547, Voice = '', Text = '你可知道，我是谁。', Speaker = 6417, Option = '不知道', Action = _EmptyTable,}, 
	[87546] = { id = 87546, Voice = '', Text = '不得不说，你很有胆量，敢和史上最伟大的魔女搭话。', Speaker = 6417, Option = '', Action = _EmptyTable,}, 
	[87396] = { id = 87396, Voice = '', Text = '准备好了吗？我可要提问了。', Speaker = 6407, Option = '{准备好了,2},{还没有,3}', Action = _EmptyTable,}, 
	[87387] = { id = 87387, Voice = '', Text = '谢谢你冒险者，请你带着哥哥的魔杖，珍惜每一分每一秒的继续冒险下去！！', Speaker = 6392, Option = '谢谢', Action = _EmptyTable,}, 
	[87386] = { id = 87386, Voice = '', Text = '没有你拯救我的命运，我可能也就不会有未来了。', Speaker = 6392, Option = '别这么说', Action = _EmptyTable,}, 
	[87385] = { id = 87385, Voice = '', Text = '这把魔杖，虽然未来的我说是哥哥留给我的，但是我想让你收着它。', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87384] = { id = 87384, Voice = '', Text = '不管怎么说，还是谢谢你冒险者，是你拯救了我的命运。', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87383] = { id = 87383, Voice = '', Text = '哥哥…哥哥他…', Speaker = 6392, Option = '我理解', Action = _EmptyTable,}, 
	[87382] = { id = 87382, Voice = '', Text = '事情怎么会变成这样！！', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87381] = { id = 87381, Voice = '', Text = '快，没有时间了，在我魔化之前，就请你们送我一程吧！！', Speaker = 6412, Option = '', Action = _EmptyTable,}, 
	[87380] = { id = 87380, Voice = '', Text = '哥哥！！！', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87379] = { id = 87379, Voice = '', Text = '我从未来带回了一把帕特里克在发狂之前为我这个弟弟留下的魔杖。', Speaker = 6412, Option = '', Action = _EmptyTable,}, 
	[87378] = { id = 87378, Voice = '', Text = '更何况，我本来就不属于这个时空。', Speaker = 6412, Option = '天哪', Action = _EmptyTable,}, 
	[87377] = { id = 87377, Voice = '', Text = '没错，我的心灵已经开始被魔药侵蚀。', Speaker = 6412, Option = '', Action = _EmptyTable,}, 
	[87376] = { id = 87376, Voice = '', Text = '所以我的时间不多了。', Speaker = 6412, Option = '什么！', Action = _EmptyTable,}, 
	[87375] = { id = 87375, Voice = '', Text = '我说过，这种魔药使用多次会使人走火入魔！！', Speaker = 6412, Option = '', Action = _EmptyTable,}, 
	[87374] = { id = 87374, Voice = '', Text = '快毁掉这份研究和魔药！！', Speaker = 6412, Option = '', Action = _EmptyTable,}, 
	[87373] = { id = 87373, Voice = '', Text = '我不得不使用这种魔药回到过去，亲手葬送自己的哥哥。', Speaker = 6412, Option = '真抱歉', Action = _EmptyTable,}, 
	[87372] = { id = 87372, Voice = '', Text = '帕特里克的魔力实在是太强大了，他研究出这种魔药后滥杀无辜，使生灵涂炭。', Speaker = 6412, Option = '太惨了', Action = _EmptyTable,}, 
	[87366] = { id = 87366, Voice = '', Text = '就让我用半成品带你们看看，我经历过的事吧。', Speaker = 6412, Option = '', Action = _EmptyTable,}, 
	[87365] = { id = 87365, Voice = '', Text = '没错，我就是你自己，鲁迪，我是从未来喝魔药穿越回来的。', Speaker = 6412, Option = '', Action = _EmptyTable,}, 
	[87364] = { id = 87364, Voice = '', Text = '什么！！？？！？', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87363] = { id = 87363, Voice = '', Text = '这时，因为刚才激烈的打斗，神秘巫师的面具裂开一道缝，随后从脸上掉了下来。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[87362] = { id = 87362, Voice = '', Text = '你问我究竟是谁！？', Speaker = 6406, Option = '', Action = _EmptyTable,}, 
	[87361] = { id = 87361, Voice = '', Text = '冒险者小心！！', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87360] = { id = 87360, Voice = '', Text = '够了，不要再重复的错下去了！！', Speaker = 6406, Option = '', Action = _EmptyTable,}, 
	[87359] = { id = 87359, Voice = '', Text = '这时，那位神秘的巫师又出现在了鲁迪面前……', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[87358] = { id = 87358, Voice = '', Text = '好，让我再试一次！', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
