	[87354] = { id = 87354, Voice = '', Text = '也许，还需要加些彩色外皮。', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87353] = { id = 87353, Voice = '', Text = '我知道了！！', Speaker = 6392, Option = '怎么', Emoji = 11, Action = _EmptyTable,}, 
	[87352] = { id = 87352, Voice = '', Text = '嗯...让我想想。', Speaker = 6392, Option = '加油', Emoji = 19, Action = _EmptyTable,}, 
	[87351] = { id = 87351, Voice = '', Text = '这中间到底还少了什么呢？', Speaker = 6392, Option = '你再想想', Action = _EmptyTable,}, 
	[87350] = { id = 87350, Voice = '', Text = '看来，只有研究出真正的成品魔药我们才能知道这一切。', Speaker = 6392, Option = '是啊', Action = _EmptyTable,}, 
	[87349] = { id = 87349, Voice = '', Text = '可是他既然没有拿走笔记，那么他是怎么拥有这种魔药的！？', Speaker = 6392, Option = '不可思议', Action = _EmptyTable,}, 
	[87348] = { id = 87348, Voice = '', Text = '那个神秘巫师！！显然也拥有时间魔药！', Speaker = 6392, Option = '对啊', Action = _EmptyTable,}, 
