	[87221] = { id = 87221, Voice = '', Text = '其他部件我这里都齐全，但还差一些时间扭曲键。', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87220] = { id = 87220, Voice = '', Text = '那可是我哥哥研究出召唤魔物的宝器，只有我们两兄弟知道制作方法。', Speaker = 6392, Option = '如何做？', Action = _EmptyTable,}, 
	[87219] = { id = 87219, Voice = '', Text = '别担心，他性情温和不会伤害人类，可也极其胆小，只有数轴之轮可以将他召唤出来。', Speaker = 6392, Option = '数轴之轮？', Action = _EmptyTable,}, 
	[87218] = { id = 87218, Voice = '', Text = '时光老人是居住在钟塔里上百年的大魔物。', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87217] = { id = 87217, Voice = '', Text = '这可就难办了，时光指针只有在时光老人的身上能找到。', Speaker = 6392, Option = '那是谁？', Action = _EmptyTable,}, 
	[87216] = { id = 87216, Voice = '', Text = '嗯嗯，原来如此，还缺一枚时光指针。', Speaker = 6392, Option = '时光指针？', Action = _EmptyTable,}, 
	[87215] = { id = 87215, Voice = '', Text = '算了，先别想这么多了。让我们来看看，哥哥的研究还缺什么材料。', Speaker = 6392, Option = '好', Action = _EmptyTable,}, 
	[87214] = { id = 87214, Voice = '', Text = '刚才那个人的声音，好耳熟…', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87213] = { id = 87213, Voice = '', Text = '我还会再回来的！', Speaker = 6406, Option = '', Action = _EmptyTable,}, 
	[87212] = { id = 87212, Voice = '', Text = '等你们失去了过去和未来就晚了！！！', Speaker = 6406, Option = '', Action = _EmptyTable,}, 
	[87211] = { id = 87211, Voice = '', Text = '你们根本就不明白，你们在做什么！！', Speaker = 6406, Option = '', Action = _EmptyTable,}, 
	[87210] = { id = 87210, Voice = '', Text = '快把你们手上的笔记交出来！', Speaker = 6406, Option = '', Action = _EmptyTable,}, 
	[87209] = { id = 87209, Voice = '', Text = '天哪！！就是这个家伙！！你这个凶手！！！', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87208] = { id = 87208, Voice = '', Text = '这时一位神秘巫师突然出现在两人面前，突兀的仿佛不是从这个时空来的一般。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[87207] = { id = 87207, Voice = '', Text = '对，虽然不知道是什么，但我们先研究出来看看。', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87206] = { id = 87206, Voice = '', Text = '不过哥哥和我说过，这魔药是有一定风险和副作用的。', Speaker = 6392, Option = '副作用？', Action = _EmptyTable,}, 
	[87205] = { id = 87205, Voice = '', Text = '不仅能找到杀害我哥的神秘巫师，还能挽救哥哥的性命。', Speaker = 6392, Option = '好主意', Action = _EmptyTable,}, 
	[87204] = { id = 87204, Voice = '', Text = '没错！这样我们就能穿越时空回到过去。', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87203] = { id = 87203, Voice = '', Text = '你可真笨，这可是穿梭时空的魔药！', Speaker = 6392, Option = '你是说', Action = _EmptyTable,}, 
	[87202] = { id = 87202, Voice = '', Text = '你问我为什么要找回这本笔记？当然是要把我哥哥的研究完成！', Speaker = 6392, Option = '为什么', Action = _EmptyTable,}, 
	[87201] = { id = 87201, Voice = '', Text = '这的确是我哥哥的笔记！！', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87200] = { id = 87200, Voice = '', Text = '好了，这是你要的笔记，我可是很守信誉的人。', Speaker = 3018, Option = '', Action = _EmptyTable,}, 
	[87199] = { id = 87199, Voice = '', Text = '我可没闲工夫在这里陪你浪费时间，说了一个都不能少！', Speaker = 3018, Option = '', Action = _EmptyTable,}, 
	[87198] = { id = 87198, Voice = '', Text = '一手交物，一手交货，我的朋友。', Speaker = 3018, Option = '{给你果实,2},{我这就去收集,3}', Action = _EmptyTable,}, 
	[87197] = { id = 87197, Voice = '', Text = '这已经很公道了，不能再少了！！', Speaker = 3018, Option = '那好吧', Action = _EmptyTable,}, 
	[87196] = { id = 87196, Voice = '', Text = '就收你5个玛丝黛拉果实好了。', Speaker = 3018, Option = '这么多！？', Action = _EmptyTable,}, 
	[87195] = { id = 87195, Voice = '', Text = '你就拿等价的东西来换吧。', Speaker = 3018, Option = '什么东西', Action = _EmptyTable,}, 
	[87194] = { id = 87194, Voice = '', Text = '不过嘛，这也好商量，想要给你也可以。', Speaker = 3018, Option = '真的吗？', Emoji = 5, Action = _EmptyTable,}, 
	[87193] = { id = 87193, Voice = '', Text = '这可是我出重金买过来的，怎么能你说要回去，就要回去。', Speaker = 3018, Option = '商议', Action = _EmptyTable,}, 
	[87192] = { id = 87192, Voice = '', Text = '我看那本笔记可颇有来头啊，年轻人。', Speaker = 3018, Option = '要回', Emoji = 5, Action = _EmptyTable,}, 
	[87191] = { id = 87191, Voice = '', Text = '噢~你说的笔记我记得，羊皮书，镶金字，确实在我这里。', Speaker = 3018, Option = '', Action = _EmptyTable,}, 
	[87190] = { id = 87190, Voice = '', Text = '那就麻烦你去梦罗克跑一趟了，我去抓这个小贼！！', Speaker = 6392, Option = '交给我', Action = _EmptyTable,}, 
	[87189] = { id = 87189, Voice = '', Text = '可恶！让这小贼给跑了！！', Speaker = 6392, Option = '怎么办', Action = _EmptyTable,}, 
	[87188] = { id = 87188, Voice = '', Text = '看来只有去梦罗克，从黑市商人那里把这本笔记再要回来了。', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87187] = { id = 87187, Voice = '', Text = '好吧好吧，我把它卖给了梦罗克的黑市商人法图娜。', Speaker = 6398, Option = '', Action = _EmptyTable,}, 
	[87186] = { id = 87186, Voice = '', Text = '你把我哥哥的笔记给卖了，还想问我们要钱，再不说！我就给你些颜色看看！！', Speaker = 6392, Option = '', Emoji = 12, Action = _EmptyTable,}, 
	[87185] = { id = 87185, Voice = '', Text = '我给这该死的高斯林做了这么多事，可是一分钱都没拿到。除非你们给我些…你懂的…', Speaker = 6398, Option = '', Emoji = 13, Action = _EmptyTable,}, 
	[87184] = { id = 87184, Voice = '', Text = '你说什么！？给卖了！！！', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87183] = { id = 87183, Voice = '', Text = '所以我就把他的笔记本给偷偷卖去了黑市，结果再回来一看，这家伙已经断气了。', Speaker = 6398, Option = '', Action = _EmptyTable,}, 
	[87182] = { id = 87182, Voice = '', Text = '哼，这家伙让我偷了这么多东西，就为了一本破笔记。', Speaker = 6398, Option = '', Action = _EmptyTable,}, 
	[87181] = { id = 87181, Voice = '', Text = '什么！？那本笔记现在在哪儿！', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87180] = { id = 87180, Voice = '', Text = '我被他捏住了些把柄，不得不替他偷齐一本该死的笔记上的材料。', Speaker = 6398, Option = '', Action = _EmptyTable,}, 
	[87179] = { id = 87179, Voice = '', Text = '我只是高斯林雇来替他偷东西的小偷而已。', Speaker = 6398, Option = '然后呢', Action = _EmptyTable,}, 
	[87178] = { id = 87178, Voice = '', Text = '都跟你们说了，高斯林的死跟我没有半毛钱关系。', Speaker = 6398, Option = '', Action = _EmptyTable,}, 
	[87172] = { id = 87172, Voice = '', Text = '时间的密殿已为你打开，要踏入这神圣的领域吗？', Speaker = 6394, Option = '{是,2},{否,3}', Action = _EmptyTable,}, 
	[87171] = { id = 87171, Voice = '', Text = '让我们一起去抓住高斯林这无耻之徒吧！', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87170] = { id = 87170, Voice = '', Text = '密室大门并没有打开，看样子你并没有理解这句提示啊。', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87169] = { id = 87169, Voice = '', Text = '看！门碑的光芒产生了变化，看来你成功的打开了密殿的大门。', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87168] = { id = 87168, Voice = '', Text = '将时间设置为：', Speaker = 6397, Option = '{XII,6},{III,0},{VI,0},{IX,0}', Action = _EmptyTable,}, 
	[87167] = { id = 87167, Voice = '', Text = '将时间设置为：', Speaker = 6397, Option = '{XII,0},{III,0},{VI,0},{IX,0}', Action = _EmptyTable,}, 
	[87166] = { id = 87166, Voice = '', Text = '将时间设置为：', Speaker = 6396, Option = '{XII,5},{III,0},{VI,0},{IX,0}', Action = _EmptyTable,}, 
	[87165] = { id = 87165, Voice = '', Text = '将时间设置为：', Speaker = 6397, Option = '{XII,0},{III,0},{VI,0},{IX,0}', Action = _EmptyTable,}, 
	[87164] = { id = 87164, Voice = '', Text = '将时间设置为：', Speaker = 6396, Option = '{XII,0},{III,0},{VI,0},{IX,0}', Action = _EmptyTable,}, 
	[87163] = { id = 87163, Voice = '', Text = '将时间设置为：', Speaker = 6395, Option = '{XII,4},{III,0},{VI,0},{IX,0}', Action = _EmptyTable,}, 
	[87162] = { id = 87162, Voice = '', Text = '我可完全没有头绪。', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87161] = { id = 87161, Voice = '', Text = '当零点的钟声敲响，神将为你打开时间的殿堂？', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87160] = { id = 87160, Voice = '', Text = '可是我们该怎么进到这个密殿中去？', Speaker = 6392, Option = '三个机关', Action = _EmptyTable,}, 
	[87159] = { id = 87159, Voice = '', Text = '看来你找到高斯林这卑鄙的家伙的藏身之处了！', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87158] = { id = 87158, Voice = '', Text = '门碑的周围缓缓升起三个装置……', Speaker = 6394, Option = '', Action = _EmptyTable,}, 
	[87156] = { id = 87156, Voice = '', Text = '看来就是这里没错。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[87155] = { id = 87155, Voice = '', Text = '我明白这句提示的确有些晦涩难懂。', Speaker = 1013, Option = '', Action = _EmptyTable,}, 
	[87154] = { id = 87154, Voice = '', Text = '当零点的钟声敲响，神将为你打开时间的殿堂。', Speaker = 1013, Option = '{懂了,2},{仍感困惑,3}', Action = _EmptyTable,}, 
	[87153] = { id = 87153, Voice = '', Text = '虽说这是教会自己设计的，但这座密殿也有百年之久了，所以我们也不知道解法，只有设计师留下来的提示。', Speaker = 1013, Option = '什么提示', Action = _EmptyTable,}, 
	[87152] = { id = 87152, Voice = '', Text = '密殿光有钥匙还不够，还有一个钟楼机关。', Speaker = 1013, Option = '什么机关', Action = _EmptyTable,}, 
	[87151] = { id = 87151, Voice = '', Text = '噢对了，还有个重要的事情忘了交代你。', Speaker = 1013, Option = '什么', Action = _EmptyTable,}, 
	[87150] = { id = 87150, Voice = '', Text = '是我得感谢你，冒险者，帮我把这个小贼给抓起来！', Speaker = 1013, Option = '交给我', Action = _EmptyTable,}, 
	[87149] = { id = 87149, Voice = '', Text = '完成~', Speaker = 1013, Option = '感谢', Action = _EmptyTable,}, 
	[87148] = { id = 87148, Voice = '', Text = '这其中一把就交给你了。', Speaker = 1013, Option = '', Action = _EmptyTable,}, 
	[87147] = { id = 87147, Voice = '', Text = '很好，这些量足够做两把钥匙了。', Speaker = 1013, Option = '', Action = _EmptyTable,}, 
	[87146] = { id = 87146, Voice = '', Text = '这些根本不够做一把钥匙！', Speaker = 1013, Option = '', Action = _EmptyTable,}, 
	[87145] = { id = 87145, Voice = '', Text = '那我可就帮不了你了啊！', Speaker = 1013, Option = '', Action = _EmptyTable,}, 
	[87144] = { id = 87144, Voice = '', Text = '你收集到5个钟塔之钥了吗？', Speaker = 1013, Option = '{给你钟塔之钥,2},{还没有,3}', Action = _EmptyTable,}, 
	[87143] = { id = 87143, Voice = '', Text = '好在这钥匙我也会制作，就是钟塔之钥的收集是个难题。', Speaker = 1013, Option = '交给我吧', Action = _EmptyTable,}, 
	[87142] = { id = 87142, Voice = '', Text = '钥匙当然是被高斯林给盗走了！', Speaker = 1013, Option = '那怎么办', Action = _EmptyTable,}, 
	[87141] = { id = 87141, Voice = '', Text = '是的，这密室是教会设计的，只有钟塔之钥融合而成的特殊钥匙才能打开密殿的大门。', Speaker = 1013, Option = '钥匙在哪', Action = _EmptyTable,}, 
	[87140] = { id = 87140, Voice = '', Text = '他窃取了教会很多珍贵的材料，躲进了艾尔帕兰城钟楼一个教会驻扎的密殿中。', Speaker = 1013, Option = '密殿？', Action = _EmptyTable,}, 
	[87139] = { id = 87139, Voice = '', Text = '可是我们大家都被他骗了。', Speaker = 1013, Option = '发生了什么', Action = _EmptyTable,}, 
	[87138] = { id = 87138, Voice = '', Text = '之前他一直帮教会做事，我们都以为他年轻善良。', Speaker = 1013, Option = '', Action = _EmptyTable,}, 
	[87137] = { id = 87137, Voice = '', Text = '那可是个误入歧途的年轻人！', Speaker = 1013, Option = '', Action = _EmptyTable,}, 
	[87136] = { id = 87136, Voice = '', Text = '你说高斯林！！我认识！', Speaker = 1013, Option = '', Action = _EmptyTable,}, 
	[87135] = { id = 87135, Voice = '', Text = '我不认识你说的这个人。', Speaker = 1228, Option = '', Action = _EmptyTable,}, 
	[87134] = { id = 87134, Voice = '', Text = '高斯林之前一直为教会服务，我相信应该能从大主教汤玛斯那里得到一些消息。', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87133] = { id = 87133, Voice = '', Text = '所以现在首当其冲就是找到失踪的高斯林。', Speaker = 6392, Option = '该怎么做', Action = _EmptyTable,}, 
	[87132] = { id = 87132, Voice = '', Text = '杀害我哥的凶手，和盗走笔记的人就是我们的好友高斯林。', Speaker = 6392, Option = '哎', Action = _EmptyTable,}, 
	[87131] = { id = 87131, Voice = '', Text = '不觉得这也太巧了一点吗？我不得不痛心地怀疑。', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87130] = { id = 87130, Voice = '', Text = '蹊跷的是，我们兄弟两从小玩到大的好友高斯林居然也在笔记被偷的同一天失踪了。', Speaker = 6392, Option = '你是说', Action = _EmptyTable,}, 
	[87129] = { id = 87129, Voice = '', Text = '我拼死保护了我哥的研究笔记，但这两天重要的研究笔记也被人偷了！', Speaker = 6392, Option = '被谁', Action = _EmptyTable,}, 
	[87128] = { id = 87128, Voice = '', Text = '就在他快要成功还差些必需的材料时，我们进到钟塔中收集，结果遇到了杀死我哥的神秘巫师。', Speaker = 6392, Option = '原来如此', Action = _EmptyTable,}, 
	[87127] = { id = 87127, Voice = '', Text = '是的，你没有听错，这种魔药可以使人回到过去或探索未来。', Speaker = 6392, Option = '天哪', Action = _EmptyTable,}, 
	[87126] = { id = 87126, Voice = '', Text = '他在做一种可以穿越时空的魔药！', Speaker = 6392, Option = '什么！？', Action = _EmptyTable,}, 
	[87125] = { id = 87125, Voice = '', Text = '我哥哥帕特里克是个年轻有灵气的魔法师，他最近在做一项危险且极富魅力的研究。', Speaker = 6392, Option = '什么研究？', Action = _EmptyTable,}, 
	[87124] = { id = 87124, Voice = '', Text = '事情要从我哥的研究说起。', Speaker = 6392, Option = '请说', Action = _EmptyTable,}, 
	[87123] = { id = 87123, Voice = '', Text = '是啊，现在这世道谁还愿意帮个陌生人呢。', Speaker = 6392, Option = '', Action = _EmptyTable,}, 
	[87122] = { id = 87122, Voice = '', Text = '我为了寻找凶手到处调查却一无所获，你愿意为我出份力吗？好心的冒险者。', Speaker = 6392, Option = '{愿意,2},{我很忙,3}', Action = _EmptyTable,}, 
	[87121] = { id = 87121, Voice = '', Text = '我们兄弟两前不久在钟楼收集材料时，被一个神秘的巫师袭击了，哥哥因此丢了性命。', Speaker = 6392, Option = '很遗憾', Action = _EmptyTable,}, 
	[87120] = { id = 87120, Voice = '', Text = '谢谢你把它交还给我，这可是哥哥重要的遗物。', Speaker = 6392, Option = '真抱歉', Action = _EmptyTable,}, 
	[87119] = { id = 87119, Voice = '', Text = '天哪！这是我哥哥遗失的时之流沙，怎么会在你手上？', Speaker = 6392, Option = '偶然拾取', Action = _EmptyTable,}, 
	[86945] = { id = 86945, Voice = '', Text = '它就当做你让我大开眼界的谢礼，交给你啦！', Speaker = 6377, Option = '', Action = _EmptyTable,}, 
	[86944] = { id = 86944, Voice = '', Text = '由于你的胆量与气魄，我觉得你才是能妥善保管，并使其不为恶所用的人。', Speaker = 6377, Option = '谢谢', Action = _EmptyTable,}, 
	[86943] = { id = 86943, Voice = '', Text = '我发现用此匕首靠近敌人，敌人将浑身无力无法动弹。', Speaker = 6377, Option = '这么厉害', Action = _EmptyTable,}, 
	[86942] = { id = 86942, Voice = '', Text = '这利刃我是在钟楼一个隐蔽的密室中找到它的。', Speaker = 6377, Option = '', Action = _EmptyTable,}, 
	[86941] = { id = 86941, Voice = '', Text = '冒险者，你可惧怕杀人于无形？', Speaker = 6377, Option = '惧怕', Action = _EmptyTable,}, 
	[86940] = { id = 86940, Voice = '', Text = '我明白你的难处，这可不是谁都能弄的来的东西。', Speaker = 6377, Option = '', Action = _EmptyTable,}, 
	[86939] = { id = 86939, Voice = '', Text = '我在你的眼里没有看到勇气这样东西。', Speaker = 6377, Option = '', Action = _EmptyTable,}, 
	[86938] = { id = 86938, Voice = '', Text = '这么稀罕的玩意儿你都没有兴趣，你究竟对什么有兴趣？', Speaker = 6377, Option = '', Action = _EmptyTable,}, 
	[86937] = { id = 86937, Voice = '', Text = '我居然在有生之年见到了天才马莱的指针，实在是太感动了，冒险者这十分珍贵请你好好保管此物。', Speaker = 6377, Option = '', Action = _EmptyTable,}, 
	[86936] = { id = 86936, Voice = '', Text = '你是真正的勇者，这闪着神秘色彩的水晶正是最好的证据，请你像奖章一般好好待它。', Speaker = 6377, Option = '', Action = _EmptyTable,}, 
	[86935] = { id = 86935, Voice = '', Text = '天哪，这就是传说中的利维坦沙漏，连里面的流沙都如此迷幻，你真是太厉害了冒险者，请你好好保存它。', Speaker = 6377, Option = '', Action = _EmptyTable,}, 
	[86934] = { id = 86934, Voice = '', Text = '别吹牛了孩子，说谎可不是什么好品德。', Speaker = 6377, Option = '', Action = _EmptyTable,}, 
	[86933] = { id = 86933, Voice = '', Text = '天才马莱所制的时钟，能随意穿梭过去与未来，真想在有生之年见见它的模样，哪怕是被折断的指针。', Speaker = 6377, Option = '{交给我,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[86932] = { id = 86932, Voice = '', Text = '迷幻的紫色水晶能通晓未来，如同上帝之眼一般审视一切，也许只有真正的勇者有机会得到它，想不想证明一下自己呢，年轻人？', Speaker = 6377, Option = '{交给我,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[86931] = { id = 86931, Voice = '', Text = '传说有个可以扭转乾坤，改变厄运的沙漏，你要是见过这样宝物能给我开开眼界吗冒险者？', Speaker = 6377, Option = '{交给我,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[86884] = { id = 86884, Voice = '', Text = '年轻人，看来你还没有理解这怀表之谜！', Speaker = 6294, Option = '', Action = _EmptyTable,}, 
	[86841] = { id = 86841, Voice = '', Text = '而到了那时，死亡丧钟将会再一次敲响。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86840] = { id = 86840, Voice = '', Text = '黑鲁死去，遗留的死亡丧钟给予人们警示，傲慢、贪婪、暴躁、奸诈、懦弱，这些人性的弱点，将在时间的流逝之下逐渐暴露。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86839] = { id = 86839, Voice = '', Text = '至于黑鲁，他从未改变过，懦弱且依赖他人，因此最终他的灵魂被魔鬼吞噬。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86838] = { id = 86838, Voice = '', Text = '伯纳德奸诈且做事刁钻，不留余地，因此遭到其余三人在事情发生后无情地舍弃。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86837] = { id = 86837, Voice = '', Text = '海廷斯暴躁且崇尚暴力，因此他最终被无尽魔物拖死。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86836] = { id = 86836, Voice = '', Text = '雷萨吉为人傲慢且刚愎自负，因此他被伯纳德所利用。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86835] = { id = 86835, Voice = '', Text = '尼克西为人贪婪而好占便宜，因此他被伯纳德所收买。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86834] = { id = 86834, Voice = '', Text = '你还没有看穿吗？再好好想想吧！', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86833] = { id = 86833, Voice = '', Text = '现在，你知道他们五人最终谁被献祭了吗？', Speaker = 6355, Option = '{尼克西,2},{雷萨吉,3},{海廷斯,4},{黑鲁,5},{伯纳德,6}', Action = _EmptyTable,}, 
	[86832] = { id = 86832, Voice = '', Text = '钟声响起，人魂尽丧，这就是死亡丧钟。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86831] = { id = 86831, Voice = '', Text = '在死亡丧钟的面前，他没有选择的余地。只能按照丧钟的时间，给予他们死亡。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86830] = { id = 86830, Voice = '', Text = '但骨子里，黑鲁依然是个懦弱的人，走到时间支配者面前也好，报仇也好，都不是依靠自己的力量。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86829] = { id = 86829, Voice = '', Text = '尽管在时间支配者的帮忙下，黑鲁完成了自己的复仇。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86828] = { id = 86828, Voice = '', Text = '懦弱而依赖他人的人，容易被欺骗，黑鲁就是这样的人。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86827] = { id = 86827, Voice = '', Text = '[c][ffff00]【死亡丧钟】[-][/c]已解锁，克莱门德会告诉你它的内容。', Speaker = 6361, Option = '', Action = _EmptyTable,}, 
	[86815] = { id = 86815, Voice = '', Text = '溯回指针似乎对这个时钟有所反应…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[86814] = { id = 86814, Voice = '', Text = '散发阴森气息的时钟，诡异得让人不想靠近……', Speaker = 6361, Option = '{捡起来,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[86813] = { id = 86813, Voice = '', Text = '关键道具[c][ffff00]【死亡丧钟】[-][/c]已出现。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86812] = { id = 86812, Voice = '', Text = '看着魔法师被魔法冰封的遗体，魔鬼放下了一个破败的钟表，此刻，钟表的时间指向十二点。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86811] = { id = 86811, Voice = '', Text = '魔鬼亲手用魔法的力量把这位追寻力量的魔法师送入地狱。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86810] = { id = 86810, Voice = '', Text = '为了增强自己的力量前来寻找传说中的【黄金时钟】，在时间支配者的干预下，只能迎来自己的死亡。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86809] = { id = 86809, Voice = '', Text = '奸诈而做事刁钻的人，容易被舍弃，伯纳德就是这样的。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86808] = { id = 86808, Voice = '', Text = '[c][ffff00]【破败钟表·十二点】[-][/c]已解锁，克莱门德会告诉你它的内容。', Speaker = 6360, Option = '', Action = _EmptyTable,}, 
	[86799] = { id = 86799, Voice = '', Text = '溯回指针似乎对这个钟表有所反应…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
