	[86797] = { id = 86797, Voice = '', Text = '魔鬼放下了一个破败的钟表，此刻，钟表的时间指向九点。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86796] = { id = 86796, Voice = '', Text = '魔鬼召唤出近乎无尽的魔物，海廷斯虽然力量不凡，但终究还是倒在了魔物手中。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86795] = { id = 86795, Voice = '', Text = '但是实际上，暴力只会产生暴力，滥用暴力的人终究会倒在更强的暴力之下。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86794] = { id = 86794, Voice = '', Text = '暴躁的人永远都是这样，自以为暴力可以解决一切。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86793] = { id = 86793, Voice = '', Text = '暴躁而崇尚暴力的人，容易被拖垮，海廷斯就是这样的人。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86792] = { id = 86792, Voice = '', Text = '[c][ffff00]【破败钟表·九点】[-][/c]已解锁，克莱门德会告诉你它的内容。', Speaker = 6359, Option = '', Action = _EmptyTable,}, 
	[86788] = { id = 86788, Voice = '', Text = '溯回指针似乎对这个钟表有所反应…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[86787] = { id = 86787, Voice = '', Text = '破碎的时钟，钟面的水晶早已破坏得面目全非。', Speaker = 6359, Option = '{捡起,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[86786] = { id = 86786, Voice = '', Text = '魔鬼放下了一个破败的钟表，此刻，钟表的时间指向三点。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86785] = { id = 86785, Voice = '', Text = '也许结局不会变成受尽折磨而死。他的傲慢与自负导致了他的死亡。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86784] = { id = 86784, Voice = '', Text = '如果他肯与伯纳德一同上路，如果他肯向魔鬼求饶道歉，如果他愿意与其他死人联手对抗时间支配者。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86783] = { id = 86783, Voice = '', Text = '他自命不凡，凭借他的炼金术天赋，瞧不起一切不如他的人。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86782] = { id = 86782, Voice = '', Text = '傲慢而刚愎自负的人，容易被利用，雷萨吉就是这样的人。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86781] = { id = 86781, Voice = '', Text = '[c][ffff00]【破败钟表·三点】[-][/c]已解锁，克莱门德会告诉你它的内容。', Speaker = 6358, Option = '', Action = _EmptyTable,}, 
	[86772] = { id = 86772, Voice = '', Text = '溯回指针似乎对这个钟表有所反应…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[86771] = { id = 86771, Voice = '', Text = '破碎的时钟，钟面的水晶早已破坏得面目全非。', Speaker = 6358, Option = '{捡起,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[86770] = { id = 86770, Voice = '', Text = '魔鬼放下一个破败的钟表，此刻，钟表的时间指向六点。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86769] = { id = 86769, Voice = '', Text = '魔鬼追上来，魔鬼的魔法使得三个木箱越来越重，最后尼克西无法负担木箱的重负，被生生压死。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86768] = { id = 86768, Voice = '', Text = '三个木箱的沉重使得尼克西不得不走一步休息一步。很快就被不知名的魔鬼追上。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86767] = { id = 86767, Voice = '', Text = '只要不先来找自己，自己就可以凭借对钟楼的熟悉率先逃出去，然后享用这数不尽的财富。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86766] = { id = 86766, Voice = '', Text = '但是他不愿意放手，他总是心怀侥幸，那个梦魇会追上其他人，伯纳德也好，海廷斯也好。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86765] = { id = 86765, Voice = '', Text = '尼克西带着三个木箱踏上了逃亡之路，三个沉重的木箱是他活命的掣肘。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86764] = { id = 86764, Voice = '', Text = '贪婪而好占便宜的人，容易被收买。尼克西就是这样的人。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86763] = { id = 86763, Voice = '', Text = '[c][ffff00]【破败钟表·六点】[-][/c]已解锁，克莱门德会告诉你它的内容。', Speaker = 6357, Option = '', Action = _EmptyTable,}, 
	[86758] = { id = 86758, Voice = '', Text = '溯回指针似乎对这个钟表有所反应…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[86757] = { id = 86757, Voice = '', Text = '破碎的时钟，钟面的水晶早已破坏得面目全非。', Speaker = 6357, Option = '{捡起,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[86756] = { id = 86756, Voice = '', Text = '关键道具[c][ffff00]【破碎时钟】[-][/c]已出现。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86755] = { id = 86755, Voice = '', Text = '我对结果很感兴趣。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86754] = { id = 86754, Voice = '', Text = '是五个人联手抗争到底，还是牺牲一个人保全剩下的四个人？', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86753] = { id = 86753, Voice = '', Text = '甚至连自己保命都是一个问题。时间支配者给了他们一个选择。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86752] = { id = 86752, Voice = '', Text = '那个家伙太过强大了，即使他们拥有勇猛的战士，出色的药剂师，神秘的魔法师，依然不能打败那个家伙。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86751] = { id = 86751, Voice = '', Text = '伯纳德为了寻找某个物品，带着他们进入钟楼，没想到有什么拦在了他们的面前…', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86750] = { id = 86750, Voice = '', Text = '任何事物都无法抗拒吞食一切的时间，溯回时针记录了一切。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86749] = { id = 86749, Voice = '', Text = '溯回指针已解锁，克莱门德会告诉你它的内容。', Speaker = 6356, Option = '', Action = _EmptyTable,}, 
	[86742] = { id = 86742, Voice = '', Text = '任何事物都无法抗拒吞食一切的时间…', Speaker = 6356, Option = '', Action = _EmptyTable,}, 
	[86741] = { id = 86741, Voice = '', Text = '溯回指针似乎想表达些什么……', Speaker = 6356, Option = '拨动指针', Action = _EmptyTable,}, 
	[86740] = { id = 86740, Voice = '', Text = '泛着微冷白芒的指针，似乎是某个钟摆的重要组件…', Speaker = 6356, Option = '{捡起,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[86739] = { id = 86739, Voice = '', Text = '他们再也没有能出来，你知道，究竟发生了什么吗？', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86738] = { id = 86738, Voice = '', Text = '这天，神秘的魔法师不知付出了什么代价，邀请码头工人、炼金术士、士兵和药剂学者一起前往钟楼内部。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86737] = { id = 86737, Voice = '', Text = '黑鲁是药剂大师卡赞的弟子，是远近闻名的药剂制作者。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86736] = { id = 86736, Voice = '', Text = '伯纳德据说是来自吉芬的魔法师，不过到底是不是…谁知道呢？', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86735] = { id = 86735, Voice = '', Text = '海廷斯是艾尔帕兰城卫队的士兵，比起其他士兵，海廷斯显得更为强大。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86734] = { id = 86734, Voice = '', Text = '雷萨吉是艾尔帕兰有名的炼金术士，他在炼金术的成就少有人比肩。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86733] = { id = 86733, Voice = '', Text = '尼克西是在运河工作的一个码头工人，他对这片土地无比熟悉。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86732] = { id = 86732, Voice = '', Text = '我已经记录过不少事件，有些事件甚至到现在还没有结论。就比如那“消失的第五人”。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86731] = { id = 86731, Voice = '', Text = '我是【时钟守护者】教团的记事员，负责记录艾尔帕兰所发生的离奇事件。', Speaker = 6355, Option = '', Action = _EmptyTable,}, 
	[86729] = { id = 86729, Voice = '', Text = '——来自卡普拉调查员的分析报告。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86728] = { id = 86728, Voice = '', Text = '这是一个悲惨的故事，当那块古怪的碎片被工人们发现时，他们的命运就已经无法改变了，因此我将它记录为[c][ffff00]【命运的捉弄】[-][/c]。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86727] = { id = 86727, Voice = '', Text = '狂暴的巨人力量席卷了钟楼的每个角落，也给每个彷徨的生命画上了句点。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86726] = { id = 86726, Voice = '', Text = '当磅礴的力量从指尖划过，这一刻他彻底释放了心中的压抑。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86725] = { id = 86725, Voice = '', Text = '沉重的压力和心理的落差使他不堪重负，他可以为了强大的力量变得冷漠阴狠，也可以为了强大的力量毁灭一切。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86724] = { id = 86724, Voice = '', Text = '他在内心质问自己，如果变成了一个人人唾弃的魔物，这一切的努力还有什么意义？', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86723] = { id = 86723, Voice = '', Text = '当他面对梦寐以求的神奇碎片时，他的内心并没有感到安稳，反而强大的巨人力量使他慢慢产生异变，这让他备受煎熬。 ', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86722] = { id = 86722, Voice = '', Text = '但令人惋惜的是，亚尔林并没有成为最后的赢家。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86721] = { id = 86721, Voice = '', Text = '这真是一个完美的计划，亚尔林的隐忍和毒辣终于使他如愿以偿。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86720] = { id = 86720, Voice = '', Text = '潜伏的亚尔林轻易混入了激斗的人群，在混乱中神鬼不知地取走了伊米尔之心碎片，这不仅让愤怒的希默梅思无功而返，也让奥德里奇陷入了无尽的自责之中。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86719] = { id = 86719, Voice = '', Text = '自私的奥德里奇和贪婪的希默梅思被欲望蒙蔽了双眼，他们只看到眼前的对手，却忽略了隐藏的敌人。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86718] = { id = 86718, Voice = '', Text = '他利用了精明的希默梅思，当亡者的大军在黑夜里出征，亚尔林则脱下隐匿的面具安静地等待着即将收获的果实。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86717] = { id = 86717, Voice = '', Text = '深渊里的恶魔争相向他咆哮着，亡者的灵魂视他为腹中的美餐，但他却旁若无人地行走着，从他口中传出的声音清晰可闻，“亡者的女武神啊，我愿意做你最忠心的仆人，如果你正感到踌躇不决，那么我可以为你指明方向。”', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86716] = { id = 86716, Voice = '', Text = '亚尔林精心设计了一个骗局，当工人们都以为他是一个被魔物杀死的悲惨家伙时，他却死而复生地戴上隐匿的面具，行走在黑暗的深渊里。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86715] = { id = 86715, Voice = '', Text = '当建筑工人们在钟楼里挖掘出一块伊米尔之心碎片时，古怪的碎片带来了强大的异变魔物，也带给了亚尔林心中无尽的希望。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86714] = { id = 86714, Voice = '', Text = '亚尔林无疑是一个悲惨的小人物，过去无情的嘲笑和无尽的羞辱使他变得沉默阴狠，他渴望得到强大的力量来改变现状，这种急切的渴望驱使着他做出了一些不可思议的事情。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86713] = { id = 86713, Voice = '', Text = '[c][ffff00]【钟楼异闻录·毁灭卷】[-][/c]已解锁……是否解读？', Speaker = 6326, Option = '解读', Action = _EmptyTable,}, 
	[86712] = { id = 86712, Voice = '', Text = '文件名称是[c][ffff00]【钟楼异闻录·毁灭卷】[-][/c]。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86711] = { id = 86711, Voice = '', Text = '这是经过我整理的关于钟楼建筑工人无故失踪的调查文件。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86710] = { id = 86710, Voice = '', Text = '失去理智的他彻底释放了伊米尔之心，在狂暴能量的席卷下，钟楼遭受了毁灭般的损坏，而这个悲惨的故事也从此尘封在久远的记忆里。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86709] = { id = 86709, Voice = '', Text = '巨大的压力和心理的落差使他不堪重负，他可以为了强大的力量变得冷漠阴狠，也可以为了强大的力量毁灭一切。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86708] = { id = 86708, Voice = '', Text = '他在内心质问自己，如果变成了一个人人唾弃的魔物，这一切的努力还有什么意义？', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86707] = { id = 86707, Voice = '', Text = '当他面对梦寐以求的神奇碎片时，他的内心并没有感到安稳，反而强大的巨人力量使他慢慢产生异变，这让他备受煎熬。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86706] = { id = 86706, Voice = '', Text = '但令人惋惜的是，亚尔林并没有成为最后的赢家。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86705] = { id = 86705, Voice = '', Text = '这真是一个完美的计划，亚尔林的隐忍和毒辣使他终于如愿以偿。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86704] = { id = 86704, Voice = '', Text = '谁也不会想到，当奥德里奇和希默梅思在争得头破血流的时候，亚尔林，这个本该死去的人，这个黑夜里的告密者，竟然悄悄取走了伊米尔之心碎片。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86703] = { id = 86703, Voice = '', Text = '在巨大的震荡中，信息戛然而止……', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[86702] = { id = 86702, Voice = '', Text = '你感到了一阵眩晕，似乎有一股强大的力量在你面前席卷而过，把整个钟楼都摧毁得支离破散。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[86701] = { id = 86701, Voice = '', Text = '不，谁也不能阻止我变得更强大！我得不到的，谁也不能占有它。', Speaker = 6351, Option = '', Action = _EmptyTable,}, 
	[86700] = { id = 86700, Voice = '', Text = '呼……难道我的努力全都付诸流水了……', Speaker = 6351, Option = '', Action = _EmptyTable,}, 
	[86699] = { id = 86699, Voice = '', Text = '不，这该死的碎片，你控制不了我！', Speaker = 6351, Option = '', Action = _EmptyTable,}, 
	[86698] = { id = 86698, Voice = '', Text = '我不要变成一个怪物，这究竟是怎么一回事？！', Speaker = 6351, Option = '', Action = _EmptyTable,}, 
	[86697] = { id = 86697, Voice = '', Text = '我原以为自己终于如愿以偿，但突如其来的变故，让我对这一切都感到质疑……', Speaker = 6351, Option = '', Action = _EmptyTable,}, 
	[86693] = { id = 86693, Voice = '', Text = '为了强大的力量，我不得不计划这个骗局，我不想永远成为失败者……', Speaker = 6351, Option = '', Action = _EmptyTable,}, 
	[86692] = { id = 86692, Voice = '', Text = '他们只知道我是那个被魔物杀死的悲惨家伙，却不会想到我这个死而复生的人将会成为最后的赢家！', Speaker = 6351, Option = '', Action = _EmptyTable,}, 
	[86691] = { id = 86691, Voice = '', Text = '相比起来，希默梅思可不是个好对付的家伙，不过当我选择成为那个隐匿告密者的时候，我就已经计划好一切了。', Speaker = 6351, Option = '', Action = _EmptyTable,}, 
	[86690] = { id = 86690, Voice = '', Text = '哼，奥德里奇这个蠢货，他想把伊米尔之心碎片献给王国，却不知道我早已把它视为囊中之物！', Speaker = 6351, Option = '', Action = _EmptyTable,}, 
	[86689] = { id = 86689, Voice = '', Text = '谢天谢地，计划到目前为止正顺利地进行着……', Speaker = 6351, Option = '', Action = _EmptyTable,}, 
	[86688] = { id = 86688, Voice = '', Text = '戴上面具的瞬间，你发现自己看到了某些独特的信息。', Speaker = 0, Option = '读取信息', Action = _EmptyTable,}, 
	[86687] = { id = 86687, Voice = '', Text = '一个散发着古怪气息的面具，直觉告诉你，如果戴上这个面具，那将没人可以认出你。', Speaker = 6332, Option = '{戴上面具,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[86686] = { id = 86686, Voice = '', Text = '关键道具[c][ffff00]隐匿的面具[-][/c]已经出现，真相即将揭晓。', Speaker = 6332, Option = '', Action = _EmptyTable,}, 
	[86685] = { id = 86685, Voice = '', Text = '在一个平静的夜晚，他用自己磨制多时的锋利匕首结束了辱人者的生命。从此在大陆上到处流浪，没人知道他的来历，也从没人真正地了解他。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86684] = { id = 86684, Voice = '', Text = '终于，他战胜了恐惧，摆脱了犹疑，他决定要让那些嘲笑他的人付出代价。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86683] = { id = 86683, Voice = '', Text = '他在心里反复地询问自己，要继续做一个人人唾弃的废物，还是成为一个万众瞩目的强者。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86682] = { id = 86682, Voice = '', Text = '当年轻的亚尔林被嘲笑是没用的废物和被遗弃的野种时，他感到了莫大的耻辱。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86677] = { id = 86677, Voice = '', Text = '嘿嘿，亚尔维斯，你要为你的无礼付出代价！', Speaker = 6350, Option = '', Action = _EmptyTable,}, 
	[86676] = { id = 86676, Voice = '', Text = '对，没人可以轻视我，任何小瞧我的人都会得到应有的下场！', Speaker = 6350, Option = '', Action = _EmptyTable,}, 
	[86675] = { id = 86675, Voice = '', Text = '亚尔林，你要冷静下来，你不是永远的失败者，你只是缺少一个机会！', Speaker = 6350, Option = '', Action = _EmptyTable,}, 
	[86674] = { id = 86674, Voice = '', Text = '不，这不公平！他们高高在上，又怎么会看到我做过的努力！', Speaker = 6350, Option = '', Action = _EmptyTable,}, 
	[86673] = { id = 86673, Voice = '', Text = '我做错了什么，他们要这么对我！还是说……我注定是一个人人唾弃的废物……', Speaker = 6350, Option = '', Action = _EmptyTable,}, 
	[86672] = { id = 86672, Voice = '', Text = '我真的那么没用吗？为什么……为什么他们都那么讨厌我……', Speaker = 6350, Option = '', Action = _EmptyTable,}, 
	[86671] = { id = 86671, Voice = '', Text = '物品上似乎封印着一段信息，是否读取？', Speaker = 6331, Option = '读取信息', Action = _EmptyTable,}, 
	[86670] = { id = 86670, Voice = '', Text = '闪着寒光的一把匕首，虽然经历了久远的岁月，依然让人不敢过分接近。', Speaker = 6331, Option = '{拿来看看,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[86669] = { id = 86669, Voice = '', Text = '关键道具[c][ffff00]锋利的匕首[-][/c]已经出现，请耐心寻找。', Speaker = 6331, Option = '', Action = _EmptyTable,}, 
	[86668] = { id = 86668, Voice = '', Text = '奥德里奇队长称赞亚尔林是王国的英雄，但他真的这么认为吗？恐怕在他的心里只在乎王国即将取得的荣耀和辉煌吧。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86667] = { id = 86667, Voice = '', Text = '虽然随即赶来的骑士团卫队消灭了魔物，但显然悲剧无法挽回了。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86666] = { id = 86666, Voice = '', Text = '碎片拥有的强大能量带来了异变的嗜血魔物，亚尔林为了拯救同伴而倒在了魔物的爪牙之下。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86665] = { id = 86665, Voice = '', Text = '在一次普通的发掘中，工人们发现了一块古怪的碎片，而这块碎片使他们大祸临头。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86664] = { id = 86664, Voice = '', Text = '这真是一个悲伤的故事……', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86655] = { id = 86655, Voice = '', Text = '被遗弃在角落里的生锈铁锤，从铁锤的外形来看，这似乎是一把用来敲打地面的普通建筑工具。', Speaker = 6330, Option = '{拿来看看,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[86654] = { id = 86654, Voice = '', Text = '我们现在虽然不清楚告密者为什么这么做，但这无疑将给艾尔帕兰带来毁灭的战火。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86653] = { id = 86653, Voice = '', Text = '当亡灵的大军在黑夜里出征时，告密者的嘴角也浮现出诡异的笑容。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86652] = { id = 86652, Voice = '', Text = '在这里，他把自己知道的一切出卖给了亡者的女武神。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86651] = { id = 86651, Voice = '', Text = '藏身在黑夜里的隐匿告密者独自进入了黑暗的深渊。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86650] = { id = 86650, Voice = '', Text = '尊贵的女武神，我什么也不需要，只要让那些小瞧我的人得到应有的下场，那就足够了。', Speaker = 6340, Option = '', Action = _EmptyTable,}, 
	[86649] = { id = 86649, Voice = '', Text = '你冒着生命危险告诉我这些，是想得到什么？', Speaker = 6341, Option = '', Action = _EmptyTable,}, 
	[86648] = { id = 86648, Voice = '', Text = '我知道在艾尔帕兰的钟楼里发现了一块古怪的碎片，根据我对它的了解，那可能正是您在寻找的伊米尔之心。', Speaker = 6340, Option = '', Action = _EmptyTable,}, 
	[86647] = { id = 86647, Voice = '', Text = '我愿意做您最忠心的仆人，如果您正感到踌躇不决，那么我可以为您指明方向。', Speaker = 6340, Option = '', Action = _EmptyTable,}, 
	[86646] = { id = 86646, Voice = '', Text = '不，亡者的女武神啊，我来到这里是向您出示我最真挚的诚意。', Speaker = 6340, Option = '', Action = _EmptyTable,}, 
	[86645] = { id = 86645, Voice = '', Text = '你的身躯将会被撕裂成碎片，而你的灵魂将会是亡灵们最可口的美味！', Speaker = 6341, Option = '', Action = _EmptyTable,}, 
	[86644] = { id = 86644, Voice = '', Text = '狂妄的人类，你孤身一人来到黑暗的深处，是准备向无尽的邪恶发起挑战吗？', Speaker = 6341, Option = '', Action = _EmptyTable,}, 
	[86643] = { id = 86643, Voice = '', Text = '物品上似乎封印着一段信息，是否读取？', Speaker = 6329, Option = '读取信息', Action = _EmptyTable,}, 
	[86642] = { id = 86642, Voice = '', Text = '用某种兽角做成的战争号角，虽然已经被腐蚀损坏，但依然可以感受到它散发出的凛冽杀意。', Speaker = 6329, Option = '{拿来看看,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[86641] = { id = 86641, Voice = '', Text = '可是，事情说到这里，这块伊米尔之心碎片究竟去了哪里呢？', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86640] = { id = 86640, Voice = '', Text = '然而事情的发展出乎了所有人的意料，伊米尔之心碎片在战斗的最后关头竟然不翼而飞，这不仅让愤怒的希默梅思无功而返，也让奥德里奇陷入了无尽的自责之中，他觉得自己辜负了王国的信任。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86639] = { id = 86639, Voice = '', Text = '女武神贪婪的欲望和奥德里奇坚决的信念犹如水火不容般激烈地碰撞着，而这都是因为这块伊米尔之心碎片。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86638] = { id = 86638, Voice = '', Text = '或许在他看来，王国的荣耀显然要比个人的安危重要得多。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86637] = { id = 86637, Voice = '', Text = '面对亡者的女武神，这位叫做奥德里奇的骑士并没有表现出丝毫的畏惧。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86636] = { id = 86636, Voice = '', Text = '这是一件被鲜血浸染的铠甲，透过它我们可以想象曾经在钟楼里发生的那次惨烈大战……', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86625] = { id = 86625, Voice = '', Text = '一副沾染着殷红血迹的铠甲，在铠甲的表面还有几个明显的缺口，显然是利物攻击造成的。', Speaker = 6328, Option = '{拿来看看,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[86624] = { id = 86624, Voice = '', Text = '果然优秀的人不管在哪里都不会被埋没啊。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86623] = { id = 86623, Voice = '', Text = '我想这个年轻人一定很出色，不然这些老到的工人可是不会认可他的。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86622] = { id = 86622, Voice = '', Text = '他在记述中多次提到了一个叫亚尔林的人，并且字里行间都对他做出了很高的评价。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86621] = { id = 86621, Voice = '', Text = '这本手札的主人似乎是当初参与建造钟楼的一个工人……', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86620] = { id = 86620, Voice = '', Text = '哎，钟楼终于快要建成了，这个时候千万别出什么意外才好，我可不想在这儿送了命。', Speaker = 6339, Option = '', Action = _EmptyTable,}, 
	[86619] = { id = 86619, Voice = '', Text = '不过他的脾气确实好得出奇，无论我们怎么捉弄他都不会生气，看来我们以后要对他好一点了。', Speaker = 6339, Option = '', Action = _EmptyTable,}, 
	[86618] = { id = 86618, Voice = '', Text = '亚尔林这个小子最近可真是卖力啊，就连骑士团的奥德里奇队长都对他另眼相看。', Speaker = 6339, Option = '', Action = _EmptyTable,}, 
	[86617] = { id = 86617, Voice = '', Text = '不过那距离我们似乎太遥远了，还是赶紧做完手里的活吧。', Speaker = 6339, Option = '', Action = _EmptyTable,}, 
	[86616] = { id = 86616, Voice = '', Text = '那些炼金术师的想法真是太大胆了，如果按照他们的设计来建造，建成的钟楼一定可以大放异彩。', Speaker = 6339, Option = '', Action = _EmptyTable,}, 
	[86615] = { id = 86615, Voice = '', Text = '我已经记不清自己来这里多久了，自从接受卡普拉公司的征召，我们就一直在这里干活。', Speaker = 6339, Option = '', Action = _EmptyTable,}, 
	[86614] = { id = 86614, Voice = '', Text = '被丢弃在地上的一本手札，手札的封面上已经布满了灰尘，如果仔细去看，隐约可见一个人名被写在表面。', Speaker = 6327, Option = '{拿来看看,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[86613] = { id = 86613, Voice = '', Text = '如今的艾尔帕兰或许早已忘却了逝去的往事，但这起古怪的失踪事件却一直被保存在尘封的档案里，等待着有一天能够被有心人发现和查明。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86612] = { id = 86612, Voice = '', Text = '令人惋惜的是，这些发现在当时并没有引起足够的重视，当地的官员以尘封定论为由拒绝了所有的调查申请。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86611] = { id = 86611, Voice = '', Text = '在这之后有人不止一次地进出钟楼，他们发现除了那些建筑工人无故消失之外，本该已经完工的钟楼结构却受到了巨大的损坏，这大大拖延了施工的时间。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86610] = { id = 86610, Voice = '', Text = '事件发生后，王国的皇家骑士团也参与到调查中，艾尔帕兰的人们都期盼着王国精英的出色表现，但随后的调查报告却仅以寥寥数语草草了事，这实在有些令人失望。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86609] = { id = 86609, Voice = '', Text = '事件的经过大致是修建钟楼的建筑工人在一夜之间全部莫名失踪，没有留下任何可供调查的蛛丝马迹。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86608] = { id = 86608, Voice = '', Text = '在钟楼建造的早期，曾经发生过一起耸人听闻的失踪事件。', Speaker = 6326, Option = '', Action = _EmptyTable,}, 
	[86606] = { id = 86606, Voice = '', Text = '——来自卡普拉调查员罗贝尔的分析报告。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86605] = { id = 86605, Voice = '', Text = '这是一个悲惨的故事，也是一直以来留在我心中的疑问，我将它记录为[c][ffff00]【钟楼的噩梦】[-][/c]。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86604] = { id = 86604, Voice = '', Text = '当他看着面前有些惊讶的恶魔，这个冷血的杀手，他以自己最无畏的灵魂向恶魔发出了最后一击……', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86603] = { id = 86603, Voice = '', Text = '明白一切的吉伯特并没有畏缩地逃跑，他将探险的任务坚持到了最后。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86602] = { id = 86602, Voice = '', Text = '或许今生他们都不会再相遇，但即使在截然不同的世界里，他们也仍然是自己最亲密的伙伴！', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86601] = { id = 86601, Voice = '', Text = '但他是幸运的，机智灵活的反应使他幸免于难，然而他的伙伴们却永远离开了他，他永远记得那些熟悉的身影通过时空甬道的传送消失在无数个平行世界的尽头。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86600] = { id = 86600, Voice = '', Text = '吉伯特和所有的探险者一样，遭遇了裂变时空的吞噬。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86599] = { id = 86599, Voice = '', Text = '一时间，钟楼内混乱的时空里形成了无数可怕的时间漩涡，任何踏入时间漩涡的人将永远从这个世界上消失。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86598] = { id = 86598, Voice = '', Text = '恶魔的眼神里没有丝毫怜悯，他利用了伊米尔之心的力量，甚至连大贤者留下的黄金时钟也成为他加速时空异变的工具，而吉伯特他们对此竟然毫不知情。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86597] = { id = 86597, Voice = '', Text = '他们不知道在前面等待着的会是多么严峻的考验，他们也不知道，此刻的钟楼早已在恶魔耶梦加得的掌控下变得冷酷血腥。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86596] = { id = 86596, Voice = '', Text = '吉伯特和他的伙伴们与其他的探险者一样，他们怀揣着对艾尔帕兰的热爱和无所畏惧的勇气，踏上了前途未知的发现之旅。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86595] = { id = 86595, Voice = '', Text = '在漫长的岁月里，围绕着钟楼的种种诡异现象，曾经有无数勇敢的探险者先后进入钟楼，然而所有的探索都是毫无结果的，因为进入钟楼的探险者从没有人能活着走出来。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86594] = { id = 86594, Voice = '', Text = '[c][ffff00]【钟楼异闻录·噩梦卷】[-][/c]已解锁……是否解读？', Speaker = 6304, Option = '解读', Action = _EmptyTable,}, 
	[86593] = { id = 86593, Voice = '', Text = '文件名称是[c][ffff00]【钟楼异闻录·噩梦卷】[-][/c]。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86592] = { id = 86592, Voice = '', Text = '这是经过我整理的关于钟楼探险者无故失踪的调查文件。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86591] = { id = 86591, Voice = '', Text = '明白一切的他愤怒地选择与恶魔耶梦加得同归于尽，他觉得只有这样才能遏止无尽的悲剧，但显然他的力量还不足以与恶魔抗衡……', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86590] = { id = 86590, Voice = '', Text = '也许从某些方面来说，吉伯特是幸运的，因为他走出了时空的漩涡。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86589] = { id = 86589, Voice = '', Text = '你在钟楼看到那些的景象，正是探险者进入平行世界之后发生的事情，无论这景象有多么诡异和难以想象，都已经和我们所在的世界毫无关联了。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86588] = { id = 86588, Voice = '', Text = '或许连他们自己都没有察觉到，在无声无息之中，他们就已经从这个世界上永远地消失了。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86587] = { id = 86587, Voice = '', Text = '破碎的时空在钟楼里形成了无数个时间漩涡，那些可怜的探险者一旦进入钟楼就被卷入时间的漩涡里，从而进入了与现实世界毫无关联的平行时空。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86586] = { id = 86586, Voice = '', Text = '事情到这里似乎就有了合理的解释，钟楼的异变原来竟是耶梦加得利用伊米尔之心而创造出的时空裂变。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86567] = { id = 86567, Voice = '', Text = '手中的怀表似乎向外散发着微弱的光芒，你的身体不受控制地被牵引到一个未知的时空。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[86566] = { id = 86566, Voice = '', Text = '看上去有些华贵的怀表，静静地安放在地上，在怀表的表面，有着一丝网状的裂痕，好像是受到硬物撞击后的产物。', Speaker = 6311, Option = '{拿来看看,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[86565] = { id = 86565, Voice = '', Text = '关键道具[c][ffff00]损坏的怀表[-][/c]已出现，请耐心寻找。', Speaker = 6311, Option = '', Action = _EmptyTable,}, 
	[86564] = { id = 86564, Voice = '', Text = '这真是太令人疑惑了，既然携带指针的探险者并没有受到外力的伤害，为什么又会无故消失在钟楼里呢？', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86563] = { id = 86563, Voice = '', Text = '可是这块指针却有些不同寻常，因为它之所以停止运行并非是由于外力的破坏，而是因为指针的能量自然耗尽……', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86562] = { id = 86562, Voice = '', Text = '在野外进行探索的时候，指针常常会向探险者指示正确的方向，因而可以说是探险者身上最常见的东西。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86561] = { id = 86561, Voice = '', Text = '这是一块完整的指针，也是每一个探险者外出必备的物品。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86547] = { id = 86547, Voice = '', Text = '从手中的指针上好像传来一股莫名的吸引力，你发现自己不受控制地被卷入了一个明亮的空间。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[86546] = { id = 86546, Voice = '', Text = '一个圆形的罗盘状物品被遗落在角落里，长久的岁月使它变得残破不堪，而它的指针也早已停止了运行……', Speaker = 6310, Option = '{拿来看看,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[86545] = { id = 86545, Voice = '', Text = '或许当时这些探险者已经陷入了生死存亡的绝境之中，他们挣扎着，呐喊着，却依然摆脱不了死亡的威胁。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86544] = { id = 86544, Voice = '', Text = '皮衣在土地里掩埋了许久，因而显得有些残破，看皮衣上沾着的这块斑驳血迹，即使已经有些模糊，但依然散发着残酷血腥的气息。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86543] = { id = 86543, Voice = '', Text = '这的确是属于探险者所有的特制皮衣，皮衣上还有显示探险者身份的独特标识。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86526] = { id = 86526, Voice = '', Text = '一股巨大的力量从破旧的皮衣中向外涌动，你发现自己不受控制地进入了另一个空间。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[86525] = { id = 86525, Voice = '', Text = '被扔在地上的深色皮衣，皮衣的表面沾有斑驳的血迹，看起来令人不寒而栗……', Speaker = 6309, Option = '{拿来看看,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[86524] = { id = 86524, Voice = '', Text = '但这似乎也不能解释为什么探险队的成员都无故消失了，这其中一定还有我们不能理解的某些东西。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86523] = { id = 86523, Voice = '', Text = '我想那些探险队一定遇到了难以想象的困难，或许他们曾与嗜血的魔物发生过激战……而这头盔就是被探险队消灭的魔物所有……', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86522] = { id = 86522, Voice = '', Text = '从头盔的规格和形制上来看，这的确是属于某种魔物所有，但以我的眼力竟然看不出到底是哪种魔物……', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86514] = { id = 86514, Voice = '', Text = '手中的头盔向外散发着一丝古怪波动，你发现自己不受控制地进入了一个全新的世界。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[86513] = { id = 86513, Voice = '', Text = '一个沾染着血迹的头盔，没有人知道为什么它会被放置在这里，如果仔细去看，你会发现头盔周围出现的古怪空间波动……', Speaker = 6308, Option = '{拿来看看,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[86512] = { id = 86512, Voice = '', Text = '如今，钟楼的局势仍然不容乐观，强大的魔物步步紧逼，而我来到这里就是要查明无数探险者的消失之谜，或许只有这样才能揭开钟楼隐藏着的秘密。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86511] = { id = 86511, Voice = '', Text = '无言的恐慌在巴特蒙德到来之后得到缓解，大贤者把黄金时钟安放在钟楼之内，并留下守护者守护黄金时钟。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86510] = { id = 86510, Voice = '', Text = '那些探险队的成员都莫名消失了，没有人能解释其中的曲折。时至今日仍有多次的探险任务，但无一例外，从没有人能走出这神秘的钟楼。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86509] = { id = 86509, Voice = '', Text = '卡普拉公司在急切之中组成了一支精英探险队，进入钟楼试图探索异变的起源，但探险的结果却令人失望透顶。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86508] = { id = 86508, Voice = '', Text = '在钟楼出现怪物的起初，巨大的舆论波动席卷了整个艾尔帕兰，人们提心吊胆，莫名的恐惧竟险些酿成了声势浩大的动乱。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86507] = { id = 86507, Voice = '', Text = '卡普拉公司虽然承担着钟楼的建造之责，但毋庸置疑，公司的内部对钟楼的异变并不十分清楚，反而因此蒙受了巨大损失。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86506] = { id = 86506, Voice = '', Text = '在钟楼建造的伊始，就被一层层的迷雾所笼罩，围绕着钟楼的建造和修葺，很多诡异的事情无法得到合理的解释。', Speaker = 6304, Option = '', Action = _EmptyTable,}, 
	[86417] = { id = 86417, Voice = '', Text = '接下来我和蒂尼会一起享受我们有限但宝贵的人生。', Speaker = 6290, Option = '', Action = _EmptyTable,}, 
	[86416] = { id = 86416, Voice = '', Text = '谢谢你，我和妹妹会永远记得你的恩情。', Speaker = 6290, Option = '这没什么', Action = _EmptyTable,}, 
	[86415] = { id = 86415, Voice = '', Text = '冒险者你做到了，你让我停止的时间重新开始转动了。', Speaker = 6290, Option = '太好了', Action = _EmptyTable,}, 
	[86414] = { id = 86414, Voice = '', Text = '就是它！它身上有哥哥你的契约！', Speaker = 6288, Option = '', Action = _EmptyTable,}, 
	[86413] = { id = 86413, Voice = '', Text = '我逃出来海拉一定不会善罢甘休，她的手下肯定会追杀我们。', Speaker = 6288, Option = '', Action = _EmptyTable,}, 
	[86412] = { id = 86412, Voice = '', Text = '是什么？', Speaker = 6290, Option = '', Action = _EmptyTable,}, 
	[86411] = { id = 86411, Voice = '', Text = '哥哥这些都过去了，现在有更重要的事。', Speaker = 6288, Option = '', Action = _EmptyTable,}, 
	[86410] = { id = 86410, Voice = '', Text = '对不起，因为我自身的贪婪才害你被困百年。', Speaker = 6290, Option = '', Action = _EmptyTable,}, 
	[86409] = { id = 86409, Voice = '', Text = '谢谢你帮我解救了我妹妹！', Speaker = 6290, Option = '不用客气', Action = _EmptyTable,}, 
	[86401] = { id = 86401, Voice = '', Text = '是否进入时间夹缝？', Speaker = 6296, Option = '{是,2},{否,3}', Action = _EmptyTable,}, 
	[86400] = { id = 86400, Voice = '', Text = '里面一定凶多吉少，我妹妹的安危就靠你了。', Speaker = 6290, Option = '', Action = _EmptyTable,}, 
	[86399] = { id = 86399, Voice = '', Text = '我妹妹一定就被困在这里面！', Speaker = 6290, Option = '对', Action = _EmptyTable,}, 
