	[86386] = { id = 86386, Voice = '', Text = '天国之门是传说看守奥丁神殿之门的兰特克力斯所拥有的神物，是奥丁赐予他自由来往神殿与俗世的传送石，我听闻这个传送石的传说，你能把它带给我看看吗？', Speaker = 6295, Option = '{拿去,2},{我没有,3}', Action = _EmptyTable,}, 
	[86385] = { id = 86385, Voice = '', Text = '血咒是如同血色一般，充满诅咒力量的宝石，是德古拉男爵最为喜爱的宝石。我听闻这个宝石的传说，你能把它带给我看看吗？', Speaker = 6295, Option = '{拿去,2},{我没有,3}', Action = _EmptyTable,}, 
	[86384] = { id = 86384, Voice = '', Text = '传说勇者莫特利所持有的武器，名叫莫特利之剑！它跟随莫特利挑战过诸多魔物，我听闻这个武器的传说，你能把它带给我看看吗？', Speaker = 6295, Option = '{拿去,2},{我没有,3}', Action = _EmptyTable,}, 
	[86383] = { id = 86383, Voice = '', Text = '恶魔露出了深邃的微笑，将一切心智不坚定的人诱惑并且堕落黑暗，我听闻这个珍藏品的传说，你能把它带给我看看吗？', Speaker = 6295, Option = '{拿去,2},{我没有,3}', Action = _EmptyTable,}, 
