	[86374] = { id = 86374, Voice = '', Text = '对的时间对的时钟将指引你开启时间的大门。', Speaker = 6294, Option = '', Action = _EmptyTable,}, 
	[86373] = { id = 86373, Voice = '', Text = '傍晚六点十五分四十五秒。', Speaker = 6294, Option = '', Action = _EmptyTable,}, 
	[86371] = { id = 86371, Voice = '', Text = '每到傍晚六点十五分四十五秒时发光的表盘，还有这句“对的时间对的时钟将指引你开启时间的大门”一定都意味这什么。', Speaker = 6290, Option = '', Action = _EmptyTable,}, 
	[86370] = { id = 86370, Voice = '', Text = '对的时间对的时钟将指引你开启时间的大门。', Speaker = 6290, Option = '{懂了,2},{仍感困惑,3}', Action = _EmptyTable,}, 
	[86369] = { id = 86369, Voice = '', Text = '怀表背后还刻了一行小字，我猜这些一定都是通往时间夹缝的线索。', Speaker = 6290, Option = '什么字', Action = _EmptyTable,}, 
	[86368] = { id = 86368, Voice = '', Text = '傍晚的六点十五分四十五秒……', Speaker = 6290, Option = '', Action = _EmptyTable,}, 
	[86367] = { id = 86367, Voice = '', Text = '这怀表很是奇怪，每到固定的时间，表盘都会发出诡异的红光。', Speaker = 6290, Option = '什么时间', Action = _EmptyTable,}, 
	[86366] = { id = 86366, Voice = '', Text = '海拉留下了一枚怀表，只要解开这枚怀表之谜，就能把我妹妹从时间的夹缝中救出来！', Speaker = 6290, Option = '该怎么做', Action = _EmptyTable,}, 
	[86365] = { id = 86365, Voice = '', Text = '我想救出我的妹妹蒂尼，这一切都是因为我的贪婪咎由自取。', Speaker = 6290, Option = '别自责了', Action = _EmptyTable,}, 
	[86364] = { id = 86364, Voice = '', Text = '几百年了，虽然我能不老不死，但我一点也不快乐。', Speaker = 6290, Option = '', Action = _EmptyTable,}, 
	[86363] = { id = 86363, Voice = '', Text = '你也看到了，因此我的妹妹被海拉的手下关进了时间的夹缝中。', Speaker = 6290, Option = '', Action = _EmptyTable,}, 
	[86362] = { id = 86362, Voice = '', Text = '当年我和海拉死神签订了时间停止的契约，所以我的时间永远停住了。', Speaker = 6290, Option = '原来如此', Action = _EmptyTable,}, 
	[86361] = { id = 86361, Voice = '', Text = '你都看到了？', Speaker = 6290, Option = '', Action = _EmptyTable,}, 
	[86350] = { id = 86350, Voice = '', Text = '打开这块表，年轻人，你将看到当年发生的一切。', Speaker = 6286, Option = '接过怀表', Action = _EmptyTable,}, 
	[86349] = { id = 86349, Voice = '', Text = '去钟楼里！', Speaker = 6286, Option = '', Action = _EmptyTable,}, 
	[86348] = { id = 86348, Voice = '', Text = '对，事情要从几百年前说起，那时候我还是钟塔里的修塔工。', Speaker = 6286, Option = '然后呢', Action = _EmptyTable,}, 
	[86347] = { id = 86347, Voice = '', Text = '这是解开时间停止之谜的关键。', Speaker = 6286, Option = '时间停止？', Action = _EmptyTable,}, 
	[86346] = { id = 86346, Voice = '', Text = '好了！！修好了，这可不是一般的怀表。', Speaker = 6286, Option = '是什么', Action = _EmptyTable,}, 
	[86345] = { id = 86345, Voice = '', Text = '这下可好了，让我来看看这破表。', Speaker = 6286, Option = '嗯', Action = _EmptyTable,}, 
	[86344] = { id = 86344, Voice = '', Text = '啊哈，有了这些指针，怀表就能修好了。', Speaker = 6286, Option = '', Action = _EmptyTable,}, 
	[86343] = { id = 86343, Voice = '', Text = '这种东西你能在钟楼的钟怪身上找到。', Speaker = 6286, Option = '', Action = _EmptyTable,}, 
	[86342] = { id = 86342, Voice = '', Text = '所以我需要你去钟楼里找一些新的指针来。', Speaker = 6286, Option = '指针？', Action = _EmptyTable,}, 
	[86341] = { id = 86341, Voice = '', Text = '我知道你是个热心人，首先我需要把我这该死的破怀表给修好。', Speaker = 6286, Option = '', Action = _EmptyTable,}, 
	[86340] = { id = 86340, Voice = '', Text = '是的，说来你可能不信，这一切都是因为我自身的愚蠢和无知。', Speaker = 6286, Option = '怎么讲', Action = _EmptyTable,}, 
	[86339] = { id = 86339, Voice = '', Text = '对，我已经上百岁了，几百年前我的时间就静止了。', Speaker = 6286, Option = '静止？', Action = _EmptyTable,}, 
	[86338] = { id = 86338, Voice = '', Text = '再猜猜，我聪明的年轻人。', Speaker = 6286, Option = '', Action = _EmptyTable,}, 
	[86337] = { id = 86337, Voice = '', Text = '你猜我有几岁了？', Speaker = 6286, Option = '{十岁,7},{二十几岁,7},{中年,7},{几百岁,4}', Action = _EmptyTable,}, 
	[86336] = { id = 86336, Voice = '', Text = '外面的世界依旧如往常一样继续着，我的世界却永远停在了那一天。', Speaker = 6286, Option = '什么意思', Action = _EmptyTable,}, 
	[86335] = { id = 86335, Voice = '', Text = '冒险者，我不会忘记你今天所做的决定。', Speaker = 6286, Option = '发生了什么事', Emoji = 9, Action = _EmptyTable,}, 
	[86334] = { id = 86334, Voice = '', Text = '年轻人，得不到你的帮助，明天将对我不再有意义。', Speaker = 6286, Option = '{我帮助你,2},{我很忙,3}', Action = _EmptyTable,}, 
	[86327] = { id = 86327, Voice = '', Text = '从这里进去就是奥尔露恩冕下的所在地了。', Speaker = 6285, Option = '', Action = _EmptyTable,}, 
	[86326] = { id = 86326, Voice = '', Text = '这时候女武神奥尔露恩冕下降临了，她带来了充满智慧的阿萨神碑。', Speaker = 6285, Option = '', Action = _EmptyTable,}, 
	[86325] = { id = 86325, Voice = '', Text = '但是凭借我们人类现有的力量恐怕不足以在瓦尔哈拉废墟中存活。', Speaker = 6285, Option = '', Action = _EmptyTable,}, 
	[86324] = { id = 86324, Voice = '', Text = '听说众神需要冒险者们进入瓦尔哈拉废墟其中寻找一种名叫“尼伯龙根”的魔金。', Speaker = 6285, Option = '', Action = _EmptyTable,}, 
	[86323] = { id = 86323, Voice = '', Text = '女武神的降临是王国从来没有想到的，国王陛下命令我来到这里看守。', Speaker = 6285, Option = '', Action = _EmptyTable,}, 
	[86322] = { id = 86322, Voice = '', Text = '众神的恩赐越强大，冒险者力量的提升就尤其明显！如果你想要变得更强，向女武神祈祷是个很好的选择！', Speaker = 6284, Option = '', Action = _EmptyTable,}, 
	[86321] = { id = 86321, Voice = '', Text = '在向众神祈祷的同时，冒险者们可以得到力量的提升，这就是祈祷的意义。', Speaker = 6284, Option = '', Action = _EmptyTable,}, 
	[86320] = { id = 86320, Voice = '', Text = '因此，女武神瓦尔基里殿下在这公会中，给予优秀的人类冒险者祈祷的权利。', Speaker = 6284, Option = '', Action = _EmptyTable,}, 
	[86319] = { id = 86319, Voice = '', Text = '英灵殿的女武神们一直关注着人类，他们把人类中赫赫有名的冒险者们当成英灵殿的预备人选。', Speaker = 6284, Option = '', Action = _EmptyTable,}, 
	[86316] = { id = 86316, Voice = '', Text = '这也是为了防止诸神黄昏的惨剧再度发生。毕竟这个世界需要更多的勇者！', Speaker = 6283, Option = '', Action = _EmptyTable,}, 
	[86315] = { id = 86315, Voice = '', Text = '而成为英灵殿的预备成员可以获得神的恩赐——“神力”，这股力量会使人类突破种族的限制，成为能与英灵们相提并论的强者。', Speaker = 6283, Option = '', Action = _EmptyTable,}, 
	[86314] = { id = 86314, Voice = '', Text = '现在女武神全面入驻人类勇者的集聚地——公会，帮助并选拔人类中的勇者，成为英灵殿的预备力量。', Speaker = 6283, Option = '', Action = _EmptyTable,}, 
	[86313] = { id = 86313, Voice = '', Text = '但是众神仍然相信人类身上拥有无限可能，经过众神的研究，他们改良了“英灵之力”的力量构成，形成一种新的“神力”，即使是以人类为载体，都可以很轻松的驾驭这种力量。', Speaker = 6283, Option = '', Action = _EmptyTable,}, 
	[86312] = { id = 86312, Voice = '', Text = '现今的人们几乎无法得到可以媲美英灵的力量，因为人类的身体根本无法负担起亡者的力量。这股力量会使人类的身材逐渐崩坏，最终死去。', Speaker = 6283, Option = '', Action = _EmptyTable,}, 
	[86311] = { id = 86311, Voice = '', Text = '英灵的力量是亡者的力量，是只有在人类死后才有机会得到的“神的恩赐”。', Speaker = 6283, Option = '', Action = _EmptyTable,}, 
	[86310] = { id = 86310, Voice = '', Text = '当残存的众神得到下一次诸神黄昏已经在酝酿当中的消息时，众神决心提前准备，决心重现英灵殿昔日的辉煌。', Speaker = 6283, Option = '', Action = _EmptyTable,}, 
	[86309] = { id = 86309, Voice = '', Text = '在诸神的黄昏后，英灵们死伤大半，当年的英灵殿如今却显得空荡无比。', Speaker = 6283, Option = '', Action = _EmptyTable,}, 
	[86308] = { id = 86308, Voice = '', Text = '长久以来，英灵们保护着神庭与天空城的安全，他们无愧于英灵的称号。', Speaker = 6283, Option = '', Action = _EmptyTable,}, 
	[86307] = { id = 86307, Voice = '', Text = '世间的勇者死后进入了伟大的神奥丁冕下建立的英灵殿，成为了一名为神，为世人而战斗的英灵。', Speaker = 6283, Option = '', Action = _EmptyTable,}, 
	[86233] = { id = 86233, Voice = '', Text = '接下来把蓝色之血倒入第二个恶魔池中。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[86215] = { id = 86215, Voice = '', Text = '今天既然让我出来了，我要杀光你们所有人！！', Speaker = 6256, Option = '', Action = _EmptyTable,}, 
	[86214] = { id = 86214, Voice = '', Text = '是我大意了！！！', Speaker = 6255, Option = '', Action = _EmptyTable,}, 
	[86213] = { id = 86213, Voice = '', Text = '哈哈哈哈哈，施耐德你这个蠢货，居然将自己灵魂和记忆分开封印。', Speaker = 6256, Option = '', Action = _EmptyTable,}, 
	[86212] = { id = 86212, Voice = '', Text = '现在储存我记忆和肉体的封印被打开，这就意味着…', Speaker = 6255, Option = '', Action = _EmptyTable,}, 
	[86211] = { id = 86211, Voice = '', Text = '不好！！！！', Speaker = 6255, Option = '怎么了', Action = _EmptyTable,}, 
	[86210] = { id = 86210, Voice = '', Text = '用尽自己全部的法力将他和我的肉体一起封印了起来。', Speaker = 6255, Option = '是这样', Action = _EmptyTable,}, 
	[86209] = { id = 86209, Voice = '', Text = '当时这个恶魔子爵无人能敌，我为了保护国王……', Speaker = 6255, Option = '', Action = _EmptyTable,}, 
	[86208] = { id = 86208, Voice = '', Text = '他不惜与恶魔做交易，误入歧途，也因此得到了强大的力量。', Speaker = 6255, Option = '太糟糕了', Action = _EmptyTable,}, 
	[86207] = { id = 86207, Voice = '', Text = '那个时候有位狂妄的子爵企图掀起政变篡夺王位。', Speaker = 6255, Option = '然后呢', Action = _EmptyTable,}, 
	[86206] = { id = 86206, Voice = '', Text = '我是几百年前为国王效命的皇家魔导士。', Speaker = 6255, Option = '原来如此', Action = _EmptyTable,}, 
	[86205] = { id = 86205, Voice = '', Text = '我全想起来了！', Speaker = 6255, Option = '', Action = _EmptyTable,}, 
	[86204] = { id = 86204, Voice = '', Text = '谢谢你冒险者，我终于找回了记忆。', Speaker = 6255, Option = '太好了', Action = _EmptyTable,}, 
	[86203] = { id = 86203, Voice = '', Text = '我感觉记忆正在涌入我的身体里！', Speaker = 6246, Option = '', Action = _EmptyTable,}, 
	[86202] = { id = 86202, Voice = '', Text = '只要把这个记忆容器打开，就能释放我的记忆和我的肉身。', Speaker = 6246, Option = '明白了', Action = _EmptyTable,}, 
	[86201] = { id = 86201, Voice = '', Text = '看来你成功了！', Speaker = 6246, Option = '', Action = _EmptyTable,}, 
