	[86166] = { id = 86166, Voice = '', Text = '将要点亮', Speaker = 6247, Option = '{红色灯柱,0},{白色灯柱,0},{黑色灯柱,4}', Action = _EmptyTable,}, 
	[86164] = { id = 86164, Voice = '', Text = '也许秘密就在“顺时针开启变革的明灯，三黑三白点燃血光之灾”这句诗里。', Speaker = 6238, Option = '', Action = _EmptyTable,}, 
	[86163] = { id = 86163, Voice = '', Text = '可是我怎么也想不起来该如何点亮了，你有头绪吗？', Speaker = 6238, Option = '{有,2},{没有,3}', Action = _EmptyTable,}, 
	[86162] = { id = 86162, Voice = '', Text = '根据诗的内容，看来我们需要点亮这些灯台。', Speaker = 6238, Option = '有道理', Action = _EmptyTable,}, 
	[86161] = { id = 86161, Voice = '', Text = '话音刚落，在不远处的圆台上便升起七盏灯台。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[86160] = { id = 86160, Voice = '', Text = '顺时针开启变革的明灯，三黑三白点燃血光之灾。', Speaker = 6238, Option = '', Action = _EmptyTable,}, 
	[86159] = { id = 86159, Voice = '', Text = '最后一段诗歌是……', Speaker = 6238, Option = '', Action = _EmptyTable,}, 
	[86158] = { id = 86158, Voice = '', Text = '有了它我就能想起最后一段诗歌了！', Speaker = 6238, Option = '太好了', Action = _EmptyTable,}, 
	[86157] = { id = 86157, Voice = '', Text = '这是我的记忆碎片！', Speaker = 6238, Option = '', Action = _EmptyTable,}, 
	[86156] = { id = 86156, Voice = '', Text = '可怕的记忆幻境碎成了无数碎片，在空中合成一块小碎片掉落在地上。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[86155] = { id = 86155, Voice = '', Text = '此时恶魔雕像前竟然出现了一个可怕的记忆幻境！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[86154] = { id = 86154, Voice = '', Text = '对，就是恶魔雕像旁的两个小水池。', Speaker = 6238, Option = '明白了', Action = _EmptyTable,}, 
	[86153] = { id = 86153, Voice = '', Text = '我猜，按照诗歌的意思，得把这血液倒入恶魔池中。', Speaker = 6238, Option = '恶魔池？', Action = _EmptyTable,}, 
	[86152] = { id = 86152, Voice = '', Text = '没错，就是这种深不可测的蓝色血液。', Speaker = 6238, Option = '', Action = _EmptyTable,}, 
	[86151] = { id = 86151, Voice = '', Text = '请你帮我弄些来吧。', Speaker = 6238, Option = '交给我吧', Action = _EmptyTable,}, 
	[86150] = { id = 86150, Voice = '', Text = '蓝色之血我知道，是蓝鼠的血。', Speaker = 6238, Option = '然后呢', Action = _EmptyTable,}, 
	[86149] = { id = 86149, Voice = '', Text = '借蓝色之血掀起涟漪，奔腾恶魔池中的浪涛。', Speaker = 6238, Option = '什么意思', Action = _EmptyTable,}, 
	[86148] = { id = 86148, Voice = '', Text = '我想起了第二段诗歌。', Speaker = 6238, Option = '请说', Action = _EmptyTable,}, 
	[86147] = { id = 86147, Voice = '', Text = '太好了！你的线索又让我想起了些什么！', Speaker = 6238, Option = '', Action = _EmptyTable,}, 
	[86146] = { id = 86146, Voice = '', Text = '我再重复一遍我的诗歌：骑士的佩剑高举过头，灵魂在把胸膛磨穿。', Speaker = 6238, Option = '', Action = _EmptyTable,}, 
	[86145] = { id = 86145, Voice = '', Text = '你是不是没有找到那些雕像？', Speaker = 6238, Option = '', Action = _EmptyTable,}, 
	[86144] = { id = 86144, Voice = '', Text = '只等这光荣', Speaker = 6238, Option = '{遮挡,0},{熄灭,0},{陨落,0},{丧尽,6}', Action = _EmptyTable,}, 
	[86143] = { id = 86143, Voice = '', Text = '只等这光荣', Speaker = 6238, Option = '{遮挡,0},{熄灭,0},{陨落,0},{丧尽,0}', Action = _EmptyTable,}, 
	[86142] = { id = 86142, Voice = '', Text = '焚毁着高耸', Speaker = 6238, Option = '{王座,0},{圣殿,5},{城墙,0},{王权,0}', Action = _EmptyTable,}, 
	[86141] = { id = 86141, Voice = '', Text = '只等这光荣', Speaker = 6238, Option = '{遮挡,0},{熄灭,0},{陨落,0},{丧尽,0}', Action = _EmptyTable,}, 
	[86140] = { id = 86140, Voice = '', Text = '焚毁着高耸', Speaker = 6238, Option = '{王座,0},{圣殿,0},{城墙,0},{王权,0}', Action = _EmptyTable,}, 
	[86139] = { id = 86139, Voice = '', Text = '谋反的屠杀', Speaker = 6238, Option = '{号角,4},{圣琴,0},{怒吼,0},{长鸣,0}', Action = _EmptyTable,}, 
	[86138] = { id = 86138, Voice = '', Text = '那就让我们来对对看，你收集到的线索。', Speaker = 6238, Option = '', Action = _EmptyTable,}, 
	[86137] = { id = 86137, Voice = '', Text = '正好，我也想起了一些片段。', Speaker = 6238, Option = '太好了', Action = _EmptyTable,}, 
	[86136] = { id = 86136, Voice = '', Text = '骑士的佩剑高举过头，灵魂在把胸膛磨穿。', Speaker = 6238, Option = '', Action = _EmptyTable,}, 
	[86135] = { id = 86135, Voice = '', Text = '我再重复一遍。', Speaker = 6238, Option = '', Action = _EmptyTable,}, 
	[86134] = { id = 86134, Voice = '', Text = '你帮我找到线索了吗？', Speaker = 6238, Option = '{找到了,2},{没找到,3}', Action = _EmptyTable,}, 
	[86130] = { id = 86130, Voice = '', Text = '我记得这古城大厅之中有三座诗歌里的雕像，应该能从那里得到一些线索。', Speaker = 6238, Option = '有道理', Action = _EmptyTable,}, 
	[86129] = { id = 86129, Voice = '', Text = '看来这诗歌果然和我的身世之谜有关。', Speaker = 6238, Option = '没错', Action = _EmptyTable,}, 
	[86128] = { id = 86128, Voice = '', Text = '这诗歌果然有古怪！似乎有什么要阻止我找回记忆。', Speaker = 6238, Option = '', Action = _EmptyTable,}, 
	[86127] = { id = 86127, Voice = '', Text = '没等施耐德念完，周围突然围上来三个袭击他的骑士灵魂。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[86126] = { id = 86126, Voice = '', Text = '骑士的佩剑高举过头，灵魂在把胸膛磨穿……', Speaker = 6238, Option = '', Action = _EmptyTable,}, 
	[86125] = { id = 86125, Voice = '', Text = '第一段是这样的……', Speaker = 6238, Option = '', Action = _EmptyTable,}, 
	[86124] = { id = 86124, Voice = '', Text = '我脑海里一直反复着一首小诗……', Speaker = 6238, Option = '什么小诗', Action = _EmptyTable,}, 
	[86123] = { id = 86123, Voice = '', Text = '这可是个好问题，也许我们该从我记得的那些片段里找找线索。', Speaker = 6238, Option = '什么片段', Action = _EmptyTable,}, 
	[86122] = { id = 86122, Voice = '', Text = '等我找回记忆，一定好好报答你，我的朋友。', Speaker = 6238, Option = '如何找回', Action = _EmptyTable,}, 
	[86121] = { id = 86121, Voice = '', Text = '是的，其余的就什么也想不起来了，你能帮我找回肉身吗？', Speaker = 6238, Option = '可以', Action = _EmptyTable,}, 
	[86120] = { id = 86120, Voice = '', Text = '还有一些零散的记忆片段。', Speaker = 6238, Option = '记忆片段？', Action = _EmptyTable,}, 
	[86119] = { id = 86119, Voice = '', Text = '我只记得有人把我的灵魂从身体里抽了出来。', Speaker = 6238, Option = '', Action = _EmptyTable,}, 
	[86118] = { id = 86118, Voice = '', Text = '我很想报答你，可是我连自己是谁都不记得了。', Speaker = 6238, Option = '为什么', Action = _EmptyTable,}, 
	[86117] = { id = 86117, Voice = '', Text = '谢谢你把我从这该死的破瓶子里放出来！', Speaker = 6238, Option = '不用谢', Action = _EmptyTable,}, 
	[86116] = { id = 86116, Voice = '', Text = '居然是一个灵魂！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[86115] = { id = 86115, Voice = '', Text = '随着玻璃瓶的瓶盖打开，从瓶子中缓缓飘出一缕青烟。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[86114] = { id = 86114, Voice = '', Text = '一个看似没什么特别的破旧玻璃瓶，要打开看看吗？', Speaker = 6237, Option = '{打开,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[86013] = { id = 86013, Voice = '', Text = '很抱歉，你身上的钱似乎有些不够呢……你还是准备好足够的钱再来吧。', Speaker = 6236, Option = '', Action = _EmptyTable,}, 
	[86012] = { id = 86012, Voice = '', Text = '那么请收好这张卷轴，凭着这张我们莱弥亚家族的古老卷轴去我哥哥莱弥亚·霍尔特处就可以挑选武器了，希望你可以收获惊喜哦。', Speaker = 6236, Option = '', Action = _EmptyTable,}, 
	[86011] = { id = 86011, Voice = '', Text = '那么，冒险者，你是否愿意用[c][ffff00]1,990,000Zeny[-][/c]来购买一张交易卷轴呢？', Speaker = 6236, Option = '{确认购买,2},{没有兴趣,3}', Action = _EmptyTable,}, 
	[86010] = { id = 86010, Voice = '', Text = '为了不使这些珍贵华丽的梦幻武器消失在默默无闻的岁月里，现在冒险者只需花费1,990,000Zeny就可以从我们莱弥亚家族购买一张交易卷轴，用以挑选一把中意的武器呢。', Speaker = 6236, Option = '', Action = _EmptyTable,}, 
	[86009] = { id = 86009, Voice = '', Text = '而现在百年时光已逝，曾经的王朝早已没落，尘封已久的皇家武器库也终于得以重见天日。', Speaker = 6236, Option = '', Action = _EmptyTable,}, 
	[86008] = { id = 86008, Voice = '', Text = '多年前风靡一时的皇家武器库曾经引发无数冒险者的疯狂追逐，而我们莱弥亚家族履行了自己对王朝的诺言，悄无声息地保护着这些价值不菲的珍贵武器。', Speaker = 6236, Option = '', Action = _EmptyTable,}, 
	[85999] = { id = 85999, Voice = '', Text = '你身上没有我所需要的！', Speaker = 6198, Option = '', Action = _EmptyTable,}, 
	[85998] = { id = 85998, Voice = '', Text = '你身上没有我所需要的！', Speaker = 6197, Option = '', Action = _EmptyTable,}, 
	[85997] = { id = 85997, Voice = '', Text = '你身上没有我所需要的！', Speaker = 6195, Option = '', Action = _EmptyTable,}, 
	[85996] = { id = 85996, Voice = '', Text = '你身上没有我所需要的！', Speaker = 6196, Option = '', Action = _EmptyTable,}, 
	[85995] = { id = 85995, Voice = '', Text = '哈哈哈哈哈，能够结识你真是一件开心的事，喏，这是我的一点小心意，收下吧。', Speaker = 6234, Option = '好的', Action = _EmptyTable,}, 
	[85994] = { id = 85994, Voice = '', Text = '真是让我惊喜，我觉得只有你，才是我的知己。', Speaker = 6234, Option = '', Action = _EmptyTable,}, 
	[85993] = { id = 85993, Voice = '', Text = '恶魔微笑，莫特利之剑，血咒，天国之门…没想到你的收藏已经丰富到了现在这个地步。', Speaker = 6234, Option = '', Action = _EmptyTable,}, 
	[85992] = { id = 85992, Voice = '', Text = '接下来你要做的，就是收集更多这样的收藏品，加油吧！', Speaker = 6198, Option = '明白', Action = _EmptyTable,}, 
	[85991] = { id = 85991, Voice = '', Text = '嘿，能收集全这些收藏品，你也算是个小有成就的收藏家了。', Speaker = 6198, Option = '', Action = _EmptyTable,}, 
	[85990] = { id = 85990, Voice = '', Text = '我很荣幸能结识像你这样的冒险者。', Speaker = 6197, Option = '我也很荣幸', Action = _EmptyTable,}, 
	[85989] = { id = 85989, Voice = '', Text = '要知道收藏这些珍品可是可以提高一个人的审美观念的。', Speaker = 6197, Option = '', Action = _EmptyTable,}, 
	[85988] = { id = 85988, Voice = '', Text = '喏，这是我送你的小礼物，希望你喜欢。', Speaker = 6195, Option = '好的', Action = _EmptyTable,}, 
	[85987] = { id = 85987, Voice = '', Text = '唔，从我这里换走了这么多收藏品，看来你也是一个爱好收藏的收藏家啊！', Speaker = 6195, Option = '', Action = _EmptyTable,}, 
	[85986] = { id = 85986, Voice = '', Text = '这是我准备的小礼物，收下吧。', Speaker = 6196, Option = '好的', Action = _EmptyTable,}, 
