	[85973] = { id = 85973, Voice = '', Text = '兑换权利的话，需要20个沾有血渍的权杖哦~', Speaker = 6195, Option = '', Action = _EmptyTable,}, 
	[85972] = { id = 85972, Voice = '', Text = '兑换八犬士的勇气的话，需要20个传说中的勇气哦~', Speaker = 6196, Option = '', Action = _EmptyTable,}, 
	[85971] = { id = 85971, Voice = '', Text = '兑换须佐之男的思念的话，需要15个神秘的眼泪哦~', Speaker = 6196, Option = '', Action = _EmptyTable,}, 
	[85970] = { id = 85970, Voice = '', Text = '兑换国纲的印记的话，需要20个不知名的印记哦~', Speaker = 6196, Option = '', Action = _EmptyTable,}, 
	[85969] = { id = 85969, Voice = '', Text = '兑换蛇鳞的话，需要20个破损的鳞哦~', Speaker = 6196, Option = '', Action = _EmptyTable,}, 
	[85968] = { id = 85968, Voice = '', Text = '兑换妖气的话，需要25个神秘气体哦~', Speaker = 6196, Option = '', Action = _EmptyTable,}, 
	[85961] = { id = 85961, Voice = '', Text = '——来自遗迹学者的调查报告。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85960] = { id = 85960, Voice = '', Text = '我将它记录为[c][ffff00]【黑暗的光明】[-][/c]。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85959] = { id = 85959, Voice = '', Text = '他的爱人妮卡最后认出了他的身份，但是逝去的，终究是逝去了…', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85958] = { id = 85958, Voice = '', Text = '在黑暗魔物大举入侵的最终时刻，黑武士爱德华用自己所有的力量以及生命，消灭了魔物的头领。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85957] = { id = 85957, Voice = '', Text = '他决定用这股力量去帮助需要帮助的人，以及…暗中守护着他的爱人。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85956] = { id = 85956, Voice = '', Text = '只有邪恶的人，没有邪恶的力量。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85955] = { id = 85955, Voice = '', Text = '他是骑士，尽管他身上充斥着黑暗的力量，但是他坚持认为，光明与黑暗只在一念之间。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85954] = { id = 85954, Voice = '', Text = '在这里，他找到了一个黑暗心核，借此他脱离了险境，代价是失去了自己的容貌，以及寿命。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85953] = { id = 85953, Voice = '', Text = '遭遇袭击的爱德华带着不死的魔物来到了一处隐蔽的洞穴。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85952] = { id = 85952, Voice = '', Text = '[c][ffff00]【施密特王朝GLC.X185号档案】[-][/c]已解锁……是否解读？', Speaker = 6201, Option = '解读', Action = _EmptyTable,}, 
	[85951] = { id = 85951, Voice = '', Text = '文件编号是[c][ffff00]【施密特王朝GLC.X185号档案】[-][/c]。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85950] = { id = 85950, Voice = '', Text = '这是经过我整理的关于历史断层的黑武士事件的文件。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85949] = { id = 85949, Voice = '', Text = '而黑武士的传说也因此渐渐从古城消失……', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85948] = { id = 85948, Voice = '', Text = '在切尔西的安慰下，妮卡与切尔西离开了克雷斯特汉姆，不知去往了哪里。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85947] = { id = 85947, Voice = '', Text = '妮卡从爱德华的身上找到了这个徽章，她发现，这其实是爱德华送给她的那条项链。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85946] = { id = 85946, Voice = '', Text = '而认出了黑武士就是爱德华的妮卡痛苦万分。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85945] = { id = 85945, Voice = '', Text = '黑武士最终牺牲了自己，换取了黑暗袭击战役的胜果。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85933] = { id = 85933, Voice = '', Text = '漆黑如墨的徽章，其上的黑暗气息还是如此浓厚…', Speaker = 6218, Option = '{捡起来看看,2},{无视它,3}', Action = _EmptyTable,}, 
	[85932] = { id = 85932, Voice = '', Text = '出现关键道具——[c][ffff00]黑武士徽章[-][/c]。', Speaker = 6218, Option = '', Action = _EmptyTable,}, 
	[85931] = { id = 85931, Voice = '', Text = '他没有办法带着这张残破的脸回到妮卡的身边，他决定用这黑暗的力量做一些事。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85930] = { id = 85930, Voice = '', Text = '他发现他获得了强大的力量，但是为此他付出了代价，他的脸被毁了。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85929] = { id = 85929, Voice = '', Text = '黑暗的力量瞬间席卷爱德华的全身，爱德华华美的骑士甲胄在黑暗的力量下逐渐变成黑色。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85928] = { id = 85928, Voice = '', Text = '那是强大无比的深渊骑士，无法忍受饥饿侵蚀的爱德华忍不住吃了充满香气的黑暗心核。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85927] = { id = 85927, Voice = '', Text = '进入了魔物们都不敢深入的洞穴，爱德华发现了一具尸体。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85918] = { id = 85918, Voice = '', Text = '虽然看上去有些残破，但其中残存的黑暗力量依然让人们心悸不已……', Speaker = 6215, Option = '{捡起来看看,2},{无视它,3}', Action = _EmptyTable,}, 
	[85917] = { id = 85917, Voice = '', Text = '出现关键道具——[c][ffff00]黑暗心核[-][/c]。', Speaker = 6215, Option = '', Action = _EmptyTable,}, 
	[85916] = { id = 85916, Voice = '', Text = '奇怪…这跟黑武士并没有联系…', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85915] = { id = 85915, Voice = '', Text = '而脱离危险的妮卡还在想怎么把野猪肉烤得更加美味，却不知道，那位想要吃的人已经永远回不来了。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85914] = { id = 85914, Voice = '', Text = '此时爱德华的心里只有一个念头，把这些不死的怪物引到远离妮卡的地方…', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85913] = { id = 85913, Voice = '', Text = '爱德华又一次解决了对手，但是这些魔物再次复活，不得已，爱德华只能逃离这里。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85912] = { id = 85912, Voice = '', Text = '看到爱人离开，爱德华终于松了一口气，他明白自己的力量已经消耗殆尽。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85911] = { id = 85911, Voice = '', Text = '对爱人的信任使得妮卡同意了爱德华的建议。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85910] = { id = 85910, Voice = '', Text = '为了保全自己的爱人，爱德华挂着轻松的笑容，告诉妮卡自己要单独引开这些魔物。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85909] = { id = 85909, Voice = '', Text = '爱德华遇到了不死的弓箭手，毫无疑问，他陷入了苦战。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85908] = { id = 85908, Voice = '', Text = '可恶！竟然复活了？…体力消耗太大了…我得想办法甩开这些讨厌的家伙…', Speaker = 6212, Option = '', Action = _EmptyTable,}, 
	[85907] = { id = 85907, Voice = '', Text = '嗯？', Speaker = 6212, Option = '', Action = _EmptyTable,}, 
	[85906] = { id = 85906, Voice = '', Text = '接下来…该解决你们了！', Speaker = 6212, Option = '', Action = _EmptyTable,}, 
	[85905] = { id = 85905, Voice = '', Text = '嗯！', Speaker = 6213, Option = '', Action = _EmptyTable,}, 
	[85904] = { id = 85904, Voice = '', Text = '放心吧妮卡！今晚我想吃你烤的野猪肉！乖乖在家等着我！', Speaker = 6212, Option = '', Action = _EmptyTable,}, 
	[85903] = { id = 85903, Voice = '', Text = '嗯…我相信你的实力，你一定要回来！', Speaker = 6213, Option = '', Action = _EmptyTable,}, 
	[85902] = { id = 85902, Voice = '', Text = '最近魔物越来越多了…妮卡，接下来，你要往城里跑，我会把它们引到相反的方向。', Speaker = 6212, Option = '', Action = _EmptyTable,}, 
	[85901] = { id = 85901, Voice = '', Text = '一个做工精美的项链，但是最重要的部分似乎已经缺失了…', Speaker = 6204, Option = '{捡起来看看,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[85900] = { id = 85900, Voice = '', Text = '爱德华又是什么人物，怎么会死去呢？', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85899] = { id = 85899, Voice = '', Text = '然而令我感到疑惑的是，自诩正义的黑武士为什么会参与到这件小事呢？', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85898] = { id = 85898, Voice = '', Text = '不得不说黑武士的力量与技巧真的强到令人震怖！', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85897] = { id = 85897, Voice = '', Text = '使用这种武器的都是强大的人物，而黑武士竟然能一下斩断铁链。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85896] = { id = 85896, Voice = '', Text = '剑戒，可是一把奇特的武器呢。剑柄上有着链子连接手中的戒指。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85895] = { id = 85895, Voice = '', Text = '…这个阿尔顿斯，简直莫名其妙！啊……妮卡还在等我！', Speaker = 6210, Option = '', Action = _EmptyTable,}, 
	[85894] = { id = 85894, Voice = '', Text = '……', Speaker = 6211, Option = '', Action = _EmptyTable,}, 
	[85893] = { id = 85893, Voice = '', Text = '总有一天我会找回场子的！等着吧黑武士！', Speaker = 6209, Option = '', Action = _EmptyTable,}, 
	[85892] = { id = 85892, Voice = '', Text = '啊！你居然弄断了我的剑戒！', Speaker = 6209, Option = '', Action = _EmptyTable,}, 
	[85891] = { id = 85891, Voice = '', Text = '真是不听劝！', Speaker = 6211, Option = '', Action = _EmptyTable,}, 
	[85890] = { id = 85890, Voice = '', Text = '嘿，听说你很厉害，想阻止我？就看你有没有这个本事了！', Speaker = 6209, Option = '', Action = _EmptyTable,}, 
	[85889] = { id = 85889, Voice = '', Text = '离开这里，阿尔顿斯！他说的没错。', Speaker = 6211, Option = '', Action = _EmptyTable,}, 
	[85888] = { id = 85888, Voice = '', Text = '黑武士？这点闲事你也要管吗？', Speaker = 6209, Option = '', Action = _EmptyTable,}, 
	[85887] = { id = 85887, Voice = '', Text = '不用再说了！打一场！', Speaker = 6209, Option = '', Action = _EmptyTable,}, 
	[85886] = { id = 85886, Voice = '', Text = '就凭你？城外的魔物一只手就能摆平你！你拿什么保护妮卡？', Speaker = 6209, Option = '', Action = _EmptyTable,}, 
	[85885] = { id = 85885, Voice = '', Text = '爱德华已经死了！妮卡应该拥有新的生活！我愿意带给她安稳的生活！', Speaker = 6210, Option = '', Action = _EmptyTable,}, 
	[85884] = { id = 85884, Voice = '', Text = '切尔西！是男人就痛快打一把！不要以为现在妮卡跟你在一起，我就不敢把你怎么样！', Speaker = 6209, Option = '', Action = _EmptyTable,}, 
	[85883] = { id = 85883, Voice = '', Text = '一个已经生锈的戒指，在戒指上还有着锁链，似乎链接着什么…', Speaker = 6203, Option = '{捡起来看看,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[85882] = { id = 85882, Voice = '', Text = '我感到很困惑…不过，这也是追寻历史的乐趣所在。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85881] = { id = 85881, Voice = '', Text = '妮卡又说对这个黑武士感到熟悉，难道他们俩存在着某种联系？', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85880] = { id = 85880, Voice = '', Text = '他突然出现在这里，没人知道为什么古城突然多出这样一个强大的人。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85879] = { id = 85879, Voice = '', Text = '在那个年代，黑武士就是正义的象征！', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85878] = { id = 85878, Voice = '', Text = '黑武士拥有着黑暗的力量，但是他又与黑暗的魔物不同，因为他崇尚正义。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85877] = { id = 85877, Voice = '', Text = '这本书可是记载着不少关于黑武士的消息……', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85876] = { id = 85876, Voice = '', Text = '你认识？…先不说这个，这里很危险，先离开这里！', Speaker = 6205, Option = '', Action = _EmptyTable,}, 
	[85875] = { id = 85875, Voice = '', Text = '这个黑武士…我从他身上感到一股熟悉的气息…', Speaker = 6206, Option = '', Action = _EmptyTable,}, 
	[85874] = { id = 85874, Voice = '', Text = '这什么人呐……说走就走……', Speaker = 6205, Option = '', Action = _EmptyTable,}, 
	[85873] = { id = 85873, Voice = '', Text = '……', Speaker = 6208, Option = '', Action = _EmptyTable,}, 
	[85872] = { id = 85872, Voice = '', Text = '我怎么会逃走？妮卡还在这里！', Speaker = 6205, Option = '', Action = _EmptyTable,}, 
	[85871] = { id = 85871, Voice = '', Text = '如果刚刚你敢一个人逃走，这时候你已经死了。', Speaker = 6208, Option = '', Action = _EmptyTable,}, 
	[85870] = { id = 85870, Voice = '', Text = '你是…黑武士？', Speaker = 6205, Option = '', Action = _EmptyTable,}, 
	[85869] = { id = 85869, Voice = '', Text = '别说了！快走！', Speaker = 6205, Option = '', Action = _EmptyTable,}, 
	[85868] = { id = 85868, Voice = '', Text = '你并不是战士！你挡不住他们的！', Speaker = 6206, Option = '', Action = _EmptyTable,}, 
	[85867] = { id = 85867, Voice = '', Text = '妮卡！你先走！这里我先挡住！', Speaker = 6205, Option = '', Action = _EmptyTable,}, 
	[85866] = { id = 85866, Voice = '', Text = '似乎是一本有趣的书，在岁月的侵蚀下显得破旧不堪…', Speaker = 6202, Option = '{捡起来看看,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[85865] = { id = 85865, Voice = '', Text = '我想，在当初的战役中，一定发生了什么…到底发生了什么呢？', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85864] = { id = 85864, Voice = '', Text = '从这往后，无论是书籍或者是破碎的水晶，都没有任何关于“黑武士”的消息。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85863] = { id = 85863, Voice = '', Text = '根据我们的考证，最后一次“黑武士”的出现是在黑暗生物进攻骑士团驻地的战役当中。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85862] = { id = 85862, Voice = '', Text = '他在夜深人静之时出没于古城，拯救着那些遇到困难的人们。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85861] = { id = 85861, Voice = '', Text = '它讲述了一位名叫“黑武士”的英雄传奇。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85860] = { id = 85860, Voice = '', Text = '在这古老的骑士团驻地中，我看到了一面刻满古代文字的墙壁。', Speaker = 6201, Option = '', Action = _EmptyTable,}, 
	[85766] = { id = 85766, Voice = '', Text = '——来自遗迹学者的调查报告。', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85765] = { id = 85765, Voice = '', Text = '我将它记录为[c][ffff00]【诅咒魔人】[-][/c]事件。', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85764] = { id = 85764, Voice = '', Text = '五王子含泪答应了代顿的请求，代顿在这大厅自刎结束了自己的一生。', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85763] = { id = 85763, Voice = '', Text = '大王子死后，代顿带回被他藏起来的五王子，告诫五王子，权力是每个人心中的恶魔，一定要战胜这个魔鬼！', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85762] = { id = 85762, Voice = '', Text = '代顿决定背叛自己所信奉的骑士精神——忠诚！为了维护自己心中的正义，他要杀死大王子！', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85761] = { id = 85761, Voice = '', Text = '权力是魔鬼，大王子被魔鬼所诱惑，陷害了三位王子，代顿作为大王子手中的利剑，自然有着无法推卸的责任。', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85760] = { id = 85760, Voice = '', Text = '对大王子产生疑心的代顿并没有第一时间消灭所谓的诅咒魔人。同时五王子告知代顿，他的三位哥哥都是死于大王子的阴谋。', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85759] = { id = 85759, Voice = '', Text = '代顿这才知道，这一切，都是大王子为了继承王位所使的阴谋。', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85758] = { id = 85758, Voice = '', Text = '大王子对外宣称发现第四位诅咒魔人，再次命令骑士代顿前往消灭诅咒魔人。', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85757] = { id = 85757, Voice = '', Text = '大王子决定让骑士代顿惩治这个邪恶的魔人，而与此同时，四王子出门狩猎已经许久没有消息传出来了。', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85756] = { id = 85756, Voice = '', Text = '诅咒魔人“争斗者”出现了，他的诅咒带来了一次次争斗，王都因为魔人的诅咒，流血事件不断发生。', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85755] = { id = 85755, Voice = '', Text = '爱民如子的大王子绝对不会允许他为祸王都，骑士代顿再次奉命讨伐。', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85754] = { id = 85754, Voice = '', Text = '不久后，三王子殿下失踪了，与此同时，王宫附近出现了一个诅咒魔人，名为“腐朽者”。', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85753] = { id = 85753, Voice = '', Text = '王宫的二王子离奇失踪后，王都出现了一个名为“暴食者”的诅咒魔人。骑士代顿奉大王子的命令前去讨伐。', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85752] = { id = 85752, Voice = '', Text = '[c][ffff00]【施密特王朝GLC.X163号档案】[-][/c]已解锁……是否解读？', Speaker = 6155, Option = '解读', Action = _EmptyTable,}, 
	[85751] = { id = 85751, Voice = '', Text = '文件编号是[c][ffff00]【施密特王朝GLC.X163号档案】[-][/c]。', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85750] = { id = 85750, Voice = '', Text = '这是经过我整理的关于历史断层的诅咒魔人事件的文件。', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85749] = { id = 85749, Voice = '', Text = '这场悲剧也终于到了落幕的时刻了…', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85748] = { id = 85748, Voice = '', Text = '而代顿无愧于骑士的称号，宁愿自己背负背叛忠诚的枷锁，也要执行心中的正义。', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85747] = { id = 85747, Voice = '', Text = '被权力这个魔鬼附身的大王子已经丧心病狂。', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85746] = { id = 85746, Voice = '', Text = '这戒指是一切悲剧的源泉…', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85720] = { id = 85720, Voice = '', Text = '一枚造型奇特的戒指…散发着幽黑的光…', Speaker = 6160, Option = '{捡起来看看,2},{无视它,3}', Action = _EmptyTable,}, 
	[85719] = { id = 85719, Voice = '', Text = '出现关键道具——[c][ffff00]心中的魔鬼[-][/c]。', Speaker = 6160, Option = '', Action = _EmptyTable,}, 
	[85718] = { id = 85718, Voice = '', Text = '“所有的罪责…就由我来承担！”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85717] = { id = 85717, Voice = '', Text = '“我已然罪孽深重，但即使是这样，我也要将危险扼杀…”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85716] = { id = 85716, Voice = '', Text = '“安洛尔竟然拥有这么邪恶的诅咒术，继位之后，对民众来说并不是好事。”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85715] = { id = 85715, Voice = '', Text = '“考虑再三，我决定把五王子藏起来…”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85714] = { id = 85714, Voice = '', Text = '“我无法回答他的话，但我知道，我已经犯下无法弥补的大错。”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85713] = { id = 85713, Voice = '', Text = '“他问我，手中沾染着三位王子的血液，现在想沾染第四位吗？”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85712] = { id = 85712, Voice = '', Text = '“五殿下流着泪笑着，他问我，是不是想要杀了他？”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85711] = { id = 85711, Voice = '', Text = '“没有想到安洛尔竟然会堕落得这么彻底。这已经不是那个英明的大王子了。”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85689] = { id = 85689, Voice = '', Text = '一件带着笑意的面具静静地埋在泥土之中…', Speaker = 6159, Option = '{捡起来看看,2},{无视它,3}', Action = _EmptyTable,}, 
	[85688] = { id = 85688, Voice = '', Text = '出现关键道具——[c][ffff00]伪善者的假面[-][/c]。', Speaker = 6159, Option = '', Action = _EmptyTable,}, 
	[85687] = { id = 85687, Voice = '', Text = '“皇室徽章…难道他真的是王子？…不！我应该相信大王子！”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85686] = { id = 85686, Voice = '', Text = '“可是它竟然自杀了…这…”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85685] = { id = 85685, Voice = '', Text = '“竟然诬陷大王子！不可饶恕之人，死去吧！”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85684] = { id = 85684, Voice = '', Text = '“疯子！只有像他这样只会争斗的恶魔，才会把所有的原因臆想在旁人身上！”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85683] = { id = 85683, Voice = '', Text = '“就算大王子安洛尔不命令我，尊崇骑士精神的我，也会消灭他！”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85682] = { id = 85682, Voice = '', Text = '“不少人在争斗中死去，这真是一场灾难！”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85681] = { id = 85681, Voice = '', Text = '“他诅咒了人们，把人们变成了只会争斗的怪物…”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85680] = { id = 85680, Voice = '', Text = '“王都中出现了大量争斗者，究其源头…竟然是一个神秘的诅咒魔人…”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85679] = { id = 85679, Voice = '', Text = '让我们来看看上面记载着什么？', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85678] = { id = 85678, Voice = '', Text = '还有保存得如此完整的皇室徽章！冒险者，这对我来说太有价值了！', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85677] = { id = 85677, Voice = '', Text = '…竟然死去了…他所说的都是真的吗？', Speaker = 6162, Option = '', Action = _EmptyTable,}, 
	[85676] = { id = 85676, Voice = '', Text = '看到了吗？！安洛尔！我！克罗多！不会死在你的狗腿子手上！', Speaker = 6164, Option = '', Action = _EmptyTable,}, 
	[85675] = { id = 85675, Voice = '', Text = '不好！', Speaker = 6162, Option = '', Action = _EmptyTable,}, 
	[85674] = { id = 85674, Voice = '', Text = '我也没想到他竟然会变成现在这样…嘿…代顿，总有一天你会明白的！', Speaker = 6164, Option = '', Action = _EmptyTable,}, 
	[85673] = { id = 85673, Voice = '', Text = '你想？！', Speaker = 6162, Option = '', Action = _EmptyTable,}, 
	[85672] = { id = 85672, Voice = '', Text = '我不是你的对手，但是我永远不会死在你的手下！', Speaker = 6164, Option = '', Action = _EmptyTable,}, 
	[85671] = { id = 85671, Voice = '', Text = '为祸王都？哼，到底是谁给我施加这种诅咒！现在竟然这么大义凛然…', Speaker = 6164, Option = '', Action = _EmptyTable,}, 
	[85670] = { id = 85670, Voice = '', Text = '你为祸王都，今天就是你的死期！', Speaker = 6162, Option = '', Action = _EmptyTable,}, 
	[85669] = { id = 85669, Voice = '', Text = '骑士代顿？呵…我就知道他不会善罢甘休…', Speaker = 6164, Option = '', Action = _EmptyTable,}, 
	[85668] = { id = 85668, Voice = '', Text = '你不会有这个机会了！', Speaker = 6162, Option = '', Action = _EmptyTable,}, 
	[85667] = { id = 85667, Voice = '', Text = '看来我要离开这里了…安洛尔…这一切都是你的手笔…总有一天我会回来找你的！', Speaker = 6164, Option = '', Action = _EmptyTable,}, 
	[85666] = { id = 85666, Voice = '', Text = '…该死…受伤了…没想到诅咒影响到的那些争斗者竟然会对我动手…', Speaker = 6164, Option = '', Action = _EmptyTable,}, 
	[85665] = { id = 85665, Voice = '', Text = '啊！该死！他们竟然把我当作猎物！', Speaker = 6164, Option = '', Action = _EmptyTable,}, 
	[85664] = { id = 85664, Voice = '', Text = '这该死的诅咒…看来我只能远离人群了…', Speaker = 6164, Option = '', Action = _EmptyTable,}, 
	[85663] = { id = 85663, Voice = '', Text = '耀眼的光芒闪过，一段影像出现了…', Speaker = 6158, Option = '', Action = _EmptyTable,}, 
	[85662] = { id = 85662, Voice = '', Text = '一枚华贵的徽章，却沾染了不明的黑色印记…', Speaker = 6158, Option = '{捡起来看看,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[85661] = { id = 85661, Voice = '', Text = '“但这魔人竟然有皇室徽章…这么说魔人跟王室有着某种联系…”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85660] = { id = 85660, Voice = '', Text = '“这个魔人竟然诅咒我！哼，恶魔就是恶魔！”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85659] = { id = 85659, Voice = '', Text = '“大哥？二哥？还真像！哼，我是不会上当的！”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85658] = { id = 85658, Voice = '', Text = '“他竟然自称三王子？这个魔人简直是在亵渎王室！”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85657] = { id = 85657, Voice = '', Text = '“大王子安洛尔派遣我解决魔人…果然在大王子心中，民众的生命远比三王子更重要吗？”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85656] = { id = 85656, Voice = '', Text = '“整整一条街道都化为了尘埃，诅咒魔人的事态已经不可控制了。”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85655] = { id = 85655, Voice = '', Text = '“在这紧要关头三王子殿下竟然失踪了？万一三王子被诅咒魔人盯上…”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85654] = { id = 85654, Voice = '', Text = '“王都中出现了名为腐朽者的诅咒魔人，他能把一切物品腐朽最终化为尘土。”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85653] = { id = 85653, Voice = '', Text = '…嘿…终于好了！让我们来看看上面记载着什么？', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85652] = { id = 85652, Voice = '', Text = '这块徽章竟然已经腐朽成这样了…我来看看能不能把它恢复原状…', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85651] = { id = 85651, Voice = '', Text = '充满腐朽气息的皇室徽章？难道三王子就在附近？', Speaker = 6162, Option = '', Action = _EmptyTable,}, 
	[85650] = { id = 85650, Voice = '', Text = '我信奉骑士精神，恶魔，你的诅咒无法施加于我，安心死去吧！', Speaker = 6162, Option = '', Action = _EmptyTable,}, 
	[85649] = { id = 85649, Voice = '', Text = '代顿！我诅咒你，你将会在悔恨中结束你的一生！', Speaker = 6163, Option = '', Action = _EmptyTable,}, 
	[85648] = { id = 85648, Voice = '', Text = '出来吧！腐朽的战士们！', Speaker = 6163, Option = '', Action = _EmptyTable,}, 
	[85647] = { id = 85647, Voice = '', Text = '哈哈哈！没什么好说的了！来吧！让我见识见识你的力量！', Speaker = 6163, Option = '', Action = _EmptyTable,}, 
	[85646] = { id = 85646, Voice = '', Text = '你在胡言乱语什么！', Speaker = 6162, Option = '', Action = _EmptyTable,}, 
	[85645] = { id = 85645, Voice = '', Text = '他想杀我…他想杀我…二哥早就劝过我要当心…没想到竟然是真的…', Speaker = 6163, Option = '', Action = _EmptyTable,}, 
	[85644] = { id = 85644, Voice = '', Text = '对…我现在的样子…一切都是他的阴谋…', Speaker = 6163, Option = '', Action = _EmptyTable,}, 
	[85643] = { id = 85643, Voice = '', Text = '乔尔？诅咒魔人这么喜欢冒充王子吗？', Speaker = 6162, Option = '', Action = _EmptyTable,}, 
	[85642] = { id = 85642, Voice = '', Text = '代顿…我认识你！你是大哥的骑士！是我啊！我是三王子乔尔！', Speaker = 6163, Option = '', Action = _EmptyTable,}, 
	[85641] = { id = 85641, Voice = '', Text = '恶魔，我的存在就是为了消灭你！', Speaker = 6162, Option = '', Action = _EmptyTable,}, 
	[85640] = { id = 85640, Voice = '', Text = '不！不要靠近我，你会死的！你的铠甲在诅咒的面前不值一提。', Speaker = 6163, Option = '', Action = _EmptyTable,}, 
	[85639] = { id = 85639, Voice = '', Text = '诡异的光芒闪过，一段影像出现了…', Speaker = 6157, Option = '', Action = _EmptyTable,}, 
	[85638] = { id = 85638, Voice = '', Text = '一枚华贵的徽章，却沾染了不明的黑色印记…', Speaker = 6157, Option = '{捡起来看看,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[85637] = { id = 85637, Voice = '', Text = '看来这就是那位骑士代顿所留下的消息了…真是让人好奇啊。', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85636] = { id = 85636, Voice = '', Text = '“二王子殿下，你究竟在哪里？”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85635] = { id = 85635, Voice = '', Text = '“现在魔人已经被我消灭了…我四处寻找，却发现了二王子的徽章…”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85634] = { id = 85634, Voice = '', Text = '“那种魔人竟然说自己就是二王子，这种恶魔的话，我怎么会相信！”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85633] = { id = 85633, Voice = '', Text = '“大王子命令我暂停寻找二王子…先行解决那个诅咒魔人…”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85632] = { id = 85632, Voice = '', Text = '“王都中出现了骇人听闻的诅咒魔人，他的诅咒能让那些普通的民众变为暴食者…” ', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85631] = { id = 85631, Voice = '', Text = '“二王子失踪了…大王子安洛尔心急如焚，他派遣我四处寻找二王子…”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85630] = { id = 85630, Voice = '', Text = '这些印记去除掉了…上面似乎记载着什么…', Speaker = 6155, Option = '写着什么', Action = _EmptyTable,}, 
	[85629] = { id = 85629, Voice = '', Text = '真是个体力活…还好我有汤玛斯大主教给我的圣水…', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85628] = { id = 85628, Voice = '', Text = '毫无疑问，这就是皇室徽章！这些黑色的印记是…', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85627] = { id = 85627, Voice = '', Text = '嗯？这是…皇室徽章？但是又沾染了诅咒的气息…到底是怎么回事…', Speaker = 6162, Option = '', Action = _EmptyTable,}, 
	[85626] = { id = 85626, Voice = '', Text = '如你所愿！', Speaker = 6162, Option = '', Action = _EmptyTable,}, 
	[85625] = { id = 85625, Voice = '', Text = '但是我不会束手就擒的，想要我死，就让我在战斗中死去！', Speaker = 6161, Option = '', Action = _EmptyTable,}, 
	[85624] = { id = 85624, Voice = '', Text = '呵呵呵…果然是这样…来吧！给我一个痛快！', Speaker = 6161, Option = '', Action = _EmptyTable,}, 
	[85623] = { id = 85623, Voice = '', Text = '二王子英勇善良，并且继承了国王的英俊容貌，你这幅恶魔的面孔，怎么可能是二王子？', Speaker = 6162, Option = '', Action = _EmptyTable,}, 
	[85622] = { id = 85622, Voice = '', Text = '代顿，如果我说，我是二王子，你相信吗？', Speaker = 6161, Option = '', Action = _EmptyTable,}, 
	[85621] = { id = 85621, Voice = '', Text = '魔人！我以骑士的名义宣誓，今日一定要消灭你！', Speaker = 6162, Option = '', Action = _EmptyTable,}, 
	[85620] = { id = 85620, Voice = '', Text = '骑士领主代顿…他居然让你过来…果然他还是不肯放过我…', Speaker = 6161, Option = '', Action = _EmptyTable,}, 
	[85619] = { id = 85619, Voice = '', Text = '亮丽的光芒闪过，一段影像出现了…', Speaker = 6156, Option = '', Action = _EmptyTable,}, 
	[85618] = { id = 85618, Voice = '', Text = '一枚华贵的徽章，却沾染了不明的黑色印记…', Speaker = 6156, Option = '{捡起来看看,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[85617] = { id = 85617, Voice = '', Text = '那些文字到这里就逐渐模糊了，这期间究竟发生了什么呢？', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85616] = { id = 85616, Voice = '', Text = '“…大王子决定讨伐这些诅咒魔人…”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85615] = { id = 85615, Voice = '', Text = '“他们的出现使得居住在王都附近的民众们都遭受了诅咒，那些居民逐渐演变为怪物…”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85614] = { id = 85614, Voice = '', Text = '“与此同时，王宫附近出现了几位诅咒魔人…”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85613] = { id = 85613, Voice = '', Text = '“大王子安洛尔接受国王殿下的命令彻查这件令人震惊的事件。”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85612] = { id = 85612, Voice = '', Text = '“接二连三的王子失踪，引起了王宫内的恐慌。”', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85611] = { id = 85611, Voice = '', Text = '冒险者，听听这段文字吧。', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85610] = { id = 85610, Voice = '', Text = '在这块徽章上记载了一种名为诅咒魔人的恶魔。', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85609] = { id = 85609, Voice = '', Text = '我曾在大厅中央的雕像后，找到一块恶魔徽章。', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85608] = { id = 85608, Voice = '', Text = '在休密兹王朝和施密特王朝之间空白的两百年，这里究竟发生了什么？', Speaker = 6155, Option = '', Action = _EmptyTable,}, 
	[85441] = { id = 85441, Voice = '', Text = '这段文字到这里戛然而止…这期间究竟发生了什么呢？', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85440] = { id = 85440, Voice = '', Text = '“恶魔…你永远都不会得到安息…愿你的灵魂永远受到火焰的拷问！”', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85439] = { id = 85439, Voice = '', Text = '“他来了！那个恶魔来了！他是想让王宫内的所有人都为卡丽莎陪葬吗？！”', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85438] = { id = 85438, Voice = '', Text = '“他带来了瘟疫，带走了生命，如今，他来到了这片城堡。”', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85437] = { id = 85437, Voice = '', Text = '“这不是神的惩罚，而是恶魔的诅咒，那个人，就是恶魔的宠儿。”', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85436] = { id = 85436, Voice = '', Text = '“王国爆发了严重的瘟疫，数以万计的人在这场瘟疫中去世。”', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85435] = { id = 85435, Voice = '', Text = '我终于得到了一点有用的线索，冒险者，听听这段文字吧。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85434] = { id = 85434, Voice = '', Text = '尽管这里充斥着邪恶魔物，我的心却向往着还原以前古城的盛况。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85433] = { id = 85433, Voice = '', Text = '当我接受王国的任命，来到这里找寻那段空缺历史的时候，我的心中无所畏惧。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85420] = { id = 85420, Voice = '', Text = '——来自遗迹学者的调查报告。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85419] = { id = 85419, Voice = '', Text = '我将它记录为[c][ffff00]【瘟疫之灾】[-][/c]。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85418] = { id = 85418, Voice = '', Text = '事情的最后，他的挚友凯莱尔带走了他的生命，使瘟疫从此消失。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85417] = { id = 85417, Voice = '', Text = '顷刻之间国王与王国事务官便双双毙命。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85416] = { id = 85416, Voice = '', Text = '他释放了自己身体中的瘟疫，经过压抑的瘟疫爆发起来更加凶残。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85415] = { id = 85415, Voice = '', Text = '古尔顿选择了报复，而这报复又在情理之中。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85414] = { id = 85414, Voice = '', Text = '卡丽莎的逝去对古尔顿造成了相当大的打击。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85413] = { id = 85413, Voice = '', Text = '当卡丽莎在绞刑架上大声斥责时，这些冷漠的人对此毫无羞愧之心。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85412] = { id = 85412, Voice = '', Text = '当危险过后，人们会把所有危险都扼杀在摇篮里，生怕自己再受到伤害，人就是这样的生物。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85411] = { id = 85411, Voice = '', Text = '好人难得好报，古尔顿看错了人心。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85410] = { id = 85410, Voice = '', Text = '古尔顿惊恐着，让卡丽莎不要过来，但是卡丽莎却毫不在意…', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85409] = { id = 85409, Voice = '', Text = '卡丽莎锲而不舍地找寻着古尔顿，终于在城外的密林处找到了他。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85408] = { id = 85408, Voice = '', Text = '但是好景不长，古尔顿身患瘟疫，不得不从人口密集的城市中搬出来。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85407] = { id = 85407, Voice = '', Text = '古尔顿攒钱给卡丽莎制作了花冠，古尔顿的心情无疑是非常开心的。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85406] = { id = 85406, Voice = '', Text = '看样子，王国利用超魔导士的力量，把几乎所有的瘟疫都集中在古尔顿身上。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85405] = { id = 85405, Voice = '', Text = '古尔顿拥有对瘟疫的超强抵抗力，这使得他成为解决这次灾难的唯一人选。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85404] = { id = 85404, Voice = '', Text = '一场突如其来的瘟疫，改变了古尔顿的一生。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85403] = { id = 85403, Voice = '', Text = '[c][ffff00]【施密特王朝GLC.X157号档案】[-][/c]已解锁……是否解读？', Speaker = 6109, Option = '解读', Action = _EmptyTable,}, 
	[85402] = { id = 85402, Voice = '', Text = '文件编号是[c][ffff00]【施密特王朝GLC.X157号档案】[-][/c]。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
