	[85344] = { id = 85344, Voice = '', Text = '掩埋泥土中的花冠，但是从设计的精巧程度上看，这是献给设计者爱人的花冠…', Speaker = 6115, Option = '{捡起,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[85343] = { id = 85343, Voice = '', Text = '不得不说，古尔顿是一个相当伟大的人，而王国的选择也无可厚非，毕竟他们总是要保全大多数人的。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85342] = { id = 85342, Voice = '', Text = '看样子，王国利用超魔导士的力量，把几乎所有的瘟疫都集中在古尔顿身上。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
