	[85326] = { id = 85326, Voice = '', Text = '[c][ffff00]国王的权杖[-][/c]关键道具已出现。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85325] = { id = 85325, Voice = '', Text = '[c][ffff00]古尔顿的日记[4][-][/c]已解锁。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85324] = { id = 85324, Voice = '', Text = '准备迎接我的怒火吧，你们这群无耻的渣滓！', Speaker = 6120, Option = '谢谢解读', Action = _EmptyTable,}, 
	[85323] = { id = 85323, Voice = '', Text = '我不再需要压抑着瘟疫了，这头盔送给凯莱尔吧。他是我的朋友，我不想让他死去。', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85322] = { id = 85322, Voice = '', Text = '一切都已经准备就绪了，我不用再带着这恶心的头盔了！', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85321] = { id = 85321, Voice = '', Text = '魅影刺客凯莱尔，我唯一的朋友，他一定会帮我的！', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85320] = { id = 85320, Voice = '', Text = '我要报复！没错，我要他们为我亲爱的卡丽莎陪葬！', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85319] = { id = 85319, Voice = '', Text = '古尔顿的日记…好吧…我来试着解读一下。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85318] = { id = 85318, Voice = '', Text = '那天真是我一辈子的噩梦…', Speaker = 6122, Option = '', Action = _EmptyTable,}, 
	[85317] = { id = 85317, Voice = '', Text = '古尔顿要报仇，于是他找到了我，而我告诉了他进入宫殿的方法…', Speaker = 6122, Option = '', Action = _EmptyTable,}, 
	[85316] = { id = 85316, Voice = '', Text = '是他…他还活着么？这不可能！除了我这个受到诅咒的人，没有人可以存活这么久！', Speaker = 6122, Option = '发生了什么', Action = _EmptyTable,}, 
	[85315] = { id = 85315, Voice = '', Text = '这是多久之前的称呼了…你怎么知道这个称呼的？', Speaker = 6122, Option = '古尔顿', Action = _EmptyTable,}, 
	[85314] = { id = 85314, Voice = '', Text = '离我远点！我是一个被诅咒的人，靠我太近可是会遭殃的！', Speaker = 6122, Option = '魅影刺客？', Action = _EmptyTable,}, 
	[85313] = { id = 85313, Voice = '', Text = '难道就是他？但是不太可能吧…都过去不知道多少年了…', Speaker = 6109, Option = '我去看看', Action = _EmptyTable,}, 
	[85312] = { id = 85312, Voice = '', Text = '凯莱尔？唔，前几天我还看到一个自称凯莱尔的人在这里大厅的角落出现呢。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85311] = { id = 85311, Voice = '', Text = '等着吧…卡丽莎…我一定会为你报仇的！', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85310] = { id = 85310, Voice = '', Text = '那些带着面具示人的伪善者，肮脏无耻的畜生，下水道的蓝鼠都比他们高贵！', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85309] = { id = 85309, Voice = '', Text = '我不敢相信这是真的！', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85308] = { id = 85308, Voice = '', Text = '一个名叫古尔顿的人写的日记，短短几页的纸张记载着些什么…', Speaker = 6113, Option = '{拿来看看,2},{不感兴趣,3}', Action = _EmptyTable,}, 
	[85307] = { id = 85307, Voice = '', Text = '[c][ffff00]破损的绞绳[-][/c]关键道具已出现。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85306] = { id = 85306, Voice = '', Text = '[c][ffff00]古尔顿的日记[3][-][/c]已解锁。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85305] = { id = 85305, Voice = '', Text = '难道是？卡…丽莎？骗我的对不对？你怎么会？', Speaker = 6120, Option = '谢谢解读', Action = _EmptyTable,}, 
	[85304] = { id = 85304, Voice = '', Text = '那是…我送给卡丽莎的花冠…为什么…会出现在绞刑架下…', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85303] = { id = 85303, Voice = '', Text = '又有谁触犯了王国的法律了吗？绞刑架上的那道身影怎么这么熟悉…', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85302] = { id = 85302, Voice = '', Text = '古尔顿的日记…好吧…我来试着解读一下。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85293] = { id = 85293, Voice = '', Text = '不行，我要去找她！再这么下去我会疯的！', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85292] = { id = 85292, Voice = '', Text = '四天了…卡丽莎已经四天没有来这里了…她难道出了什么意外吗？', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85291] = { id = 85291, Voice = '', Text = '卡丽莎已经很久没有来看我了…难道她已经变心了？', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85290] = { id = 85290, Voice = '', Text = '一个名叫古尔顿的人写的日记，短短几页的纸张记载着些什么…', Speaker = 6112, Option = '{拿来看看,2},{不感兴趣,3}', Action = _EmptyTable,}, 
	[85289] = { id = 85289, Voice = '', Text = '[c][ffff00]肮脏的花冠[-][/c]关键道具已出现。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85288] = { id = 85288, Voice = '', Text = '[c][ffff00]古尔顿的日记[2][-][/c]已解锁。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85287] = { id = 85287, Voice = '', Text = '哈！有了这些玉蝶花，我的花冠一定会非常漂亮。卡丽莎一定很喜欢！', Speaker = 6120, Option = '谢谢解读', Action = _EmptyTable,}, 
	[85286] = { id = 85286, Voice = '', Text = '可是我要去哪里找到那些玉蝶花呢？城外的密林里或许有吧…', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85285] = { id = 85285, Voice = '', Text = '要想做出我心目中最适合卡丽莎的花冠，还缺一些玉蝶花…', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85284] = { id = 85284, Voice = '', Text = '微笑小姐真是好人呐，我可是节省了一大笔开支呢。', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85283] = { id = 85283, Voice = '', Text = '古尔顿的日记…好吧…我来试着解读一下。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85280] = { id = 85280, Voice = '', Text = '今天的天气真不错！我该为卡丽莎准备她的生日礼物了！', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85279] = { id = 85279, Voice = '', Text = '我很感谢她，像她这么漂亮的女孩却愿意和我这样的穷人在一起，她本应该有更好的生活…', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85278] = { id = 85278, Voice = '', Text = '卡丽莎总是幻想着有一天能够带上美丽的花冠，在朋友们的祝福下成为我的妻子…', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85277] = { id = 85277, Voice = '', Text = '卡丽莎的生日快到了，攒了这么久的钱，应该是足够了吧…', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85276] = { id = 85276, Voice = '', Text = '一个名叫古尔顿的人写的日记，短短几页的纸张记载着些什么…', Speaker = 6111, Option = '{拿来看看,2},{不感兴趣,3}', Action = _EmptyTable,}, 
	[85275] = { id = 85275, Voice = '', Text = '[c][ffff00]腐烂的骨骼[-][/c]关键道具已出现。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85274] = { id = 85274, Voice = '', Text = '[c][ffff00]古尔顿的日记[1][-][/c]已解锁。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85273] = { id = 85273, Voice = '', Text = '我弄明白了！我弄明白了！他们真的是中了瘟疫！难道是因为雷克特那次去下水道修葺被蓝鼠咬伤了腿吗？', Speaker = 6120, Option = '谢谢解读', Action = _EmptyTable,}, 
	[85272] = { id = 85272, Voice = '', Text = '雷克特最近并没有接触到什么可能带有瘟疫的东西啊…为什么会死于瘟疫呢？', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85271] = { id = 85271, Voice = '', Text = '那些无精打采的人们都被护卫队带走了…也不知道他们怎么样了…据说雷克特是死于瘟疫…天啊！这太可怕了！', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85270] = { id = 85270, Voice = '', Text = '雷克特死了…事情闹得很大，王国的护卫队都来了…好像发生了什么了不得的事情…', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85269] = { id = 85269, Voice = '', Text = '古尔顿的日记…好吧…我来试着解读一下。', Speaker = 6109, Option = '', Action = _EmptyTable,}, 
	[85267] = { id = 85267, Voice = '', Text = '他们最近怎么了…一个个都无精打采的…雷克特似乎得了什么病，脸上的皮肤都开始溃烂了…可是哪个牧师肯为雷克特这个穷鬼治疗呢？', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85266] = { id = 85266, Voice = '', Text = '雷克特他的样子有点不对劲儿…无精打采的…背着木材的时候好几次都差点摔倒…', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85265] = { id = 85265, Voice = '', Text = '这天可真热啊，我和我的伙计们还得在烈日下卖力干活。快赚点钱吧…卡丽莎的生日快到了，得准备个像样的礼物…', Speaker = 6120, Option = '', Action = _EmptyTable,}, 
	[85264] = { id = 85264, Voice = '', Text = '一个名叫古尔顿的人写的日记，短短几页的纸张记载着些什么…', Speaker = 6110, Option = '{拿来看看,2},{不感兴趣,3}', Action = _EmptyTable,}, 
	[85052] = { id = 85052, Voice = '', Text = '冒险者，期待与你进行更精彩的冒险！', Speaker = 6072, Option = '', Action = _EmptyTable,}, 
	[85051] = { id = 85051, Voice = '', Text = '嗯，我们的未来一定会充满着光明与希望！', Speaker = 6072, Option = '', Action = _EmptyTable,}, 
	[85050] = { id = 85050, Voice = '', Text = '我也看到了你，冒险者，感谢你为了我而奋不顾身…', Speaker = 6072, Option = '醒来就好', Action = _EmptyTable,}, 
	[85049] = { id = 85049, Voice = '', Text = '在梦里，我找到了自己的初心，战胜了自己的恐惧与挫折…', Speaker = 6072, Option = '', Action = _EmptyTable,}, 
	[85048] = { id = 85048, Voice = '', Text = '我好像做了一个梦…', Speaker = 6072, Option = '', Action = _EmptyTable,}, 
	[85047] = { id = 85047, Voice = '', Text = '聆听亲人的呼唤，在众神之光的照耀下，归来！', Speaker = 6070, Option = '', Action = _EmptyTable,}, 
	[85046] = { id = 85046, Voice = '', Text = '希望与曙光的降临将驱逐一切黑暗！', Speaker = 6071, Option = '', Action = _EmptyTable,}, 
	[85045] = { id = 85045, Voice = '', Text = '众神的光芒终将照耀一切！', Speaker = 6068, Option = '', Action = _EmptyTable,}, 
	[85044] = { id = 85044, Voice = '', Text = '真挚的信仰，开启着希望的大门！', Speaker = 6069, Option = '', Action = _EmptyTable,}, 
	[85043] = { id = 85043, Voice = '', Text = '那么我们开始吧！', Speaker = 6069, Option = '', Action = _EmptyTable,}, 
	[85042] = { id = 85042, Voice = '', Text = '接下来，我们就安心等待他们的到来吧！', Speaker = 6065, Option = '', Action = _EmptyTable,}, 
	[85041] = { id = 85041, Voice = '', Text = '这里还算比较开阔，想必能达到雷藏和摩根的要求了。', Speaker = 6065, Option = '', Action = _EmptyTable,}, 
	[85040] = { id = 85040, Voice = '', Text = '你现在先去寻找海陆曼，我和摩根准备一下，很快就会赶到！', Speaker = 4271, Option = '好的', Action = _EmptyTable,}, 
	[85039] = { id = 85039, Voice = '', Text = '这营地太过狭小，海陆曼团长已经带着庞德的身体去寻找一个开阔的地方了。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[85038] = { id = 85038, Voice = '', Text = '我们得抓紧时间，尽快唤醒他，否则他依然会受到严重的创伤。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[85037] = { id = 85037, Voice = '', Text = '庞德的灵魂已经成功解救出来了，现在我们要做的就是唤醒他。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[85036] = { id = 85036, Voice = '', Text = '虽然过程很惊险…不过总算是做到了！', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[85008] = { id = 85008, Voice = '', Text = '冒险者请尽快准备好，留给我们的时间已经不多了！', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[85007] = { id = 85007, Voice = '', Text = '艾勒梅斯已经成功进入异世界了，接下来，我要把你送过去了。', Speaker = 4422, Option = '好的', Action = _EmptyTable,}, 
	[85006] = { id = 85006, Voice = '', Text = '好的！', Speaker = 6064, Option = '', Action = _EmptyTable,}, 
	[85005] = { id = 85005, Voice = '', Text = '好吧…准备好了吗？我现在就送你进去！', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[85004] = { id = 85004, Voice = '', Text = '先送我进去吧！', Speaker = 6064, Option = '', Action = _EmptyTable,}, 
	[85003] = { id = 85003, Voice = '', Text = '现在我就送你们进入黑暗之王的异世界。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[85002] = { id = 85002, Voice = '', Text = '魔力充沛的感觉真棒！', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[85001] = { id = 85001, Voice = '', Text = '稍等冒险者，我需要恢复一下…', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[85000] = { id = 85000, Voice = '', Text = '请你加快速度，我感觉莎西尔撑不了多久了！', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[84999] = { id = 84999, Voice = '', Text = '多亏你了，冒险者，这么多蓝色药水足够支撑我送你们进入异世界了！', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[84998] = { id = 84998, Voice = '', Text = '这点蓝色药水恐怕不能够支持我送你们进入异世界…', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[84997] = { id = 84997, Voice = '', Text = '冒险者你带来了我需要的[c][ffff00]200瓶蓝色药水[-][/c]吗？', Speaker = 4422, Option = '{给你蓝色药水,2},{我这就去收集,3}', Action = _EmptyTable,}, 
	[84996] = { id = 84996, Voice = '', Text = '我这个样子…起码需要消耗200瓶才会恢复充沛的魔力…', Speaker = 4422, Option = '交给我吧！', Action = _EmptyTable,}, 
	[84995] = { id = 84995, Voice = '', Text = '这次来得匆忙，我没有携带补充魔力的药剂。我希望你能够找一些可以恢复魔力的蓝色药水给我。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[84994] = { id = 84994, Voice = '', Text = '之前送你们进入异世界，我消耗了大量的魔力。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[84993] = { id = 84993, Voice = '', Text = '好吧，冒险者，恐怕又要麻烦你了。', Speaker = 4422, Option = '请说', Action = _EmptyTable,}, 
	[84992] = { id = 84992, Voice = '', Text = '当然，我们刺客公会的人怕过谁吗？', Speaker = 6064, Option = '', Action = _EmptyTable,}, 
	[84991] = { id = 84991, Voice = '', Text = '就算这样，你也坚持前往异世界吗？', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[84990] = { id = 84990, Voice = '', Text = '一不小心就会灵魂缺失，在这个世界永远消失！', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[84989] = { id = 84989, Voice = '', Text = '艾勒梅斯，你要知道，进入这个异世界可不是闹着玩的！', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[84988] = { id = 84988, Voice = '', Text = '没想到莎西尔竟然有困难…嘿嘿，看来我得帮帮她了。', Speaker = 6064, Option = '', Action = _EmptyTable,}, 
	[84987] = { id = 84987, Voice = '', Text = '无名者希望你们尽快处理完古城的事务。', Speaker = 6064, Option = '', Action = _EmptyTable,}, 
	[84986] = { id = 84986, Voice = '', Text = '无名者要我来通知你们，我们刺客公会在艾尔帕兰发现了一些异常。', Speaker = 6064, Option = '', Action = _EmptyTable,}, 
	[84985] = { id = 84985, Voice = '', Text = '刺客公会的人为什么会出现在这里？', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[84984] = { id = 84984, Voice = '', Text = '看样子她是遇到困难了。哼，也罢，让我进去帮她一把吧！', Speaker = 6064, Option = '', Action = _EmptyTable,}, 
	[84983] = { id = 84983, Voice = '', Text = '莎西尔进入异空间了？', Speaker = 6064, Option = '是的', Action = _EmptyTable,}, 
	[84982] = { id = 84982, Voice = '', Text = '莎西尔在哪里？！', Speaker = 6064, Option = '', Action = _EmptyTable,}, 
	[84981] = { id = 84981, Voice = '', Text = '现在的情况很不妙，我感到莎西尔在里面遇到了困难…', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[84980] = { id = 84980, Voice = '', Text = '所以我与雷藏商量之后，决定派遣莎西尔进入黑暗之王的异世界探查情况。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[84979] = { id = 84979, Voice = '', Text = '在你进入异世界不久，我就感觉到庞德剩余的灵魂不再安稳。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[84978] = { id = 84978, Voice = '', Text = '根据莎西尔传回来的消息，现在的情况不容乐观…', Speaker = 4422, Option = '莎西尔？', Action = _EmptyTable,}, 
	[84977] = { id = 84977, Voice = '', Text = '去找摩根吧，他会把你送入庞德最后的灵魂所在！', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[84976] = { id = 84976, Voice = '', Text = '摩根一直在监视庞德剩余灵魂的动向，我想现在我们应该行动起来了。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[84975] = { id = 84975, Voice = '', Text = '不过现在仍有一部分灵魂在那些邪恶的魔物手中，我们必须把庞德的灵魂夺回来！', Speaker = 4271, Option = '是的', Action = _EmptyTable,}, 
	[84974] = { id = 84974, Voice = '', Text = '很好冒险者，在你的努力下，我们夺回来庞德的大部分灵魂。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[84580] = { id = 84580, Voice = '', Text = '好了，现在我就要送你前往庞德灵魂所在地，那位墓地迷幻之王身体中的“异世界”了。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[84579] = { id = 84579, Voice = '', Text = '在里面不能呆时间太久，否则你的灵魂也会迷失在“异世界”深处，永远回不来！', Speaker = 4422, Option = '我明白了。', Action = _EmptyTable,}, 
	[84578] = { id = 84578, Voice = '', Text = '有了这本日记，我想你在那个“异世界”应该不会轻举妄动。但是我还是必须叮嘱你，一定要小心。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[84577] = { id = 84577, Voice = '', Text = '有所收获吗？冒险者。', Speaker = 4422, Option = '找到了日记', Action = _EmptyTable,}, 
	[84576] = { id = 84576, Voice = '', Text = '看来你已经找到了些许线索了。快去找摩根大贤者吧。看他的样子似乎很着急。', Speaker = 4555, Option = '', Action = _EmptyTable,}, 
	[84575] = { id = 84575, Voice = '', Text = '…下次比试我绝对不会再输给那个可恨的家伙！绝不！', Speaker = 4528, Option = '', Action = _EmptyTable,}, 
	[84574] = { id = 84574, Voice = '', Text = '…魔法的奥秘真是令我着迷……', Speaker = 4528, Option = '', Action = _EmptyTable,}, 
	[84573] = { id = 84573, Voice = '', Text = '…可恶…竟敢小瞧我！……', Speaker = 4528, Option = '', Action = _EmptyTable,}, 
	[84572] = { id = 84572, Voice = '', Text = '这本小册子就给你了，你打开看看吧。', Speaker = 4555, Option = '', Action = _EmptyTable,}, 
	[84571] = { id = 84571, Voice = '', Text = '我不确定是不是他的日记本，毕竟我从来没有打开看过。', Speaker = 4555, Option = '', Action = _EmptyTable,}, 
	[84570] = { id = 84570, Voice = '', Text = '庞德被夺走灵魂的时候，我从他失去灵魂的躯壳中找到了这本小册子。', Speaker = 4555, Option = '', Action = _EmptyTable,}, 
	[84569] = { id = 84569, Voice = '', Text = '庞德经常在一个随身小册子上写着什么，我猜测这个小册子应该是庞德的日记本。', Speaker = 4555, Option = '', Action = _EmptyTable,}, 
	[84568] = { id = 84568, Voice = '', Text = '很遗憾，关于这点，我无法帮助你，不过，我这里还是有点线索的。', Speaker = 4555, Option = '', Action = _EmptyTable,}, 
	[84567] = { id = 84567, Voice = '', Text = '现在的庞德倒是有了足以令我正视的资格，但是我并不是一个喜欢追问别人过去的人。', Speaker = 4555, Option = '', Action = _EmptyTable,}, 
	[84566] = { id = 84566, Voice = '', Text = '我的身份地位与刚刚进入吉芬的庞德有着天壤之别，我怎么会了解他的事呢？', Speaker = 4555, Option = '', Action = _EmptyTable,}, 
	[84565] = { id = 84565, Voice = '', Text = '我当然不知道。在庞德进入吉芬的时候，我就已经是魔法公会重点培养的天才魔法师了。', Speaker = 4555, Option = '', Action = _EmptyTable,}, 
	[84564] = { id = 84564, Voice = '', Text = '抱歉，我不太清楚。', Speaker = 4555, Option = '你不知道？', Action = _EmptyTable,}, 
	[84563] = { id = 84563, Voice = '', Text = '嗯？过去的庞德？你是指刚刚进入吉芬的时候吗？', Speaker = 4555, Option = '是的', Action = _EmptyTable,}, 
	[84562] = { id = 84562, Voice = '', Text = '去问问凯特莉娜，毕竟她跟庞德相处的时间比较久呢。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[84561] = { id = 84561, Voice = '', Text = '既然是利用庞德的记忆，那么我们就可以在庞德回忆中寻找线索。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[84560] = { id = 84560, Voice = '', Text = '在这个梦境之中，一旦我们轻举妄动，都会令庞德的灵魂消散！', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[84559] = { id = 84559, Voice = '', Text = '我猜测恐怕迷幻之王利用了庞德在吉芬的记忆，构造出一个近乎完美的梦境。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[84558] = { id = 84558, Voice = '', Text = '我略微感应了一下，发现墓地这位迷幻之王的“异世界”，竟然是吉芬的景象！', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[84557] = { id = 84557, Voice = '', Text = '我对雷藏的特殊印记再熟悉不过了，因此找到墓地那位迷幻之王的位置很容易。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[84556] = { id = 84556, Voice = '', Text = '雷藏曾经与迷幻之王交手，在迷幻之王身上留下了他特殊的印记。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[84481] = { id = 84481, Voice = '', Text = '现在我需要找到一个开阔的地方，方便施展魔法，如果你准备好了就直接来找我。', Speaker = 4554, Option = '', Action = _EmptyTable,}, 
	[84480] = { id = 84480, Voice = '', Text = '请务必小心，你的行动将会直接影响到庞德灵魂的安全。', Speaker = 4554, Option = '', Action = _EmptyTable,}, 
	[84479] = { id = 84479, Voice = '', Text = '既然这样，我就出点力吧！听着冒险者，接下来，我会把你传送到“异世界”。', Speaker = 4554, Option = '', Action = _EmptyTable,}, 
	[84478] = { id = 84478, Voice = '', Text = '那么，冒险者，你愿意前往“异世界”去拯救庞德吗？', Speaker = 4271, Option = '{愿意,2},{再等等,3}', Action = _EmptyTable,}, 
	[84477] = { id = 84477, Voice = '', Text = '我记得，当初推荐庞德来到吉芬的，就是冒险者你吧。看来，你对庞德应该有所了解。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[84476] = { id = 84476, Voice = '', Text = '一旦你进入“异世界”时间太久，魔力通道无法永久存在，恐怕你也很难回到现实世界了。', Speaker = 4554, Option = '我明白了', Action = _EmptyTable,}, 
	[84475] = { id = 84475, Voice = '', Text = '此外你必须记住，你只是灵魂进入了“异世界”，你的身体还存在于现实世界。', Speaker = 4554, Option = '', Action = _EmptyTable,}, 
	[84474] = { id = 84474, Voice = '', Text = '但是要注意的是，送入“异世界”的人，必须对庞德有所了解，否则在“异世界”中走错一步，都可能导致庞德灵魂的缺失！', Speaker = 4554, Option = '', Action = _EmptyTable,}, 
	[84473] = { id = 84473, Voice = '', Text = '当然！我可以通过死灵魔法与“异世界”建立联系，只要有个人能在“异世界”中成功引导灵魂出来就可以了。', Speaker = 4554, Option = '', Action = _EmptyTable,}, 
	[84472] = { id = 84472, Voice = '', Text = '嘿嘿，不过我研究多年的死灵魔法，这点小事可难不住我！', Speaker = 4554, Option = '你有办法？', Action = _EmptyTable,}, 
	[84471] = { id = 84471, Voice = '', Text = '恐怕现在庞德的灵魂已经储藏在黑暗之王和迷幻之王身上的“异世界”之中了。', Speaker = 4554, Option = '', Action = _EmptyTable,}, 
	[84470] = { id = 84470, Voice = '', Text = '这种世界是由被吸走的灵魂的记忆做构筑，如果不能打破这记忆的枷锁，即使你消灭了施法者，被掠夺的灵魂也无法被解放。', Speaker = 4554, Option = '', Action = _EmptyTable,}, 
	[84469] = { id = 84469, Voice = '', Text = '令人害怕的不仅如此，被他们收走的灵魂，都会沉浸在自己的“异世界”中。', Speaker = 4554, Option = '', Action = _EmptyTable,}, 
	[84468] = { id = 84468, Voice = '', Text = '这种法术直接吸取灵魂，并且储藏在自己身上，等到使用灵魂的时候，取出来也是极其方便。', Speaker = 4554, Option = '', Action = _EmptyTable,}, 
	[84467] = { id = 84467, Voice = '', Text = '原来是这样…摄取灵魂的魔法…难道是摄魂术？', Speaker = 4554, Option = '', Action = _EmptyTable,}, 
	[84466] = { id = 84466, Voice = '', Text = '好吧好吧…说说看，发生了什么？', Speaker = 4554, Option = '讲述经过', Action = _EmptyTable,}, 
	[84465] = { id = 84465, Voice = '', Text = '够了！摩根，说正事！', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[84464] = { id = 84464, Voice = '', Text = '你这个家伙一脸纠结的样子我好久没见到了，哈哈哈哈哈…真是畅快啊！', Speaker = 4554, Option = '', Action = _EmptyTable,}, 
	[84463] = { id = 84463, Voice = '', Text = '嘿嘿…这个冒失的小姑娘差点撞倒了我，我可不是会吃亏的人，所以我就跟着凯特莉娜来旅游啦。', Speaker = 4554, Option = '', Action = _EmptyTable,}, 
	[84462] = { id = 84462, Voice = '', Text = '我？我在哥布灵森林收集魔法材料的时候，遇到了急匆匆的凯特莉娜。', Speaker = 4554, Option = '', Action = _EmptyTable,}, 
	[84461] = { id = 84461, Voice = '', Text = '不来怎么知道你编排我！', Speaker = 4554, Option = '', Action = _EmptyTable,}, 
	[84460] = { id = 84460, Voice = '', Text = '你怎么来了？', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[84459] = { id = 84459, Voice = '', Text = '这才多久不见，你就编排我，是不是想打架啊！', Speaker = 4554, Option = '', Action = _EmptyTable,}, 
	[84458] = { id = 84458, Voice = '', Text = '嘿，雷藏，又在背后编排我什么？', Speaker = 4554, Option = '', Action = _EmptyTable,}, 
	[84457] = { id = 84457, Voice = '', Text = '这种时候那个家伙在就好了，这家伙所学庞杂，应该会对死灵魔法有所了解…', Speaker = 4271, Option = '那家伙？', Action = _EmptyTable,}, 
	[84456] = { id = 84456, Voice = '', Text = '如果实在没有办法拯救庞德，我们只能放弃他了。相比而言，我认为同盟军的计划更为重要。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[84455] = { id = 84455, Voice = '', Text = '太多不确定的因素，这使得营救庞德的任务困难重重，留给我们的时间已经不多了。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[84454] = { id = 84454, Voice = '', Text = '死灵魔法以驭使灵魂著称，但死灵魔法一般驾驭的都是死去的亡魂。像黑暗之王这种直接剥离灵魂的魔法，我还是第一次见到。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[84453] = { id = 84453, Voice = '', Text = '在古城郊外，黑暗之王摄取灵魂的魔法，像极了传说中的死灵魔法。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[84452] = { id = 84452, Voice = '', Text = '庞德还在黑暗之王手中，我们能不能把他拯救出来，还是个未知数。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[84451] = { id = 84451, Voice = '', Text = '冒险者，同盟军已经准备就绪了。接下来，我们就要行动起来了…', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[84159] = { id = 84159, Voice = '', Text = '——来自一位秘密调查员的报告。', Speaker = 4476, Option = '', Action = _EmptyTable,}, 
	[84158] = { id = 84158, Voice = '', Text = '或许整个王城都仍然沉浸在婚礼的钟声中，王军溃败之时，那些士兵们仍聆听着那钟声的悠长之音，堕入地狱……', Speaker = 4476, Option = '', Action = _EmptyTable,}, 
	[84157] = { id = 84157, Voice = '', Text = '我将此记录为【“梦中婚礼”安魂曲事件】。', Speaker = 4476, Option = '', Action = _EmptyTable,}, 
	[84156] = { id = 84156, Voice = '', Text = '他用悲怨的声音，向聆听者索取着什么……', Speaker = 4476, Option = '', Action = _EmptyTable,}, 
	[84155] = { id = 84155, Voice = '', Text = '我发现，加布里尔的亡魂并没有散去。', Speaker = 4476, Option = '', Action = _EmptyTable,}, 
	[84154] = { id = 84154, Voice = '', Text = '我在一次偶然的机会下，来到这个地下墓穴查探关于那些“神秘乐曲”的真相。', Speaker = 4476, Option = '', Action = _EmptyTable,}, 
	[84153] = { id = 84153, Voice = '', Text = '听到这些乐曲的人，据说渐渐的会精神失常，只剩下疯狂的念头，不停在墓穴中寻找什么，而忘记自己是谁。', Speaker = 4476, Option = '', Action = _EmptyTable,}, 
	[84152] = { id = 84152, Voice = '', Text = '古城的地下墓穴中，时常会响起诡异的乐曲声，并没有人在弹奏它们。', Speaker = 4476, Option = '', Action = _EmptyTable,}, 
	[84136] = { id = 84136, Voice = '', Text = '……谢谢……', Speaker = 4493, Option = '', Action = _EmptyTable,}, 
	[84135] = { id = 84135, Voice = '', Text = '桂瑟兰妮……你愿意再和我跳一支华尔兹吗？', Speaker = 4493, Option = '桂瑟兰妮的眼泪', Action = _EmptyTable,}, 
	[84111] = { id = 84111, Voice = '', Text = '神父用庄重的声音，说道……', Speaker = 3422, Option = '', Action = _EmptyTable,}, 
	[84110] = { id = 84110, Voice = '', Text = '能想象吗？在这聚满了不死魔物的黑暗教堂里，当年却是鲜花满地，福音满堂。宾客们等待着神圣仪式开始，桂瑟兰妮挽着加布里尔王子站在神父面前……', Speaker = 3422, Option = '', Action = _EmptyTable,}, 
	[84109] = { id = 84109, Voice = '', Text = '婚礼即将进行，这是加布里尔王子和侯爵女儿桂瑟兰妮小姐的大婚，请去教堂等候仪式开始。', Speaker = 4492, Option = '', Action = _EmptyTable,}, 
	[83988] = { id = 83988, Voice = '', Text = '[c][ffff00]【第七乐章】[-][/c]枯黄的落叶。“如果，我不能在神父的面前吻你，幸福永不会降临在你我身上。”表达着如此爱意的人，在希默梅思的降临中，永远沉浸入梦中的婚礼。落叶带走生命的韵律。', Speaker = 4477, Option = '', Action = _EmptyTable,}, 
	[83987] = { id = 83987, Voice = '', Text = '[c][ffff00]【第六乐章】[-][/c]碎裂的镜子。王子在富丽堂皇的舞池中央丢下假面，戏谑嘲笑道：“来不及了，你这个贪心势利的女人。破镜永不能恢复原貌。”璀璨迷人的水晶灯光下，童话似乎就在眼前破碎，再不会有浪漫的华尔兹。', Speaker = 4477, Option = '', Action = _EmptyTable,}, 
	[83986] = { id = 83986, Voice = '', Text = '[c][ffff00]【第五乐章】[-][/c]绅士的手帕。修道院中，忏悔的言语有谁在聆听？抑郁的容颜是为谁思念？绅士迈着轻轻的脚步靠近正在祈祷的女子身边，递出手帕款款温柔说：“请不要因为后悔而哭泣，它不适合你。我亲爱的桂瑟兰妮。”', Speaker = 4477, Option = '', Action = _EmptyTable,}, 
	[83985] = { id = 83985, Voice = '', Text = '[c][ffff00]【第四乐章】[-][/c]远方而来的战讯。“血色胜利”之战，王国军于险恶重重中获得艰难的胜利，战讯传至王城，桂瑟兰妮在侯爵府邸落下悲伤的眼泪，懊悔凝结在无声泪水中。', Speaker = 4477, Option = '', Action = _EmptyTable,}, 
	[83984] = { id = 83984, Voice = '', Text = '[c][ffff00]【第三乐章】[-][/c]细长锋利的西洋剑。桂瑟兰妮收到这件礼物时，不知该夸赞将军心意别致还是叹息他不懂情调。很长一段时间，它都被挂在墙上当一件精致的装饰品，直到桂瑟兰妮把它拿下来，放到长眠的将军身边，并说：“谢谢你的守护。”', Speaker = 4477, Option = '', Action = _EmptyTable,}, 
	[83983] = { id = 83983, Voice = '', Text = '[c][ffff00]【第二乐章】[-][/c]凋零的荆棘玫瑰。它被收藏在加布里尔的书桌右手边第一格抽屉中，制成了精巧的干花，夹在一本名为《梦中婚礼》的书中。当死讯传来之时，桂瑟兰妮亲手将它放进了加布里尔的遗棺中。', Speaker = 4477, Option = '', Action = _EmptyTable,}, 
	[83982] = { id = 83982, Voice = '', Text = '[c][ffff00]【第一乐章】[-][/c]假面舞会上的面具。它记录着桂瑟兰妮与加布里尔在假面之下开始的一段美丽华尔兹。初见之时，未知的一切，华丽的盛装，彼此的吸引，在富丽堂皇的舞池中相映生辉，童话一般的美好而浪漫。', Speaker = 4477, Option = '', Action = _EmptyTable,}, 
	[83981] = { id = 83981, Voice = '', Text = '婚礼的钟声响了……那应该是他们的婚礼吧？', Speaker = 4491, Option = '', Action = _EmptyTable,}, 
	[83980] = { id = 83980, Voice = '', Text = '世间的爱情，就是这么玄妙吧。', Speaker = 4491, Option = '', Action = _EmptyTable,}, 
	[83979] = { id = 83979, Voice = '', Text = '第三次，他们又同时出现了。那个男人向那位美丽的小姐表达歉意，接着下跪求婚。美丽的小姐脸上笑得很幸福。', Speaker = 4491, Option = '', Action = _EmptyTable,}, 
	[83978] = { id = 83978, Voice = '', Text = '第二次，只有那位美丽的小姐一个人。她在树下坐了很久，慢慢的，我听到她用抑制的声音在说对不起……对不起谁？那个她不想再见的男人吗？', Speaker = 4491, Option = '', Action = _EmptyTable,}, 
	[83977] = { id = 83977, Voice = '', Text = '第一次，我看到他们在树下争吵，那名叫桂瑟兰妮的美丽小姐打了叫加布里尔的男人一巴掌，他们彼此面红耳赤吼着以后再也不想见到对方。', Speaker = 4491, Option = '', Action = _EmptyTable,}, 
	[83976] = { id = 83976, Voice = '', Text = '这是我第三次看到他们。', Speaker = 4491, Option = '', Action = _EmptyTable,}, 
	[83975] = { id = 83975, Voice = '', Text = '我在这棵树下呆了很久了，很久很久……', Speaker = 4491, Option = '', Action = _EmptyTable,}, 
	[83974] = { id = 83974, Voice = '', Text = '悠扬的乐声吸引着灵魂，恍惚中，有种情不自禁想要去触碰的冲动……', Speaker = 4484, Option = '{拾取,2},{离开,3}', Action = _EmptyTable,}, 
	[83973] = { id = 83973, Voice = '', Text = '加布里尔王子早就看厌了她的虚情假意吧。现在，痴心妄想的是她呢。', Speaker = 4490, Option = '', Action = _EmptyTable,}, 
	[83972] = { id = 83972, Voice = '', Text = '不过显然桂瑟兰妮小姐认为是千真万确的。看她在王宫宴会上那殷勤的样子，明显是想和人家重修旧好。', Speaker = 4490, Option = '', Action = _EmptyTable,}, 
	[83971] = { id = 83971, Voice = '', Text = '说不定是搞错了呢？国王下落不明，这个流落在外的私生子身份到底是不是真的，谁知道呢。海因里希只是不想让克雷霍尔伦家族趁虚而入吧。', Speaker = 4490, Option = '', Action = _EmptyTable,}, 
	[83970] = { id = 83970, Voice = '', Text = '真是难以置信，加布里尔，那个一再被桂瑟兰妮小姐拒之门外的男人，竟然现在成了王位继承人！这世上还有什么不可能吗？', Speaker = 4490, Option = '', Action = _EmptyTable,}, 
	[83969] = { id = 83969, Voice = '', Text = '刺耳的乐声吸引着灵魂，恍惚中，有种情不自禁想要去触碰的冲动……', Speaker = 4483, Option = '{拾取,2},{离开,3}', Action = _EmptyTable,}, 
	[83968] = { id = 83968, Voice = '', Text = '……然后，年轻男人独自离开了。', Speaker = 4489, Option = '', Action = _EmptyTable,}, 
	[83967] = { id = 83967, Voice = '', Text = '那应该是个已经消失在这世上的人，但他的确还活着。', Speaker = 4489, Option = '', Action = _EmptyTable,}, 
	[83966] = { id = 83966, Voice = '', Text = '年轻男人向她递出了一块手帕，她没有接，只低头埋住脸哭泣。男人拭去了她眼睫上的泪。', Speaker = 4489, Option = '', Action = _EmptyTable,}, 
	[83965] = { id = 83965, Voice = '', Text = '她总是会在忏悔之后，无声啜泣，她的心底有什么让她懊悔不已，尽管她从不愿诉说。', Speaker = 4489, Option = '', Action = _EmptyTable,}, 
	[83964] = { id = 83964, Voice = '', Text = '只是今天有点不同，那个年轻男人进来以后，他在她身后站了很久，而她在专心祷告，没有发现他的存在。', Speaker = 4489, Option = '', Action = _EmptyTable,}, 
	[83963] = { id = 83963, Voice = '', Text = '也许没有人理解她的忏悔，但公正的神明会原谅所有愿意忏悔的人。', Speaker = 4489, Option = '', Action = _EmptyTable,}, 
	[83962] = { id = 83962, Voice = '', Text = '从那以后，她每天都来祷告。', Speaker = 4489, Option = '', Action = _EmptyTable,}, 
	[83961] = { id = 83961, Voice = '', Text = '清澈的乐声吸引着灵魂，恍惚中，有种情不自禁想要去触碰的冲动……', Speaker = 4482, Option = '{拾取,2},{离开,3}', Action = _EmptyTable,}, 
	[83960] = { id = 83960, Voice = '', Text = '他们俩总是缺少那么点缘分。', Speaker = 4488, Option = '', Action = _EmptyTable,}, 
	[83959] = { id = 83959, Voice = '', Text = '他的葬礼似乎和伊多墨将军在同一天，小姐恐怕不会去参加他的葬礼了。', Speaker = 4488, Option = '', Action = _EmptyTable,}, 
	[83958] = { id = 83958, Voice = '', Text = '这场仗真是打得太艰难了。', Speaker = 4488, Option = '', Action = _EmptyTable,}, 
	[83957] = { id = 83957, Voice = '', Text = '更可怜的是加布里尔先生，他真不应该一气之下去加入远征军，现在说不定……', Speaker = 4488, Option = '', Action = _EmptyTable,}, 
	[83956] = { id = 83956, Voice = '', Text = '不过，小姐可能真的深爱着伊多墨将军，她在人前表现得若无其事，可我看到她在房间里哭得伤心欲绝。', Speaker = 4488, Option = '', Action = _EmptyTable,}, 
	[83955] = { id = 83955, Voice = '', Text = '哦，老爷说的这是什么鬼话。如果是那样，小姐现在岂不成了寡妇……应该说，幸好还没有办过婚礼吧。', Speaker = 4488, Option = '', Action = _EmptyTable,}, 
	[83954] = { id = 83954, Voice = '', Text = '听老爷说，如果在伊多墨将军出征前，先把婚事办了就好了。', Speaker = 4488, Option = '', Action = _EmptyTable,}, 
	[83953] = { id = 83953, Voice = '', Text = '这真是太不幸了……', Speaker = 4488, Option = '', Action = _EmptyTable,}, 
	[83952] = { id = 83952, Voice = '', Text = '凄清的乐声吸引着灵魂，恍惚中，有种情不自禁想要去触碰的冲动……', Speaker = 4481, Option = '{拾取,2},{离开,3}', Action = _EmptyTable,}, 
	[83951] = { id = 83951, Voice = '', Text = '我怎么还在这跟你闲扯，小姐让我把这把西洋剑找个地方挂起来呢，可是挂哪里好呢？……', Speaker = 4487, Option = '', Action = _EmptyTable,}, 
	[83950] = { id = 83950, Voice = '', Text = '他们俩算是彻底完了。', Speaker = 4487, Option = '', Action = _EmptyTable,}, 
	[83949] = { id = 83949, Voice = '', Text = '小姐不知为什么特别生气，打了加布里尔先生一巴掌……哦，天啊。', Speaker = 4487, Option = '', Action = _EmptyTable,}, 
	[83948] = { id = 83948, Voice = '', Text = '跟你说，我今天可是看到小姐和加布里尔先生在后花园里吵起来了，争论的内容当然是关于订婚的事……', Speaker = 4487, Option = '', Action = _EmptyTable,}, 
	[83947] = { id = 83947, Voice = '', Text = '总之，我还有点难以接受……虽然伊多墨将军和我们小姐很般配，老爷也很满意，但……', Speaker = 4487, Option = '', Action = _EmptyTable,}, 
	[83946] = { id = 83946, Voice = '', Text = '你说婚礼能顺利举行吗？可别像可怜的卡珊德拉公主那样，最后一切化成泡影。', Speaker = 4487, Option = '', Action = _EmptyTable,}, 
	[83945] = { id = 83945, Voice = '', Text = '幽婉的乐声吸引着灵魂，恍惚中，有种情不自禁想要去触碰的冲动……', Speaker = 4480, Option = '{拾取,2},{离开,3}', Action = _EmptyTable,}, 
	[83944] = { id = 83944, Voice = '', Text = '真不明白，为什么他还会拜托我把求婚失败的玫瑰制成干花，他简直被侯爵千金迷得脑袋疯了。', Speaker = 4486, Option = '', Action = _EmptyTable,}, 
	[83943] = { id = 83943, Voice = '', Text = '的确，他在剑术方面能胜过那些追求者，夺下比武冠军。但我打赌，他的脑子不比他们机灵多少，至少在桂瑟兰妮面前是这样。', Speaker = 4486, Option = '', Action = _EmptyTable,}, 
	[83942] = { id = 83942, Voice = '', Text = '他怎么会有痴心妄想的念头呢？追求侯爵千金的男人还不够多吗，他们都把这位大小姐宠坏了，所以才要用比武的方式选择丈夫。', Speaker = 4486, Option = '', Action = _EmptyTable,}, 
	[83941] = { id = 83941, Voice = '', Text = '也许在爱情面前，最聪明的人也会蠢得像猪！', Speaker = 4486, Option = '', Action = _EmptyTable,}, 
	[83940] = { id = 83940, Voice = '', Text = '我一直认为加布里尔才智过人，挑选爱人的眼光也不会差到哪去。', Speaker = 4486, Option = '', Action = _EmptyTable,}, 
	[83939] = { id = 83939, Voice = '', Text = '愉悦的乐声吸引着灵魂，恍惚中，有种情不自禁想要去触碰的冲动……', Speaker = 4479, Option = '{拾取,2},{离开,3}', Action = _EmptyTable,}, 
	[83938] = { id = 83938, Voice = '', Text = '桂瑟兰妮可是出了名的高傲呢。', Speaker = 4485, Option = '', Action = _EmptyTable,}, 
	[83937] = { id = 83937, Voice = '', Text = '不过就像是青蛙和高贵的天鹅。他的确很英俊，但侯爵千金绝不会因为他英俊就爱上他。', Speaker = 4485, Option = '', Action = _EmptyTable,}, 
	[83936] = { id = 83936, Voice = '', Text = '当然，谁也没有桂瑟兰妮小姐那么引人注目，还有和她跳华尔兹的那个英俊男人，哦……我们回去以后，都在猜他是谁。', Speaker = 4485, Option = '', Action = _EmptyTable,}, 
	[83935] = { id = 83935, Voice = '', Text = '真不敢相信，像我这样的农夫女儿，有生之年也能参加一次舞会。', Speaker = 4485, Option = '', Action = _EmptyTable,}, 
	[83934] = { id = 83934, Voice = '', Text = '那是一场梦幻般的舞会，我花了三天三夜准备礼服和假面。侯爵大人为他的独生女桂瑟兰妮小姐庆祝生日，他慷慨地邀请了城里所有的人。', Speaker = 4485, Option = '', Action = _EmptyTable,}, 
	[83933] = { id = 83933, Voice = '', Text = '优美的乐声吸引着灵魂，恍惚中，有种情不自禁想要去触碰的冲动……', Speaker = 4478, Option = '{拾取,2},{离开,3}', Action = _EmptyTable,}, 
	[83932] = { id = 83932, Voice = '', Text = '冒险者，你为我带来了什么神秘之音呢？', Speaker = 4477, Option = '梦中的婚礼章', Action = _EmptyTable,}, 
	[83931] = { id = 83931, Voice = '', Text = '冒险者，你为我带来了什么神秘之音呢？', Speaker = 4477, Option = '王子的嘲笑声', Action = _EmptyTable,}, 
	[83930] = { id = 83930, Voice = '', Text = '冒险者，你为我带来了什么神秘之音呢？', Speaker = 4477, Option = '教堂的圣福音', Action = _EmptyTable,}, 
	[83929] = { id = 83929, Voice = '', Text = '冒险者，你为我带来了什么神秘之音呢？', Speaker = 4477, Option = '月光的小夜调', Action = _EmptyTable,}, 
	[83928] = { id = 83928, Voice = '', Text = '冒险者，你为我带来了什么神秘之音呢？', Speaker = 4477, Option = '爱情的圆舞曲', Action = _EmptyTable,}, 
	[83927] = { id = 83927, Voice = '', Text = '冒险者，你为我带来了什么神秘之音呢？', Speaker = 4477, Option = '玫瑰的踢踏舞', Action = _EmptyTable,}, 
	[83926] = { id = 83926, Voice = '', Text = '冒险者，你为我带来了什么神秘之音呢？', Speaker = 4477, Option = '初见的华尔兹', Action = _EmptyTable,}, 
	[83924] = { id = 83924, Voice = '', Text = '据我所知，有一位神秘的聆听者正在收集这些亡灵的安魂曲。如果你发现了“他们”，就将“他们”的逝者之音带给那位聆听者。', Speaker = 4476, Option = '', Action = _EmptyTable,}, 
	[83923] = { id = 83923, Voice = '', Text = '仔细倾听“他们”的诉说，这是“他们”长久以来的等待。等待着有人带走“他们”的秘密。', Speaker = 4476, Option = '', Action = _EmptyTable,}, 
	[83922] = { id = 83922, Voice = '', Text = '那一夜，希默梅思忽然降临，而教堂中正在举行着一场盛大的婚礼。如果你在这片墓地中慢慢行走，就会发现“他们”久久未曾离去。', Speaker = 4476, Option = '', Action = _EmptyTable,}, 
	[83921] = { id = 83921, Voice = '', Text = '如果没有人愿意聆听，如果“他们”的愿望不能得以被世人了解，这忧伤的乐曲就终年不会止息……', Speaker = 4476, Option = '', Action = _EmptyTable,}, 
	[83920] = { id = 83920, Voice = '', Text = '听到了吗？这充满悲伤的乐曲……那是这里死去的亡魂想要向世人诉说故事。', Speaker = 4476, Option = '', Action = _EmptyTable,}, 
	[83919] = { id = 83919, Voice = '', Text = '听……这是一首安魂曲。', Speaker = 4476, Option = '', Action = _EmptyTable,}, 
	[83917] = { id = 83917, Voice = '', Text = '留下的驱魔人，将背负着那些逝者的罪孽，继续活在黑夜中。就像乌鸦一身漆黑的羽毛，每一根都曾被血染过，只有黑夜能将它的姿态隐藏起来。', Speaker = 4426, Option = '', Action = _EmptyTable,}, 
	[83916] = { id = 83916, Voice = '', Text = '这四本书，被称为“驱魔人之书”。', Speaker = 4426, Option = '留下的驱魔人', Action = _EmptyTable,}, 
	[83915] = { id = 83915, Voice = '', Text = '憎恶之书、恐惧之书、怯懦之书、嫉妒之书。这就是他所得到的遗产。', Speaker = 4426, Option = '', Action = _EmptyTable,}, 
	[83914] = { id = 83914, Voice = '', Text = '唯有乌鸦驱魔人依然行走在黑夜里。一天晚上，他走入了卡伦赞德的房间，亲手驱除了卡伦赞德被恶魔吞噬的邪恶灵魂。', Speaker = 4426, Option = '驱魔人之书', Action = _EmptyTable,}, 
	[83913] = { id = 83913, Voice = '', Text = '毒蝎驱魔人因太过聪明，引来了杀身之祸。', Speaker = 4426, Option = '', Action = _EmptyTable,}, 
	[83912] = { id = 83912, Voice = '', Text = '月狼驱魔人被孤独摧残着，当他听说国王被杀害时，就在牢狱中引咎自杀了。', Speaker = 4426, Option = '', Action = _EmptyTable,}, 
	[83911] = { id = 83911, Voice = '', Text = '猎鹰驱魔人将灵魂献给了恶魔。', Speaker = 4426, Option = '', Action = _EmptyTable,}, 
	[83910] = { id = 83910, Voice = '', Text = '你再好好想一想……', Speaker = 4426, Option = '', Action = _EmptyTable,}, 
	[83909] = { id = 83909, Voice = '', Text = '现在，你知道是谁赢得了赌约吗？', Speaker = 4426, Option = '{猎鹰驱魔人,2},{月狼驱魔人,3},{乌鸦驱魔人,4},{毒蝎驱魔人,5}', Action = _EmptyTable,}, 
	[83890] = { id = 83890, Voice = '', Text = '[c][ffff00]【尾声】[-][/c]驱魔人行走在夜间，以狩猎魔鬼为职业的他，今晚要去狩猎一个真正的恶魔。他握着锋利的武器，内心默念：“卡伦赞德，你曾是我最重要的朋友，所以也必须由我来洗净你的罪孽。而我将背负罪孽，永生潜行在黑夜中。”', Speaker = 4426, Option = '', Action = _EmptyTable,}, 
	[83889] = { id = 83889, Voice = '', Text = '[c][ffff00]【嫉妒之书·真相】[-][/c]卡伦赞德的野心愈加庞大，最后，他看向了那顶璀璨的冕冠。华丽的外表下，早已是枯朽的灵魂。他泯灭了一切良善，冷酷无情得就像一个真正的恶魔。欲望的终点，往往是穷途末路。', Speaker = 4430, Option = '', Action = _EmptyTable,}, 
	[83888] = { id = 83888, Voice = '', Text = '[c][ffff00]【嫉妒之书·睿智】[-][/c]古列弗深爱书籍，他相信那是它最忠诚而值得信赖的朋友。因此，他有一颗博大精深的头脑，总是能想出绝妙的主意扭转乾坤。但这并没有给他带来好下场。智者千虑却必有一失。', Speaker = 4430, Option = '', Action = _EmptyTable,}, 
	[83887] = { id = 83887, Voice = '', Text = '[c][ffff00]【怯懦之书·真相】[-][/c]金钱就像是这世间的一切诱惑，过度纵容，就会导致极端的后果。为了满足逐渐贪得无厌的卡伦赞德的要求，驱魔人放弃了一切，而这并没有带来善果。', Speaker = 4429, Option = '', Action = _EmptyTable,}, 
	[83886] = { id = 83886, Voice = '', Text = '[c][ffff00]【怯懦之书·仁慈】[-][/c]放置在我们眼前的普通食物，困苦的时候，它会比金子更珍贵渴求，而富裕之时，它却廉价得仿佛脚边的野草，随意可践踏忽视。', Speaker = 4429, Option = '', Action = _EmptyTable,}, 
	[83885] = { id = 83885, Voice = '', Text = '[c][ffff00]【恐惧之书·真相】[-][/c]牢房狭小的窗洞漏进清冷的月光，当骄傲的驱魔人失去自由之时，仿佛也失去了所有，包括自己存在的价值。链铐的响动在提醒着他犯下的错误。他无法向任何人倾诉苦衷，因为是他自己将自己推进了孤独的深渊。', Speaker = 4428, Option = '', Action = _EmptyTable,}, 
	[83884] = { id = 83884, Voice = '', Text = '[c][ffff00]【恐惧之书·孤独】[-][/c]阴冷的监狱中，往往隐藏着禁忌、罪恶的故事。而用来开启它们的钥匙，就像是巫婆的苹果。至情至义的驱魔人知道这样做违背了法律与秩序，但他还是用钥匙打开了牢门，放走了他最好的朋友。', Speaker = 4428, Option = '', Action = _EmptyTable,}, 
	[83883] = { id = 83883, Voice = '', Text = '[c][ffff00]【憎恶之书·真相】[-][/c]驱魔人的符上，狂乱的字迹流露出书写者内心的挣扎与不安，是用力量维护公正，还是用力量解救落难的生命？驱魔人明亮的眼睛能看透黑暗，却看不透世间的正义。', Speaker = 4427, Option = '', Action = _EmptyTable,}, 
	[83882] = { id = 83882, Voice = '', Text = '[c][ffff00]【憎恶之书·公正】[-][/c]“是献出你的灵魂，还是让我夺走你朋友的生命，做出选择吧！”恶魔用它那似乎能洞察人类内心的眼睛，微笑着流露出狰狞的目光，对面前的驱魔人说道。卡伦赞德正是被这双魔眼所蛊惑。', Speaker = 4427, Option = '', Action = _EmptyTable,}, 
	[83881] = { id = 83881, Voice = '', Text = '[c][ffff00]【楔子】[-][/c]许多故事的开始，都是伴随着阴谋、凶杀、冤情、争斗。那天晚上，莫里茨公爵冰冷地躺在床上，脚边倒着一瓶毒药。', Speaker = 4426, Option = '', Action = _EmptyTable,}, 
	[83787] = { id = 83787, Voice = '', Text = '【尾声】已解锁，影子驱魔人会告诉你它的内容。', Speaker = 4440, Option = '', Action = _EmptyTable,}, 
	[83786] = { id = 83786, Voice = '', Text = '——来自一位秘密调查员的报告。', Speaker = 3422, Option = '', Action = _EmptyTable,}, 
	[83785] = { id = 83785, Voice = '', Text = '不知是否有缘能再见一见驱魔人？', Speaker = 3422, Option = '', Action = _EmptyTable,}, 
	[83784] = { id = 83784, Voice = '', Text = '我将它记录为【四个驱魔人档案】。', Speaker = 3422, Option = '', Action = _EmptyTable,}, 
	[83783] = { id = 83783, Voice = '', Text = '这件事被收录在施密特王朝档案中，但它发生的年代可能是更久远以前。', Speaker = 3422, Option = '', Action = _EmptyTable,}, 
	[83782] = { id = 83782, Voice = '', Text = '与黑暗为伍，最终也将被黑暗吞噬。', Speaker = 3422, Option = '', Action = _EmptyTable,}, 
	[83781] = { id = 83781, Voice = '', Text = '为追求智慧，从而生出“嫉妒”。', Speaker = 3422, Option = '', Action = _EmptyTable,}, 
	[83780] = { id = 83780, Voice = '', Text = '为追求仁善，从而生出“怯懦”。', Speaker = 3422, Option = '', Action = _EmptyTable,}, 
	[83779] = { id = 83779, Voice = '', Text = '为追求情义，从而生出“孤独”。', Speaker = 3422, Option = '', Action = _EmptyTable,}, 
	[83778] = { id = 83778, Voice = '', Text = '为追求公正，从而生出“憎恶”。', Speaker = 3422, Option = '', Action = _EmptyTable,}, 
	[83777] = { id = 83777, Voice = '', Text = '四个驱魔人，一个代号为“猎鹰”，一个代号为“月狼”，一个代号为“乌鸦”，一个代号为“毒蝎”。', Speaker = 3422, Option = '', Action = _EmptyTable,}, 
	[83776] = { id = 83776, Voice = '', Text = '【施密特王朝GLC.X114号档案】已解锁……是否解读？', Speaker = 3422, Option = '解读', Action = _EmptyTable,}, 
	[83775] = { id = 83775, Voice = '', Text = '卡伦赞德回应恶魔道：“是的，没有人不会改变。”', Speaker = 3422, Option = '', Action = _EmptyTable,}, 
	[83774] = { id = 83774, Voice = '', Text = '“并且，你现在变得丑陋不堪。”', Speaker = 3422, Option = '', Action = _EmptyTable,}, 
	[83773] = { id = 83773, Voice = '', Text = '恶魔说：“看，你的灵魂终究还是堕入了我的手掌。”', Speaker = 3422, Option = '', Action = _EmptyTable,}, 
	[83772] = { id = 83772, Voice = '', Text = '就在这时，恶魔又出现在他面前。', Speaker = 3422, Option = '恶魔说……', Action = _EmptyTable,}, 
	[83771] = { id = 83771, Voice = '', Text = '那天夜晚，他迎来了他的驱魔人朋友。寒意伴着夜风吹到他脚边……', Speaker = 3422, Option = '', Action = _EmptyTable,}, 
	[83770] = { id = 83770, Voice = '', Text = '卡伦赞德杀死了国王，为他所遭遇的不公和落魄一雪前耻。', Speaker = 3422, Option = '', Action = _EmptyTable,}, 
	[83767] = { id = 83767, Voice = '', Text = '拾取后你发现物品上封印着一段神秘的信息……', Speaker = 0, Option = '读取信息', Action = _EmptyTable,}, 
	[83766] = { id = 83766, Voice = '', Text = '不知是哪本书的结尾部分，泛黄的纸页上写着【尾声】两字。', Speaker = 4440, Option = '{拾取,2},{离开,3}', Action = _EmptyTable,}, 
	[83765] = { id = 83765, Voice = '', Text = '【嫉妒之书·真相】已解锁，毒蝎驱魔人会告诉你它的内容。', Speaker = 4439, Option = '', Action = _EmptyTable,}, 
	[83764] = { id = 83764, Voice = '', Text = '如果你死了，那么就没有人知道这个秘密了。', Speaker = 4443, Option = '', Action = _EmptyTable,}, 
	[83763] = { id = 83763, Voice = '', Text = '这本来就是我对皇后撒的一个谎。', Speaker = 4430, Option = '', Action = _EmptyTable,}, 
	[83762] = { id = 83762, Voice = '', Text = '你知道我没有国王的秘密。', Speaker = 4443, Option = '', Action = _EmptyTable,}, 
	[83761] = { id = 83761, Voice = '', Text = '如今，你虽然必须带着面具示人，但你拥有了比过去更多的东西。你还有什么顾虑？', Speaker = 4430, Option = '', Action = _EmptyTable,}, 
	[83760] = { id = 83760, Voice = '', Text = '不过，我有一个顾虑。', Speaker = 4443, Option = '', Action = _EmptyTable,}, 
	[83759] = { id = 83759, Voice = '', Text = '你说的对。', Speaker = 4443, Option = '', Action = _EmptyTable,}, 
	[83758] = { id = 83758, Voice = '', Text = '千万别让她发现，你并没有掌握国王的什么秘密。', Speaker = 4430, Option = '', Action = _EmptyTable,}, 
	[83757] = { id = 83757, Voice = '', Text = '我终于明白，为什么皇后不希望我死了。', Speaker = 4443, Option = '', Action = _EmptyTable,}, 
	[83756] = { id = 83756, Voice = '', Text = '拾取后你发现物品上封印着一段神秘的信息……', Speaker = 0, Option = '读取信息', Action = _EmptyTable,}, 
	[83755] = { id = 83755, Voice = '', Text = '【驱魔人之书】第四卷，下册。', Speaker = 4439, Option = '{拾取,2},{离开,3}', Action = _EmptyTable,}, 
	[83754] = { id = 83754, Voice = '', Text = '【嫉妒之书·真相】的关键道具已出现。', Speaker = 4430, Option = '', Action = _EmptyTable,}, 
	[83753] = { id = 83753, Voice = '', Text = '【嫉妒之书·睿智】已解锁，毒蝎驱魔人会告诉你它的内容。', Speaker = 4438, Option = '', Action = _EmptyTable,}, 
	[83743] = { id = 83743, Voice = '', Text = '拾取后你发现物品上封印着一段神秘的信息……', Speaker = 0, Option = '读取信息', Action = _EmptyTable,}, 
	[83742] = { id = 83742, Voice = '', Text = '【驱魔人之书】第四卷，上册。', Speaker = 4438, Option = '{拾取,2},{离开,3}', Action = _EmptyTable,}, 
	[83741] = { id = 83741, Voice = '', Text = '【怯懦之书·真相】已解锁，乌鸦驱魔人会告诉你它的内容。', Speaker = 4437, Option = '', Action = _EmptyTable,}, 
	[83740] = { id = 83740, Voice = '', Text = '看来我目前还无法答应你，不过我会想办法。', Speaker = 4429, Option = '', Action = _EmptyTable,}, 
	[83739] = { id = 83739, Voice = '', Text = '驱魔人不用在众人面前露出面貌。我可以成为你，这样我就能回到过去我所熟悉的那座王城。', Speaker = 4443, Option = '{给予1WZeny,4},{给予信仰之证,5},{给予友情之证,6}', Action = _EmptyTable,}, 
	[83738] = { id = 83738, Voice = '', Text = '如果你这句话是出自真心的，如果你真的如众人所说的那样无私善良。那么，你何不把你并不在意的那些东西借给我？', Speaker = 4443, Option = '', Action = _EmptyTable,}, 
	[83737] = { id = 83737, Voice = '', Text = '有什么我可以帮助你的吗？我会尽一切所能。', Speaker = 4429, Option = '', Action = _EmptyTable,}, 
	[83736] = { id = 83736, Voice = '', Text = '可我为什么要忍耐？我不应该呆在这里成为一个一无是处的废物！', Speaker = 4443, Option = '', Action = _EmptyTable,}, 
	[83735] = { id = 83735, Voice = '', Text = '你必须忍耐。', Speaker = 4429, Option = '', Action = _EmptyTable,}, 
	[83734] = { id = 83734, Voice = '', Text = '我失去了一切。即便有你的帮助，我不至于饿死冻死，但你无法理解我的感受。', Speaker = 4443, Option = '', Action = _EmptyTable,}, 
	[83733] = { id = 83733, Voice = '', Text = '我时常在想，我为什么会在这里。', Speaker = 4443, Option = '', Action = _EmptyTable,}, 
	[83732] = { id = 83732, Voice = '', Text = '你是怎么了，我的朋友。为什么你看上去很沮丧？', Speaker = 4429, Option = '', Action = _EmptyTable,}, 
	[83731] = { id = 83731, Voice = '', Text = '拾取后你发现物品上封印着一段神秘的信息……', Speaker = 0, Option = '读取信息', Action = _EmptyTable,}, 
	[83730] = { id = 83730, Voice = '', Text = '【驱魔人之书】第三卷，下册。', Speaker = 4437, Option = '{拾取,2},{离开,3}', Action = _EmptyTable,}, 
	[83729] = { id = 83729, Voice = '', Text = '【怯懦之书·真相】的关键道具已出现。', Speaker = 4429, Option = '', Action = _EmptyTable,}, 
	[83728] = { id = 83728, Voice = '', Text = '【怯懦之书·仁慈】已解锁，乌鸦驱魔人会告诉你它的内容。', Speaker = 4436, Option = '', Action = _EmptyTable,}, 
	[83723] = { id = 83723, Voice = '', Text = '拾取后你发现物品上封印着一段神秘的信息……', Speaker = 0, Option = '读取信息', Action = _EmptyTable,}, 
	[83722] = { id = 83722, Voice = '', Text = '【驱魔人之书】第三卷，上册。', Speaker = 4436, Option = '{拾取,2},{离开,3}', Action = _EmptyTable,}, 
	[83721] = { id = 83721, Voice = '', Text = '【恐惧之书·真相】已解锁，月狼驱魔人会告诉你它的内容。', Speaker = 4435, Option = '', Action = _EmptyTable,}, 
	[83720] = { id = 83720, Voice = '', Text = '那么你就在这牢房里一辈子反省你的错误吧！', Speaker = 4445, Option = '', Action = _EmptyTable,}, 
	[83719] = { id = 83719, Voice = '', Text = '如果必须有人偿命，就让我来偿还吧。', Speaker = 4428, Option = '', Action = _EmptyTable,}, 
	[83718] = { id = 83718, Voice = '', Text = '莫里茨也是我的生死至交！我们并肩作战了十几年！', Speaker = 4445, Option = '', Action = _EmptyTable,}, 
	[83717] = { id = 83717, Voice = '', Text = '想想他为克雷斯特汉姆所做的一切吧，您愿意让他过往所立下的功德受辱吗？难道，他的墓碑上应该写上谋杀者的罪名？', Speaker = 4428, Option = '', Action = _EmptyTable,}, 
	[83716] = { id = 83716, Voice = '', Text = '他是我的朋友。我不能看着他背负冤屈和污名而死。', Speaker = 4428, Option = '', Action = _EmptyTable,}, 
	[83715] = { id = 83715, Voice = '', Text = '你凭什么判断他的品性不会改变？', Speaker = 4445, Option = '他是我的朋友', Action = _EmptyTable,}, 
	[83714] = { id = 83714, Voice = '', Text = '国王陛下，我相信以卡伦赞德的品性，一定不会做出卑劣的事。所以我放跑了他。', Speaker = 4428, Option = '', Action = _EmptyTable,}, 
	[83713] = { id = 83713, Voice = '', Text = '谁让你擅作主张，把他放跑的？！', Speaker = 4445, Option = '', Action = _EmptyTable,}, 
	[83712] = { id = 83712, Voice = '', Text = '拾取后你发现物品上封印着一段神秘的信息……', Speaker = 0, Option = '读取信息', Action = _EmptyTable,}, 
	[83711] = { id = 83711, Voice = '', Text = '【驱魔人之书】第二卷，下册。', Speaker = 4435, Option = '{拾取,2},{离开,3}', Action = _EmptyTable,}, 
	[83710] = { id = 83710, Voice = '', Text = '【恐惧之书·真相】的关键道具已出现。', Speaker = 4428, Option = '', Action = _EmptyTable,}, 
	[83709] = { id = 83709, Voice = '', Text = '【恐惧之书·孤独】已解锁，月狼驱魔人会告诉你它的内容。', Speaker = 4434, Option = '', Action = _EmptyTable,}, 
	[83708] = { id = 83708, Voice = '', Text = '我知道有条密道能从下水道逃出去，快走吧！', Speaker = 4428, Option = '', Action = _EmptyTable,}, 
	[83707] = { id = 83707, Voice = '', Text = '那也比让我袖手旁观看着你含冤下地狱，要好一点。我心意已决。但愿国王尽快找回他的理智。', Speaker = 4428, Option = '', Action = _EmptyTable,}, 
	[83706] = { id = 83706, Voice = '', Text = '我的朋友，你这么做，会让你失去现在的一切！', Speaker = 4443, Option = '', Action = _EmptyTable,}, 
	[83705] = { id = 83705, Voice = '', Text = '我会留在这里，代替卡伦赞德接收王庭审判。', Speaker = 4428, Option = '', Action = _EmptyTable,}, 
	[83704] = { id = 83704, Voice = '', Text = '可是，如果你放跑了他，国王会大发雷霆。即使是阁下您，国王恐怕也不会善罢甘休。', Speaker = 4444, Option = '', Action = _EmptyTable,}, 
	[83703] = { id = 83703, Voice = '', Text = '我不能眼看着我的朋友卡伦赞德蒙冤受苦，而仍然若无其事地享用国王恩赐的美酒佳肴。', Speaker = 4428, Option = '', Action = _EmptyTable,}, 
	[83702] = { id = 83702, Voice = '', Text = '拾取后你发现物品上封印着一段神秘的信息……', Speaker = 0, Option = '读取信息', Action = _EmptyTable,}, 
	[83701] = { id = 83701, Voice = '', Text = '【驱魔人之书】第二卷，上册。', Speaker = 4434, Option = '{拾取,2},{离开,3}', Action = _EmptyTable,}, 
	[83700] = { id = 83700, Voice = '', Text = '【憎恶之书·真相】已解锁，猎鹰驱魔人会告诉你它的内容。', Speaker = 4433, Option = '', Action = _EmptyTable,}, 
	[83699] = { id = 83699, Voice = '', Text = '可笑！邪恶之物，你不配拥有我的灵魂！', Speaker = 4427, Option = '', Action = _EmptyTable,}, 
	[83698] = { id = 83698, Voice = '', Text = '你愿意献上你的灵魂吗？', Speaker = 4442, Option = '与恶魔做交易！', Action = _EmptyTable,}, 
	[83697] = { id = 83697, Voice = '', Text = '怎么样？这是一个不错的交换吧？', Speaker = 4442, Option = '', Action = _EmptyTable,}, 
	[83696] = { id = 83696, Voice = '', Text = '驱魔人，如果你杀死我，你的朋友依然无法得救。但如果你愿意把灵魂献给我，我可以放弃你的朋友。', Speaker = 4442, Option = '', Action = _EmptyTable,}, 
	[83695] = { id = 83695, Voice = '', Text = '原来，都是你在捣鬼！以人类的痛苦为食的魔鬼，在我面前消失吧！', Speaker = 4427, Option = '', Action = _EmptyTable,}, 
	[83694] = { id = 83694, Voice = '', Text = '拾取后你发现物品上封印着一段神秘的信息……', Speaker = 0, Option = '读取信息', Action = _EmptyTable,}, 
	[83693] = { id = 83693, Voice = '', Text = '【驱魔人之书】第一卷，下册。', Speaker = 4433, Option = '{拾取,2},{离开,3}', Action = _EmptyTable,}, 
	[83692] = { id = 83692, Voice = '', Text = '【憎恶之书·真相】的关键道具已出现。', Speaker = 4427, Option = '', Action = _EmptyTable,}, 
	[83691] = { id = 83691, Voice = '', Text = '【憎恶之书·公正】已解锁，猎鹰驱魔人会告诉你它的内容。', Speaker = 4432, Option = '', Action = _EmptyTable,}, 
	[83690] = { id = 83690, Voice = '', Text = '你会的……你会心甘情愿交出你的心。', Speaker = 4442, Option = '', Action = _EmptyTable,}, 
	[83689] = { id = 83689, Voice = '', Text = '即便是这样，我也绝不会受到恶魔的诱惑！', Speaker = 4443, Option = '', Action = _EmptyTable,}, 
	[83688] = { id = 83688, Voice = '', Text = '你现在拒绝了我，以后会失去更多。', Speaker = 4442, Option = '', Action = _EmptyTable,}, 
	[83687] = { id = 83687, Voice = '', Text = '呵呵呵，你会的。', Speaker = 4442, Option = '', Action = _EmptyTable,}, 
	[83686] = { id = 83686, Voice = '', Text = '没有任何东西，能取代我现在所拥有的一切。恶魔，我不会和你做这个交易。', Speaker = 4443, Option = '', Action = _EmptyTable,}, 
	[83685] = { id = 83685, Voice = '', Text = '卡伦赞德面对恶魔，毫不犹豫地拔出了剑。', Speaker = 3422, Option = '', Action = _EmptyTable,}, 
	[83684] = { id = 83684, Voice = '', Text = '你愿意和我做个交易吗？把你那颗纯净的心灵给我，而我可以给你名利、地位、财富、还有永远爱你的美女。', Speaker = 4442, Option = '', Action = _EmptyTable,}, 
	[83683] = { id = 83683, Voice = '', Text = '勇敢的骑士，你为你们的国王浴血奋战无所畏惧，我看到你的内心如此纯净。那光明的源泉正是我最喜爱的东西。', Speaker = 4442, Option = '', Action = _EmptyTable,}, 
	[83682] = { id = 83682, Voice = '', Text = '拾取后你发现物品上封印着一段神秘的信息……', Speaker = 0, Option = '读取信息', Action = _EmptyTable,}, 
	[83681] = { id = 83681, Voice = '', Text = '【驱魔人之书】第一卷，上册。', Speaker = 4432, Option = '{拾取,2},{离开,3}', Action = _EmptyTable,}, 
	[83680] = { id = 83680, Voice = '', Text = '【尾声】的关键道具已出现。', Speaker = 4426, Option = '', Action = _EmptyTable,}, 
	[83679] = { id = 83679, Voice = '', Text = '【楔子】已解锁，影子驱魔人会告诉你它的内容。', Speaker = 4431, Option = '', Action = _EmptyTable,}, 
	[83673] = { id = 83673, Voice = '', Text = '然而，一天夜里，发生了一起命案。它改变了卡伦赞德的命运。', Speaker = 3422, Option = '', Action = _EmptyTable,}, 
	[83672] = { id = 83672, Voice = '', Text = '四个与黑暗为伴的使者，都很喜欢这位为帝国带来光明的朋友。', Speaker = 3422, Option = '', Action = _EmptyTable,}, 
	[83671] = { id = 83671, Voice = '', Text = '驱魔人在黑夜中为王国驱除邪恶灾难时，卡伦赞德则挥舞着他的长枪为国王披肝沥胆，立下赫赫战功。', Speaker = 3422, Option = '', Action = _EmptyTable,}, 
	[83670] = { id = 83670, Voice = '', Text = '四个驱魔人，他们有一个共同的朋友，卡伦赞德骑士团长。', Speaker = 3422, Option = '', Action = _EmptyTable,}, 
	[83669] = { id = 83669, Voice = '', Text = '拾取后你发现物品上封印着一段神秘的信息……', Speaker = 0, Option = '读取信息', Action = _EmptyTable,}, 
	[83668] = { id = 83668, Voice = '', Text = '不知是哪本书的开头部分，泛黄的纸页上写着【楔子】两字。', Speaker = 4431, Option = '{拾取,2},{离开,3}', Action = _EmptyTable,}, 
	[83667] = { id = 83667, Voice = '', Text = '请提交解锁[c][ffff00]【嫉妒之书·真相】[-][/c]的关键道具。', Speaker = 4430, Option = '嫉妒之书·真相', Action = _EmptyTable,}, 
	[83666] = { id = 83666, Voice = '', Text = '请提交解锁[c][ffff00]【嫉妒之书·睿智】[-][/c]的关键道具。', Speaker = 4430, Option = '嫉妒之书·睿智', Action = _EmptyTable,}, 
	[83665] = { id = 83665, Voice = '', Text = '请提交解锁[c][ffff00]【怯懦之书·真相】[-][/c]的关键道具。', Speaker = 4429, Option = '怯懦之书·真相', Action = _EmptyTable,}, 
	[83664] = { id = 83664, Voice = '', Text = '请提交解锁[c][ffff00]【怯懦之书·仁慈】[-][/c]的关键道具。', Speaker = 4429, Option = '怯懦之书·仁慈', Action = _EmptyTable,}, 
	[83663] = { id = 83663, Voice = '', Text = '请提交解锁[c][ffff00]【恐惧之书·真相】[-][/c]的关键道具。', Speaker = 4428, Option = '恐惧之书·真相', Action = _EmptyTable,}, 
	[83662] = { id = 83662, Voice = '', Text = '请提交解锁[c][ffff00]【恐惧之书·孤独】[-][/c]的关键道具。', Speaker = 4428, Option = '恐惧之书·孤独', Action = _EmptyTable,}, 
	[83661] = { id = 83661, Voice = '', Text = '请提交解锁[c][ffff00]【憎恶之书·真相】[-][/c]的关键道具。', Speaker = 4427, Option = '憎恶之书·真相', Action = _EmptyTable,}, 
	[83660] = { id = 83660, Voice = '', Text = '请提交解锁[c][ffff00]【憎恶之书·公正】[-][/c]的关键道具。', Speaker = 4427, Option = '憎恶之书·公正', Action = _EmptyTable,}, 
	[83659] = { id = 83659, Voice = '', Text = '请提交解锁[c][ffff00]【尾声】[-][/c]的关键道具。', Speaker = 4426, Option = '尾声', Action = _EmptyTable,}, 
	[83658] = { id = 83658, Voice = '', Text = '请提交解锁[c][ffff00]【楔子】[-][/c]的关键道具。', Speaker = 4426, Option = '楔子', Action = _EmptyTable,}, 
	[83653] = { id = 83653, Voice = '', Text = '你知道，他们谁赢得了赌约？', Speaker = 4426, Option = '', Action = _EmptyTable,}, 
	[83652] = { id = 83652, Voice = '', Text = '“猎鹰”驱魔人正直勇敢，“月狼”驱魔人重情重义，“乌鸦”驱魔人善良仁慈，“毒蝎”驱魔人聪明睿智。', Speaker = 4426, Option = '', Action = _EmptyTable,}, 
	[83651] = { id = 83651, Voice = '', Text = '谁能活的最久，永不放弃驱魔人的道路，另外三个人的遗产就将都归属于他。', Speaker = 4426, Option = '', Action = _EmptyTable,}, 
	[83650] = { id = 83650, Voice = '', Text = '有一天，为了寻找那种意义，他们定下了一个赌约。', Speaker = 4426, Option = '', Action = _EmptyTable,}, 
	[83649] = { id = 83649, Voice = '', Text = '这份工作同时也折磨着他们的灵魂，让他们看尽了那些阴谋、丑恶、死亡、别离。甚至开始怀疑他们所做的一切是否有意义。', Speaker = 4426, Option = '', Action = _EmptyTable,}, 
	[83648] = { id = 83648, Voice = '', Text = '很久以前，有四个驱魔人。他们都是国王所仰赖的亲信，他们都很骄傲、自负，并且拥有炽热的灵魂，四处游猎，驱散世间的邪恶。', Speaker = 4426, Option = '', Action = _EmptyTable,}, 
	[83647] = { id = 83647, Voice = '', Text = '不过，我可以给你讲一个故事。是关于一个“赌约”。', Speaker = 4426, Option = '', Action = _EmptyTable,}, 
	[83646] = { id = 83646, Voice = '', Text = '我是谁？不，你不会知道我的名字。', Speaker = 4426, Option = '', Action = _EmptyTable,}, 
	[83644] = { id = 83644, Voice = '', Text = '我能理解，毕竟并不是人人都有勇气牺牲自己去解救他人的。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[83643] = { id = 83643, Voice = '', Text = '现在万事俱备！我们可以前往下水道的迷幻之王身体中寻找庞德失落的灵魂了。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[83642] = { id = 83642, Voice = '', Text = '一道深邃的幽光渐渐顺着手指蔓延，直到遍布全身…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83641] = { id = 83641, Voice = '', Text = '现在你只需要轻轻一触碰，我再施展魔法，你就可以成功进入迷幻之王的“异世界”了。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[83640] = { id = 83640, Voice = '', Text = '瞧！这些黑暗粉末在我的处理下已经变成了黑暗元素的结晶体。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[83639] = { id = 83639, Voice = '', Text = '请你稍等片刻，我需要处理一下这些元素。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[83638] = { id = 83638, Voice = '', Text = '冒险者，你的效率令我惊讶！', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[83637] = { id = 83637, Voice = '', Text = '古城下水道的邪恶使者身上应该会有大量黑暗元素，冒险者，快去收集这些元素吧。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[83636] = { id = 83636, Voice = '', Text = '在不惊动迷幻之王的前提下想要精准的定位迷幻之王的位置，我们只能从这些魔物入手。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[83635] = { id = 83635, Voice = '', Text = '这也是为什么魔物们会听从迷幻之王命令的原因。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[83634] = { id = 83634, Voice = '', Text = '嘿…迷幻之王身上污浊不堪的气息会逐渐影响四周的所有魔物。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[83633] = { id = 83633, Voice = '', Text = '我现在要准备前往“异世界”的魔法阵，无法脱身，冒险者，只能你去寻找了。', Speaker = 4422, Option = '交给我吧', Action = _EmptyTable,}, 
	[83632] = { id = 83632, Voice = '', Text = '我听雷藏说，如果我们能够拿到迷幻之王身上的东西，那么我就可以根据物品的气息来定位，这样你前往“异世界”的风险也会减小。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[83631] = { id = 83631, Voice = '', Text = '相反，在这里施展死灵魔法威力反而会大大增加。虽然没有了魔力的后顾之忧，但是我却需要精准的定位下水道的迷幻之王的位置。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[83630] = { id = 83630, Voice = '', Text = '但是我将要施展的魔法，是死灵魔法，他们同出一源，所以我的魔力不会有太多的损耗。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[83629] = { id = 83629, Voice = '', Text = '这里弥散着死亡的气息，如你所见，这种雾气阻挡着魔力的流通，魔法的威力在这里大打折扣。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[83628] = { id = 83628, Voice = '', Text = '当你觉得已经准备充分的时候再来找我吧。', Speaker = 4422, Option = '', Action = _EmptyTable,}, 
	[83627] = { id = 83627, Voice = '', Text = '你已经做好前往迷幻之王身体中的“异世界”的准备了吗？', Speaker = 4422, Option = '{准备好了,2},{我再等等,3}', Action = _EmptyTable,}, 
	[83591] = { id = 83591, Voice = '', Text = '——来自一位秘密调查员的报告。', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83590] = { id = 83590, Voice = '', Text = '百年风霜抹去了真相。古城的秘密被封印在恐怖死寂中。如果能进去探索，也许可以找到答案？', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83589] = { id = 83589, Voice = '', Text = '关于这名“叛变者”，我锁定了十五个嫌疑人，并将调查报告记录为[c][ffff00]【古城末日“叛变者”档案】[-][/c]。', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83588] = { id = 83588, Voice = '', Text = '只有一份记录留下了线索。“叛变者”向希默梅思告密，说：“现在，是时候了。这是您等待已久的机会，也是我等待已久的时刻。我将引导您的不死军团通过捷径进入那固若金汤之城。不，它已不再固若金汤，因为对它了解至深的我，就是它的弱点。”', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83587] = { id = 83587, Voice = '', Text = '逝者再也不能诉说实情。那个隐藏在幕后的“叛变者”也从此消失在死亡军团的血色旗帜中，没有任何记录说明他之后的去向……', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83586] = { id = 83586, Voice = '', Text = '国王不知去向，他早已不在王城中。守护着这座王城的海因里希司令官被人背后放暗箭，重伤死在希默梅思剑下。', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83585] = { id = 83585, Voice = '', Text = 'A.W800年，希默梅思的不死军团数次对克雷斯特汉姆王城久攻不下，而这一夜，亡灵大军轻易踏平了这个王朝。血色就像是天边红霞，映照着王城中潮水般倒下的士兵。', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83584] = { id = 83584, Voice = '', Text = 'A.W796年，王朝的命运，就像是公主的病患一般，日渐衰落，迎向暮晚最绚丽也最悲伤的余晖。', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83583] = { id = 83583, Voice = '', Text = 'A.W795年，“悲剧的婚礼”事件。就在婚礼当天，公主宣布自己得了传染病，无法出现在婚礼上。世人谴责欧律皮罗斯男爵太薄情寡义，就此抛弃了公主。而他的仕途却正宏图高展。', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83582] = { id = 83582, Voice = '', Text = '婚礼随后在公主养伤的日子里大张旗鼓筹办，国王昭告天下，小公主将大婚。尽管舆论四起，欧律皮罗斯男爵仍然非常坚定地要娶公主。', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83581] = { id = 83581, Voice = '', Text = 'A.W794年，王城夜半发生大火，公主恰巧去了被熊熊大火吞噬的宫殿。巧合的是，刚刚夜巡归来的欧律皮罗斯男爵立刻就冲进火烧的宫殿，救出了被烧伤的卡珊德拉公主。', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83580] = { id = 83580, Voice = '', Text = '他送给公主的定情信物，一直被公主佩戴在秀发上，两人很快陷入热恋……', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83579] = { id = 83579, Voice = '', Text = 'A.W793年，之前在文献记载中从未出现过的“卡珊德拉公主的未婚夫”忽然出现在我们眼前。', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83578] = { id = 83578, Voice = '', Text = 'A.W781年，发生了一场规模庞大的佣兵叛乱事件，海因里希领兵耗时三个月镇压了这场叛乱，事后牵连下狱者无数。', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83577] = { id = 83577, Voice = '', Text = '在对古城末日档案深入研究的过程中，我渐渐发现，解释一切谜团的关键，就是卡珊德拉公主“悲剧的婚礼”事件。', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83576] = { id = 83576, Voice = '', Text = '尝试把两份残卷拼在一块，看看会发生什么！', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83564] = { id = 83564, Voice = '', Text = '唔……这件古物的确值得研究，让我告诉你鉴定结果吧。', Speaker = 3402, Option = '国王的宝剑', Action = _EmptyTable,}, 
	[83563] = { id = 83563, Voice = '', Text = '唔……这件古物的确值得研究，让我告诉你鉴定结果吧。', Speaker = 3402, Option = '贵妇的礼帽', Action = _EmptyTable,}, 
	[83562] = { id = 83562, Voice = '', Text = '唔……这件古物的确值得研究，让我告诉你鉴定结果吧。', Speaker = 3402, Option = '宰相的怀表', Action = _EmptyTable,}, 
	[83561] = { id = 83561, Voice = '', Text = '唔……这件古物的确值得研究，让我告诉你鉴定结果吧。', Speaker = 3402, Option = '骑士的羽穗', Action = _EmptyTable,}, 
	[83560] = { id = 83560, Voice = '', Text = '哇，我忽然不困了。你带来的东西有点意思，让我仔细研究下！', Speaker = 3401, Option = '腐蚀的头骨', Action = _EmptyTable,}, 
	[83559] = { id = 83559, Voice = '', Text = '哇，我忽然不困了。你带来的东西有点意思，让我仔细研究下！', Speaker = 3401, Option = '士兵的日记', Action = _EmptyTable,}, 
	[83558] = { id = 83558, Voice = '', Text = '哇，我忽然不困了。你带来的东西有点意思，让我仔细研究下！', Speaker = 3401, Option = '罗娜的魔书', Action = _EmptyTable,}, 
	[83557] = { id = 83557, Voice = '', Text = '哇，我忽然不困了。你带来的东西有点意思，让我仔细研究下！', Speaker = 3401, Option = '祈祷的项链', Action = _EmptyTable,}, 
	[83556] = { id = 83556, Voice = '', Text = '啊，你已经有线索了吗？让我看看……', Speaker = 3400, Option = '破碎的宝石', Action = _EmptyTable,}, 
	[83555] = { id = 83555, Voice = '', Text = '啊，你已经有线索了吗？让我看看……', Speaker = 3400, Option = '深红的披风', Action = _EmptyTable,}, 
	[83554] = { id = 83554, Voice = '', Text = '啊，你已经有线索了吗？让我看看……', Speaker = 3400, Option = '古旧的勋章', Action = _EmptyTable,}, 
	[83553] = { id = 83553, Voice = '', Text = '啊，你已经有线索了吗？让我看看……', Speaker = 3400, Option = '损毁的头盔', Action = _EmptyTable,}, 
	[83548] = { id = 83548, Voice = '', Text = '你知道“叛变者”是谁了吗？', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83531] = { id = 83531, Voice = '', Text = '拼接的瞬间，完整的残卷发出了耀目的光芒，似乎要把人吸进另一个空间……', Speaker = 0, Option = '{进入记忆空间,2},{再等一下,3}', Action = _EmptyTable,}, 
	[83530] = { id = 83530, Voice = '', Text = '没错，正是这半份残卷！现在，我可以把它拼全了……咦，残卷上好像附有魔力……', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83529] = { id = 83529, Voice = '', Text = '当然我已经有眉目了，另外半份残卷，可能在鹗枭男爵手里。只是，那么多的鹗枭男爵，天知道是哪一个。你再帮我去古城中找找吧！', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83528] = { id = 83528, Voice = '', Text = '不过，还差最后一个环节。要解读那份档案，我还需要解开一份残卷上的内容，但我手里只有一半。', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83527] = { id = 83527, Voice = '', Text = '真不敢相信，你竟然帮我把那些物证都找齐了。', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83526] = { id = 83526, Voice = '', Text = '唔……这件古物的确值得研究，让我告诉你鉴定结果吧。', Speaker = 3402, Option = '残缺的石碑', Action = _EmptyTable,}, 
	[83525] = { id = 83525, Voice = '', Text = '哇，我忽然不困了。你带来的东西有点意思，让我仔细研究下！', Speaker = 3401, Option = '残旧的匕首', Action = _EmptyTable,}, 
	[83524] = { id = 83524, Voice = '', Text = '啊，你已经有线索了吗？让我看看……', Speaker = 3400, Option = '少女的发簪', Action = _EmptyTable,}, 
	[83523] = { id = 83523, Voice = '', Text = '希默梅思用它斩下了海因里希的头颅，然后随手把它丢弃了，于是它所承载的一代王朝落下帷幕。', Speaker = 3402, Option = '', Action = _EmptyTable,}, 
	[83522] = { id = 83522, Voice = '', Text = '最后一任国王将它留在了克雷斯特汉姆古城，远赴他乡再也没有回来。海因里希执政官总是遥遥望着这把国王宝剑叹息。', Speaker = 3402, Option = '', Action = _EmptyTable,}, 
	[83521] = { id = 83521, Voice = '', Text = '[c][ffff00]【古城末日“叛变者”调查档案15号】[-][/c]初代施密特国王使用过的剑，此后，每一任国王都会在加冕仪式上接过它。它见证了施密特王族的兴衰。', Speaker = 3402, Option = '', Action = _EmptyTable,}, 
	[83520] = { id = 83520, Voice = '', Text = '不过严肃古板的执政官阁下，可不像是会偷偷赠送贵妇礼帽的那种男人啊，大家都认为他这辈子唯一的情人就是他的工作。', Speaker = 3402, Option = '', Action = _EmptyTable,}, 
	[83519] = { id = 83519, Voice = '', Text = '[c][ffff00]【古城末日“叛变者”调查档案14号】[-][/c]海因里希执政期间，曾因为这顶礼帽而闹出过一段绯闻，它原先被摆放在海因里希的书房中，后来却出现在了一位贵妇的头上。', Speaker = 3402, Option = '', Action = _EmptyTable,}, 
	[83518] = { id = 83518, Voice = '', Text = '但它现在却没有陪伴着宰相的遗体，是有人将它偷走了吗？怀表本身价值不菲，曾经有人向宰相高价收取这块怀表，当然被拒绝了。', Speaker = 3402, Option = '', Action = _EmptyTable,}, 
	[83517] = { id = 83517, Voice = '', Text = '[c][ffff00]【古城末日“叛变者”调查档案13号】[-][/c]奥德修斯宰相的怀表，是他非常珍爱之物。据说是妻子的遗物，宰相日理万机时总是打开它确认时间，从未让它离开过身边。', Speaker = 3402, Option = '', Action = _EmptyTable,}, 
	[83516] = { id = 83516, Voice = '', Text = '男爵在破城之日，于公主墓碑前刎颈自杀，当时他手中握着这根羽穗，在墓碑前的地上用剑刻下了愧疚之言。男爵倒在血泊中时，羽穗随风飘远，爱意随风而逝。', Speaker = 3402, Option = '', Action = _EmptyTable,}, 
	[83515] = { id = 83515, Voice = '', Text = '[c][ffff00]【古城末日“叛变者”调查档案12号】[-][/c]欧律皮罗斯男爵家族特有的象征，它被拔下来，伴随着卡珊德拉公主度过了病患余生。', Speaker = 3402, Option = '', Action = _EmptyTable,}, 
	[83514] = { id = 83514, Voice = '', Text = '石碑上写着她的墓志铭：愿宽容如烈火带来温暖，愿善良之人终得一切恩露之报。', Speaker = 3402, Option = '', Action = _EmptyTable,}, 
	[83513] = { id = 83513, Voice = '', Text = '[c][ffff00]【古城末日“叛变者”调查档案11号】[-][/c]卡珊德拉公主的墓碑，这位公主死于一场传染病，临终前十分孤独凄惨，只有一名忠心的侍女照顾着她。', Speaker = 3402, Option = '', Action = _EmptyTable,}, 
	[83512] = { id = 83512, Voice = '', Text = '总之，这颗神秘的头骨被封存在王族后花园地窖中很多年。触碰头骨，似乎能看到头骨上飘出一个白糊糊的鬼影……', Speaker = 3401, Option = '', Action = _EmptyTable,}, 
	[83511] = { id = 83511, Voice = '', Text = '[c][ffff00]【古城末日“叛变者”调查档案10号】[-][/c]据传是施密特王朝第一任国王的头骨，又相传是国王祖父的头骨，也有流传说是先代国王的战友大魔导士路西菲尔的头骨……', Speaker = 3401, Option = '', Action = _EmptyTable,}, 
	[83510] = { id = 83510, Voice = '', Text = '最后一篇日记停留在两百年前的那个夜晚，上面写道：恶魔来了，但海因里希将军会带领我们战胜恶魔。等我凯旋归来，我决定对那条流浪犬好一点……', Speaker = 3401, Option = '', Action = _EmptyTable,}, 
	[83509] = { id = 83509, Voice = '', Text = '[c][ffff00]【古城末日“叛变者”调查档案9号】[-][/c]古城守备士兵的日记，里面写满了枯燥的巡逻日常、成年人的深夜笑话、下水道的鬼怪传说、施密特王族的那些王家闹剧、流浪狗“小柯”的饲养日志、对修道院里传出的琴声品评……', Speaker = 3401, Option = '', Action = _EmptyTable,}, 
	[83508] = { id = 83508, Voice = '', Text = '这本书在她死后，被转交给安德斯神父保管。不久之后，安德斯神父又对一位来访者宣称，遗失了这本书……', Speaker = 3401, Option = '', Action = _EmptyTable,}, 
	[83507] = { id = 83507, Voice = '', Text = '罗娜被认为是邪恶的巫师，她孤独地居住在一间偏僻小屋里，研究着在人们眼中被视为最邪恶禁忌的魔法，因此没有人愿意接近她的住处，只有安德斯神父愿意和她交谈。她送了神父一条项链，说是能带来幸福。', Speaker = 3401, Option = '', Action = _EmptyTable,}, 
	[83506] = { id = 83506, Voice = '', Text = '[c][ffff00]【古城末日“叛变者”调查档案8号】[-][/c]罗娜的魔法书，记录着她抄录的那些失传的古魔法咒语。', Speaker = 3401, Option = '', Action = _EmptyTable,}, 
	[83505] = { id = 83505, Voice = '', Text = '谣传他过去是皇家宫廷乐师，因被某位伯爵诬陷暗中爱慕王后，为了王后的名誉而选择离开王庭，隐居在修道院中，除了祷告之外，又领养了许多贫苦孤儿。', Speaker = 3401, Option = '', Action = _EmptyTable,}, 
	[83504] = { id = 83504, Voice = '', Text = '[c][ffff00]【古城末日“叛变者”调查档案7号】[-][/c]安德斯神父的十字架项链，过去，他常常握着它祈祷施密特王族的永久康盛，并祝福他的爱人幸福。', Speaker = 3401, Option = '', Action = _EmptyTable,}, 
	[83503] = { id = 83503, Voice = '', Text = '这把匕首后来一直被克雷霍尔伦家族保存，最后一位主人将它锁进了箱子里，如同想要将克雷霍尔伦家族那不能明言的过去藏起，渴望那沉重的负担从此被人遗忘。', Speaker = 3401, Option = '', Action = _EmptyTable,}, 
	[83502] = { id = 83502, Voice = '', Text = '篡位者吉奥布罗克国王的王后阿芙洛忒命一位手艺精湛的工匠锻造了它，并赠送给丈夫。', Speaker = 3401, Option = '', Action = _EmptyTable,}, 
	[83501] = { id = 83501, Voice = '', Text = '[c][ffff00]【古城末日“叛变者”调查档案6号】[-][/c]“阿芙洛忒的匕首”是它久违的流传之名，刀柄上的纹章属于克雷霍尔伦家族，因此它被称为“弑君者匕首”。', Speaker = 3401, Option = '', Action = _EmptyTable,}, 
	[83500] = { id = 83500, Voice = '', Text = '大贤者巴特蒙德曾警告过国王，将这颗宝石从权杖上取下并丢弃，否则王国必将迎来末日。于是，这颗宝石就被锁进了宝箱，国王称它为“恶魔的诱惑”。', Speaker = 3400, Option = '', Action = _EmptyTable,}, 
	[83499] = { id = 83499, Voice = '', Text = '[c][ffff00]【古城末日“叛变者”调查档案5号】[-][/c]这颗宝石曾被镶嵌在施密特国王的权杖上，世代相传。宝石的碎裂，映照着这个王族的末路。', Speaker = 3400, Option = '', Action = _EmptyTable,}, 
	[83498] = { id = 83498, Voice = '', Text = '不过关于这位将军的死，传言却是扑朔迷离。据说将军并非战死，而是被暗杀。“俄思鲁将军秘密处死男爵家仆”的王家丑闻事件，被认为与伊多墨将军之死有关。', Speaker = 3400, Option = '', Action = _EmptyTable,}, 
	[83497] = { id = 83497, Voice = '', Text = '[c][ffff00]【古城末日“叛变者”调查档案4号】[-][/c]披风里衬上有伊多墨将军的名字，这位赫赫有名的施密特王朝大将，与俄思鲁将军并称“王国双璧”，生前两人亦是挚友。', Speaker = 3400, Option = '', Action = _EmptyTable,}, 
	[83496] = { id = 83496, Voice = '', Text = '它并非代表荣耀，而是成为施密特王家的影子，执行秘密的任务。在这枚勋章上，竟然刻着卢恩王国七大家族之一的鲁贝布勒格家族的姓氏。', Speaker = 3400, Option = '', Action = _EmptyTable,}, 
	[83495] = { id = 83495, Voice = '', Text = '[c][ffff00]【古城末日“叛变者”调查档案3号】[-][/c]这是施密特王朝特制的战士勋章，徽纹代表着持有者为施密特家族的亲信，骑士团中只有极少数人会被国王授予这种勋章。', Speaker = 3400, Option = '', Action = _EmptyTable,}, 
	[83494] = { id = 83494, Voice = '', Text = '后来，这位将军因骁勇善战不苟言笑，被卡珊德拉公主赐予“寒冰锻造的利刃”之称，并拔剑在头盔上划下了另一道痕迹。不知公主是不是在开玩笑，不过这个头盔伴随着将军一生。', Speaker = 3400, Option = '', Action = _EmptyTable,}, 
	[83493] = { id = 83493, Voice = '', Text = '[c][ffff00]【古城末日“叛变者”调查档案2号】[-][/c]俄思鲁将军的头盔，其上有一道深而长的刀痕，那是和某位男爵决斗时留下的耻辱。', Speaker = 3400, Option = '', Action = _EmptyTable,}, 
	[83492] = { id = 83492, Voice = '', Text = '发簪有烧伤过的痕迹，传说公主曾经因一场大火而毁容，但男爵仍然承诺将娶她为妻。', Speaker = 3400, Option = '', Action = _EmptyTable,}, 
	[83491] = { id = 83491, Voice = '', Text = '[c][ffff00]【古城末日“叛变者”调查档案1号】[-][/c]卡珊德拉公主的饰物，上面刻有她的名字缩写，是当时流行的款式。由欧律皮罗斯男爵赠送给公主作为定情信物。', Speaker = 3400, Option = '', Action = _EmptyTable,}, 
	[83490] = { id = 83490, Voice = '', Text = '饱经沧桑的剑，锈迹斑斑的剑刃伤痕累累，但仍然掩盖不住它所散发出的王者之气。', Speaker = 3417, Option = '{捡起,2},{不感兴趣,3}', Action = _EmptyTable,}, 
	[83489] = { id = 83489, Voice = '', Text = '可能是被风吹到这里的，一顶华美的礼帽，显然是贵妇喜爱的饰物。', Speaker = 3416, Option = '{捡起,2},{不感兴趣,3}', Action = _EmptyTable,}, 
	[83488] = { id = 83488, Voice = '', Text = '一块陈旧的铜制怀表，表面玻璃有许多裂痕，指针永久停留在某个时间。', Speaker = 3415, Option = '{捡起,2},{不感兴趣,3}', Action = _EmptyTable,}, 
	[83487] = { id = 83487, Voice = '', Text = '属于骑士头盔上的殷红羽穗，头盔不知去向，只有这根羽穗，似乎是被刻意拔下来的，羽毛很完整，感觉它被很好地爱惜着。', Speaker = 3414, Option = '{捡起,2},{不感兴趣,3}', Action = _EmptyTable,}, 
	[83486] = { id = 83486, Voice = '', Text = '像是一块残缺的墓碑，字迹不全，难以辨认。', Speaker = 3413, Option = '{捡起,2},{不感兴趣,3}', Action = _EmptyTable,}, 
	[83485] = { id = 83485, Voice = '', Text = '阴森狰狞的惨白头骨，摸一下，不知道会不会倒霉。', Speaker = 3421, Option = '', Action = _EmptyTable,}, 
	[83484] = { id = 83484, Voice = '', Text = '一只宝箱，可能藏有宝物？', Speaker = 3412, Option = '{打开,2},{不打开,3}', Action = _EmptyTable,}, 
	[83483] = { id = 83483, Voice = '', Text = '一本破旧的日记，显然年代久远。', Speaker = 3411, Option = '{捡起,2},{不感兴趣,3}', Action = _EmptyTable,}, 
	[83482] = { id = 83482, Voice = '', Text = '被锁在箱子中的一本古旧的书，让人很有打开瞧一瞧的想法……', Speaker = 3420, Option = '', Action = _EmptyTable,}, 
	[83481] = { id = 83481, Voice = '', Text = '一只宝箱，可能藏有宝物？', Speaker = 3410, Option = '{打开,2},{不打开,3}', Action = _EmptyTable,}, 
	[83480] = { id = 83480, Voice = '', Text = '银质的十字架项链，朴素至极，可能是用来祈祷的，不太值钱。', Speaker = 3409, Option = '{捡起,2},{不感兴趣,3}', Action = _EmptyTable,}, 
	[83479] = { id = 83479, Voice = '', Text = '满是豁口的匕首，刀柄很精致，细巧而便于携带，像是属于一位女性。', Speaker = 3419, Option = '', Action = _EmptyTable,}, 
	[83478] = { id = 83478, Voice = '', Text = '一只宝箱，可能藏有宝物？', Speaker = 3408, Option = '{打开,2},{不打开,3}', Action = _EmptyTable,}, 
	[83477] = { id = 83477, Voice = '', Text = '硕大的红宝石，就像照耀着克雷斯特汉姆古城的晚霞一样神秘。拥有它的人一定身份尊贵无比。不过宝石上布满了细碎的裂痕，仿佛象征着王族的支离破碎。', Speaker = 3418, Option = '', Action = _EmptyTable,}, 
	[83476] = { id = 83476, Voice = '', Text = '一只宝箱，可能藏有宝物？', Speaker = 3407, Option = '{打开,2},{不打开,3}', Action = _EmptyTable,}, 
	[83475] = { id = 83475, Voice = '', Text = '一件破烂的披风，款式和古城魔物鹗枭男爵的披风很像，发黑的斑斑痕迹看上去像是溅洒在上面的血迹。', Speaker = 3406, Option = '{捡起,2},{不感兴趣,3}', Action = _EmptyTable,}, 
	[83474] = { id = 83474, Voice = '', Text = '一枚发黑老旧的金属徽章，上面有特殊的徽纹，被遗落在骑士团公会门口的草丛中，不知有何意味。', Speaker = 3405, Option = '{捡起,2},{不感兴趣,3}', Action = _EmptyTable,}, 
	[83473] = { id = 83473, Voice = '', Text = '腐锈得几乎看不出原貌，头盔上可怖的伤痕，让人联想到它曾经历过一场多么残酷的战争。', Speaker = 3404, Option = '{捡起,2},{不感兴趣,3}', Action = _EmptyTable,}, 
	[83472] = { id = 83472, Voice = '', Text = '似乎是纯金打造，不知为什么会落在这边的泥叶里，工艺精巧，上面的雕花精致无伦，应该很值钱。', Speaker = 3403, Option = '{捡起,2},{不感兴趣,3}', Action = _EmptyTable,}, 
	[83471] = { id = 83471, Voice = '', Text = '我负责鉴定[c][ffff00]古城末日“叛变者”档案[-][/c]的11号~15号，你有线索快说，没事就别来烦我。', Speaker = 3402, Option = '', Action = _EmptyTable,}, 
	[83470] = { id = 83470, Voice = '', Text = '哈~好困，真想回家洗澡……对了，我负责鉴定[c][ffff00]古城末日“叛变者”档案[-][/c]的6号~10号，什么，你有线索？', Speaker = 3401, Option = '', Action = _EmptyTable,}, 
	[83469] = { id = 83469, Voice = '', Text = '是的，我负责鉴定[c][ffff00]古城末日“叛变者”档案[-][/c]的1号~5号，有相关线索，就来找我。', Speaker = 3400, Option = '', Action = _EmptyTable,}, 
	[83468] = { id = 83468, Voice = '', Text = '如果你发现了什么线索，请先找我的另外三名队友夏梧、秋槡和冬松做鉴定。当所有疑点得到解决时，我相信会有重大发现。', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83467] = { id = 83467, Voice = '', Text = '[c][ffff00]我需要一些证据来核实，古城中必然有线索能帮助我们[-][/c]。', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83466] = { id = 83466, Voice = '', Text = '但档案的解读仍然存在着许多疑点，共有[c][ffff00]十五个部分[-][/c]需要考证。', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83465] = { id = 83465, Voice = '', Text = '这真是令人亢奋的发现，是不是？我背上的鸡皮疙瘩都起来了……', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83464] = { id = 83464, Voice = '', Text = '雄狮大军一败涂地的原因，是有一个“告密人”向死亡军团泄露了军机。', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83463] = { id = 83463, Voice = '', Text = '据这份档案记录，当年，所向披靡的施密特王朝战无不胜，连异界魔神都惧怕这支军队，却一夜之间被希默梅思破城血洗……', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83462] = { id = 83462, Voice = '', Text = '目前，我正在着手考查一份名为[c][ffff00]古城末日“叛变者”档案[-][/c]的卷宗。', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83461] = { id = 83461, Voice = '', Text = '作为一名学术严谨的历史研究员，我们需要严格考据那些文献的真伪。', Speaker = 3399, Option = '', Action = _EmptyTable,}, 
	[83195] = { id = 83195, Voice = '', Text = '即将将你传送到古城地下墓地，请注意安全，冒险者！', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83194] = { id = 83194, Voice = '', Text = '感谢你所做的一切，冒险者。那么接下来，同盟军也该行动起来了，让那些魔物见识一下我们人类的力量吧！', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83193] = { id = 83193, Voice = '', Text = '就在传送回来之前，我成功地在墓地留下了自己的魔法印记，从现在开始，前往墓地的传送阵就可以投入使用了！', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83192] = { id = 83192, Voice = '', Text = '结果总算是有惊无险，很庆幸，冒险者，你完成得相当出色！', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83191] = { id = 83191, Voice = '', Text = '雷藏大人留下一具魔法分身骗过了迷幻之王，成功掩护我们离开了。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83190] = { id = 83190, Voice = '', Text = '雷藏大人非常重视，他让我用暗黑手札感应墓地的状态，如果一旦感应到，就会直接传送过去！', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83189] = { id = 83189, Voice = '', Text = '在你走后，我想来想去，始终觉得不太放心，于是我向雷藏大人报告了这次事件。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83188] = { id = 83188, Voice = '', Text = '冒险者，跟我走！', Speaker = 4385, Option = '', Action = _EmptyTable,}, 
	[83187] = { id = 83187, Voice = '', Text = '冒险者，你先和莉莎离开这里！我来挡住迷幻之王！', Speaker = 4384, Option = '', Action = _EmptyTable,}, 
	[83185] = { id = 83185, Voice = '', Text = '蝼蚁！你竟敢破坏如此重要的仪式，你的灵魂，我收下了！', Speaker = 4383, Option = '', Action = _EmptyTable,}, 
	[83184] = { id = 83184, Voice = '', Text = '发生了什么事……', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83181] = { id = 83181, Voice = '', Text = '就是现在！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83180] = { id = 83180, Voice = '', Text = '竟然有这么恶毒的仪式…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83178] = { id = 83178, Voice = '', Text = '他们好像在祭祀着什么…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83176] = { id = 83176, Voice = '', Text = '就在前面…他们在说些什么…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83175] = { id = 83175, Voice = '', Text = '快！冒险者，我侦测到墓地中的暗黑魔力正在急速聚集，恐怕仪式已经开始了！拜托你了！', Speaker = 4273, Option = '交给我吧', Action = _EmptyTable,}, 
	[83174] = { id = 83174, Voice = '', Text = '冒险者，借由暗黑手札，我感应到墓地的雾气稀薄，无法阻止我的探查了。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83173] = { id = 83173, Voice = '', Text = '那么，祝你成功！', Speaker = 4376, Option = '', Action = _EmptyTable,}, 
	[83172] = { id = 83172, Voice = '', Text = '看来你已经成功拿到隐身药剂了呢！留给我们的时间不多了，冒险者，我们必须尽快阻止他们神秘的仪式。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83171] = { id = 83171, Voice = '', Text = '注意安全！冒险者！', Speaker = 4376, Option = '', Action = _EmptyTable,}, 
	[83170] = { id = 83170, Voice = '', Text = '好了，冒险者！解决墓地的问题就交给你了！我和安布罗斯先在古城休整一段时间，一有机会，我们就会离开这里。', Speaker = 4376, Option = '', Action = _EmptyTable,}, 
	[83169] = { id = 83169, Voice = '', Text = '现在材料都足够了~那么现在我就要制作隐身药剂了，请稍等片刻。', Speaker = 4376, Option = '', Action = _EmptyTable,}, 
	[83168] = { id = 83168, Voice = '', Text = '虽然蓝灵草随处可见，可你的效率也太惊人了吧！', Speaker = 4376, Option = '', Action = _EmptyTable,}, 
	[83167] = { id = 83167, Voice = '', Text = '太棒了！冒险者，这正是我所需要的！', Speaker = 4376, Option = '', Action = _EmptyTable,}, 
	[83166] = { id = 83166, Voice = '', Text = '要抓紧时间冒险者，祭祀就快开始了！', Speaker = 4376, Option = '', Action = _EmptyTable,}, 
	[83165] = { id = 83165, Voice = '', Text = '恐怕又要麻烦你去一趟古城墓地了…', Speaker = 4376, Option = '{好吧…,2},{我再等等,3}', Action = _EmptyTable,}, 
	[83164] = { id = 83164, Voice = '', Text = '蓝灵草在这古城里倒是随处可见，只是这蝙蝠弓箭手的利齿…', Speaker = 4376, Option = '', Action = _EmptyTable,}, 
	[83163] = { id = 83163, Voice = '', Text = '我需要一些蝙蝠弓箭手的利齿和一小部分蓝灵草…', Speaker = 4376, Option = '', Action = _EmptyTable,}, 
	[83162] = { id = 83162, Voice = '', Text = '那些材料，我自己就随身带着一些，现在我就说说紧缺的材料吧。', Speaker = 4376, Option = '', Action = _EmptyTable,}, 
	[83161] = { id = 83161, Voice = '', Text = '太好了！快说说怎么样才能制作一瓶隐身药水！', Speaker = 4273, Option = '', Emoji = 28, Action = _EmptyTable,}, 
	[83160] = { id = 83160, Voice = '', Text = '很高兴能够帮到你们，这是我的荣幸！', Speaker = 4376, Option = '', Action = _EmptyTable,}, 
	[83159] = { id = 83159, Voice = '', Text = '别紧张…喏，我要再次郑重介绍我的妻子，塔米！虽然她是一位剑士，但是她也是一位小有成就的药剂师！', Speaker = 4375, Option = '', Action = _EmptyTable,}, 
	[83158] = { id = 83158, Voice = '', Text = '啊？你竟然用光了？？？', Speaker = 4273, Option = '', Emoji = 1, Action = _EmptyTable,}, 
	[83157] = { id = 83157, Voice = '', Text = '只是…最后一瓶就在刚刚被我用掉了…', Speaker = 4375, Option = '', Emoji = 15, Action = _EmptyTable,}, 
	[83156] = { id = 83156, Voice = '', Text = '对啊！那种隐身药水…如果不是事情紧急，我真想好好研究一番！', Speaker = 4273, Option = '', Emoji = 11, Action = _EmptyTable,}, 
	[83155] = { id = 83155, Voice = '', Text = '我倒是有个办法能让你们成功潜入…想必你刚刚也看到了我的隐身药水的效果了…如果用它的话……', Speaker = 4375, Option = '', Action = _EmptyTable,}, 
	[83154] = { id = 83154, Voice = '', Text = '如果现在我们能够潜入进墓地，看看那些魔物究竟在干什么就好了……', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83153] = { id = 83153, Voice = '', Text = '根据大法师路西菲尔的著作《魔法的奥秘》书中的理论，阻挡魔力的必然是相同层级的力量，那么我们可以推测，那些诡异的雾气，一定是某种魔法的产物。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83139] = { id = 83139, Voice = '', Text = '戒备森严的墓地！果然，他们是在举行仪式！嘿嘿…没想到你还能活着回来啊~', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83138] = { id = 83138, Voice = '', Text = '敌人越来越多了！得先离开这里！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83134] = { id = 83134, Voice = '', Text = '束手就擒吧！你不可能活着离开！', Speaker = 4377, Option = '', Action = _EmptyTable,}, 
	[83133] = { id = 83133, Voice = '', Text = '入侵者…不能让他打扰王的进食！杀死他！', Speaker = 4377, Option = '', Action = _EmptyTable,}, 
	[83132] = { id = 83132, Voice = '', Text = '…还有谁在暗中窥视…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83131] = { id = 83131, Voice = '', Text = '一股令人不安的气息…谁在前面！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83130] = { id = 83130, Voice = '', Text = '雾气有点浓郁了，看来距离目的地不远了…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83129] = { id = 83129, Voice = '', Text = '嗯？竟然不能传送！这里的雾气果然有古怪！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83128] = { id = 83128, Voice = '', Text = '雾气很稀薄，尝试与莉莎建立连接…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83127] = { id = 83127, Voice = '', Text = '很高兴你能接受我们的请求，但我们现阶段无法给你足够的帮助，一切都拜托你了，冒险者。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83126] = { id = 83126, Voice = '', Text = '看来要进入那片危险的区域了，为了传送阵的顺利建立，冒险者，你愿意帮助我吗？', Speaker = 4273, Option = '{乐意效劳,2},{委婉拒绝,3}', Action = _EmptyTable,}, 
	[83125] = { id = 83125, Voice = '', Text = '我算算了时间，距离下一次他们集合也已经快了…如果要探查，就要尽快！', Speaker = 4375, Option = '', Action = _EmptyTable,}, 
	[83124] = { id = 83124, Voice = '', Text = '所以我们必须得搞清楚，他们究竟在祭祀着什么，深邃的黑暗背后，又隐藏着什么…', Speaker = 4375, Option = '', Action = _EmptyTable,}, 
	[83123] = { id = 83123, Voice = '', Text = '我感觉到他们的力量都在流逝，似乎是在祭祀着什么，每次祭祀过后，那些诡异的雾又会浓郁几分…', Speaker = 4375, Option = '', Action = _EmptyTable,}, 
	[83122] = { id = 83122, Voice = '', Text = '你无法想象，那些不知名的魔物，邪恶的祭司和神官，他们聚集在一起，对着漆黑一片的深处膜拜。', Speaker = 4375, Option = '', Action = _EmptyTable,}, 
	[83121] = { id = 83121, Voice = '', Text = '在手札中的日子里，每隔一段时间，我就能看到外面的景象。', Speaker = 4375, Option = '', Action = _EmptyTable,}, 
	[83120] = { id = 83120, Voice = '', Text = '看来普通的办法确实是行不通了…安布罗斯有什么好办法吗？', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83119] = { id = 83119, Voice = '', Text = '……我传递出去的魔力波动，在抵达某片区域的时候中断了与我的联系，就像是…被吞噬了一样…', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83118] = { id = 83118, Voice = '', Text = '嗯？还有这种诡异的雾气吗？我试着感应一下。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83117] = { id = 83117, Voice = '', Text = '安布罗斯还有其他的要求吗？让他放心，我们一定会尽快送他和他的妻子出去的。', Speaker = 4273, Option = '讲述担忧', Action = _EmptyTable,}, 
	[83116] = { id = 83116, Voice = '', Text = '我也是在那个时候被突然出现的暗神官击败吞噬的…', Speaker = 4375, Option = '明白了', Action = _EmptyTable,}, 
	[83115] = { id = 83115, Voice = '', Text = '我曾经在墓地中使用汤玛斯大主教亲手制作的传送卷轴，我看到卷轴爆发出肉眼可见的魔力，却被墓地诡异的迷雾尽数吞噬。', Speaker = 4375, Option = '', Action = _EmptyTable,}, 
	[83114] = { id = 83114, Voice = '', Text = '无意冒犯…我认为仅仅凭借这本手札是无法定位到墓地的…那片墓地…有种说不上来的诡异…', Speaker = 4375, Option = '', Action = _EmptyTable,}, 
	[83113] = { id = 83113, Voice = '', Text = '刚刚我没听错的话，这位魔法师是想建立一个前往墓地的传送阵？', Speaker = 4375, Option = '是的', Action = _EmptyTable,}, 
	[83111] = { id = 83111, Voice = '', Text = '那么接下来，我就要与墓地建立联系了…', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83110] = { id = 83110, Voice = '', Text = '你送来的这本手札里面的魔力储藏非常多…甚至可以用恐怖来形容！总之足够用来当作传送识别的地标了。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83109] = { id = 83109, Voice = '', Text = '这么说，他们是被这本手札吞噬的冒险者？好吧，我们会负责送他们离开这里。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83108] = { id = 83108, Voice = '', Text = '啊哈…貌似隐身药水失效了…', Speaker = 4375, Option = '', Action = _EmptyTable,}, 
	[83107] = { id = 83107, Voice = '', Text = '他们？', Speaker = 4273, Option = '', Emoji = 1, Action = _EmptyTable,}, 
	[83106] = { id = 83106, Voice = '', Text = '嗯…暗神官的手札么…这么多年黑暗力量的侵蚀也使得这个物品成为了一个魔法物品了呢…勉强能用它确定墓地的位置…', Speaker = 4273, Option = '看看他们', Action = _EmptyTable,}, 
	[83105] = { id = 83105, Voice = '', Text = '冒险者，我感应到你身上一股浓烈的黑暗气息…你带回了什么？', Speaker = 4273, Option = '交出手札', Action = _EmptyTable,}, 
	[83104] = { id = 83104, Voice = '', Text = '嗯？吉芬的魔法师？难道魔法公会已经占领了这里面吗？', Speaker = 4375, Option = '', Action = _EmptyTable,}, 
	[83103] = { id = 83103, Voice = '', Text = '冒险者，慢一点！我们隐身状态不能移动太快的！', Speaker = 4375, Option = '', Action = _EmptyTable,}, 
	[83102] = { id = 83102, Voice = '', Text = '冒险者，我和我的妻子就跟在你身后…不要担心，我们用了隐身药水！', Speaker = 4375, Option = '', Action = _EmptyTable,}, 
	[83101] = { id = 83101, Voice = '', Text = '这里充斥着凶恶的魔物，如果没有人帮助我们的话，我们永远也逃不出这里。', Speaker = 4375, Option = '跟我走吧', Action = _EmptyTable,}, 
	[83100] = { id = 83100, Voice = '', Text = '看到这里的景象，我就知道，恐怕我们来到了传说中废弃已久的首都——克雷斯特汉姆了。', Speaker = 4375, Option = '', Action = _EmptyTable,}, 
	[83099] = { id = 83099, Voice = '', Text = '我与塔米在一次冒险中，踏入了一个古旧的传送阵，在一阵短暂的空间闪现中，我们便出现在这里。', Speaker = 4375, Option = '', Action = _EmptyTable,}, 
	[83098] = { id = 83098, Voice = '', Text = '多谢你冒险者，容我介绍一下自己，我是来自普隆德拉的安布罗斯，这位是我的妻子塔米。', Speaker = 4375, Option = '为什么在这里？', Action = _EmptyTable,}, 
	[83095] = { id = 83095, Voice = '', Text = '打开封面，一阵强有力的诡异吸引力骤然出现…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83094] = { id = 83094, Voice = '', Text = '一本破旧的笔记本，间歇地传出一阵阵微弱的魔力波动…', Speaker = 4373, Option = '{捡起来,2},{不感兴趣,3}', Action = _EmptyTable,}, 
	[83082] = { id = 83082, Voice = '', Text = '即将将你传送到古城下水道，请千万小心，冒险者！', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83061] = { id = 83061, Voice = '', Text = '好吧，我很期待我们的小英雄这一次的表现！', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83060] = { id = 83060, Voice = '', Text = '这么说，下水道的那只迷幻之王是你要必须解决的目标吗？', Speaker = 4273, Option = '为了救庞德', Action = _EmptyTable,}, 
	[83059] = { id = 83059, Voice = '', Text = '好在你身上还有一些魔粉，我现在就把你传送出来，请小心冒险者。', Speaker = 4273, Option = '拜托了', Action = _EmptyTable,}, 
	[83058] = { id = 83058, Voice = '', Text = '这么久还没回来！我就知道出事了，我在你身边感应到了强烈的扭曲力量。', Speaker = 4273, Option = '莉莎？', Action = _EmptyTable,}, 
	[83057] = { id = 83057, Voice = '', Text = '纯净的灵魂为我带来更强大的力量！！', Speaker = 4371, Option = '可恶！', Action = _EmptyTable,}, 
	[83056] = { id = 83056, Voice = '', Text = '你也像之前那个魔法师一样，分给我一点灵魂怎么样啊？呵呵呵呵呵！', Speaker = 4371, Option = '庞德的灵魂！', Action = _EmptyTable,}, 
	[83055] = { id = 83055, Voice = '', Text = '纯净的灵魂，是为我们准备的吗？！', Speaker = 4371, Option = '完蛋了！', Action = _EmptyTable,}, 
	[83054] = { id = 83054, Voice = '', Text = '这里还有更多……好发达的科技，很难想象曾经的克雷斯特汉姆是多么繁荣的景象。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83053] = { id = 83053, Voice = '', Text = '好巨大的抽水机……我从未在普隆德拉的下水道见过这么大的…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83052] = { id = 83052, Voice = '', Text = '它是在引导我吗？等等……这些是什么？', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83051] = { id = 83051, Voice = '', Text = '又消失了？很可疑啊！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83050] = { id = 83050, Voice = '', Text = '迷幻之王？！没想到在这里遇见了！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83049] = { id = 83049, Voice = '', Text = '迷幻之王突然出现在冒险者的视线范围内，稍纵即逝……', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83048] = { id = 83048, Voice = '', Text = '这样似乎就完成了…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[83047] = { id = 83047, Voice = '', Text = '真遗憾，你似乎变得不那么值得依靠了……', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83046] = { id = 83046, Voice = '', Text = '你需要再一次进入古城下水道，但这次就很简单了，只需要把粉末撒在传送信标周围就完成了。', Speaker = 4273, Option = '{立刻就去,2},{我需要再等等,3}', Action = _EmptyTable,}, 
	[83045] = { id = 83045, Voice = '', Text = '我又一次注入了魔法在这些粉末中，虽然这很麻烦，不过只有这样才能让我们在古城的冒险更加安全。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83044] = { id = 83044, Voice = '', Text = '这……有点太细了，不过还算能用吧！', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83043] = { id = 83043, Voice = '', Text = '拿好了冒险者，可别让它们从你的指缝里溜走了。', Speaker = 4272, Option = '', Action = _EmptyTable,}, 
	[83042] = { id = 83042, Voice = '', Text = '小菜一碟！！！！！', Speaker = 4272, Option = '', Action = _EmptyTable,}, 
	[83041] = { id = 83041, Voice = '', Text = '那么，你是想让我用真气将这些金属变成粉末吗？', Speaker = 4272, Option = '是的', Action = _EmptyTable,}, 
	[83040] = { id = 83040, Voice = '', Text = '我已经从那件事中走了出来！', Speaker = 4272, Option = '这样才有精神', Action = _EmptyTable,}, 
	[83039] = { id = 83039, Voice = '', Text = '其实这才是真正的我，很抱歉之前给你那样的感觉。', Speaker = 4272, Option = '', Action = _EmptyTable,}, 
	[83038] = { id = 83038, Voice = '', Text = '那个没礼貌的小丫头！不过我还挺喜欢这个称呼，哈哈哈哈哈！！', Speaker = 4272, Option = '你变开朗了', Action = _EmptyTable,}, 
	[83037] = { id = 83037, Voice = '', Text = '那么你能帮我拜托那位像“超赛”一样的家伙，帮我把这些变成粉末吗？', Speaker = 4273, Option = '没问题', Action = _EmptyTable,}, 
	[83036] = { id = 83036, Voice = '', Text = '那么接下来我需要把这些金属变成粉末……', Speaker = 4273, Option = '需要真气！', Action = _EmptyTable,}, 
	[83035] = { id = 83035, Voice = '', Text = '欧，那么快就回来了！恩，数量正好！', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83034] = { id = 83034, Voice = '', Text = '为我带一些钢钉回来，我将制作一些专用的魔粉来中和传送信标的魔力波动。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83033] = { id = 83033, Voice = '', Text = '由于钢钉长期地安插在魔物身体中，这些东西上已经沾满了它的气息。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83032] = { id = 83032, Voice = '', Text = '根据情报，下水道有一种叫做邪恶使者的人形魔物，这些魔物身上的钢钉是最合适的素材。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83031] = { id = 83031, Voice = '', Text = '我要将传送信标所散发出来的魔力辐射变得和下水道魔物相同。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83030] = { id = 83030, Voice = '', Text = '好脆弱的冒险者！', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83029] = { id = 83029, Voice = '', Text = '这一次我需要彻底解决传送点附近的安全问题。', Speaker = 4273, Option = '{告诉我做法,2},{我很累,3}', Action = _EmptyTable,}, 
	[83028] = { id = 83028, Voice = '', Text = '原来就是这只魔物，魔剑碎片就是源自它的身上，难怪它会对我的传送如此敏感。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83026] = { id = 83026, Voice = '', Text = '那只邪恶的魔物一定会感应到魔力，你现在去下水道金戒指放置地点附近，那只魔物一旦现身请立刻击败它，不然每一个传送过去的冒险者都可能发生危险。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83025] = { id = 83025, Voice = '', Text = '冒险者，我现在将灌注大量法力进入重铸魔剑中。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83024] = { id = 83024, Voice = '', Text = '我的法力被下水道里一股很强大的邪恶力量所注意到了，我想可能是因为刀刃碎片的关系。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[83023] = { id = 83023, Voice = '', Text = '虽然我不想那么快就再次委托你，不过传送的确出现了一些问题。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82989] = { id = 82989, Voice = '', Text = '之后你就可以快捷地通过我的传送进入古城下水道了。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82988] = { id = 82988, Voice = '', Text = '那么，古城下水道的通路我已经为你打开了。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82987] = { id = 82987, Voice = '', Text = '好了瑞克，我们回头再见。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82986] = { id = 82986, Voice = '', Text = '你们完成得非常棒，瑞克，你也像个男子汉了啊。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82981] = { id = 82981, Voice = '', Text = '当你准备好的时候，请立刻来找我。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82980] = { id = 82980, Voice = '', Text = '等你准备好的时候告诉我。', Speaker = 4273, Option = '{现在就出发,2},{让我准备一下,3}', Action = _EmptyTable,}, 
	[82979] = { id = 82979, Voice = '', Text = '在建立连接的同时由于魔力的动荡，你的行踪将会暴露在那些魔物的视线内。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82978] = { id = 82978, Voice = '', Text = '当你放下戒指之后我们将开始建立连接，这可能会花费一些时间。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82977] = { id = 82977, Voice = '', Text = '那么下一步，请你将这枚戒指放到下水道入口，我会派一位助手跟随你。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82976] = { id = 82976, Voice = '', Text = '嗯，看上去很顺利，你做得很好，冒险者。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82975] = { id = 82975, Voice = '', Text = '………嗯，好吧，我现在就开始附魔。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82974] = { id = 82974, Voice = '', Text = '噢，突然送我戒指，你这是要干什么！', Speaker = 4273, Option = '很有质量的东西', Action = _EmptyTable,}, 
	[82973] = { id = 82973, Voice = '', Text = '是的，请在附近的恶魔侍者身上找一件质量很高的东西给我。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82972] = { id = 82972, Voice = '', Text = '这件物品必须是一件质量很高的物品，在我附魔之后，再将它放到下水道的入口，这样我就能建立起两边的连接了。', Speaker = 4273, Option = '质量很高的？', Action = _EmptyTable,}, 
	[82971] = { id = 82971, Voice = '', Text = '那么下一步，我需要一件这里的恶魔身上的物品。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82970] = { id = 82970, Voice = '', Text = '我得恢复一下…', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82969] = { id = 82969, Voice = '', Text = '请给我一点时间，我现在就将刀刃残片与魔剑碎片合并，制作出一把仿制的魔剑。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82968] = { id = 82968, Voice = '', Text = '很棒的冒险者，这原本非常危险，不过你完成得很出色！', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82967] = { id = 82967, Voice = '', Text = '深渊骑士手中的魔化重剑是很好的材料，击败他们，然后拾取一些重剑的残片给我！', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82966] = { id = 82966, Voice = '', Text = '目前这块碎片的本体容量太小，我需要更多的碎片来用魔法暂时打造出一把仿制的魔剑，这样我的法力才能完全附魔在剑上。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82965] = { id = 82965, Voice = '', Text = '这样才能确保我的传送过程会保持安全和舒适。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82964] = { id = 82964, Voice = '', Text = '好，那么第二步，我需要强化这块碎片的注魔量，我得把我的魔法更多的灌注到碎片之中。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82963] = { id = 82963, Voice = '', Text = '你愿意帮助我吗？', Speaker = 4273, Option = '{乐意效劳,2},{委婉拒绝,3}', Action = _EmptyTable,}, 
	[82962] = { id = 82962, Voice = '', Text = '冒险者，找到这个碎片只是第一步，要建立传送通路的话我还需要一些其他的帮助。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82961] = { id = 82961, Voice = '', Text = '这真是太棒了，有了它我就能够感应到下水道的具体方位了。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82960] = { id = 82960, Voice = '', Text = '这片碎片上充满了古城下水道的污秽气息，我想莉莎可能正在寻找这样一件东西。', Speaker = 4266, Option = '', Action = _EmptyTable,}, 
	[82959] = { id = 82959, Voice = '', Text = '至于黑色的战士，我们应该感谢他们的努力，不过同时我也想让那些高高在上的神知道，人类的力量远超他们所想。', Speaker = 4266, Option = '', Action = _EmptyTable,}, 
	[82958] = { id = 82958, Voice = '', Text = '是的，至于是不是哈沃德打造的我也无法得知，我询问他关于这把剑的来历时他总是沉默不语。', Speaker = 4266, Option = '', Action = _EmptyTable,}, 
	[82957] = { id = 82957, Voice = '', Text = '弑神者…我第一次在哈沃德手中见到它的时候就被这把剑中所蕴含的诡异气息所吸引。', Speaker = 4266, Option = '哈沃德？', Action = _EmptyTable,}, 
	[82955] = { id = 82955, Voice = '', Text = '顿时一阵迷雾袭来，周遭的空气似乎都凝结住了…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[82954] = { id = 82954, Voice = '', Text = '一小块像镜子一样的碎片，路过时发出了微弱的奇怪声音。', Speaker = 4365, Option = '{捡起来,2},{置之不理,3}', Action = _EmptyTable,}, 
	[82953] = { id = 82953, Voice = '', Text = '[c][ffff00]请探索这片区域，说不定能找到一些可以作为媒介的物品能让我通过魔力灌注来定位目标[-][/c]。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82952] = { id = 82952, Voice = '', Text = '只是这里的魔法力场非常不稳定，我无法准确的定位到其他几个区域。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82951] = { id = 82951, Voice = '', Text = '冒险者，古城地形复杂，危机四伏，我会想办法为你打开通路，使你能快速地在古城区域内进行传送。', Speaker = 4273, Option = '', Action = _EmptyTable,}, 
	[82785] = { id = 82785, Voice = '', Text = '我们得准备一下，也是时候让这些魔物见识一下我们克雷斯特同盟军的力量了！', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82784] = { id = 82784, Voice = '', Text = '看上去，这次古城行动并不会太过顺利。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82783] = { id = 82783, Voice = '', Text = '如果庞德能够成功被救回，我会让他好好感谢你，谢谢你为他所做的一切，冒险者。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82782] = { id = 82782, Voice = '', Text = '每位深渊骑士都有各自的领地，我想他们在古城的地位不会低。从他们身上找点消息吧。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82781] = { id = 82781, Voice = '', Text = '根据营地周围的魔法师守卫的描述，这里除了恶魔，还有另外一种魔物——深渊骑士。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82780] = { id = 82780, Voice = '', Text = '想要找到黑暗之王，只能先解决迷幻之王，那么现在我们应该去探听迷幻之王的下落。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82779] = { id = 82779, Voice = '', Text = '庞德的灵魂被分成三份了？这可不好办了。如果不尽快夺回庞德的灵魂，就算是我也很难救庞德回来。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82778] = { id = 82778, Voice = '', Text = '很不错的冒险者。看来你的收获很令人满意，欢迎回来。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82766] = { id = 82766, Voice = '', Text = '我是十字刺客！我的目标不应该死在你们同盟军的手上！', Speaker = 4348, Option = '', Action = _EmptyTable,}, 
	[82765] = { id = 82765, Voice = '', Text = '如果你想去救你的朋友，那么就拔出这把拳刃，带着我的灵魂一起走。', Speaker = 4348, Option = '为什么？', Action = _EmptyTable,}, 
	[82764] = { id = 82764, Voice = '', Text = '这种人的灵魂比起普通的灵魂纯净数倍，理所当然的，也是黑暗之王口中的美食盛宴，我想，你的朋友应该就是这种人。', Speaker = 4348, Option = '', Action = _EmptyTable,}, 
	[82763] = { id = 82763, Voice = '', Text = '有一种人，他们天生与魔法的契合度极高，学习魔法的速度很快，无论是什么魔法都一点就通。', Speaker = 4348, Option = '', Action = _EmptyTable,}, 
	[82762] = { id = 82762, Voice = '', Text = '自暴君休密兹新建古城至今数百年，无数冒险者进入这里冒险，而现在古城所剩的灵魂又有多少？', Speaker = 4348, Option = '', Action = _EmptyTable,}, 
	[82761] = { id = 82761, Voice = '', Text = '为什么？这些肮脏的魔物以灵魂为食，不然这里为什么囚禁这么多灵魂？', Speaker = 4348, Option = '', Action = _EmptyTable,}, 
	[82760] = { id = 82760, Voice = '', Text = '你不知道？哼，也罢，就让你长长见识吧。囚禁灵魂，是迷幻之王和黑暗之王一贯的做法。', Speaker = 4348, Option = '为什么？', Action = _EmptyTable,}, 
	[82759] = { id = 82759, Voice = '', Text = '又是灵魂，那些肮脏的魔物就不能换个新花样吗？我竟然刺杀这种目标都会失败。', Speaker = 4348, Option = '你知道？', Action = _EmptyTable,}, 
	[82758] = { id = 82758, Voice = '', Text = '现在的冒险者都是这么不怕死吗？古城也敢随意乱闯！', Speaker = 4348, Option = '讲述经过', Action = _EmptyTable,}, 
	[82757] = { id = 82757, Voice = '', Text = '看上去不是迷幻之王的手下呢…身上没有那些令人恶心的臭味…', Speaker = 4348, Option = '…', Action = _EmptyTable,}, 
	[82754] = { id = 82754, Voice = '', Text = '微微触碰，一股冷意直冲心底…令人感到头皮发麻…', Speaker = 4346, Option = '', Action = _EmptyTable,}, 
	[82753] = { id = 82753, Voice = '', Text = '拳刃通体暗红…也不知到底饮了多少敌人的血…', Speaker = 4346, Option = '', Action = _EmptyTable,}, 
	[82752] = { id = 82752, Voice = '', Text = '一把布满锈迹与豁口的拳刃…拳刃把手都已碎成两段…', Speaker = 4346, Option = '{拿起来看看,2},{没什么好看的,3}', Action = _EmptyTable,}, 
	[82750] = { id = 82750, Voice = '', Text = '离开吧冒险者，你的朋友还在等你！', Speaker = 4345, Option = '好吧…', Action = _EmptyTable,}, 
	[82749] = { id = 82749, Voice = '', Text = '我？呵呵呵…这三百年的寂寞日子，我早已经呆够了，我会找到当初压制我的那位深渊骑士，再战一场！虽死无憾！', Speaker = 4345, Option = '', Action = _EmptyTable,}, 
	[82748] = { id = 82748, Voice = '', Text = '对了，走的时候，把这把龙骨弓也带走吧…总有一天，我这把龙骨弓会找到适合他的主人…', Speaker = 4345, Option = '那你呢？', Action = _EmptyTable,}, 
	[82747] = { id = 82747, Voice = '', Text = '是啊，如果不尽快取回，恐怕那位小魔法师…', Speaker = 4345, Option = '', Action = _EmptyTable,}, 
	[82746] = { id = 82746, Voice = '', Text = '哦…差点忘了，那位小魔法师的灵魂被撕扯成三份，一份黑暗之王自己带走了，还有两份被他的手下迷幻之王带走了。', Speaker = 4345, Option = '三…三份？', Action = _EmptyTable,}, 
	[82745] = { id = 82745, Voice = '', Text = '这我怎么会知道？我只是看到一个被封印的魔法师灵魂被他们带走而已。', Speaker = 4345, Option = '', Action = _EmptyTable,}, 
	[82744] = { id = 82744, Voice = '', Text = '嗯？你是说黑暗之王带来的小魔法师的灵魂？我有见过哦…', Speaker = 4345, Option = '他在哪儿？', Action = _EmptyTable,}, 
	[82743] = { id = 82743, Voice = '', Text = '刚刚感到一股火焰的气息…要知道我被称之为冰霜猎人，最讨厌的就是火焰了，就忍不住试探了一下…', Speaker = 4345, Option = '请问…', Action = _EmptyTable,}, 
	[82742] = { id = 82742, Voice = '', Text = '别瞪着我…开个玩笑嘛，我在这里已经寂寞了数百年，除了与那些古城魔物战斗，我还真找不到人交流。', Speaker = 4345, Option = '', Action = _EmptyTable,}, 
	[82741] = { id = 82741, Voice = '', Text = '身手还不错嘛，我叫珀尔，你可以称呼我为冰霜猎人珀尔！这是我三百年前的称号！', Speaker = 4345, Option = '这…', Action = _EmptyTable,}, 
	[82737] = { id = 82737, Voice = '', Text = '尽管沾满了泥土，但弓还是散发着一股令人心悸的气息。', Speaker = 4343, Option = '', Action = _EmptyTable,}, 
	[82736] = { id = 82736, Voice = '', Text = '看样子像是一把龙骨制成的弓…', Speaker = 4343, Option = '', Action = _EmptyTable,}, 
	[82735] = { id = 82735, Voice = '', Text = '一把弓弦闪烁着寒光的弓，硬生生地插在地上…', Speaker = 4343, Option = '{拿起来看看,2},{没什么好看的,3}', Action = _EmptyTable,}, 
	[82734] = { id = 82734, Voice = '', Text = '冒险者，离开这里吧…', Speaker = 4340, Option = '', Action = _EmptyTable,}, 
	[82733] = { id = 82733, Voice = '', Text = '我要回归神的怀抱了…长久的封印逼得我几欲发疯，杰理又变成了这个样子…我已经无法再坚持下去了…', Speaker = 4340, Option = '', Action = _EmptyTable,}, 
	[82732] = { id = 82732, Voice = '', Text = '好了，冒险者，我能告诉你的就是这么多。这里众多的灵魂总会有人知道你要的消息…带着我的宝石离开吧。', Speaker = 4340, Option = '那你…', Action = _EmptyTable,}, 
	[82731] = { id = 82731, Voice = '', Text = '也许迷幻之王就是黑暗之王的一部分也说不定…我想你终究是要面对黑暗之王的吧…与迷幻之王的较量是不可避免了…', Speaker = 4340, Option = '', Action = _EmptyTable,}, 
	[82730] = { id = 82730, Voice = '', Text = '想引出黑暗之王的方法只有一个，那就是击败迷幻之王。迷幻之王作为最早出现在黑暗之王身边的魔物，他们之间也许存在着某种联系。', Speaker = 4340, Option = '', Action = _EmptyTable,}, 
	[82729] = { id = 82729, Voice = '', Text = '冒险者，关于你朋友的下落，很遗憾，我不知道他的消息。不过我倒是可以告诉你关于黑暗之王的消息。', Speaker = 4340, Option = '', Action = _EmptyTable,}, 
	[82727] = { id = 82727, Voice = '', Text = '可恶！没想到你还有帮手，迟早我会让你投入黑暗的怀抱！', Speaker = 4342, Option = '', Action = _EmptyTable,}, 
	[82715] = { id = 82715, Voice = '', Text = '裂缝之中似乎有一股神圣的力量正在向外涌动…你不受控制地被向着某个地方牵引…', Speaker = 4338, Option = '', Action = _EmptyTable,}, 
	[82714] = { id = 82714, Voice = '', Text = '宝石似乎损坏了…表面上有着一丝裂痕…', Speaker = 4338, Option = '', Action = _EmptyTable,}, 
	[82713] = { id = 82713, Voice = '', Text = '破旧的权杖上似乎有什么在闪着荧光…', Speaker = 4338, Option = '{查看宝石,2},{没什么好看的,3}', Action = _EmptyTable,}, 
	[82712] = { id = 82712, Voice = '', Text = '我要回归神的怀抱了…冒险者，黑暗之王就交给你们了…', Speaker = 4337, Option = '', Action = _EmptyTable,}, 
	[82711] = { id = 82711, Voice = '', Text = '至于怎么召唤出黑暗之王…也许其他封印的灵魂会给你答案，他们有些人封印时间更久…会知道的更多…', Speaker = 4337, Option = '', Action = _EmptyTable,}, 
	[82710] = { id = 82710, Voice = '', Text = '没错，黑暗之王就在古城墓地，你那位魔法师朋友的灵魂，肯定在黑暗之王手中！', Speaker = 4337, Option = '', Action = _EmptyTable,}, 
	[82709] = { id = 82709, Voice = '', Text = '这百年来，我无时无刻不在想着脱离封印，但是很遗憾，我的力量不足以通过那危机四伏的古城墓地…', Speaker = 4337, Option = '', Action = _EmptyTable,}, 
	[82708] = { id = 82708, Voice = '', Text = '黑暗之王降临古城已经数百年之久，我就是百年前探索古城被封印的神圣骑士。', Speaker = 4337, Option = '', Action = _EmptyTable,}, 
	[82707] = { id = 82707, Voice = '', Text = '你想知道黑暗之王的消息是吗？', Speaker = 4337, Option = '是的', Action = _EmptyTable,}, 
	[82706] = { id = 82706, Voice = '', Text = '她已经消散了吗…没想到当年探索的小队也只剩下我一个人了，也许我也该离开，回归神的怀抱了…', Speaker = 4337, Option = '', Action = _EmptyTable,}, 
	[82705] = { id = 82705, Voice = '', Text = '当我圣洁的灵魂遭到黑暗压制的时候，我看到了你的英勇。冒险者，告诉我，为什么火焰纹章会出现在你身上。', Speaker = 4337, Option = '讲述经过', Action = _EmptyTable,}, 
	[82700] = { id = 82700, Voice = '', Text = '一股巨大的吸引力从断裂的圣剑中喷涌而出…似乎要把你的身体包裹进去…', Speaker = 4335, Option = '', Action = _EmptyTable,}, 
	[82699] = { id = 82699, Voice = '', Text = '随着触碰，银白色的光芒越来越亮…', Speaker = 4335, Option = '', Action = _EmptyTable,}, 
	[82698] = { id = 82698, Voice = '', Text = '这好像是一把断裂的剑，在地上闪烁着银白色的光芒…', Speaker = 4335, Option = '{仔细地端详,2},{没什么好看的,3}', Action = _EmptyTable,}, 
	[82610] = { id = 82610, Voice = '', Text = '[c][ffff00]那么冒险者，你可以出发了，为了安全起见，我建议带上你的朋友一起探索[-][/c]！', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82609] = { id = 82609, Voice = '', Text = '在魔导师炽魂的火焰纹章中我感觉到了魔力的波动，也许这纹章会给你一些帮助。', Speaker = 4271, Option = '我明白了', Action = _EmptyTable,}, 
	[82608] = { id = 82608, Voice = '', Text = '[c][ffff00]古城当中有着许许多多的物品，不要小看任何一个物品，他们很可能会带给我们新的线索[-][/c]。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82607] = { id = 82607, Voice = '', Text = '思来想去，冒险者，只有你才能胜任这个任务。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82606] = { id = 82606, Voice = '', Text = '古城对于我们来说太过神秘，我们不能大规模进驻古城，否则会遇到难以想象的困难。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82605] = { id = 82605, Voice = '', Text = '我想，这些灵魂应该会知道更多的消息。如果我们能够从他们身上得到这些消息，那么同盟军的行动会轻松很多。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82604] = { id = 82604, Voice = '', Text = '那位炽魂说过，这古城存在着许许多多的封印点。有些灵魂在这里封印了数百年之久。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82584] = { id = 82584, Voice = '', Text = '那位先贤的灵魂已经消散了吗……我一定会小心谨慎的。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82583] = { id = 82583, Voice = '', Text = '我快要消散了…告诉雷藏，在这片古城之中一定要小心谨慎，这片土地，远远没有想象的那么简单！', Speaker = 4330, Option = '', Action = _EmptyTable,}, 
	[82582] = { id = 82582, Voice = '', Text = '虽然我不知道那个小魔法师的消息，但是古城众多封印的灵魂肯定有人知道。我的火焰纹章能够帮助你找到其中真正强大的灵魂。', Speaker = 4330, Option = '那你怎么办？', Action = _EmptyTable,}, 
	[82581] = { id = 82581, Voice = '', Text = '原谅我的私心，冒险者，我绝不容许吉芬的支柱陷入险地。你手中的火焰纹章，就送给你作为礼物。', Speaker = 4330, Option = '', Action = _EmptyTable,}, 
	[82580] = { id = 82580, Voice = '', Text = '如果惹出迷幻之王甚至是黑暗之王，就算是雷藏，恐怕也无法轻易脱身。', Speaker = 4330, Option = '', Action = _EmptyTable,}, 
	[82579] = { id = 82579, Voice = '', Text = '是，迷幻之王，这古城众多灵魂都是由迷幻之王亲手封印，我知道雷藏很强，但是在古城，这里的环境对他极其不利。', Speaker = 4330, Option = '', Action = _EmptyTable,}, 
	[82578] = { id = 82578, Voice = '', Text = '清醒？啊…随着时间的流逝，我的力量渐渐被这死气萦绕的古城掠夺，确实没有足够的力量对抗迷幻之王了…', Speaker = 4330, Option = '迷幻之王？', Action = _EmptyTable,}, 
	[82577] = { id = 82577, Voice = '', Text = '雷藏那个小家伙说的就是你吧，冒险者，果然是实力不凡…', Speaker = 4330, Option = '您清醒了？', Action = _EmptyTable,}, 
	[82573] = { id = 82573, Voice = '', Text = '随着触碰，碎片发出异常强烈的红色光芒…你好像被卷入了一个奇妙的空间…', Speaker = 4329, Option = '', Action = _EmptyTable,}, 
	[82572] = { id = 82572, Voice = '', Text = '这圆形碎片究竟是什么…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[82571] = { id = 82571, Voice = '', Text = '这块显得略微有些残旧的碎片发出细微的声响，一丝不寻常的波动出现了…', Speaker = 4329, Option = '', Action = _EmptyTable,}, 
	[82570] = { id = 82570, Voice = '', Text = '在你的仔细观察下，这个奇怪的东西在不断散发着闪烁的光芒…', Speaker = 4329, Option = '{看看是什么,2},{不感兴趣,3}', Action = _EmptyTable,}, 
	[82569] = { id = 82569, Voice = '', Text = '前方好像有着什么东西存在……', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[82568] = { id = 82568, Voice = '', Text = '现在看来你更适合从她口中取得一些有用的情报。去和她谈谈吧！至少得找些有用的线索。', Speaker = 4271, Option = '好吧', Action = _EmptyTable,}, 
	[82567] = { id = 82567, Voice = '', Text = '她藏身在这古城的南部，在一个小小的火焰纹章之中，周围还有不少恶魔仆从。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82566] = { id = 82566, Voice = '', Text = '她似乎也有不得已的苦衷，出于对先贤的尊敬，我并没有追问。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82565] = { id = 82565, Voice = '', Text = '但无论我怎么问，她都不肯告诉我更多。如果我强行动手，肯定会吸引来大批的魔物，而她的灵魂也会因此而消散。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82564] = { id = 82564, Voice = '', Text = '我曾经与她有过一番交流，尽管她忘却了大部分事情，但她还是记得自己成为残魂之后的事情。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82563] = { id = 82563, Voice = '', Text = '在你来之前我已经做过一番调查，古城似乎封印着无数残缺的灵魂，其中就有我们吉芬的先贤。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82562] = { id = 82562, Voice = '', Text = '我们在阻止黑暗之王的同时，还得把庞德的灵魂找回来。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82561] = { id = 82561, Voice = '', Text = '黑暗之王，真是一个古老而又令人厌恶的名字，我的学生庞德的灵魂也被他带走了，至今毫无消息。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82560] = { id = 82560, Voice = '', Text = '我们的本意是想阻止女神之手的阴谋，但是现在，事情往不可控的方向发展了。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82559] = { id = 82559, Voice = '', Text = '看看这血液，嗯…这其中所蕴含的魔力真是丰富，这些许血液足够我们使用一段时间了。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82558] = { id = 82558, Voice = '', Text = '那么，愿你平安归来，冒险者。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82557] = { id = 82557, Voice = '', Text = '如果可以，还是把它们单独引诱出来吧。[c][ffff00]要知道有时候你面对的，可不仅仅是一个恶魔女仆那么简单[-][/c]。', Speaker = 4271, Option = '我会小心的', Action = _EmptyTable,}, 
	[82556] = { id = 82556, Voice = '', Text = '不过我得告诫你，那些恶魔女仆可不是那么好对付，它们远比外面的魔物更加强大！', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82555] = { id = 82555, Voice = '', Text = '你知道吗冒险者，我最欣赏你的就是这一点！', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82554] = { id = 82554, Voice = '', Text = '我们的魔法师正忙于营地建设，无暇顾及，所以冒险者，你能为我们跑一趟吗？', Speaker = 4271, Option = '{义不容辞,2},{我很忙,3}', Action = _EmptyTable,}, 
	[82553] = { id = 82553, Voice = '', Text = '经过我的探查，古城中有着一种魔物，名为恶魔女仆，它们的血液含有丰富的魔力，是维持魔法阵的不二之选。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82552] = { id = 82552, Voice = '', Text = '在进入这里不久，我们的魔法物资就严重不足，主要是用于维持魔法阵的日常消耗。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82551] = { id = 82551, Voice = '', Text = '我在这片营地周围布下了魔法阵，隔绝了古城死气的侵蚀，如果不是这样，恐怕即便是我们训练有素的魔法师，也支持不了多久。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82550] = { id = 82550, Voice = '', Text = '但是令人感到遗憾的是，以往辉煌的克雷斯特汉姆竟然变成了充满瘴气与恶魔的绝地，这真是…', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82549] = { id = 82549, Voice = '', Text = '真是宏伟的古城，如果不是来到这里，我也不会相信，古书中记载的施密特王竟然有这么大手笔与魄力！', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82548] = { id = 82548, Voice = '', Text = '古城的瘴气真是令我厌恶，你身上的气息令我尤为怀念，欢迎你的到来，冒险者。', Speaker = 4271, Option = '', Action = _EmptyTable,}, 
	[82547] = { id = 82547, Voice = '', Text = '去寻找雷藏吧，我想他见到你一定很开心。', Speaker = 2657, Option = '', Action = _EmptyTable,}, 
	[82546] = { id = 82546, Voice = '', Text = '雷藏率领他们的魔法师已经进入古城，并且建立了营地。', Speaker = 2657, Option = '', Action = _EmptyTable,}, 
	[82545] = { id = 82545, Voice = '', Text = '好在我们现在还有吉芬方面的支援，他们能够凭借精湛的魔法深入其中。', Speaker = 2657, Option = '', Action = _EmptyTable,}, 
	[82544] = { id = 82544, Voice = '', Text = '现在普通的士兵们只能在城外驻守，那些黑衣人说的并不是没有道理…', Speaker = 2657, Option = '', Action = _EmptyTable,}, 
	[82543] = { id = 82543, Voice = '', Text = '我们曾经试图进入古城，但是如你所见，我们伤亡惨重！', Speaker = 2657, Option = '', Action = _EmptyTable,}, 
	[82542] = { id = 82542, Voice = '', Text = '他们的死因？哼！还不是因为古城里那些令人厌恶的死气，以及无处不在的恶魔！', Speaker = 2657, Option = '', Action = _EmptyTable,}, 
	[82541] = { id = 82541, Voice = '', Text = '我的士兵们…我视他们为子，毫无疑问，他们是伟大的！', Speaker = 2657, Option = '他们的死因', Action = _EmptyTable,}, 
	[82540] = { id = 82540, Voice = '', Text = '抱歉冒险者，刚刚我的情绪不佳…', Speaker = 2657, Option = '没关系，将军', Action = _EmptyTable,}, 
	[82539] = { id = 82539, Voice = '', Text = '现在，我要送我逝去的士兵最后一程。', Speaker = 4324, Option = '请节哀', Action = _EmptyTable,}, 
	[82538] = { id = 82538, Voice = '', Text = '很不巧，冒险者，现在我不想聊天，可以的话，之后你来营地找我。', Speaker = 4324, Option = '', Action = _EmptyTable,}, 
	[82533] = { id = 82533, Voice = '', Text = '遵命！将军！', Speaker = 4326, Option = '', Action = _EmptyTable,}, 
	[82532] = { id = 82532, Voice = '', Text = '很好！那么，艾洛娜！为我们逝去的士兵们送去祈祷，愿他们的灵魂安息！', Speaker = 4324, Option = '', Action = _EmptyTable,}, 
	[82531] = { id = 82531, Voice = '', Text = '不能！', Speaker = 4327, Option = '', Action = _EmptyTable,}, 
	[82530] = { id = 82530, Voice = '', Text = '他们！是最英勇的士兵！他们！是王国坚实的厚盾！如今他们逝去了，告诉我，能让他们的鲜血白流吗？！', Speaker = 4324, Option = '', Action = _EmptyTable,}, 
	[82529] = { id = 82529, Voice = '', Text = '我们的敌人，就在那座残破的古城中！而我们，绝对不能在这里停下脚步！', Speaker = 4324, Option = '', Action = _EmptyTable,}, 
	[82528] = { id = 82528, Voice = '', Text = '现在女神之手已经退却，而我们又有了新的敌人！一旦他们离开古城，那么王国的安全就会受到威胁！', Speaker = 4324, Option = '', Action = _EmptyTable,}, 
	[82527] = { id = 82527, Voice = '', Text = '为了王国人民的和谐安稳，我们必须站出来，保护他们，杜绝一切危险！', Speaker = 4324, Option = '', Action = _EmptyTable,}, 
	[82526] = { id = 82526, Voice = '', Text = '他们将会看到，我们克雷斯特同盟军为了对抗扰乱王国和平的女神之手，所流下的泪水和热血！', Speaker = 4324, Option = '', Action = _EmptyTable,}, 
	[82525] = { id = 82525, Voice = '', Text = '想想看！普隆德拉的人们正在注视着这里，王国的人们正在注视着这里！', Speaker = 4324, Option = '', Action = _EmptyTable,}, 
	[82524] = { id = 82524, Voice = '', Text = '唉…议会的那群老顽固不知道什么时候才会答应支援…总之，冒险者，祝你平安。', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[82523] = { id = 82523, Voice = '', Text = '有你前去，我会放心很多，那就交给你了，冒险者。', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[82522] = { id = 82522, Voice = '', Text = '放心，王国不会强迫你们前去的，但是愿意前去的人，王国也不会亏待他们！', Speaker = 1022, Option = '{似乎很有趣,2},{我没时间,3}', Action = _EmptyTable,}, 
	[82521] = { id = 82521, Voice = '', Text = '但是在议会通过之前，我只能请冒险者们前往古城支援了。冒险者，你愿意去克雷斯特汉姆支援同盟军吗？', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[82520] = { id = 82520, Voice = '', Text = '在这种情况下，克雷斯特同盟军的安危实在是令人担忧，我已经向王国议会提出支援申请。', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[82519] = { id = 82519, Voice = '', Text = '关于施密特王朝，就连我们的皇家图书馆都难以找到资料。经历过一次劫难的古城必定伴随着未知的危险。', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[82518] = { id = 82518, Voice = '', Text = '现在克雷斯特同盟军已经成功驻扎进古城了，但是克雷斯特汉姆毕竟是施密特王朝的国都。', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
	[82517] = { id = 82517, Voice = '', Text = '之前我还在担心，以你的实力恐怕无法帮助到我们，没想到你现在就给了我一个大大的惊喜！', Speaker = 1022, Option = '', Action = _EmptyTable,}, 
