	[59967] = { id = 59967, Voice = '', Text = '我们公司正在研究火焰精灵，精灵的粉尘对我司来说是非常宝贵的资源。', Speaker = 5718, Option = '', Action = _EmptyTable,}, 
	[59966] = { id = 59966, Voice = '', Text = '这种昂贵的科技需要收取高昂的报酬。', Speaker = 5718, Option = '什么报酬', Action = _EmptyTable,}, 
	[59965] = { id = 59965, Voice = '', Text = '可我们也不会免费提供这项技术，你应该懂。', Speaker = 5718, Option = '', Action = _EmptyTable,}, 
	[59964] = { id = 59964, Voice = '', Text = '对，没有错，我们雷根贝勒公司确实有这项科技。', Speaker = 5718, Option = '太好了', Action = _EmptyTable,}, 
	[59963] = { id = 59963, Voice = '', Text = '这样吧，我也是听拿普多说的，你们还是去问问他！', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59962] = { id = 59962, Voice = '', Text = '包括这簇毛发的主人之前的记忆~', Speaker = 5702, Option = '这么厉害！？', Action = _EmptyTable,}, 
	[59961] = { id = 59961, Voice = '', Text = '是这样的，如果有这怪物的毛发，应该可以分析出很多资讯。', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59960] = { id = 59960, Voice = '', Text = '我听雷根贝勒的研究员拿普多说，他们最新的研究可以从物品中提取主人的信息！', Speaker = 5702, Option = '哦？', Action = _EmptyTable,}, 
	[59959] = { id = 59959, Voice = '', Text = '嗯，可以这么说呀~', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59958] = { id = 59958, Voice = '', Text = '呼，还以为是什么世纪难题。', Speaker = 5702, Option = '你有办法？', Emoji = 17, Action = _EmptyTable,}, 
	[59957] = { id = 59957, Voice = '', Text = '发生什么事，怎么神色都那样凝重？', Speaker = 5702, Option = '回答', Action = _EmptyTable,}, 
	[59956] = { id = 59956, Voice = '', Text = '塞壬~怎么愁眉苦脸的？', Speaker = 5702, Option = '', Emoji = 25, Action = _EmptyTable,}, 
	[59955] = { id = 59955, Voice = '', Text = '如果是这样的话…', Speaker = 5713, Option = '', Action = _EmptyTable,}, 
	[59954] = { id = 59954, Voice = '', Text = '嗯…', Speaker = 5713, Option = '', Emoji = 15, Action = _EmptyTable,}, 
	[59952] = { id = 59952, Voice = '', Text = '带回去给塞壬看看，说不定能发现什么。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[59950] = { id = 59950, Voice = '', Text = '地上有些颜色泛着淡淡绿色的血液，看着像是塞壬所说的怪物留下的。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[59949] = { id = 59949, Voice = '', Text = '凌乱的打斗痕迹，空气中还弥漫着魔物淡淡的血腥味。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[59948] = { id = 59948, Voice = '', Text = '一定会有些线索的，但愿有!', Speaker = 5713, Option = '', Action = _EmptyTable,}, 
	[59947] = { id = 59947, Voice = '', Text = '这样好吗，你去刚才我们战斗的地方搜查一下。', Speaker = 5713, Option = '好', Action = _EmptyTable,}, 
	[59946] = { id = 59946, Voice = '', Text = '它一定没有这么简单，它背后一定藏着许多秘密！', Speaker = 5713, Option = '我同意', Action = _EmptyTable,}, 
	[59945] = { id = 59945, Voice = '', Text = '不知道，冒险者，我真的不知道。', Speaker = 5713, Option = '', Action = _EmptyTable,}, 
	[59944] = { id = 59944, Voice = '', Text = '我说不出来，我有种奇怪的预感。', Speaker = 5713, Option = '什么', Emoji = 7, Action = _EmptyTable,}, 
	[59943] = { id = 59943, Voice = '', Text = '看来你和我的见解不太一样…', Speaker = 5713, Option = '', Action = _EmptyTable,}, 
	[59942] = { id = 59942, Voice = '', Text = '那么它为什么在攻击时不反击，反而带着自己的魔物大军撤退了呢？', Speaker = 5713, Option = '{它饿了,10},{有隐情,8},{它困了,10}', Action = _EmptyTable,}, 
	[59941] = { id = 59941, Voice = '', Text = '你觉得它是为什么进攻朱诺？', Speaker = 5713, Option = '{消灭曙光队,10},{杀害大总统,10},{破坏水晶,5}', Action = _EmptyTable,}, 
	[59940] = { id = 59940, Voice = '', Text = '出现在朱诺城门口的怪物实在是让我没了头绪！', Speaker = 5713, Option = '哦？', Action = _EmptyTable,}, 
	[59939] = { id = 59939, Voice = '', Text = '全员撤退！', Speaker = 5713, Option = '', Action = _EmptyTable,}, 
	[59938] = { id = 59938, Voice = '', Text = '回城里见我，我有事要分配给你做！', Speaker = 5713, Option = '', Action = _EmptyTable,}, 
	[59937] = { id = 59937, Voice = '', Text = '虽然不知道它的目的到底是什么，但这值得调查一下！', Speaker = 5713, Option = '没错', Action = _EmptyTable,}, 
	[59936] = { id = 59936, Voice = '', Text = '我攻击它两次，它都没有反击。', Speaker = 5713, Option = '', Action = _EmptyTable,}, 
	[59935] = { id = 59935, Voice = '', Text = '不过这头丑陋的魔物看着有些奇怪。', Speaker = 5713, Option = '是啊', Action = _EmptyTable,}, 
	[59916] = { id = 59916, Voice = '', Text = '击退来犯的魔物，保护朱诺的城民。', Speaker = 5716, Option = '', Action = _EmptyTable,}, 
	[59915] = { id = 59915, Voice = '', Text = '我没关系，你快去帮助塞壬。', Speaker = 5716, Option = '', Action = _EmptyTable,}, 
	[59914] = { id = 59914, Voice = '', Text = '（对着你嘱咐）带大总统回到总统府。', Speaker = 5713, Option = '', Action = _EmptyTable,}, 
	[59913] = { id = 59913, Voice = '', Text = '（沉着）我去阻止魔物！', Speaker = 5713, Option = '', Action = _EmptyTable,}, 
	[59912] = { id = 59912, Voice = '', Text = '总统大人！有魔物袭城！请快回到总统府。', Speaker = 5720, Option = '', Action = _EmptyTable,}, 
	[59910] = { id = 59910, Voice = '', Text = '（用手指着方向）在那边，将钥匙放入海姆达之心里。', Speaker = 5718, Option = '', Action = _EmptyTable,}, 
	[59909] = { id = 59909, Voice = '', Text = '使用钥匙就能够开启海姆达水晶，保护朱诺不受到魔物的袭击了。', Speaker = 5718, Option = '', Action = _EmptyTable,}, 
	[59908] = { id = 59908, Voice = '', Text = '（拿普多松开自己的手）……为了朱诺。', Speaker = 5718, Option = '', Action = _EmptyTable,}, 
	[59907] = { id = 59907, Voice = '', Text = '钥匙，是这个……', Speaker = 5718, Option = '松手啊', Action = _EmptyTable,}, 
	[59720] = { id = 59720, Voice = '', Text = '魔物索斯挣扎着，突破曙光队的阵型逃走了。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[59582] = { id = 59582, Voice = '', Text = '情况刻不容缓，希望你时刻准备！', Speaker = 5713, Option = '', Action = _EmptyTable,}, 
	[59581] = { id = 59581, Voice = '', Text = '你准备好迎击了嘛！？', Speaker = 5713, Option = '{准备好了,3},{还没有,4}', Action = _EmptyTable,}, 
	[59580] = { id = 59580, Voice = '', Text = '魔物正在源源不断的从南门涌入！', Speaker = 5713, Option = '', Action = _EmptyTable,}, 
	[59579] = { id = 59579, Voice = '', Text = '你现在就和我一起去朱诺南门！', Speaker = 5713, Option = '', Action = _EmptyTable,}, 
	[59578] = { id = 59578, Voice = '', Text = '你做的很好，但现在情况紧急！', Speaker = 5713, Option = '', Action = _EmptyTable,}, 
	[59577] = { id = 59577, Voice = '', Text = '大总统已经回到总统府了吗？', Speaker = 5713, Option = '是的', Action = _EmptyTable,}, 
	[59576] = { id = 59576, Voice = '', Text = '情况还没有我们想象的乐观，快去找他吧。', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[59575] = { id = 59575, Voice = '', Text = '塞壬，他们应该在朱诺南门附近抵抗闯入的魔物。', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[59574] = { id = 59574, Voice = '', Text = '看来这里已经安全了。', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[59573] = { id = 59573, Voice = '', Text = '清理了总统府附近的魔物，快去查看大总统是否安全。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[59572] = { id = 59572, Voice = '', Text = '清理了广场旁街道上的魔物，快去总统府查看大总统的安危。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[59571] = { id = 59571, Voice = '', Text = '异国人，请你帮我们清除前方的魔物。', Speaker = 5720, Option = '', Action = _EmptyTable,}, 
	[59570] = { id = 59570, Voice = '', Text = '总统大人，快跟我走吧。', Speaker = 5720, Option = '', Action = _EmptyTable,}, 
	[59569] = { id = 59569, Voice = '', Text = '年轻人，快去看看附近的城民！', Speaker = 5716, Option = '', Action = _EmptyTable,}, 
	[59568] = { id = 59568, Voice = '', Text = '没想到，朱诺已经这么不安全了。', Speaker = 5716, Option = '', Action = _EmptyTable,}, 
	[59567] = { id = 59567, Voice = '', Text = '有一群魔物突破了守卫，企图攻击大总统。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[59566] = { id = 59566, Voice = '', Text = '请雷根贝勒的研究员将“钥匙”给你吧。', Speaker = 5716, Option = '', Action = _EmptyTable,}, 
	[59565] = { id = 59565, Voice = '', Text = '做得很好。', Speaker = 5716, Option = '', Action = _EmptyTable,}, 
	[59564] = { id = 59564, Voice = '', Text = '已经引导所有的小精灵在正确的位置上。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[59562] = { id = 59562, Voice = '', Text = '先将精灵蛋中的精灵释放，引导它们至海姆达水晶四个方位。', Speaker = 5716, Option = '', Action = _EmptyTable,}, 
	[59561] = { id = 59561, Voice = '', Text = '那么开始吧！', Speaker = 5716, Option = '', Action = _EmptyTable,}, 
	[59560] = { id = 59560, Voice = '', Text = '小精灵会帮助我们点亮元素水晶。', Speaker = 5713, Option = '', Action = _EmptyTable,}, 
	[59559] = { id = 59559, Voice = '', Text = '总统大人，贤者学院支持您的决定。', Speaker = 5713, Option = '', Action = _EmptyTable,}, 
	[59558] = { id = 59558, Voice = '', Text = '雷根贝勒公司愿意全力支持。', Speaker = 5718, Option = '', Action = _EmptyTable,}, 
	[59557] = { id = 59557, Voice = '', Text = '宝勒世伏大人说，如果大总统执意要开启海姆达水晶。', Speaker = 5718, Option = '', Action = _EmptyTable,}, 
	[59556] = { id = 59556, Voice = '', Text = '（低声）我希望，永远没有激活海姆达水晶的机会……', Speaker = 5716, Option = '', Action = _EmptyTable,}, 
	[59555] = { id = 59555, Voice = '', Text = '它就能向传说中的海姆达神一样，保护我们伟大的朱诺！', Speaker = 5716, Option = '好！', Action = _EmptyTable,}, 
	[59554] = { id = 59554, Voice = '', Text = '当危险逼近朱诺之时，点亮四周每颗元素水晶，就能激活整个海姆达水晶。', Speaker = 5716, Option = '', Action = _EmptyTable,}, 
	[59553] = { id = 59553, Voice = '', Text = '朱诺升空以后，雷根贝勒公司联合贤者学院建造了这颗水晶。', Speaker = 5716, Option = '', Action = _EmptyTable,}, 
	[59552] = { id = 59552, Voice = '', Text = '瞭望神域之外世界树上一切的生灵，聆听万物发出的声音。', Speaker = 5716, Option = '', Action = _EmptyTable,}, 
	[59551] = { id = 59551, Voice = '', Text = '海姆达是神域阿斯加德的守护者，他手持奥拉尔号角，终日站在比弗罗斯特彩虹桥前。', Speaker = 5716, Option = '', Action = _EmptyTable,}, 
	[59550] = { id = 59550, Voice = '', Text = '很壮观对吗？', Speaker = 5716, Option = '是的', Action = _EmptyTable,}, 
	[59549] = { id = 59549, Voice = '', Text = '你看，这就是朱诺的海姆达水晶。', Speaker = 5716, Option = '', Action = _EmptyTable,}, 
	[59548] = { id = 59548, Voice = '', Text = '（很苦恼）我会努力说服他的，为了朱诺。', Speaker = 5718, Option = '', Action = _EmptyTable,}, 
	[59547] = { id = 59547, Voice = '', Text = '我会与宝勒世伏大人商议的。', Speaker = 5718, Option = '？', Action = _EmptyTable,}, 
	[59546] = { id = 59546, Voice = '', Text = '不、不，我、我什么都没说。', Speaker = 5718, Option = '', Action = _EmptyTable,}, 
	[59545] = { id = 59545, Voice = '', Text = '他有自己的方法，一定能拯救所有的人类……', Speaker = 5718, Option = '你在说什么', Action = _EmptyTable,}, 
	[59544] = { id = 59544, Voice = '', Text = '（看上去很迷茫）我知道，魔物袭击很危险。只是宝勒世伏大人他……', Speaker = 5718, Option = '', Action = _EmptyTable,}, 
	[59543] = { id = 59543, Voice = '', Text = '我不知道，我也不能决定。', Speaker = 5718, Option = '', Action = _EmptyTable,}, 
	[59542] = { id = 59542, Voice = '', Text = '开启海姆达水晶？大总统的命令吗？', Speaker = 5718, Option = '', Action = _EmptyTable,}, 
	[59540] = { id = 59540, Voice = '', Text = '我不强求，只希望你好好考虑一下。', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[59539] = { id = 59539, Voice = '', Text = '那么，快去吧……为了朱诺……', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[59538] = { id = 59538, Voice = '', Text = '异国人，虽然你不是我朱诺的城民，但是你愿意与雷根贝勒公司接洽，帮我传达我的命令吗？', Speaker = 5944, Option = '{愿意,3},{不愿意,4}', Action = _EmptyTable,}, 
	[59537] = { id = 59537, Voice = '', Text = '（无奈）请雷根贝勒公司及宝勒世伏先生配合……', Speaker = 5944, Option = '……', Action = _EmptyTable,}, 
	[59536] = { id = 59536, Voice = '', Text = '为了朱诺城民的安全。', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[59535] = { id = 59535, Voice = '', Text = '（闭眼皱眉）这是我的决定！开启海姆达水晶！', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[59534] = { id = 59534, Voice = '', Text = '您还没有问过宝勒世伏大人。', Speaker = 5717, Option = '', Action = _EmptyTable,}, 
	[59533] = { id = 59533, Voice = '', Text = '大总统！开启海姆达水晶需要雷根贝勒公司配合！', Speaker = 5717, Option = '', Action = _EmptyTable,}, 
	[59532] = { id = 59532, Voice = '', Text = '开启海姆达水晶！', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[59531] = { id = 59531, Voice = '', Text = '（调整表情后）如果朱诺周边的魔物发生暴动，后果不堪设想。', Speaker = 5944, Option = '怎么办？', Action = _EmptyTable,}, 
	[59530] = { id = 59530, Voice = '', Text = '（四处张望）嘘……我相信你。', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[59529] = { id = 59529, Voice = '', Text = '（小声)他们的行动，谁都不知道详情。', Speaker = 5944, Option = '{大声说,5},{你说什么,5},{什么意思,5}', Action = _EmptyTable,}, 
	[59528] = { id = 59528, Voice = '', Text = '（紧紧抓住你的手）要小心，小心身边的一切。', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[59527] = { id = 59527, Voice = '', Text = '没想到，他们……', Speaker = 5944, Option = '什么？', Action = _EmptyTable,}, 
	[59526] = { id = 59526, Voice = '', Text = '……已经这么严峻了吗？', Speaker = 5944, Option = '', Action = _EmptyTable,}, 
	[59525] = { id = 59525, Voice = '', Text = '欢迎你，远道而来的贵客。', Speaker = 5944, Option = '陈述情况', Action = _EmptyTable,}, 
	[59524] = { id = 59524, Voice = '', Text = '那么交给你了。', Speaker = 5713, Option = '', Action = _EmptyTable,}, 
	[59523] = { id = 59523, Voice = '', Text = '也好，我还需要巡逻朱诺周边。', Speaker = 5713, Option = '', Action = _EmptyTable,}, 
	[59522] = { id = 59522, Voice = '', Text = '是啊，我也很诧异。这件事情需要尽快禀报给大总统。', Speaker = 5713, Option = '我去吧！', Action = _EmptyTable,}, 
	[59521] = { id = 59521, Voice = '', Text = '而像是受到什么指使……', Speaker = 5713, Option = '怎么会', Action = _EmptyTable,}, 
	[59520] = { id = 59520, Voice = '', Text = '我跟踪你们时发现，那些魔物不像是无端地暴动。', Speaker = 5713, Option = '', Action = _EmptyTable,}, 
	[59519] = { id = 59519, Voice = '', Text = '如你所见，目前朱诺周边非常不安全。', Speaker = 5713, Option = '', Action = _EmptyTable,}, 
	[59518] = { id = 59518, Voice = '', Text = '（闭眼微笑）难怪你什么都不肯说，原来是为了[c][ffff00]伊米尔之书[-][/c]。', Speaker = 5713, Option = '', Action = _EmptyTable,}, 
	[59517] = { id = 59517, Voice = '', Text = '（满脸惊讶）原来西芙老师说的冒险者就是你。', Speaker = 5713, Option = '', Action = _EmptyTable,}, 
	[59516] = { id = 59516, Voice = '', Text = '不错，就是我。', Speaker = 5713, Option = '伊米尔之书', Action = _EmptyTable,}, 
	[59515] = { id = 59515, Voice = '', Text = '你……怎么又是你？', Speaker = 5713, Option = '你是赛壬', Action = _EmptyTable,}, 
	[59514] = { id = 59514, Voice = '', Text = '他就在贤者学院外，穿着帅气的衣服，你有什么事情快去找他吧。', Speaker = 5714, Option = '', Action = _EmptyTable,}, 
	[59513] = { id = 59513, Voice = '', Text = '不错，塞壬大人就是这样的人，也是我一生的目标！', Speaker = 5714, Option = '', Action = _EmptyTable,}, 
	[59512] = { id = 59512, Voice = '', Text = '我跟你没什么好聊的，再见！', Speaker = 5714, Option = '', Action = _EmptyTable,}, 
	[59511] = { id = 59511, Voice = '', Text = '你知道，塞壬大人擅长什么攻击方式吗？', Speaker = 5714, Option = '{刀剑,7},{魔法,7},{都不擅长,7},{都擅长,8}', Action = _EmptyTable,}, 
	[59510] = { id = 59510, Voice = '', Text = '你知道塞壬大人是什么职业吗？', Speaker = 5714, Option = '{初心者,5},{剑士,5},{不知道,5},{贤者,6}', Action = _EmptyTable,}, 
	[59509] = { id = 59509, Voice = '', Text = '你根本就不是塞壬大人的崇拜者，我跟你没什么好说的。', Speaker = 5714, Option = '', Action = _EmptyTable,}, 
	[59508] = { id = 59508, Voice = '', Text = '除非，你能回答我的问题！', Speaker = 5714, Option = '{回答问题,3},{没时间,4}', Action = _EmptyTable,}, 
	[59507] = { id = 59507, Voice = '', Text = '你想做什么？我可不会把大人的信息告诉你。', Speaker = 5714, Option = '怎样才说', Action = _EmptyTable,}, 
	[59506] = { id = 59506, Voice = '', Text = '我也想成为像塞壬大人那样，[c][ffff00]既强大又温柔，刀术与魔法兼修[-][/c]的人。', Speaker = 5714, Option = '他在哪', Action = _EmptyTable,}, 
	[59504] = { id = 59504, Voice = '', Text = '我只是一个想成为贤者的魔法师而已，你找错人了！', Speaker = 5715, Option = '', Action = _EmptyTable,}, 
	[59503] = { id = 59503, Voice = '', Text = '你怎么会认为是我呢？', Speaker = 5715, Option = '', Action = _EmptyTable,}, 
	[59502] = { id = 59502, Voice = '', Text = '贤者塞壬？这一听就是个男孩子的名字。', Speaker = 5715, Option = '', Action = _EmptyTable,}, 
	[59499] = { id = 59499, Voice = '', Text = '帮你打听到了，他喜欢在贤者学院看书，也许你能在贤者学院碰见他呢。', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59498] = { id = 59498, Voice = '', Text = '你快去将伊米尔之书送给西芙的学生吧。', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59497] = { id = 59497, Voice = '', Text = '我们之间还需要说什么谢谢呢？', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59496] = { id = 59496, Voice = '', Text = '终于到了？每次都是你最后，真慢~', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59495] = { id = 59495, Voice = '', Text = '（凑近你的耳边）我会关注你的，不要想在这个城市做些什么！', Speaker = 5704, Option = '', Action = _EmptyTable,}, 
	[59494] = { id = 59494, Voice = '', Text = '面对这么多凶猛的魔物，一定要进入朱诺，而且连一个正当的理由都没有。', Speaker = 5704, Option = '', Action = _EmptyTable,}, 
	[59493] = { id = 59493, Voice = '', Text = '……你们很可疑', Speaker = 5704, Option = '', Action = _EmptyTable,}, 
	[59492] = { id = 59492, Voice = '', Text = '你的朋友？她已经进入朱诺了。', Speaker = 5704, Option = '多谢', Action = _EmptyTable,}, 
	[59491] = { id = 59491, Voice = '', Text = '刚刚帮忙的曙光队队长站在旁边，不妨问问他塞尼亚去哪儿了。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[59490] = { id = 59490, Voice = '', Text = '塞尼亚愉快地向朱诺跑去，快跟上她！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[59489] = { id = 59489, Voice = '', Text = '别又被魔物追上了！', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59488] = { id = 59488, Voice = '', Text = '哈哈哈，我不闹了，我们快走吧。', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59487] = { id = 59487, Voice = '', Text = '（一脸憋笑）咳咳，他可能是看上我们的小[c][ffff00][PlayerName][-][/c]了吧！', Speaker = 5702, Option = '……', Action = _EmptyTable,}, 
	[59486] = { id = 59486, Voice = '', Text = '依我看……', Speaker = 5702, Option = '嗯？', Action = _EmptyTable,}, 
	[59485] = { id = 59485, Voice = '', Text = '一脸不近人情，实际上在背后偷偷跟着我们，保护我们。', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59484] = { id = 59484, Voice = '', Text = '哈哈，我只是觉得他憋着难受，逗他好玩~', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59483] = { id = 59483, Voice = '', Text = '这个队长话可真少~对吗？', Speaker = 5702, Option = '你别闹', Action = _EmptyTable,}, 
	[59482] = { id = 59482, Voice = '', Text = '我还有事要去熔洞，你们先走吧。', Speaker = 5710, Option = '', Action = _EmptyTable,}, 
	[59481] = { id = 59481, Voice = '', Text = '（一脸阴沉）你能帮上什么忙？还是不用了！', Speaker = 5710, Option = '', Action = _EmptyTable,}, 
	[59480] = { id = 59480, Voice = '', Text = '请往前走，跟我的队员在前方汇合。', Speaker = 5704, Option = '', Action = _EmptyTable,}, 
	[59479] = { id = 59479, Voice = '', Text = '喂喂~傲娇队长，你终于说了超过三句话了？', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59478] = { id = 59478, Voice = '', Text = '我将护送各位前往朱诺。', Speaker = 5704, Option = '', Action = _EmptyTable,}, 
	[59477] = { id = 59477, Voice = '', Text = '我是朱诺大总统特派守卫队——曙光队的队长。', Speaker = 5704, Option = '', Action = _EmptyTable,}, 
	[59476] = { id = 59476, Voice = '', Text = '各位冒险者，不必惊慌！', Speaker = 5704, Option = '', Action = _EmptyTable,}, 
	[59475] = { id = 59475, Voice = '', Text = '嗯~~', Speaker = 5702, Option = '', Emoji = 25, Action = _EmptyTable,}, 
	[59474] = { id = 59474, Voice = '', Text = '担心我们却不主动说，还偷偷跟着我们！', Speaker = 5702, Option = '应该是的', Action = _EmptyTable,}, 
	[59473] = { id = 59473, Voice = '', Text = '这家伙，莫非就是传说中的傲娇？', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59472] = { id = 59472, Voice = '', Text = '跟着我吧，看你们自己也无法走到朱诺。', Speaker = 5704, Option = '', Action = _EmptyTable,}, 
	[59471] = { id = 59471, Voice = '', Text = '哼……', Speaker = 5704, Option = '{谢谢你,5},{跟踪我们？,5},{...,5}', Action = _EmptyTable,}, 
	[59463] = { id = 59463, Voice = '', Text = '嘿！好伙伴，那么就一起行动吧！', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59462] = { id = 59462, Voice = '', Text = '你还带着伊米尔之书，方便吗？', Speaker = 5702, Option = '没问题', Emoji = 26, Action = _EmptyTable,}, 
	[59461] = { id = 59461, Voice = '', Text = '我听见前方有呼喊声，想去看看情况。', Speaker = 5702, Option = '跟你一起', Action = _EmptyTable,}, 
	[59459] = { id = 59459, Voice = '', Text = '让我先去前面看看有没有魔物，[c][ffff00][PlayerName][-][/c]，快点跟上来哦！', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59458] = { id = 59458, Voice = '', Text = '等我们把书送到朱诺，一起去熔洞冒险吧，我还想拍摄一点熔洞的照片呢~', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59457] = { id = 59457, Voice = '', Text = '当然啦~好不容易跟你团聚。', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59456] = { id = 59456, Voice = '', Text = '好想去里面探险啊！', Speaker = 5702, Option = '先送书', Action = _EmptyTable,}, 
	[59455] = { id = 59455, Voice = '', Text = '我听说，共和国原野上有一个深不可测的熔洞！', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59454] = { id = 59454, Voice = '', Text = '虽然是个冒险者，也很少见过这么深的峡谷呢！', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59453] = { id = 59453, Voice = '', Text = '呼，走的好累啊。', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59452] = { id = 59452, Voice = '', Text = '（对着你）走过这里，就快到朱诺城了，我们加把劲吧！', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59451] = { id = 59451, Voice = '', Text = '朱诺的人，又嚣张又没礼貌！', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59450] = { id = 59450, Voice = '', Text = '我说，这个家伙也太过分了。', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59449] = { id = 59449, Voice = '', Text = '哼！', Speaker = 5704, Option = '', Action = _EmptyTable,}, 
	[59448] = { id = 59448, Voice = '', Text = '那么，就恕我不奉陪，你们自己小心注意。', Speaker = 5704, Option = '等等！', Action = _EmptyTable,}, 
	[59447] = { id = 59447, Voice = '', Text = '你们准备去朱诺做什么？', Speaker = 5704, Option = '……不能说', Action = _EmptyTable,}, 
	[59446] = { id = 59446, Voice = '', Text = '愚蠢！', Speaker = 5704, Option = '', Action = _EmptyTable,}, 
	[59445] = { id = 59445, Voice = '', Text = '如果方便，请回普隆德拉吧。', Speaker = 5704, Option = '不行', Action = _EmptyTable,}, 
	[59444] = { id = 59444, Voice = '', Text = '近期朱诺周边不安全，前方的原野有更多的魔物。', Speaker = 5704, Option = '', Action = _EmptyTable,}, 
	[59443] = { id = 59443, Voice = '', Text = '（一脸严肃）你们两个是冒险者吗？', Speaker = 5704, Option = '是的', Action = _EmptyTable,}, 
	[59442] = { id = 59442, Voice = '', Text = '你们没事吧？', Speaker = 5704, Option = '', Action = _EmptyTable,}, 
	[59441] = { id = 59441, Voice = '', Text = '年轻的曙光队队长小心地从地上拾起了发光的项链，他看上去很伤心。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[59440] = { id = 59440, Voice = '', Text = '住手！魔物！', Speaker = 5704, Option = '', Action = _EmptyTable,}, 
	[59439] = { id = 59439, Voice = '', Text = '父亲的项链……', Speaker = 5704, Option = '', Action = _EmptyTable,}, 
	[59437] = { id = 59437, Voice = '', Text = '准备腐蚀药水，听我的命令行事。', Speaker = 5704, Option = '', Action = _EmptyTable,}, 
	[59436] = { id = 59436, Voice = '', Text = '它的身体正在快速复原。', Speaker = 5704, Option = '', Action = _EmptyTable,}, 
	[59435] = { id = 59435, Voice = '', Text = '阻止它！', Speaker = 5704, Option = '', Action = _EmptyTable,}, 
	[59432] = { id = 59432, Voice = '', Text = '你快跑，带着伊米尔之书！', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59431] = { id = 59431, Voice = '', Text = '（虚弱）我可能…', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59430] = { id = 59430, Voice = '', Text = '好强！！这个魔物……', Speaker = 5702, Option = '', Emoji = 18, Action = _EmptyTable,}, 
	[59429] = { id = 59429, Voice = '', Text = '我来对付这个大块头！后面的那些魔物就交给你了！', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59428] = { id = 59428, Voice = '', Text = '合作！', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59425] = { id = 59425, Voice = '', Text = '看上去像受到指使！我们快走！', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59424] = { id = 59424, Voice = '', Text = '这些魔物好像不是附近栖息的魔物，而且行动有很强的目的性。', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59423] = { id = 59423, Voice = '', Text = '后面几个交给你了！', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59422] = { id = 59422, Voice = '', Text = '这点魔物算什么，我来解决前面几个！', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59419] = { id = 59419, Voice = '', Text = '发、发生了什么？', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59418] = { id = 59418, Voice = '', Text = '让我继续为你清除前方的魔物！', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59417] = { id = 59417, Voice = '', Text = '耽误了这么多时间，我们该走了！', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59416] = { id = 59416, Voice = '', Text = '哇！你的拍照技术见长嘛！', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59415] = { id = 59415, Voice = '', Text = '嘿嘿偷个懒应该没什么问题，那要帮我拍得很美哦~', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59414] = { id = 59414, Voice = '', Text = '虽然在执行任务，但还是好想拍张照片纪念一下这一刻。', Speaker = 5702, Option = '我来帮你', Action = _EmptyTable,}, 
	[59413] = { id = 59413, Voice = '', Text = '也许下次再来，风景会完全不一样呢！', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59412] = { id = 59412, Voice = '', Text = '这里的风景真美啊，朱诺升空以后这片森林的气候也发生了变化。', Speaker = 5702, Option = '是嘛', Action = _EmptyTable,}, 
	[59411] = { id = 59411, Voice = '', Text = '和你并肩作战，让我想起来小时候我们在一起训练的时光。', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59410] = { id = 59410, Voice = '', Text = '前面的魔物都被我赶跑了，我们快过去吧！', Speaker = 5702, Option = '好的', Action = _EmptyTable,}, 
	[59409] = { id = 59409, Voice = '', Text = '跟我来！', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59408] = { id = 59408, Voice = '', Text = '可恶！前方的魔物太多了，只能从另外一条路走了。', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59407] = { id = 59407, Voice = '', Text = '这块地方还有许多其他的魔物，我去帮你开开路吧~', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59406] = { id = 59406, Voice = '', Text = '干得不错嘛~一段时间不见，你成长了许多。', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59405] = { id = 59405, Voice = '', Text = '这边有许多魔物，其中最危险的是鸟人哈比，想要顺利通过国境先清理一下周围的鸟人哈比吧。', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59404] = { id = 59404, Voice = '', Text = '（小声说）看了半天这个破石像正无聊呢~', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59403] = { id = 59403, Voice = '', Text = '哦~原来是为了护送伊米尔之书啊~那我帮你吧~', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59402] = { id = 59402, Voice = '', Text = '好久不见，你也到朱诺来玩了吗？', Speaker = 5702, Option = '伊米尔之书', Action = _EmptyTable,}, 
	[59401] = { id = 59401, Voice = '', Text = '哟！', Speaker = 5702, Option = '', Action = _EmptyTable,}, 
	[59399] = { id = 59399, Voice = '', Text = '朱诺升空后造成巨大的动静，许多魔物都受到惊吓变得不太稳定。', Speaker = 5701, Option = '', Action = _EmptyTable,}, 
	[59398] = { id = 59398, Voice = '', Text = '护送伊米尔之书的冒险者，在修发兹共和国境内走动时要注意安全。', Speaker = 5701, Option = '', Action = _EmptyTable,}, 
	[59397] = { id = 59397, Voice = '', Text = '如果要护送伊米尔之书，可以从国境出发，那里有许多王国的守卫官。', Speaker = 5727, Option = '', Action = _EmptyTable,}, 
	[59396] = { id = 59396, Voice = '', Text = '请将书取走吧。', Speaker = 5727, Option = '', Action = _EmptyTable,}, 
	[59395] = { id = 59395, Voice = '', Text = '是西芙大人的请求吗？好吧，这本书威力巨大，一定要好好守护。', Speaker = 5727, Option = '没问题！', Action = _EmptyTable,}, 
	[59394] = { id = 59394, Voice = '', Text = '这是我的手信，请你拿给看守伊米尔之书的皇家卫士吧。', Speaker = 2602, Option = '', Action = _EmptyTable,}, 
	[59393] = { id = 59393, Voice = '', Text = '请你将伊米尔之书护送给我的学生塞壬，他是我最有天赋的学生。', Speaker = 2602, Option = '', Action = _EmptyTable,}, 
	[59392] = { id = 59392, Voice = '', Text = '那么请将这本伊米尔之书送到朱诺城里。', Speaker = 2602, Option = '', Action = _EmptyTable,}, 
	[59391] = { id = 59391, Voice = '', Text = '可惜我还需要在普隆德拉待一段时间，不如由你帮我将伊米尔之书送到朱诺吧。', Speaker = 2602, Option = '{没问题,2},{我拒绝,1}', Action = _EmptyTable,}, 
	[59390] = { id = 59390, Voice = '', Text = '朱诺已经升空，寄放在普隆德拉的伊米尔之书也应该送回去了。', Speaker = 2602, Option = '', Action = _EmptyTable,}, 
	[59389] = { id = 59389, Voice = '', Text = '（感慨）一直在普隆德拉守护伊米尔之书，很想念朱诺的美食啊……', Speaker = 2602, Option = '', Action = _EmptyTable,}, 
	[58169] = { id = 58169, Voice = '', Text = '失去了记忆，永生无法解脱。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58168] = { id = 58168, Voice = '', Text = '而巫女不忍心自己的朋友被这样对待，用自己的灵魂代替小狐狸变为家族的守护式神。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58167] = { id = 58167, Voice = '', Text = '巫女家族觊觎小狐狸身上仙狐的灵力，想要去除它的记忆，变成家族的守护式神。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58166] = { id = 58166, Voice = '', Text = '它遇见了美丽又善良的巫女，因为一块樱花蛋糕跟巫女成为了好朋友。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58165] = { id = 58165, Voice = '', Text = '两百年前，小狐狸离开仙山来到了天水之国。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58164] = { id = 58164, Voice = '', Text = '终于，找到了我的小狐狸。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58163] = { id = 58163, Voice = '', Text = '（对着你）终于找到了我的小狐狸，谢谢你，善良的年轻人。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58162] = { id = 58162, Voice = '', Text = '（微笑）好了好了，别哭了，你没事，就很好了。', Speaker = 5187, Option = '', Emoji = 30, Action = _EmptyTable,}, 
	[58161] = { id = 58161, Voice = '', Text = '杏，也不会失去记忆变成式神，呜呜呜呜……', Speaker = 5189, Option = '', Action = _EmptyTable,}, 
	[58160] = { id = 58160, Voice = '', Text = '狐狸爸爸，呜呜呜……如果不是我要跑出来玩，也不会变成这样。', Speaker = 5189, Option = '', Action = _EmptyTable,}, 
	[58159] = { id = 58159, Voice = '', Text = '以后，仙山交给别人了，一定要好好照顾居住在上面的生灵哦。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58158] = { id = 58158, Voice = '', Text = '哦呀哦呀，给大家添麻烦了呢，我愿意接受这个处罚。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58157] = { id = 58157, Voice = '', Text = '肆意在尘世使用复活法术，已被削除神籍。', Speaker = 5538, Option = '', Action = _EmptyTable,}, 
	[58156] = { id = 58156, Voice = '', Text = '仙山之神，你违反了神界的规定。', Speaker = 5538, Option = '', Action = _EmptyTable,}, 
	[58155] = { id = 58155, Voice = '', Text = '没关系，我来找你了呀。别害怕，别害怕。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58154] = { id = 58154, Voice = '', Text = '呜呜呜呜……', Speaker = 5189, Option = '', Action = _EmptyTable,}, 
	[58153] = { id = 58153, Voice = '', Text = '我…被打伤了，还把你给的宝玉送给别人了，回不了家了！', Speaker = 5189, Option = '', Action = _EmptyTable,}, 
	[58152] = { id = 58152, Voice = '', Text = '狐狸爸爸！我在做梦吗？', Speaker = 5189, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[58151] = { id = 58151, Voice = '', Text = '我感觉到了小狐狸的灵力！', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58150] = { id = 58150, Voice = '', Text = '让我去看看吧……好久没见到我的小狐狸了，不知道它可怜的灵魂是否还记得我呢？', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58149] = { id = 58149, Voice = '', Text = '原来小狐狸的灵魂被束缚在石桥上。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58148] = { id = 58148, Voice = '', Text = '而有一名伊达家族的巫女救了我的小狐狸啊。', Speaker = 5187, Option = '是的', Action = _EmptyTable,}, 
	[58147] = { id = 58147, Voice = '', Text = '什么？伊达家族伤害了小狐狸吗？', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58134] = { id = 58134, Voice = '', Text = '桥上的石狐雕像里冲出来一只狐狸的灵魂，撞进身体里。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[58133] = { id = 58133, Voice = '', Text = '桥上雕刻了一只小狐狸，走过时狐狸的眼睛好像眨动了，是否仔细观察？', Speaker = 5537, Option = '{观察,2},{放弃,3}', Action = _EmptyTable,}, 
	[58132] = { id = 58132, Voice = '', Text = '可以回到仙山。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58131] = { id = 58131, Voice = '', Text = '它依然想要去别的地方游历，也希望，有一天在疲惫的时候。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58130] = { id = 58130, Voice = '', Text = '面对巫女相伴的请求，我的小狐狸拒绝了。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58129] = { id = 58129, Voice = '', Text = '可是，一切的恩爱相聚都如同清晨的露水，短暂无常难以持久。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58128] = { id = 58128, Voice = '', Text = '它与巫女成为了朋友，一起度过了难忘的时光。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58127] = { id = 58127, Voice = '', Text = '下山游历的小狐狸，遇见了美丽温柔的巫女。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58126] = { id = 58126, Voice = '', Text = '如果它没有成为式神，又去了哪里呢？', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58125] = { id = 58125, Voice = '', Text = '原来是这样……', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58124] = { id = 58124, Voice = '', Text = '我的小狐狸，不愿意成为巫女的式神吗？', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58123] = { id = 58123, Voice = '', Text = '那些只是清晨的雾气，凝聚的露珠而已。', Speaker = 5190, Option = '', Action = _EmptyTable,}, 
	[58122] = { id = 58122, Voice = '', Text = '……', Speaker = 5190, Option = '', Action = _EmptyTable,}, 
	[58121] = { id = 58121, Voice = '', Text = '飘下的樱花上有你的泪水……', Speaker = 5189, Option = '', Action = _EmptyTable,}, 
	[58120] = { id = 58120, Voice = '', Text = '……没有哦……', Speaker = 5190, Option = '', Action = _EmptyTable,}, 
	[58119] = { id = 58119, Voice = '', Text = '（仔细地听）杏，你在哭吗？', Speaker = 5189, Option = '', Action = _EmptyTable,}, 
	[58118] = { id = 58118, Voice = '', Text = '（声音很低落）杏，我想回家了，我想狐狸爸爸了……', Speaker = 5189, Option = '', Action = _EmptyTable,}, 
	[58117] = { id = 58117, Voice = '', Text = '小狐狸，你愿意……（声音越来越小）做我的式神吗？', Speaker = 5190, Option = '', Action = _EmptyTable,}, 
	[58116] = { id = 58116, Voice = '', Text = '露珠带领你重回过去，看清真实与虚妄。', Speaker = 5536, Option = '', Action = _EmptyTable,}, 
	[58115] = { id = 58115, Voice = '', Text = '我并没有你想要的东西，请回吧。', Speaker = 5536, Option = '', Action = _EmptyTable,}, 
	[58114] = { id = 58114, Voice = '', Text = '而狐狸的故事，又是跟谁有关？', Speaker = 5536, Option = '{伊达家族,6},{武藏家族,7},{鹤田家族,7},{松本家族,7}', Action = _EmptyTable,}, 
	[58113] = { id = 58113, Voice = '', Text = '我并没有你想要的东西，请回吧。', Speaker = 5536, Option = '', Action = _EmptyTable,}, 
	[58112] = { id = 58112, Voice = '', Text = '你想从露珠之中寻找什么故事？', Speaker = 5536, Option = '{鹦鹉的故事,4},{老虎的故事,4},{狐狸的故事,5},{乌龟的故事,4}', Action = _EmptyTable,}, 
	[58111] = { id = 58111, Voice = '', Text = '什么人触碰了我的露珠，又是为了什么事情？', Speaker = 5536, Option = '', Action = _EmptyTable,}, 
	[58110] = { id = 58110, Voice = '', Text = '樱花瓣上有一滴露珠，在薄暮之中微微颤抖……是否将其拭去？', Speaker = 5199, Option = '{拭去,2},{放弃,3}', Action = _EmptyTable,}, 
	[58109] = { id = 58109, Voice = '', Text = '教会我的小狐狸使用仙术，是一件正确的事情吗？', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58108] = { id = 58108, Voice = '', Text = '狐之火，狐之火，是狐狸灵力的象征。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58107] = { id = 58107, Voice = '', Text = '不过，它也是个温柔的孩子，从来不强迫其他生命做任何事情。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58106] = { id = 58106, Voice = '', Text = '那个时候，它可威风了，仙山的灵物都听从它的吩咐。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58105] = { id = 58105, Voice = '', Text = '我慢慢教他修炼，最后成为了仙山的守护仙狐。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58104] = { id = 58104, Voice = '', Text = '当初见到小狐狸，它还什么都不会呢。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58103] = { id = 58103, Voice = '', Text = '事情的发展，让我有种不安的感觉……', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58102] = { id = 58102, Voice = '', Text = '伊达家族的巫女，听起来应该是信奉神明的家族，她们跟小狐狸发生了矛盾吗？', Speaker = 5187, Option = '还不清楚', Action = _EmptyTable,}, 
	[58101] = { id = 58101, Voice = '', Text = '我的小狐狸碰见了什么事情吗？为什么会释放狐火呢？', Speaker = 5187, Option = '陈述', Action = _EmptyTable,}, 
	[58100] = { id = 58100, Voice = '', Text = '怎么会，你手上的是小狐狸的狐之火。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58099] = { id = 58099, Voice = '', Text = '……我只是想试试那只仙狐……', Speaker = 5194, Option = '', Action = _EmptyTable,}, 
	[58098] = { id = 58098, Voice = '', Text = '我知道，可是你也不应该在神社释放恶灵！', Speaker = 5193, Option = '', Action = _EmptyTable,}, 
	[58097] = { id = 58097, Voice = '', Text = '看见了吗？那只狐狸，应该是传说中的仙狐吧……', Speaker = 5194, Option = '', Action = _EmptyTable,}, 
	[58096] = { id = 58096, Voice = '', Text = '它们从这边跑了，我们快追！', Speaker = 5189, Option = '', Action = _EmptyTable,}, 
	[58095] = { id = 58095, Voice = '', Text = '杏，小心！', Speaker = 5189, Option = '', Action = _EmptyTable,}, 
	[58094] = { id = 58094, Voice = '', Text = '怎么会，神社怎么会有恶灵？', Speaker = 5190, Option = '', Action = _EmptyTable,}, 
	[58093] = { id = 58093, Voice = '', Text = '你最好了~', Speaker = 5189, Option = '', Action = _EmptyTable,}, 
	[58092] = { id = 58092, Voice = '', Text = '（宠溺的口气）知道啦知道啦~你的狐狸爸爸最好啦~', Speaker = 5190, Option = '', Action = _EmptyTable,}, 
	[58091] = { id = 58091, Voice = '', Text = '（一脸骄傲）我家更大哦，狐狸爸爸那里有一座很大的山，都是我家哦~', Speaker = 5189, Option = '', Action = _EmptyTable,}, 
	[58090] = { id = 58090, Voice = '', Text = '（四处张望）哇，这里的建筑好漂亮啊，你家可真大！', Speaker = 5189, Option = '', Action = _EmptyTable,}, 
	[58089] = { id = 58089, Voice = '', Text = '你看，这就是我家的神社了，是不是很漂亮呀？', Speaker = 5190, Option = '', Action = _EmptyTable,}, 
	[58088] = { id = 58088, Voice = '', Text = '这是一团燃烧着的狐之火，触碰之下闪动着光影，好像记录了一些事情。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[58087] = { id = 58087, Voice = '', Text = '空气里飘散着一股神秘的力量，是否调查这股力量？', Speaker = 5196, Option = '{调查,2},{放弃,3}', Action = _EmptyTable,}, 
	[58086] = { id = 58086, Voice = '', Text = '神明的一生很漫长，有只狐狸可以回忆，也是一件不错的事情呢。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58085] = { id = 58085, Voice = '', Text = '就算小狐狸离开我了，我们相伴的那些日子，已经足够我回忆了。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58084] = { id = 58084, Voice = '', Text = '不过命运和缘分就是这样。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58083] = { id = 58083, Voice = '', Text = '我还是有些难过的呢……', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58082] = { id = 58082, Voice = '', Text = '我养了小狐狸好几年，突然之间听说它想去做其他人的式神。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58081] = { id = 58081, Voice = '', Text = '可，我还是想见它一面，毕竟是自己亲手养大的狐狸啊。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58080] = { id = 58080, Voice = '', Text = '我的小狐狸，长大了呢……', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58079] = { id = 58079, Voice = '', Text = '保护这个国家，也是一件伟大的使命。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58078] = { id = 58078, Voice = '', Text = '如果，这是它自己的选择，我会祝福它成为一个优秀的式神。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58077] = { id = 58077, Voice = '', Text = '是这样吗？我的小狐狸选择成为年轻巫女的式神了吗？', Speaker = 5187, Option = '不知道', Action = _EmptyTable,}, 
	[58076] = { id = 58076, Voice = '', Text = '（看上去有些悲伤）……', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58075] = { id = 58075, Voice = '', Text = '哦呀哦呀，年轻人，你找到我的小狐狸了吗？', Speaker = 5187, Option = '告知', Action = _EmptyTable,}, 
	[58074] = { id = 58074, Voice = '', Text = '符咒中的纸片式神已经被击败，将小狐狸的信息告诉狐狸脸地藏灵。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[58073] = { id = 58073, Voice = '', Text = '小狐狸没有攻击突然现身的纸片式神，反而快速的离开了。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[58072] = { id = 58072, Voice = '', Text = '这些式神，杏……狐狸爸爸，我应该怎么做？', Speaker = 5189, Option = '', Action = _EmptyTable,}, 
	[58071] = { id = 58071, Voice = '', Text = '我真是一只自私的狐狸！', Speaker = 5189, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[58070] = { id = 58070, Voice = '', Text = '可是我，想回家找狐狸爸爸，好久没见了……', Speaker = 5189, Option = '', Action = _EmptyTable,}, 
	[58069] = { id = 58069, Voice = '', Text = '杏，很辛苦吧？', Speaker = 5189, Option = '', Action = _EmptyTable,}, 
	[58068] = { id = 58068, Voice = '', Text = '（急切地说）如果，它把你当成真正的朋友，会愿意为了你留下来的！', Speaker = 5194, Option = '', Action = _EmptyTable,}, 
	[58067] = { id = 58067, Voice = '', Text = '（声音越来越低）可是，它还要回家找它的狐狸爸爸呢……', Speaker = 5190, Option = '', Action = _EmptyTable,}, 
	[58066] = { id = 58066, Voice = '', Text = '杏，如果小狐狸真的喜欢你，它会愿意留下来成为你的式神。', Speaker = 5194, Option = '', Action = _EmptyTable,}, 
	[58065] = { id = 58065, Voice = '', Text = '上天祝福你，成为伊达家族有史以来最伟大的巫女。', Speaker = 5194, Option = '', Action = _EmptyTable,}, 
	[58064] = { id = 58064, Voice = '', Text = '式神原本就是巫女最亲密的朋友，你能碰见小狐狸也许是神明的安排。', Speaker = 5194, Option = '', Action = _EmptyTable,}, 
	[58063] = { id = 58063, Voice = '', Text = '式神？不，小狐狸是我的朋友，它不是我的属下。', Speaker = 5190, Option = '', Emoji = 14, Action = _EmptyTable,}, 
	[58062] = { id = 58062, Voice = '', Text = '如果你能够收服它成为式神，就能更好的帮助伊达家族净化地底的灵了。', Speaker = 5194, Option = '', Action = _EmptyTable,}, 
	[58061] = { id = 58061, Voice = '', Text = '那只狐狸是神明饲养的仙狐，它拥有安抚恶灵的能力。', Speaker = 5194, Option = '', Action = _EmptyTable,}, 
	[58060] = { id = 58060, Voice = '', Text = '别婆婆妈妈了，哥哥！', Speaker = 5194, Option = '', Action = _EmptyTable,}, 
	[58059] = { id = 58059, Voice = '', Text = '我们希望……', Speaker = 5193, Option = '', Action = _EmptyTable,}, 
	[58058] = { id = 58058, Voice = '', Text = '杏，你的狐狸朋友……身份很不寻常……', Speaker = 5193, Option = '', Action = _EmptyTable,}, 
	[58057] = { id = 58057, Voice = '', Text = '触碰符咒的瞬间，感觉到手指一阵刺痛，眼前出现过去的幻象。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[58056] = { id = 58056, Voice = '', Text = '樱花树上贴了一张古怪的符咒，将它从树上取下来？', Speaker = 5192, Option = '{取下,2},{放弃,3}', Action = _EmptyTable,}, 
	[58055] = { id = 58055, Voice = '', Text = '等到那个时候，我们一家人就可以幸福地生活下去……', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58054] = { id = 58054, Voice = '', Text = '只要通过宝玉开启的通道，就可以带母狐狸回到仙山了。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58053] = { id = 58053, Voice = '', Text = '我还跟它说，如果碰见了心仪的母狐狸，也可以将宝玉送给它。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58052] = { id = 58052, Voice = '', Text = '任何时候，只要念出宝玉里的咒语，都可以打开仙山的大门，它就可以从时空通道回家了。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58051] = { id = 58051, Voice = '', Text = '小狐狸离开仙山的时候，我担心它的安危，为他制作了一枚宝玉。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58050] = { id = 58050, Voice = '', Text = '（伤心）不生气不生气，只是，还有以后吗？我的狐狸去了哪里呢？', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58049] = { id = 58049, Voice = '', Text = '以后再也不会专门为它准备什么了！', Speaker = 5187, Option = '息怒', Action = _EmptyTable,}, 
	[58048] = { id = 58048, Voice = '', Text = '我跟它说，要好好保管这枚玉，除非碰见心仪的母狐狸，要不然不要送给别人。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58047] = { id = 58047, Voice = '', Text = '我就知道这个喜欢送东西的败家子不可靠！', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58046] = { id = 58046, Voice = '', Text = '跟巫女成为了朋友吗…还把我的宝玉送给了巫女？', Speaker = 5187, Option = '', Emoji = 8, Action = _EmptyTable,}, 
	[58045] = { id = 58045, Voice = '', Text = '果然我的狐狸在这里，到底发生了什么，让它回不了家了呢？', Speaker = 5187, Option = '遇见巫女', Action = _EmptyTable,}, 
	[58044] = { id = 58044, Voice = '', Text = '这…是我送给小狐狸的宝玉。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58026] = { id = 58026, Voice = '', Text = '宝玉中传来一阵炫目的光，眼前一阵晕眩后好像回到了过去。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[58025] = { id = 58025, Voice = '', Text = '樱花树下有一块晶莹剔透的玉，是否拾取？', Speaker = 5188, Option = '{拾取,2},{放弃,3}', Action = _EmptyTable,}, 
	[58024] = { id = 58024, Voice = '', Text = '不知道此生还能不能相见呢？', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58023] = { id = 58023, Voice = '', Text = '(表情落寞）我离开仙山已经太久了，如果还不能找到我的小狐狸，我就要回去了。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58022] = { id = 58022, Voice = '', Text = '可是，没人能看见我，我也没看见我的狐狸。', Speaker = 5187, Option = '我很抱歉', Action = _EmptyTable,}, 
	[58021] = { id = 58021, Voice = '', Text = '没有……我在这里又找了一百年……', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58020] = { id = 58020, Voice = '', Text = '百年之后，我决定偷偷从仙山溜走，来到天水之国寻找我的狐狸。', Speaker = 5187, Option = '找到了吗？', Action = _EmptyTable,}, 
	[58019] = { id = 58019, Voice = '', Text = '我呀，在仙山等了足足一百年。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58018] = { id = 58018, Voice = '', Text = '可是，从它寄给我这个狐狸面具以后，我就再也没有得到过它的消息。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58017] = { id = 58017, Voice = '', Text = '（眯着眼睛笑）对了，快看，我脸上的狐狸脸面具就是它在天水之国寄给我的呢。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58016] = { id = 58016, Voice = '', Text = '是的，它寄了一封信告诉我它在这里过得很开心，遇见了奇妙的人和奇妙的事。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58015] = { id = 58015, Voice = '', Text = '（表情落寞）它去了一个叫做天水之国的地方。', Speaker = 5187, Option = '这里……', Action = _EmptyTable,}, 
	[58014] = { id = 58014, Voice = '', Text = '（表情落寞）直到有一天……', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58013] = { id = 58013, Voice = '', Text = '还给我寄了许多许多的小玩意呢。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58012] = { id = 58012, Voice = '', Text = '它在这个世界游历，走过了不同的地方，看过了不同的风景。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58011] = { id = 58011, Voice = '', Text = '是的，百年前我的狐狸从我掌管的仙山里跑出来玩。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[58010] = { id = 58010, Voice = '', Text = '不错不错，老夫是另一个时空的神明，来到这边是为了寻找我的狐狸。', Speaker = 5187, Option = '狐狸？', Action = _EmptyTable,}, 
	[58009] = { id = 58009, Voice = '', Text = '你的灵力一定非常的强，能够突破神明与人类之间的屏障。', Speaker = 5187, Option = '神明？', Action = _EmptyTable,}, 
	[58008] = { id = 58008, Voice = '', Text = '不容易，不容易，老夫在这里站了许久，从未有人看见过我。', Speaker = 5187, Option = '', Action = _EmptyTable,}, 
	[57887] = { id = 57887, Voice = '', Text = '干的好！！还剩最后一个了！冒险者！', Speaker = 7209, Option = '', Action = _EmptyTable,}, 
	[57836] = { id = 57836, Voice = '', Text = '不跟你废话啦，喝酒去咯！！~~', Speaker = 7219, Option = '', Action = _EmptyTable,}, 
	[57835] = { id = 57835, Voice = '', Text = '说了这么多，我都累了。', Speaker = 7219, Option = '', Action = _EmptyTable,}, 
	[57834] = { id = 57834, Voice = '', Text = '决绝也好，妥协也罢，人生，注定是一场无法回头的浪游罢了。', Speaker = 7219, Option = '…', Action = _EmptyTable,}, 
	[57833] = { id = 57833, Voice = '', Text = '他们的爱情，用他们整段生命也无法倾诉。', Speaker = 7219, Option = '', Action = _EmptyTable,}, 
	[57832] = { id = 57832, Voice = '', Text = '我们是不会懂秀明的，年轻人。', Speaker = 7219, Option = '也是', Action = _EmptyTable,}, 
	[57831] = { id = 57831, Voice = '', Text = '这不是你自己的决定嘛！', Speaker = 7219, Option = '', Action = _EmptyTable,}, 
	[57830] = { id = 57830, Voice = '', Text = '什么？你问我这样是否妥当？', Speaker = 7219, Option = '是啊', Action = _EmptyTable,}, 
	[57829] = { id = 57829, Voice = '', Text = '傻小樱，别哭。', Speaker = 7218, Option = '', Action = _EmptyTable,}, 
	[57828] = { id = 57828, Voice = '', Text = '一颗颗闪闪发亮的泪珠顺着小樱的脸颊滚下来，但秀明却笑得像春日的朝阳一样灿烂。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[57827] = { id = 57827, Voice = '', Text = '这将会是我人生中最幸福的五年！！', Speaker = 7218, Option = '', Emoji = 9, Action = _EmptyTable,}, 
	[57826] = { id = 57826, Voice = '', Text = '我真是太幸福了啊！', Speaker = 7218, Option = '嗯', Action = _EmptyTable,}, 
	[57825] = { id = 57825, Voice = '', Text = '能用我的寿命来换小樱留下来。', Speaker = 7218, Option = '', Action = _EmptyTable,}, 
	[57824] = { id = 57824, Voice = '', Text = '相对的，秀明变得虚弱不堪。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[57823] = { id = 57823, Voice = '', Text = '小樱的灵力逐渐回到了她的体内。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[57822] = { id = 57822, Voice = '', Text = '秀明…', Speaker = 7217, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[57821] = { id = 57821, Voice = '', Text = '是你告诉我，什么是幸福。', Speaker = 7218, Option = '', Action = _EmptyTable,}, 
	[57820] = { id = 57820, Voice = '', Text = '只有你在，我才有活下去的意义。', Speaker = 7218, Option = '', Action = _EmptyTable,}, 
	[57819] = { id = 57819, Voice = '', Text = '小樱...', Speaker = 7218, Option = '', Action = _EmptyTable,}, 
	[57818] = { id = 57818, Voice = '', Text = '什么！', Speaker = 7217, Option = '', Emoji = 7, Action = _EmptyTable,}, 
	[57817] = { id = 57817, Voice = '', Text = '作为代价，他将只有五年寿命。', Speaker = 7219, Option = '', Action = _EmptyTable,}, 
	[57816] = { id = 57816, Voice = '', Text = '将魔法用在秀明身上，他会把小樱所有的灵力都还给小樱。', Speaker = 7219, Option = '', Action = _EmptyTable,}, 
	[57815] = { id = 57815, Voice = '', Text = '好，就实现秀明的心愿吧！', Speaker = 7219, Option = '', Action = _EmptyTable,}, 
	[57814] = { id = 57814, Voice = '', Text = '不跟你废话啦，累死我啦！喝酒去咯！！', Speaker = 7219, Option = '', Action = _EmptyTable,}, 
	[57813] = { id = 57813, Voice = '', Text = '谁知道呢，我怎么知道！！？？', Speaker = 7219, Option = '', Action = _EmptyTable,}, 
	[57812] = { id = 57812, Voice = '', Text = '也许在未来的某一天，他们会再度重逢。', Speaker = 7219, Option = '是吗？', Action = _EmptyTable,}, 
	[57811] = { id = 57811, Voice = '', Text = '让他去吧。', Speaker = 7219, Option = '', Action = _EmptyTable,}, 
	[57810] = { id = 57810, Voice = '', Text = '我要走了，再见，陌生人！', Speaker = 7218, Option = '', Action = _EmptyTable,}, 
	[57809] = { id = 57809, Voice = '', Text = '我要...去寻找这重要的事，虽然我现在想不起那是什么。', Speaker = 7218, Option = '', Action = _EmptyTable,}, 
	[57808] = { id = 57808, Voice = '', Text = '你凭什么定义我的幸福？你只是个陌生人…', Speaker = 7218, Option = '', Action = _EmptyTable,}, 
	[57807] = { id = 57807, Voice = '', Text = '有些事，不记起是种幸福。', Speaker = 7219, Option = '', Action = _EmptyTable,}, 
	[57806] = { id = 57806, Voice = '', Text = '不，我仿佛忘记了非常非常重要的事…', Speaker = 7218, Option = '', Action = _EmptyTable,}, 
	[57805] = { id = 57805, Voice = '', Text = '它和这里所有的花瓣都不一样。', Speaker = 7218, Option = '别深究了', Action = _EmptyTable,}, 
	[57804] = { id = 57804, Voice = '', Text = '我不知道自己怎么了，总觉得这片花瓣对我来说很重要。', Speaker = 7218, Option = '', Action = _EmptyTable,}, 
	[57803] = { id = 57803, Voice = '', Text = '嗯…', Speaker = 7218, Option = '怎么了', Action = _EmptyTable,}, 
	[57802] = { id = 57802, Voice = '', Text = '秀明顿了一下，眼里露出了如同一秒之间过了整个春夏秋冬般的忧伤。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[57801] = { id = 57801, Voice = '', Text = '也对。', Speaker = 7218, Option = '', Emoji = 10, Action = _EmptyTable,}, 
	[57800] = { id = 57800, Voice = '', Text = '这里到处都是樱花树，有片花瓣不奇怪吧。', Speaker = 7219, Option = '', Action = _EmptyTable,}, 
	[57799] = { id = 57799, Voice = '', Text = '为什么有片樱花瓣…', Speaker = 7218, Option = '', Action = _EmptyTable,}, 
	[57798] = { id = 57798, Voice = '', Text = '不行，头有点痛…我的手掌里，', Speaker = 7218, Option = '', Action = _EmptyTable,}, 
	[57797] = { id = 57797, Voice = '', Text = '你是？', Speaker = 7218, Option = '', Action = _EmptyTable,}, 
	[57796] = { id = 57796, Voice = '', Text = '我在这里干什么呢？', Speaker = 7218, Option = '呃，你', Action = _EmptyTable,}, 
	[57795] = { id = 57795, Voice = '', Text = '我…', Speaker = 7218, Option = '', Action = _EmptyTable,}, 
	[57794] = { id = 57794, Voice = '', Text = '只有一片落在秀明的手掌心里。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[57793] = { id = 57793, Voice = '', Text = '小樱化成无数片花瓣，飘散在空气中。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[57792] = { id = 57792, Voice = '', Text = '要好好活下去…', Speaker = 7217, Option = '', Action = _EmptyTable,}, 
	[57791] = { id = 57791, Voice = '', Text = '认识你，我很幸福…', Speaker = 7217, Option = '', Action = _EmptyTable,}, 
	[57790] = { id = 57790, Voice = '', Text = '秀明…', Speaker = 7217, Option = '', Action = _EmptyTable,}, 
	[57789] = { id = 57789, Voice = '', Text = '小樱！！！！不！！！！！！', Speaker = 7218, Option = '', Action = _EmptyTable,}, 
	[57788] = { id = 57788, Voice = '', Text = '忘了我吧，你会有个很好的未来。', Speaker = 7217, Option = '', Action = _EmptyTable,}, 
	[57787] = { id = 57787, Voice = '', Text = '将魔法用在秀明身上，他会忘记与小樱的一切，不会有任何痛苦。', Speaker = 7219, Option = '', Action = _EmptyTable,}, 
	[57786] = { id = 57786, Voice = '', Text = '好，就实现小樱的心愿吧！', Speaker = 7219, Option = '', Action = _EmptyTable,}, 
	[57785] = { id = 57785, Voice = '', Text = '你还在犹豫不决，我等你的决定。', Speaker = 7219, Option = '', Action = _EmptyTable,}, 
	[57784] = { id = 57784, Voice = '', Text = '你的选择将会影响他们两的未来和结局，你确定要这么选择吗？', Speaker = 7219, Option = '{坚持选小樱,22},{坚持选秀明,21}', Action = _EmptyTable,}, 
	[57783] = { id = 57783, Voice = '', Text = '你的选择将会影响他们两的未来和结局，你确定要这么选择吗？', Speaker = 7219, Option = '{坚持选小樱,11},{坚持选秀明,12}', Action = _EmptyTable,}, 
	[57782] = { id = 57782, Voice = '', Text = '那么你决定帮助小樱还是帮助秀明？', Speaker = 7219, Option = '{小樱,10},{秀明,20}', Action = _EmptyTable,}, 
	[57781] = { id = 57781, Voice = '', Text = '你想好了吗？', Speaker = 7219, Option = '{想好了,2},{没想好,1}', Action = _EmptyTable,}, 
	[57780] = { id = 57780, Voice = '', Text = '你选择帮助小樱实现他的愿望？还是帮助秀明实现他的愿望？', Speaker = 7219, Option = '', Action = _EmptyTable,}, 
	[57779] = { id = 57779, Voice = '', Text = '一直以来你都在摇摆不定，所以这次我希望你来做决定！', Speaker = 7219, Option = '', Action = _EmptyTable,}, 
	[57778] = { id = 57778, Voice = '', Text = '你没听错，但只能选一个人。', Speaker = 7219, Option = '', Action = _EmptyTable,}, 
	[57777] = { id = 57777, Voice = '', Text = '那能实现小樱或秀明其中一个人的愿望！', Speaker = 7219, Option = '什么', Action = _EmptyTable,}, 
	[57776] = { id = 57776, Voice = '', Text = '我这里有一颗从吉芬带来的魔法珠。', Speaker = 7219, Option = '那是什么', Action = _EmptyTable,}, 
	[57775] = { id = 57775, Voice = '', Text = '你听了他们的愿望了吧。', Speaker = 7219, Option = '是的', Action = _EmptyTable,}, 
	[57774] = { id = 57774, Voice = '', Text = '这样，他以后就是个健健康康的年轻人了，他会有个美好的未来！', Speaker = 7217, Option = '', Action = _EmptyTable,}, 
	[57773] = { id = 57773, Voice = '', Text = '让我用最后的一点灵力，将秀明完全治愈吧！', Speaker = 7217, Option = '', Action = _EmptyTable,}, 
	[57772] = { id = 57772, Voice = '', Text = '冒险者，我求求你，我愿意牺牲我的一切！', Speaker = 7217, Option = '', Action = _EmptyTable,}, 
	[57771] = { id = 57771, Voice = '', Text = '只要能和她在一起，世界末日我都不会害怕！', Speaker = 7218, Option = '明白了', Action = _EmptyTable,}, 
	[57770] = { id = 57770, Voice = '', Text = '来换小樱的存在！我希望她不要消失。', Speaker = 7218, Option = '', Action = _EmptyTable,}, 
	[57769] = { id = 57769, Voice = '', Text = '冒险者，求求你，我愿意牺牲我的一切！', Speaker = 7218, Option = '', Action = _EmptyTable,}, 
	[57768] = { id = 57768, Voice = '', Text = '他们两都有自己的心愿，你愿意聆听他们的愿望吗？', Speaker = 7219, Option = '愿意', Action = _EmptyTable,}, 
	[57767] = { id = 57767, Voice = '', Text = '我们平凡人能做的，很少。', Speaker = 7219, Option = '原来如此', Action = _EmptyTable,}, 
	[57766] = { id = 57766, Voice = '', Text = '没告诉你，是因为我想让你知道，有时候，命运有它自己的走向。', Speaker = 7219, Option = '', Action = _EmptyTable,}, 
	[57765] = { id = 57765, Voice = '', Text = '原来摩根从头到尾都知道！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[57764] = { id = 57764, Voice = '', Text = '她是因为把自己的灵力用来治疗秀明才变得如此虚弱。', Speaker = 7219, Option = '', Action = _EmptyTable,}, 
	[57763] = { id = 57763, Voice = '', Text = '没错，我从见到小樱的第一眼就知道了。', Speaker = 7219, Option = '', Action = _EmptyTable,}, 
	[57762] = { id = 57762, Voice = '', Text = '这是迟早的事情，我从一开始就看出来了。', Speaker = 7219, Option = '', Action = _EmptyTable,}, 
	[57761] = { id = 57761, Voice = '', Text = '冒险者，樱要消失了…', Speaker = 7218, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[57760] = { id = 57760, Voice = '', Text = '你去看看他吧…看看他和那位樱小姐，现在还好吗。', Speaker = 7242, Option = '交给我吧', Action = _EmptyTable,}, 
	[57759] = { id = 57759, Voice = '', Text = '我准备不再干涉他，尊重他自己做的决定。', Speaker = 7242, Option = '好！', Action = _EmptyTable,}, 
	[57758] = { id = 57758, Voice = '', Text = '嗯，秀明通过这件事，应该成长了许多。', Speaker = 7242, Option = '', Action = _EmptyTable,}, 
	[57757] = { id = 57757, Voice = '', Text = '一直以来是我误会那个灵了。', Speaker = 7242, Option = '别自责', Action = _EmptyTable,}, 
	[57756] = { id = 57756, Voice = '', Text = '一切都结束了，似乎一切都像一场梦一样！', Speaker = 7242, Option = '', Action = _EmptyTable,}, 
	[57687] = { id = 57687, Voice = '', Text = '你准备好就进入秀明的结界吧！！', Speaker = 7209, Option = '{准备好了,3},{没准备好,2}', Action = _EmptyTable,}, 
	[57686] = { id = 57686, Voice = '', Text = '千代子已经进去了！我在这里为你们守住出来的通路！', Speaker = 7209, Option = '', Action = _EmptyTable,}, 
	[57685] = { id = 57685, Voice = '', Text = '冒险者，你太慢了！', Speaker = 7209, Option = '抱歉', Action = _EmptyTable,}, 
	[57684] = { id = 57684, Voice = '', Text = '没有时间多说了，现在就动身进入秀明的结界内吧！！', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57683] = { id = 57683, Voice = '', Text = '我们一定要阻止他！', Speaker = 7153, Option = '没错', Action = _EmptyTable,}, 
	[57682] = { id = 57682, Voice = '', Text = '秀明是我的弟弟，我也有一定的责任！', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57681] = { id = 57681, Voice = '', Text = '大地开始震动了！', Speaker = 7153, Option = '怎么办！', Action = _EmptyTable,}, 
	[57680] = { id = 57680, Voice = '', Text = '事情居然会发展成这样！', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57679] = { id = 57679, Voice = '', Text = '外面的女孩子会佩戴什么样的头饰呢？', Speaker = 5131, Option = '', Action = _EmptyTable,}, 
	[57678] = { id = 57678, Voice = '', Text = '虽然樱花饼很好吃，但我也想看看外面的世界。', Speaker = 5131, Option = '', Action = _EmptyTable,}, 
	[57677] = { id = 57677, Voice = '', Text = '每天在酒馆这里跳舞，有点无聊呢！', Speaker = 5131, Option = '', Action = _EmptyTable,}, 
	[57676] = { id = 57676, Voice = '', Text = '我想一辈子待在天水之国。', Speaker = 5130, Option = '', Action = _EmptyTable,}, 
	[57675] = { id = 57675, Voice = '', Text = '真难想象一个没有樱花的世界，是什么样的。', Speaker = 5130, Option = '', Action = _EmptyTable,}, 
	[57674] = { id = 57674, Voice = '', Text = '外乡人，你们那边也会长樱花吗？', Speaker = 5130, Option = '', Action = _EmptyTable,}, 
	[57673] = { id = 57673, Voice = '', Text = '参加樱花小姐选美的时候，吃了它会带来好运哦。', Speaker = 5129, Option = '', Action = _EmptyTable,}, 
	[57672] = { id = 57672, Voice = '', Text = '樱花饼，是我国女孩子都喜欢的小甜点。', Speaker = 5129, Option = '', Action = _EmptyTable,}, 
	[57671] = { id = 57671, Voice = '', Text = '谢谢云雀姐姐，也谢谢你，你看，我跳舞好看吗？', Speaker = 5128, Option = '', Action = _EmptyTable,}, 
	[57670] = { id = 57670, Voice = '', Text = '呀，是云雀姐姐让你来给我送樱花饼吗？', Speaker = 5128, Option = '', Action = _EmptyTable,}, 
	[57669] = { id = 57669, Voice = '', Text = '她们就在酒馆的门前跳舞。', Speaker = 7259, Option = '', Action = _EmptyTable,}, 
	[57668] = { id = 57668, Voice = '', Text = '我制作了一些樱花饼，你可以帮我送给那些练舞的少女吗？', Speaker = 7259, Option = '', Action = _EmptyTable,}, 
	[57667] = { id = 57667, Voice = '', Text = '在我们天水之国呢，樱花盛典的这一天，每个想要成为樱花小姐的人，都应该吃一枚樱花饼哦。', Speaker = 7259, Option = '', Action = _EmptyTable,}, 
	[57666] = { id = 57666, Voice = '', Text = '你知道我们天水之国的风俗吗？', Speaker = 7259, Option = '不清楚', Action = _EmptyTable,}, 
	[57665] = { id = 57665, Voice = '', Text = '我会天天为你祈福的！', Speaker = 7101, Option = '', Action = _EmptyTable,}, 
	[57664] = { id = 57664, Voice = '', Text = '果然，这个世界上，最美的是樱花还有喜欢樱花的人们。', Speaker = 7101, Option = '', Action = _EmptyTable,}, 
	[57663] = { id = 57663, Voice = '', Text = '好呀，如果你能拍摄神社前的樱花和观看樱花的人们，我愿意天天为你祈福。', Speaker = 7101, Option = '', Action = _EmptyTable,}, 
	[57662] = { id = 57662, Voice = '', Text = '有这样的东西……难以置信！', Speaker = 7101, Option = '拍给你看', Action = _EmptyTable,}, 
	[57661] = { id = 57661, Voice = '', Text = '照相机？是什么？', Speaker = 7101, Option = '记录美景', Action = _EmptyTable,}, 
	[57660] = { id = 57660, Voice = '', Text = '真怀念那幅美景呀，还有人们脸上幸福的表情。', Speaker = 7101, Option = '照相机', Action = _EmptyTable,}, 
	[57659] = { id = 57659, Voice = '', Text = '只有我一个人，在樱花花瓣铺成的路上，一直走……', Speaker = 7101, Option = '', Action = _EmptyTable,}, 
	[57658] = { id = 57658, Voice = '', Text = '我走在神社前的花道上，周围都是前来贺喜的人们。', Speaker = 7101, Option = '', Action = _EmptyTable,}, 
	[57657] = { id = 57657, Voice = '', Text = '嗯，看着跃跃欲试的女孩子，让我想起来我当选樱花小姐的时候。', Speaker = 7101, Option = '', Action = _EmptyTable,}, 
	[57656] = { id = 57656, Voice = '', Text = '谢谢你了，年轻人。', Speaker = 7260, Option = '', Action = _EmptyTable,}, 
	[57655] = { id = 57655, Voice = '', Text = '看来这批樱花佳酿一定会非常美味吧。', Speaker = 7260, Option = '', Action = _EmptyTable,}, 
	[57654] = { id = 57654, Voice = '', Text = '哎呀，这么大的花瓣，婆婆好久没见过了。', Speaker = 7260, Option = '', Action = _EmptyTable,}, 
	[57653] = { id = 57653, Voice = '', Text = '最适合制作樱花佳酿了。', Speaker = 7260, Option = '', Action = _EmptyTable,}, 
	[57652] = { id = 57652, Voice = '', Text = '快去吧，靠近神社的那些樱花树是最古老的，花瓣也是最大最多肉的。', Speaker = 7260, Option = '', Action = _EmptyTable,}, 
	[57651] = { id = 57651, Voice = '', Text = '好吧，年轻人总是有许许多多做不完的热闹事，希望你在天水之国玩得开心哦。', Speaker = 7260, Option = '', Action = _EmptyTable,}, 
	[57650] = { id = 57650, Voice = '', Text = '你愿意在神社前的樱花树下，帮婆婆收集一点樱花花瓣吗？', Speaker = 7260, Option = '{愿意,3},{我没时间,4}', Action = _EmptyTable,}, 
	[57649] = { id = 57649, Voice = '', Text = '只是，没人能帮我们收集一些樱花花瓣。', Speaker = 7260, Option = '', Action = _EmptyTable,}, 
	[57648] = { id = 57648, Voice = '', Text = '要多酿制一点樱花佳酿了呢！', Speaker = 7260, Option = '', Action = _EmptyTable,}, 
	[57647] = { id = 57647, Voice = '', Text = '哎呀，今年的游客特别的多呢，我们用来招待游客的酒都快没有了。', Speaker = 7260, Option = '', Action = _EmptyTable,}, 
	[57646] = { id = 57646, Voice = '', Text = '请度过一个愉快的樱花盛典吧。', Speaker = 7261, Option = '', Action = _EmptyTable,}, 
	[57645] = { id = 57645, Voice = '', Text = '天水之国永远欢迎你到来，热心的游客。', Speaker = 7261, Option = '', Action = _EmptyTable,}, 
	[57644] = { id = 57644, Voice = '', Text = '为什么？这些樱花之印对你来说也没有什么用处。', Speaker = 7261, Option = '', Action = _EmptyTable,}, 
	[57643] = { id = 57643, Voice = '', Text = '……嗯，太好了看来你已经收集到12枚樱花之印了，请你把樱花之印交给我吧。', Speaker = 7261, Option = '{好的,12},{不愿意,13}', Action = _EmptyTable,}, 
	[57642] = { id = 57642, Voice = '', Text = '这位游客，你的背包里没有12枚樱花之印，请你收集12枚樱花之印后再来找我。', Speaker = 7261, Option = '', Action = _EmptyTable,}, 
	[57641] = { id = 57641, Voice = '', Text = '嗯，那么继续努力吧。我相信你是我国最优秀的志愿者！', Speaker = 7261, Option = '', Action = _EmptyTable,}, 
	[57640] = { id = 57640, Voice = '', Text = '已经收集到12枚樱花之印了吗？', Speaker = 7261, Option = '{是的,7},{没有,8}', Action = _EmptyTable,}, 
	[57639] = { id = 57639, Voice = '', Text = '集齐[c][ffff00]12枚[-][/c]樱花之印，樱花盛典就可以顺利进行了！', Speaker = 7261, Option = '', Action = _EmptyTable,}, 
	[57638] = { id = 57638, Voice = '', Text = '分担她们的难处，她们会给你一枚樱花之印。', Speaker = 7261, Option = '', Action = _EmptyTable,}, 
	[57637] = { id = 57637, Voice = '', Text = '请你去帮助她们吧。', Speaker = 7261, Option = '', Action = _EmptyTable,}, 
	[57636] = { id = 57636, Voice = '', Text = '很好！选美大赛有三位评委分别是娜娜美、云雀和锦裳婆婆，她们目前都碰见了一些难处。', Speaker = 7261, Option = '', Action = _EmptyTable,}, 
	[57635] = { id = 57635, Voice = '', Text = '唉，缺人可真是麻烦，如果天水之国的人才更多一些就好了。', Speaker = 7261, Option = '', Action = _EmptyTable,}, 
	[57634] = { id = 57634, Voice = '', Text = '你愿意成为我国樱花盛典的志愿者，维持选美比赛的秩序吗？', Speaker = 7261, Option = '{愿意,2},{不愿意,3}', Action = _EmptyTable,}, 
	[57633] = { id = 57633, Voice = '', Text = '因为今年来参观的人，比往年多了不少，选美比赛遇见了许多的困难。', Speaker = 7261, Option = '', Action = _EmptyTable,}, 
	[57632] = { id = 57632, Voice = '', Text = '近期我国正在举办一年一度的樱花小姐选美比赛。', Speaker = 7261, Option = '', Action = _EmptyTable,}, 
	[57611] = { id = 57611, Voice = '', Text = '（小声说）我……我……你怎么会……看见……', Speaker = 5124, Option = '', Action = _EmptyTable,}, 
	[57352] = { id = 57352, Voice = '', Text = '秀明！！你到底做了什么！！！', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57351] = { id = 57351, Voice = '', Text = '你先回去告诉千代子！我在这里打通连接结界的通道！！', Speaker = 7209, Option = '好！', Action = _EmptyTable,}, 
	[57350] = { id = 57350, Voice = '', Text = '我们一定要谨慎！', Speaker = 7209, Option = '', Action = _EmptyTable,}, 
	[57349] = { id = 57349, Voice = '', Text = '这结界入口散发着恶灵的味道和非常浓重的不祥气息！', Speaker = 7209, Option = '是啊', Action = _EmptyTable,}, 
	[57348] = { id = 57348, Voice = '', Text = '你看！！！', Speaker = 7209, Option = '', Action = _EmptyTable,}, 
	[57347] = { id = 57347, Voice = '', Text = '太好了！！结界打开了！', Speaker = 7209, Option = '', Action = _EmptyTable,}, 
	[57341] = { id = 57341, Voice = '', Text = '触碰这块结界柱！', Speaker = 7209, Option = '', Action = _EmptyTable,}, 
	[57340] = { id = 57340, Voice = '', Text = '只要解开这些通道，就能摧毁这个结界柱！', Speaker = 7209, Option = '怎么做！', Action = _EmptyTable,}, 
	[57339] = { id = 57339, Voice = '', Text = '陋的意思是通往异空间的通道、桥梁、枢纽。', Speaker = 7209, Option = '原来如此', Action = _EmptyTable,}, 
