	[57248] = { id = 57248, Voice = '', Text = '没有那个变态摩根，我们更顺利了，他果然是来拖后腿的！', Speaker = 7153, Option = '嘿嘿', Action = _EmptyTable,}, 
	[57247] = { id = 57247, Voice = '', Text = '我们已经把秀明仪式需要用到的阵法都破坏了，剩下只需要慢慢找到他就可以了。', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
