	[57239] = { id = 57239, Voice = '', Text = '不，保护神社是我的使命。', Speaker = 7153, Option = '我来想办法', Action = _EmptyTable,}, 
	[57238] = { id = 57238, Voice = '', Text = '可是，我不能把神社里的灯打碎，我是从家族继承的神社。', Speaker = 7153, Option = '不要纠结', Action = _EmptyTable,}, 
	[57237] = { id = 57237, Voice = '', Text = '我这里出现了一点问题，神社的阵法我已经找到了，在神社左边的灯里。', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57236] = { id = 57236, Voice = '', Text = '你已经解决了其他两个阵法吗？', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57234] = { id = 57234, Voice = '', Text = '樱花树下，四季之印。解！', Speaker = 7149, Option = '', Action = _EmptyTable,}, 
