	[57232] = { id = 57232, Voice = '', Text = '看来，你不是真的喜欢天水之国。', Speaker = 7147, Option = '', Action = _EmptyTable,}, 
	[57231] = { id = 57231, Voice = '', Text = '真是一个奇怪的船长，但是他从来不会耽误时间，对吧。', Speaker = 7147, Option = '', Action = _EmptyTable,}, 
	[57230] = { id = 57230, Voice = '', Text = '你知道谁是天水之国航线的掌舵人吗？', Speaker = 7147, Option = '{卡鲁,15},{武藏重继,16},{夏之助,16},{千代子,16}', Action = _EmptyTable,}, 
	[57229] = { id = 57229, Voice = '', Text = '我很喜欢这里，因为天水之国的樱花在冬天也会开放。', Speaker = 7147, Option = '', Action = _EmptyTable,}, 
	[57228] = { id = 57228, Voice = '', Text = '……不要让秀明做出不可挽回的事情！', Speaker = 7147, Option = '', Action = _EmptyTable,}, 
	[57227] = { id = 57227, Voice = '', Text = '我会解除封印，而你……', Speaker = 7147, Option = '', Action = _EmptyTable,}, 
	[57226] = { id = 57226, Voice = '', Text = '（不再说话，闭上眼睛一动不动）', Speaker = 7147, Option = '', Action = _EmptyTable,}, 
	[57225] = { id = 57225, Voice = '', Text = '击败我，封印自然会解除。', Speaker = 7147, Option = '不行', Action = _EmptyTable,}, 
	[57224] = { id = 57224, Voice = '', Text = '你说得对，但是我不能违背我对朋友做出的承诺。', Speaker = 7147, Option = '', Action = _EmptyTable,}, 
	[57223] = { id = 57223, Voice = '', Text = '……我知道秀明想做什么，他对我们一直很好，我，愿意帮助他。', Speaker = 7147, Option = '不能这样', Action = _EmptyTable,}, 
	[57222] = { id = 57222, Voice = '', Text = '是为了封印而来吗？我没有问题想问你，不会解开封印的。', Speaker = 7147, Option = '', Action = _EmptyTable,}, 
	[57221] = { id = 57221, Voice = '', Text = '问题回答正确，我帮你解除封印。', Speaker = 7146, Option = '', Action = _EmptyTable,}, 
	[57220] = { id = 57220, Voice = '', Text = '你懂得很多啊，油纸糊的雨伞是天水之国的特色雨具，秀明还送了我一把呢。', Speaker = 7146, Option = '', Action = _EmptyTable,}, 
	[57219] = { id = 57219, Voice = '', Text = '你确定吗，如果用这个应该会被淋湿吧！', Speaker = 7146, Option = '', Action = _EmptyTable,}, 
