	[57217] = { id = 57217, Voice = '', Text = '想要解除封印就回答我的问题吧！', Speaker = 7146, Option = '', Action = _EmptyTable,}, 
	[57216] = { id = 57216, Voice = '', Text = '有些时候，秀明和小樱会陪我一起度过这段时间。', Speaker = 7146, Option = '', Action = _EmptyTable,}, 
	[57215] = { id = 57215, Voice = '', Text = '蝉鸣、夜雨、荷花和艳阳组成的夏天，是我最爱的风景……', Speaker = 7146, Option = '', Action = _EmptyTable,}, 
	[57214] = { id = 57214, Voice = '', Text = '什么！解开封印？如果是你的话，我愿意解开封印。', Speaker = 7145, Option = '', Action = _EmptyTable,}, 
	[57213] = { id = 57213, Voice = '', Text = '樱粉，樱粉，樱花的粉色，这个名字真好听，那么这种颜色就叫做樱粉吧。', Speaker = 7145, Option = '', Action = _EmptyTable,}, 
	[57212] = { id = 57212, Voice = '', Text = '我觉得这个名字不太合适啊。', Speaker = 7145, Option = '', Action = _EmptyTable,}, 
	[57211] = { id = 57211, Voice = '', Text = '你看天水之国的樱花是不是很美，这种美妙的颜色，我们应该给它取什么名字呢？', Speaker = 7145, Option = '{月光银,7},{新芽绿,7},{鹅黄,7},{樱粉,8}', Action = _EmptyTable,}, 
	[57210] = { id = 57210, Voice = '', Text = '我……连问题都听不懂……', Speaker = 7142, Option = '', Action = _EmptyTable,}, 
	[57209] = { id = 57209, Voice = '', Text = '真不知道这些神灵，为什么会帮助秀明。', Speaker = 7142, Option = '那你还等什么', Action = _EmptyTable,}, 
	[57208] = { id = 57208, Voice = '', Text = '如果能回答他们的问题，他们也许会愿意解开封印。', Speaker = 7142, Option = '', Action = _EmptyTable,}, 
	[57207] = { id = 57207, Voice = '', Text = '他们四个是樱花城的地藏灵，秀明利用他们的灵力制作了封印。', Speaker = 7142, Option = '', Action = _EmptyTable,}, 
	[57202] = { id = 57202, Voice = '', Text = '我帮你去除上面的障眼法！', Speaker = 7142, Option = '', Action = _EmptyTable,}, 
	[57201] = { id = 57201, Voice = '', Text = '大人，那里有个隐藏的法阵。', Speaker = 7142, Option = '', Action = _EmptyTable,}, 
	[57200] = { id = 57200, Voice = '', Text = '嘤嘤嘤……嘤嘤嘤……', Speaker = 7142, Option = '说人话', Action = _EmptyTable,}, 
	[57199] = { id = 57199, Voice = '', Text = '千代子的式神似乎在这里，问问它有什么新的发现。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[57198] = { id = 57198, Voice = '', Text = '卢西娅把手心里的药瓶递过来，害羞的跳入海水中，回到海底神殿里。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
