	[57178] = { id = 57178, Voice = '', Text = '这三个地方，是神社、神社前的桥梁和樱花林！', Speaker = 7153, Option = '我去看看', Action = _EmptyTable,}, 
	[57177] = { id = 57177, Voice = '', Text = '恐怕，秀明的阵法就布置在天水之国的北边、东边和西边。', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57176] = { id = 57176, Voice = '', Text = '龟壳、龙角和虎皮。这三样东西象征了三个方向。', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57175] = { id = 57175, Voice = '', Text = '这木箱子里装的是咒法需要的材料。', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57174] = { id = 57174, Voice = '', Text = '天水之国樱花树下所有的灵，都将聚集到小樱的体内。', Speaker = 7153, Option = '可怕', Action = _EmptyTable,}, 
	[57173] = { id = 57173, Voice = '', Text = '如果他成功了。', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57172] = { id = 57172, Voice = '', Text = '秀明想要在天水之国进行一场无比庞大的聚灵仪式。', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57171] = { id = 57171, Voice = '', Text = '原来是这样！', Speaker = 7153, Option = '怎么了？', Action = _EmptyTable,}, 
	[57170] = { id = 57170, Voice = '', Text = '让我打开看看……', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57169] = { id = 57169, Voice = '', Text = '这个，是海船运过来的东西，这个跟秀明有什么关系。', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57168] = { id = 57168, Voice = '', Text = '千代子的式神慢慢升到天空中，往神社的方向飞去。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[57167] = { id = 57167, Voice = '', Text = '（式神指着前面的两个箱子）嘤嘤嘤~', Speaker = 7142, Option = '', Action = _EmptyTable,}, 
	[57166] = { id = 57166, Voice = '', Text = '嘤嘤嘤？', Speaker = 7142, Option = '', Action = _EmptyTable,}, 
	[57165] = { id = 57165, Voice = '', Text = '嘤嘤嘤……嘤嘤嘤……嘤嘤嘤……', Speaker = 7142, Option = '居然是嘤嘤怪', Action = _EmptyTable,}, 
	[57164] = { id = 57164, Voice = '', Text = '要小心行事，秀明很谨慎。', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57163] = { id = 57163, Voice = '', Text = '我的式神去了船坞附近，也许你可以在那边找找秀明的踪迹。', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57162] = { id = 57162, Voice = '', Text = '去！', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57160] = { id = 57160, Voice = '', Text = '起！', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57159] = { id = 57159, Voice = '', Text = '哼，让我招个式神，去查一查秀明的踪迹。', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57158] = { id = 57158, Voice = '', Text = '我之前用来跟踪秀明的式神被你们毁掉了，要不然也不会出现这么严重的事情。', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57157] = { id = 57157, Voice = '', Text = '我的式神比他更厉害！', Speaker = 7153, Option = '', Emoji = 25, Action = _EmptyTable,}, 
	[57156] = { id = 57156, Voice = '', Text = '不过~', Speaker = 7153, Option = '什么', Action = _EmptyTable,}, 
	[57155] = { id = 57155, Voice = '', Text = '秀明对巫术的研究非常深，尤其是一些式神之术。', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57154] = { id = 57154, Voice = '', Text = '看来，天水之国的命运只能掌握在你我手中了。', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57153] = { id = 57153, Voice = '', Text = '是吗？又换上女装跳舞去了吗？哼！', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57152] = { id = 57152, Voice = '', Text = '不要让我失望哦~', Speaker = 7140, Option = '', Action = _EmptyTable,}, 
	[57151] = { id = 57151, Voice = '', Text = '你可千万不要随随便便就被伊达秀明解决掉了啊！', Speaker = 7140, Option = '', Action = _EmptyTable,}, 
	[57150] = { id = 57150, Voice = '', Text = '那我就先走一步啦~', Speaker = 7140, Option = '', Action = _EmptyTable,}, 
	[57149] = { id = 57149, Voice = '', Text = '刚说完摩根又拿出了女装和化妆品打扮了起来。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[57148] = { id = 57148, Voice = '', Text = '我先走一步。', Speaker = 7141, Option = '', Action = _EmptyTable,}, 
	[57147] = { id = 57147, Voice = '', Text = '我呢，需要回吉芬取一点材料。', Speaker = 7141, Option = '', Action = _EmptyTable,}, 
	[57146] = { id = 57146, Voice = '', Text = '哎……你别忙着高兴，你需要继续跟伊达秀明周旋。', Speaker = 7141, Option = '', Action = _EmptyTable,}, 
	[57145] = { id = 57145, Voice = '', Text = '我会帮助你的，吉芬的法师不会眼睁睁地看着一位年轻的冒险者这么苦恼。', Speaker = 7141, Option = '太好了！', Action = _EmptyTable,}, 
	[57144] = { id = 57144, Voice = '', Text = '喝点酒，睡会觉，就没时间被人利用了！', Speaker = 7141, Option = '说正事', Action = _EmptyTable,}, 
	[57143] = { id = 57143, Voice = '', Text = '哟，还挺有骨气的~', Speaker = 7141, Option = '', Action = _EmptyTable,}, 
	[57142] = { id = 57142, Voice = '', Text = '跟我一样多喝点酒，多跳点舞，就不会出现这种情况了。', Speaker = 7141, Option = '歪理', Action = _EmptyTable,}, 
	[57141] = { id = 57141, Voice = '', Text = '你只是太年轻啦，容易被人利用而已。', Speaker = 7141, Option = '', Action = _EmptyTable,}, 
	[57140] = { id = 57140, Voice = '', Text = '你没错，有一颗乐于助人的心，从来都是一件好事情。', Speaker = 7141, Option = '', Action = _EmptyTable,}, 
	[57139] = { id = 57139, Voice = '', Text = '别一副愁眉苦脸的样子，当初想要帮助伊达秀明的时候，不是挺坚决吗？', Speaker = 7141, Option = '我错了', Action = _EmptyTable,}, 
	[57138] = { id = 57138, Voice = '', Text = '还是去桥前见我吧，每次在这里跟小姐姐聚会都会被你抓住！', Speaker = 7140, Option = '', Action = _EmptyTable,}, 
	[57137] = { id = 57137, Voice = '', Text = '阿黄，你不要这样，哎呀，真恼火，让我先去换套衣服。', Speaker = 7140, Option = '', Action = _EmptyTable,}, 
	[57136] = { id = 57136, Voice = '', Text = '你又想让我帮忙了吗？喂喂，我也是很忙的，怎么有什么辛苦的累活，都想到我了。', Speaker = 7140, Option = '求求你', Action = _EmptyTable,}, 
	[57135] = { id = 57135, Voice = '', Text = '还有伊达秀明的阴谋？', Speaker = 7140, Option = '', Action = _EmptyTable,}, 
	[57134] = { id = 57134, Voice = '', Text = '所以，你知道了樱灵和伊达秀明的往事。', Speaker = 7140, Option = '', Action = _EmptyTable,}, 
	[57133] = { id = 57133, Voice = '', Text = '是觉得我好欺负吗？', Speaker = 7140, Option = '陈述真相', Action = _EmptyTable,}, 
	[57132] = { id = 57132, Voice = '', Text = '你还过来打扰我跟小姐姐聚会。', Speaker = 7140, Option = '', Action = _EmptyTable,}, 
	[57131] = { id = 57131, Voice = '', Text = '又是你，喂，我已经帮助秀明拿到了书。', Speaker = 7140, Option = '', Action = _EmptyTable,}, 
	[57130] = { id = 57130, Voice = '', Text = '那个叫做摩根的人，虽然他是个女装变态，但我能看出来他的实力强劲。', Speaker = 7165, Option = '', Action = _EmptyTable,}, 
	[57129] = { id = 57129, Voice = '', Text = '我明白，我都明白，你先去找你的朋友吧。', Speaker = 7165, Option = '', Action = _EmptyTable,}, 
	[57128] = { id = 57128, Voice = '', Text = '如果他做了这么充分的准备，事情也许会变得不可挽回！', Speaker = 7165, Option = '不行', Action = _EmptyTable,}, 
	[57127] = { id = 57127, Voice = '', Text = '只是，我原本以为秀明只是为了哄那个灵开心。', Speaker = 7165, Option = '', Action = _EmptyTable,}, 
	[57126] = { id = 57126, Voice = '', Text = '当然！', Speaker = 7165, Option = '', Action = _EmptyTable,}, 
	[57125] = { id = 57125, Voice = '', Text = '我没有干涉他们相处，没想到秀明居然准备使用咒法书里的阵法。', Speaker = 7165, Option = '阻止他', Action = _EmptyTable,}, 
	[57124] = { id = 57124, Voice = '', Text = '我早就知道秀明身边一直有个灵，只是小樱她周身闪着白光，并不是恶灵。', Speaker = 7165, Option = '', Action = _EmptyTable,}, 
	[57123] = { id = 57123, Voice = '', Text = '……秀明，是他的主意吗？', Speaker = 7165, Option = '', Action = _EmptyTable,}, 
	[57122] = { id = 57122, Voice = '', Text = '一定是这个灵鼓动我弟弟，才让他偏离了正道。', Speaker = 7165, Option = '阐述真相', Action = _EmptyTable,}, 
	[57121] = { id = 57121, Voice = '', Text = '你查明小樱和秀明想搞什么鬼了吗？', Speaker = 7165, Option = '', Action = _EmptyTable,}, 
	[57120] = { id = 57120, Voice = '', Text = '小樱已经远去，赶快向千代子说明情况吧！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[57119] = { id = 57119, Voice = '', Text = '越来越多的式神聚集过来，小樱将式神都引开了。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[57118] = { id = 57118, Voice = '', Text = '救救秀明！请你一定要救救秀明！', Speaker = 7179, Option = '', Action = _EmptyTable,}, 
	[57117] = { id = 57117, Voice = '', Text = '秀明的式神，他发现我在这里了，我必须走了。', Speaker = 7179, Option = '', Action = _EmptyTable,}, 
	[57116] = { id = 57116, Voice = '', Text = '救救秀明，只有你们能救他了。', Speaker = 7179, Option = '', Action = _EmptyTable,}, 
	[57115] = { id = 57115, Voice = '', Text = '我不想吞噬天水之国的灵，她们，都是我的朋友。', Speaker = 7179, Option = '', Action = _EmptyTable,}, 
	[57114] = { id = 57114, Voice = '', Text = '你都看见了，不要怪罪秀明。', Speaker = 7179, Option = '', Action = _EmptyTable,}, 
	[57113] = { id = 57113, Voice = '', Text = '你先用这些灵力维持一下，等我从姐姐那里拿到书，我会让你变回原来的样子。', Speaker = 7183, Option = '', Action = _EmptyTable,}, 
	[57112] = { id = 57112, Voice = '', Text = '你好多了，身体没那么虚弱了……', Speaker = 7183, Option = '', Action = _EmptyTable,}, 
	[57111] = { id = 57111, Voice = '', Text = '小樱，你要离开我了吗？', Speaker = 7183, Option = '', Action = _EmptyTable,}, 
	[57110] = { id = 57110, Voice = '', Text = '我不愿意！', Speaker = 7179, Option = '', Action = _EmptyTable,}, 
	[57109] = { id = 57109, Voice = '', Text = '这些都是无意识的灵力，你吸收了，就能恢复了。', Speaker = 7183, Option = '', Action = _EmptyTable,}, 
	[57108] = { id = 57108, Voice = '', Text = '我会补充你的灵力，不会让你离开。', Speaker = 7183, Option = '', Action = _EmptyTable,}, 
	[57107] = { id = 57107, Voice = '', Text = '不要离开我，小樱。', Speaker = 7183, Option = '', Action = _EmptyTable,}, 
	[57106] = { id = 57106, Voice = '', Text = '把一切交给我吧，我会让你恢复的。', Speaker = 7183, Option = '', Action = _EmptyTable,}, 
	[57105] = { id = 57105, Voice = '', Text = '你不能这样做…… ', Speaker = 7179, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[57104] = { id = 57104, Voice = '', Text = '我不接受！', Speaker = 7179, Option = '', Action = _EmptyTable,}, 
	[57103] = { id = 57103, Voice = '', Text = '你不会这么虚弱了。', Speaker = 7183, Option = '', Action = _EmptyTable,}, 
	[57102] = { id = 57102, Voice = '', Text = '有了那本书，我就可以恢复你的灵力。', Speaker = 7183, Option = '', Action = _EmptyTable,}, 
	[57101] = { id = 57101, Voice = '', Text = '发生了什么事情，真的不想告诉我吗？', Speaker = 7182, Option = '', Action = _EmptyTable,}, 
	[57100] = { id = 57100, Voice = '', Text = '真是个笨蛋。', Speaker = 7179, Option = '', Action = _EmptyTable,}, 
	[57099] = { id = 57099, Voice = '', Text = '帮你把裙子提起来，飘下来了。', Speaker = 7182, Option = '', Action = _EmptyTable,}, 
	[57098] = { id = 57098, Voice = '', Text = '你在做什么？ ', Speaker = 7179, Option = '', Emoji = 7, Action = _EmptyTable,}, 
	[57097] = { id = 57097, Voice = '', Text = '我从没听过这种说法！', Speaker = 7182, Option = '', Action = _EmptyTable,}, 
	[57096] = { id = 57096, Voice = '', Text = '我是个灵，变透明是很正常的……', Speaker = 7179, Option = '', Action = _EmptyTable,}, 
	[57095] = { id = 57095, Voice = '', Text = '（颤抖的声音）你怎么，怎么变透明了？', Speaker = 7182, Option = '', Action = _EmptyTable,}, 
	[57094] = { id = 57094, Voice = '', Text = '大概……是生命的鲜活吧。', Speaker = 7179, Option = '', Action = _EmptyTable,}, 
	[57093] = { id = 57093, Voice = '', Text = '有什么好的，花痴女，都蠢死了。', Speaker = 7182, Option = '', Action = _EmptyTable,}, 
	[57092] = { id = 57092, Voice = '', Text = '真好呀。  ', Speaker = 7179, Option = '', Action = _EmptyTable,}, 
	[57091] = { id = 57091, Voice = '', Text = '（举起手想要触碰秀明的脸）我听见有些女孩在讨论你……', Speaker = 7179, Option = '', Action = _EmptyTable,}, 
	[57090] = { id = 57090, Voice = '', Text = '已经长成了能迷倒女孩子的小少年。', Speaker = 7179, Option = '', Action = _EmptyTable,}, 
	[57089] = { id = 57089, Voice = '', Text = '怎么说出这样的话。', Speaker = 7182, Option = '', Action = _EmptyTable,}, 
	[57088] = { id = 57088, Voice = '', Text = '又是一年樱花开，你也有点小大人的样子了啊。', Speaker = 7179, Option = '', Action = _EmptyTable,}, 
	[57087] = { id = 57087, Voice = '', Text = '（指着一个地方）那边……去那边……', Speaker = 7176, Option = '', Action = _EmptyTable,}, 
	[57086] = { id = 57086, Voice = '', Text = '不要伤害秀明，他真的不是坏人。', Speaker = 7176, Option = '', Action = _EmptyTable,}, 
	[57085] = { id = 57085, Voice = '', Text = '我没事，我没有多少力气了。', Speaker = 7176, Option = '', Action = _EmptyTable,}, 
	[57084] = { id = 57084, Voice = '', Text = '秀明的式神……', Speaker = 7176, Option = '', Action = _EmptyTable,}, 
	[57083] = { id = 57083, Voice = '', Text = '糟糕，她们…她们来了。', Speaker = 7176, Option = '什么', Action = _EmptyTable,}, 
	[57082] = { id = 57082, Voice = '', Text = '咳咳……就在这……', Speaker = 7176, Option = '', Action = _EmptyTable,}, 
	[57081] = { id = 57081, Voice = '', Text = '……这里，这里有另外一段回忆。', Speaker = 7176, Option = '', Action = _EmptyTable,}, 
	[57080] = { id = 57080, Voice = '', Text = '怎么会害羞的跑掉了，早知道就装作看不见了。', Speaker = 7180, Option = '', Action = _EmptyTable,}, 
	[57079] = { id = 57079, Voice = '', Text = '啊啊啊！！！！！看得见我！！！！', Speaker = 7176, Option = '', Action = _EmptyTable,}, 
	[57078] = { id = 57078, Voice = '', Text = '（对着小樱眨眼睛）我不会跟别人说的，你可以再亲一下！', Speaker = 7180, Option = '', Action = _EmptyTable,}, 
	[57077] = { id = 57077, Voice = '', Text = '什么？', Speaker = 7176, Option = '', Action = _EmptyTable,}, 
	[57076] = { id = 57076, Voice = '', Text = '……樱花姐姐，你这么喜欢我呀。', Speaker = 7180, Option = '', Action = _EmptyTable,}, 
	[57075] = { id = 57075, Voice = '', Text = '让姐姐亲一下。', Speaker = 7176, Option = '', Action = _EmptyTable,}, 
	[57074] = { id = 57074, Voice = '', Text = '呐，谢谢……', Speaker = 7176, Option = '', Action = _EmptyTable,}, 
	[57073] = { id = 57073, Voice = '', Text = '这个小孩还蛮可爱的嘛~', Speaker = 7176, Option = '', Action = _EmptyTable,}, 
	[57072] = { id = 57072, Voice = '', Text = '收！', Speaker = 7180, Option = '', Action = _EmptyTable,}, 
	[57071] = { id = 57071, Voice = '', Text = '两个傻蛋。', Speaker = 7180, Option = '', Action = _EmptyTable,}, 
	[57070] = { id = 57070, Voice = '', Text = '你…你…你怎么跑这么快，等等我。', Speaker = 7177, Option = '', Action = _EmptyTable,}, 
	[57069] = { id = 57069, Voice = '', Text = '快溜吧，直也，我先跑了。', Speaker = 7178, Option = '', Action = _EmptyTable,}, 
	[57068] = { id = 57068, Voice = '', Text = '我不摘樱花了，让这两只手走吧。', Speaker = 7177, Option = '', Action = _EmptyTable,}, 
	[57067] = { id = 57067, Voice = '', Text = '不要，不要，啊……', Speaker = 7178, Option = '', Action = _EmptyTable,}, 
	[57066] = { id = 57066, Voice = '', Text = '那么，我也帮你们找些玩伴吧~ ', Speaker = 7180, Option = '', Emoji = 5, Action = _EmptyTable,}, 
	[57065] = { id = 57065, Voice = '', Text = '是吗？', Speaker = 7180, Option = '', Action = _EmptyTable,}, 
	[57064] = { id = 57064, Voice = '', Text = '没人喜欢跟你一起玩，回家找你姐姐去吧，像个女孩子一样学绣花去吧。', Speaker = 7177, Option = '', Action = _EmptyTable,}, 
	[57063] = { id = 57063, Voice = '', Text = '变态秀明又来了，有完没完。', Speaker = 7178, Option = '', Action = _EmptyTable,}, 
	[57062] = { id = 57062, Voice = '', Text = '喂，你们两个捣蛋鬼，不要摘那只樱花。', Speaker = 7180, Option = '', Action = _EmptyTable,}, 
	[57061] = { id = 57061, Voice = '', Text = '只有樱花，陪着我……', Speaker = 7176, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[57060] = { id = 57060, Voice = '', Text = '根本听不见我，也看不见我……', Speaker = 7176, Option = '', Action = _EmptyTable,}, 
	[57059] = { id = 57059, Voice = '', Text = '等我一会嘛，哈哈哈哈……', Speaker = 7177, Option = '', Action = _EmptyTable,}, 
	[57058] = { id = 57058, Voice = '', Text = '你蹲下来呀，太高了，我站不稳！', Speaker = 7178, Option = '', Action = _EmptyTable,}, 
	[57057] = { id = 57057, Voice = '', Text = '听见了吗？', Speaker = 7176, Option = '', Action = _EmptyTable,}, 
	[57056] = { id = 57056, Voice = '', Text = '你们这群小坏蛋，不要摘我的樱花树。', Speaker = 7176, Option = '', Action = _EmptyTable,}, 
	[57055] = { id = 57055, Voice = '', Text = '好，我爬到你身上。', Speaker = 7178, Option = '', Action = _EmptyTable,}, 
	[57054] = { id = 57054, Voice = '', Text = '喂！正和，我把你抱起来，你上去摘那只樱花。', Speaker = 7177, Option = '', Action = _EmptyTable,}, 
	[57053] = { id = 57053, Voice = '', Text = '我带你去看我和秀明的往事，我真的想所有的事情都没有发生。', Speaker = 7176, Option = '', Action = _EmptyTable,}, 
	[57052] = { id = 57052, Voice = '', Text = '不要，我没有恶意。', Speaker = 7176, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[57051] = { id = 57051, Voice = '', Text = '（转头看着你）收服她！', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57050] = { id = 57050, Voice = '', Text = '我很早就看见你在秀明身边了，但我以为你是个善良的灵，所以没有干预你们。', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57049] = { id = 57049, Voice = '', Text = '你以为我们会相信你吗？', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57048] = { id = 57048, Voice = '', Text = '（越来越虚弱）阻止秀明，不要让他聚集灵力……', Speaker = 7176, Option = '', Action = _EmptyTable,}, 
	[57047] = { id = 57047, Voice = '', Text = '是，她们是我的朋友。', Speaker = 7176, Option = '', Action = _EmptyTable,}, 
	[57046] = { id = 57046, Voice = '', Text = '但是你们的实力太强了，击败了我的朋友。', Speaker = 7176, Option = '樱花鬼女？', Action = _EmptyTable,}, 
	[57045] = { id = 57045, Voice = '', Text = '（满脸忧伤地看着你）我甚至还请我的朋友们拦住你和摩根。', Speaker = 7176, Option = '', Action = _EmptyTable,}, 
	[57044] = { id = 57044, Voice = '', Text = '我不希望他这样做的。', Speaker = 7176, Option = '', Action = _EmptyTable,}, 
	[57043] = { id = 57043, Voice = '', Text = '为了你，他居然要将天水之国的灵全聚集起来。', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57042] = { id = 57042, Voice = '', Text = '我弟弟从小就很叛逆，但是他从来没有做过这样的事情！', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57041] = { id = 57041, Voice = '', Text = '我的灵力越来越弱，已经快要消失了，他为了补充我的灵力，才聚集全国的灵。', Speaker = 7176, Option = '', Action = _EmptyTable,}, 
	[57040] = { id = 57040, Voice = '', Text = '他只是为了我。', Speaker = 7176, Option = '', Action = _EmptyTable,}, 
	[57039] = { id = 57039, Voice = '', Text = '不要……伤害秀明，我们…… ', Speaker = 7176, Option = '质问', Action = _EmptyTable,}, 
	[57038] = { id = 57038, Voice = '', Text = '小樱看上去有些犹豫，没有攻击，好像有话想说。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[57037] = { id = 57037, Voice = '', Text = '我，我不想与你们战斗……', Speaker = 7176, Option = '', Action = _EmptyTable,}, 
	[57036] = { id = 57036, Voice = '', Text = '抓住她！', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57035] = { id = 57035, Voice = '', Text = '秀明和我……他不是想控制我……', Speaker = 7176, Option = '', Action = _EmptyTable,}, 
	[57034] = { id = 57034, Voice = '', Text = '不，并不是这样的。', Speaker = 7176, Option = '', Action = _EmptyTable,}, 
	[57033] = { id = 57033, Voice = '', Text = '而且，只听从秀明的吩咐。 ', Speaker = 7153, Option = '必须阻止他', Action = _EmptyTable,}, 
	[57032] = { id = 57032, Voice = '', Text = '没有意识，却充满了破坏力。', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57031] = { id = 57031, Voice = '', Text = '因为小樱根本承受不了这么多的灵力，她会变成一股恐怖的力量。', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57030] = { id = 57030, Voice = '', Text = '必须阻止他。', Speaker = 7153, Option = ' 为什么', Action = _EmptyTable,}, 
	[57029] = { id = 57029, Voice = '', Text = '秀明……他是想将这些灵的灵力聚集到小樱的身上！', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57028] = { id = 57028, Voice = '', Text = '它里面记载了我们家族的聚灵法术，这个法术可以聚集一个区域的灵。 ', Speaker = 7153, Option = '这？', Action = _EmptyTable,}, 
	[57027] = { id = 57027, Voice = '', Text = '这本书的作用可不止提高灵力啊。', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[57026] = { id = 57026, Voice = '', Text = '秀明……他想要做什么？', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[56922] = { id = 56922, Voice = '', Text = '摩根竟然就这样丢下你，独自一个人跑了。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[56921] = { id = 56921, Voice = '', Text = '你是……', Speaker = 5137, Option = '', Action = _EmptyTable,}, 
	[56836] = { id = 56836, Voice = '', Text = '那么作为代替，还有别的事情需要你们帮忙去做。', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56835] = { id = 56835, Voice = '', Text = '没什么！', Speaker = 7151, Option = '', Emoji = 17, Action = _EmptyTable,}, 
	[56834] = { id = 56834, Voice = '', Text = '真是有点后悔当初叫你们两帮忙！', Speaker = 7151, Option = '你说什么？', Action = _EmptyTable,}, 
	[56833] = { id = 56833, Voice = '', Text = '伊达秀明的脸上流露出了从未有过的嫌弃和厌恶。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[56832] = { id = 56832, Voice = '', Text = '什么？你们暴露了？所以就没拿到那卷轴？？', Speaker = 7151, Option = '是的', Action = _EmptyTable,}, 
	[56831] = { id = 56831, Voice = '', Text = '顺其自然吧，先回去告诉秀明这件事吧。', Speaker = 5132, Option = '', Action = _EmptyTable,}, 
	[56830] = { id = 56830, Voice = '', Text = '看来我们说什么都是没有用的了。', Speaker = 5132, Option = '怎么办？', Action = _EmptyTable,}, 
	[56829] = { id = 56829, Voice = '', Text = '我自然有我的理由。', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56828] = { id = 56828, Voice = '', Text = '既然你们是秀明派来的，这份卷轴自然不能给你们。', Speaker = 5138, Option = '为什么！', Action = _EmptyTable,}, 
	[56827] = { id = 56827, Voice = '', Text = '你们不要用这样的眼神看着我。', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56826] = { id = 56826, Voice = '', Text = '那就是绝对不能把这个给我弟弟，这不是他该得到的东西！', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56825] = { id = 56825, Voice = '', Text = '刚才不是说到借给你们咒法卷轴的条件吗？', Speaker = 5138, Option = '是的', Action = _EmptyTable,}, 
	[56824] = { id = 56824, Voice = '', Text = '你朋友手上的墨水都化开了，不用再演了。', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56823] = { id = 56823, Voice = '', Text = '你们俩是受我弟弟秀明所托才来的吧。', Speaker = 5138, Option = '{坦白,2},{否认,3}', Action = _EmptyTable,}, 
	[56822] = { id = 56822, Voice = '', Text = '你们实在是不需要再把这些拙劣的演技放在台面上了。', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56821] = { id = 56821, Voice = '', Text = '你们俩…其实不是巫女的后代吧。', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56820] = { id = 56820, Voice = '', Text = '好吧…你们也不需要知道这么多。', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56819] = { id = 56819, Voice = '', Text = '知道这意味着什么吗…', Speaker = 5138, Option = '不知道', Action = _EmptyTable,}, 
	[56818] = { id = 56818, Voice = '', Text = '灵火居然在白天出现！？这可不是什么吉兆…', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56817] = { id = 56817, Voice = '', Text = '哇！！又是什么鬼东西…好可怕…', Speaker = 5132, Option = '', Emoji = 18, Action = _EmptyTable,}, 
	[56816] = { id = 56816, Voice = '', Text = '好，我的条件就是…', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56815] = { id = 56815, Voice = '', Text = '果然是两只蟑螂，我没什么好说的了，世界要完蛋了…我死了…', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56814] = { id = 56814, Voice = '', Text = '你就说你答不答应吧？', Speaker = 5138, Option = '{答应,2},{不答应,3}', Action = _EmptyTable,}, 
	[56813] = { id = 56813, Voice = '', Text = '可以…但我有一个条件。', Speaker = 5138, Option = '还有条件？', Action = _EmptyTable,}, 
	[56812] = { id = 56812, Voice = '', Text = '对，咒法卷轴，你们要借阅是吗？', Speaker = 5138, Option = '是的', Action = _EmptyTable,}, 
	[56811] = { id = 56811, Voice = '', Text = '又正常了！？', Speaker = 5132, Option = '咒法卷轴', Emoji = 1, Action = _EmptyTable,}, 
	[56810] = { id = 56810, Voice = '', Text = '好吧，言归正传。', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56809] = { id = 56809, Voice = '', Text = '我觉得她疯了。', Speaker = 5132, Option = '', Action = _EmptyTable,}, 
	[56808] = { id = 56808, Voice = '', Text = '生活没有了希望，这个世界要完蛋了！', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56807] = { id = 56807, Voice = '', Text = '为什么！！为什么！！', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56806] = { id = 56806, Voice = '', Text = '啊啊是啊！！这个生八桥的内馅居然不是豆沙的！！反而负面情绪直线上升啊！！', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56805] = { id = 56805, Voice = '', Text = '这种笨蛋都能说对的问题，你们要是答不对，是看不懂那些咒法卷轴的！', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56804] = { id = 56804, Voice = '', Text = '负面属性一共上升了多少？', Speaker = 5138, Option = '{十六,4},{十七,4},{十八,4},{十九,5}', Action = _EmptyTable,}, 
	[56803] = { id = 56803, Voice = '', Text = '正面属性一共上升了多少？', Speaker = 5138, Option = '{三,2},{四,2},{五,3},{六,2}', Action = _EmptyTable,}, 
	[56802] = { id = 56802, Voice = '', Text = '负面参数在不断上升啊喂！！！', Speaker = 5132, Option = '', Action = _EmptyTable,}, 
	[56801] = { id = 56801, Voice = '', Text = '绝望度上升八！', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56800] = { id = 56800, Voice = '', Text = '疲劳度上升六！', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56799] = { id = 56799, Voice = '', Text = '无力度上升五！', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56798] = { id = 56798, Voice = '', Text = '速度上升二！', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56797] = { id = 56797, Voice = '', Text = '力度上升三！', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56796] = { id = 56796, Voice = '', Text = '啪啦啦啦啦啪啪，我感觉我体内的能量在恢复和上升！', Speaker = 5138, Option = '？？', Action = _EmptyTable,}, 
	[56795] = { id = 56795, Voice = '', Text = '当当当当当！诅咒解开了。', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56794] = { id = 56794, Voice = '', Text = '怕了你们了，我卖给你们，给…这是今天最后两份…', Speaker = 5118, Option = '', Action = _EmptyTable,}, 
	[56793] = { id = 56793, Voice = '', Text = '你们…你们…要尊老爱幼…不要动粗，凡事好商量。', Speaker = 5118, Option = '', Action = _EmptyTable,}, 
	[56792] = { id = 56792, Voice = '', Text = '臭老太婆！这么多排队的人太麻烦了，不如把他们都打跑吧！', Speaker = 5132, Option = '', Action = _EmptyTable,}, 
	[56791] = { id = 56791, Voice = '', Text = '不过等一下还有没有剩，就不知道了。', Speaker = 5118, Option = '', Action = _EmptyTable,}, 
	[56790] = { id = 56790, Voice = '', Text = '没看到后面这么多人排队吗！！等一下再来吧！', Speaker = 5118, Option = '', Action = _EmptyTable,}, 
	[56789] = { id = 56789, Voice = '', Text = '真是麻烦，那就给她买一些樱花生八桥吧！看她到时候还怎么敷衍我们。', Speaker = 5132, Option = '', Action = _EmptyTable,}, 
	[56788] = { id = 56788, Voice = '', Text = '感觉这大姐只是肚子饿了而已吧。', Speaker = 5132, Option = '好像是', Emoji = 10, Action = _EmptyTable,}, 
	[56787] = { id = 56787, Voice = '', Text = '我怎么觉得她耍无赖的时候跟我有点像！？', Speaker = 5132, Option = '', Action = _EmptyTable,}, 
	[56786] = { id = 56786, Voice = '', Text = '我…我真的中了诅咒…樱花生八桥…快给我樱花生八桥…', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56785] = { id = 56785, Voice = '', Text = '她吐我口水…', Speaker = 5132, Option = '', Action = _EmptyTable,}, 
	[56784] = { id = 56784, Voice = '', Text = '啊啊啊啊！！', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56783] = { id = 56783, Voice = '', Text = '看来她是在装疯卖傻！', Speaker = 0, Option = '{摇晃她,4},{让摩根亲她,5}', Action = _EmptyTable,}, 
	[56782] = { id = 56782, Voice = '', Text = '对，我中了诅咒，我是谁我在哪儿？咒法卷轴是什么…', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56781] = { id = 56781, Voice = '', Text = '别和我说话，我中了诅咒…', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56780] = { id = 56780, Voice = '', Text = '我感觉眼前有点黑！！', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56779] = { id = 56779, Voice = '', Text = '摩根向千代子展示了他手心的结印。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[56778] = { id = 56778, Voice = '', Text = '我就是巫女继承人嘛小姐姐~~~', Speaker = 5132, Option = '', Emoji = 2, Action = _EmptyTable,}, 
	[56777] = { id = 56777, Voice = '', Text = '况且咒法典籍只有巫女继承人可以翻阅。', Speaker = 5138, Option = '', Action = _EmptyTable,}, 
	[56776] = { id = 56776, Voice = '', Text = '抱歉，那不是你们这种蟑螂能读得懂的。', Speaker = 5138, Option = '', Emoji = 7, Action = _EmptyTable,}, 
	[56775] = { id = 56775, Voice = '', Text = '什么？？你说你们要借阅咒法卷轴？', Speaker = 5138, Option = '是的', Emoji = 7, Action = _EmptyTable,}, 
	[56774] = { id = 56774, Voice = '', Text = '樱花小姐选美大赛就在隔壁，麻烦你把这个人带走。', Speaker = 5138, Option = '说明来意', Action = _EmptyTable,}, 
	[56773] = { id = 56773, Voice = '', Text = '？？这人你认识？？', Speaker = 5138, Option = '', Emoji = 7, Action = _EmptyTable,}, 
	[56772] = { id = 56772, Voice = '', Text = '好了，我们该办正事了，我去找巫女小姐姐了！！先走一步！', Speaker = 5132, Option = '', Action = _EmptyTable,}, 
	[56771] = { id = 56771, Voice = '', Text = '讨厌鬼！不要耍流氓啦~', Speaker = 5132, Option = '', Action = _EmptyTable,}, 
	[56770] = { id = 56770, Voice = '', Text = '明明被我迷住了，真不坦率啊年轻人。', Speaker = 5132, Option = '', Action = _EmptyTable,}, 
	[56769] = { id = 56769, Voice = '', Text = '我看起来怎么样~~', Speaker = 5132, Option = '{不怎么样,2},{很美,3}', Action = _EmptyTable,}, 
	[56768] = { id = 56768, Voice = '', Text = '少女身什么的变给你看啦~~耶~~', Speaker = 5133, Option = '', Emoji = 2, Action = _EmptyTable,}, 
	[56767] = { id = 56767, Voice = '', Text = '凉凉的，居然有点舒服。', Speaker = 5133, Option = '', Action = _EmptyTable,}, 
	[56766] = { id = 56766, Voice = '', Text = '好了，用墨汁在摩根先生手心画个三角形！', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56765] = { id = 56765, Voice = '', Text = '这么多？！用不了这么多！', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56764] = { id = 56764, Voice = '', Text = '樱花树的树干里可以取到许多树浆来作墨水~！', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56763] = { id = 56763, Voice = '', Text = '用墨水画一个就好了！', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56762] = { id = 56762, Voice = '', Text = '所以我想了一招，让摩根先生假扮巫女继承人去借咒法卷轴，姐姐一定会同意的。', Speaker = 7151, Option = '要怎么做？', Action = _EmptyTable,}, 
	[56761] = { id = 56761, Voice = '', Text = '是个小小的三角形纹案！', Speaker = 7151, Option = '原来如此', Action = _EmptyTable,}, 
	[56760] = { id = 56760, Voice = '', Text = '是的，我姐姐出生时，手上有巫女正统继承人的结印。', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56759] = { id = 56759, Voice = '', Text = '再加上这份卷轴只有巫女正统继承人才能查看…', Speaker = 7151, Option = '正统继承人？', Action = _EmptyTable,}, 
	[56758] = { id = 56758, Voice = '', Text = '因为她傲慢，自命清高，从小就不把我放在眼里。', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56757] = { id = 56757, Voice = '', Text = '可我去拜托过她好几回，她就是不愿意将卷轴借给我。', Speaker = 7151, Option = '为什么？', Action = _EmptyTable,}, 
	[56756] = { id = 56756, Voice = '', Text = '所以由她掌管神社这份重要的咒法卷轴。', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56755] = { id = 56755, Voice = '', Text = '姐姐千代子是伊达家族的正统巫女继承人，顺理成章的变成了神社的巫女。', Speaker = 7151, Option = '然后呢？', Action = _EmptyTable,}, 
	[56754] = { id = 56754, Voice = '', Text = '是的，之前没有告诉你，我们伊达家族世世代代都是为神社服务的。', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56753] = { id = 56753, Voice = '', Text = '这卷卷轴现在在我姐姐千代子那里。', Speaker = 7151, Option = '姐姐？', Action = _EmptyTable,}, 
	[56752] = { id = 56752, Voice = '', Text = '小樱似乎想开口说些什么，但欲言又止。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[56751] = { id = 56751, Voice = '', Text = '对，那里面有能帮我恢复法力的咒法，也能帮助我看见小樱。', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56750] = { id = 56750, Voice = '', Text = '我的时间不多了，你们得帮我弄到神社里的那本咒法卷轴。', Speaker = 7151, Option = '咒法卷轴？', Action = _EmptyTable,}, 
	[56749] = { id = 56749, Voice = '', Text = '我知道大家都很想我，我来了~', Speaker = 5133, Option = '', Action = _EmptyTable,}, 
	[56748] = { id = 56748, Voice = '', Text = '很高兴你的导师…呃…前辈？终于改变主意了。', Speaker = 7151, Option = '是的', Action = _EmptyTable,}, 
	[56684] = { id = 56684, Voice = '', Text = '只有恢复我的灵力，我才能永恒的和小樱在一起！', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56683] = { id = 56683, Voice = '', Text = '我想，也许是因为我的灵力变弱了。', Speaker = 7151, Option = '那该怎么办', Action = _EmptyTable,}, 
	[56682] = { id = 56682, Voice = '', Text = '甚至，活生生地看她在我眼前变得透明。', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56681] = { id = 56681, Voice = '', Text = '我，有时候，会见不到小樱。', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56680] = { id = 56680, Voice = '', Text = '是啊，可最近却发生了一些奇怪的现象。', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56679] = { id = 56679, Voice = '', Text = '就这样，我跟小樱度过了悠长的时光。', Speaker = 7151, Option = '真美', Action = _EmptyTable,}, 
	[56678] = { id = 56678, Voice = '', Text = '这里的其他人都见不到小樱，只有我和姐姐，身体里有巫师的灵力。', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56677] = { id = 56677, Voice = '', Text = '她就是现在这个样子，在樱花之下起舞，我第一次感觉到什么是悸动。', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56676] = { id = 56676, Voice = '', Text = '在我9岁的那个夏天，我第一次见到了小樱。', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56675] = { id = 56675, Voice = '', Text = '真的吗？那真是太好了。', Speaker = 7151, Option = '说吧', Action = _EmptyTable,}, 
	[56674] = { id = 56674, Voice = '', Text = '而我是一个人……', Speaker = 7151, Option = '他会帮忙', Action = _EmptyTable,}, 
	[56673] = { id = 56673, Voice = '', Text = '……我可以理解，毕竟小樱是一个灵。', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56672] = { id = 56672, Voice = '', Text = '摩根先生……没有跟你同行，他不愿意牵涉到其中吗？', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56671] = { id = 56671, Voice = '', Text = '你去问问那位喜欢灵的少年，到底在烦恼些什么吧。', Speaker = 5133, Option = '', Action = _EmptyTable,}, 
	[56670] = { id = 56670, Voice = '', Text = '我可没说我不帮他，你完成了我的任务，我自然会遵守我的承诺。', Speaker = 5133, Option = '', Action = _EmptyTable,}, 
	[56669] = { id = 56669, Voice = '', Text = '既然，你明白了，那我们去找伊达秀明吧。', Speaker = 5133, Option = '为什么？', Action = _EmptyTable,}, 
	[56668] = { id = 56668, Voice = '', Text = '那就不应该把事情交给另外一个人。', Speaker = 5133, Option = '……我明白了', Emoji = 17, Action = _EmptyTable,}, 
	[56667] = { id = 56667, Voice = '', Text = '看来你终于明白了，如果希望帮助别人。', Speaker = 5133, Option = '', Action = _EmptyTable,}, 
	[56666] = { id = 56666, Voice = '', Text = '……', Speaker = 5133, Option = '', Action = _EmptyTable,}, 
	[56665] = { id = 56665, Voice = '', Text = '什么？', Speaker = 5133, Option = '我自己帮助他', Action = _EmptyTable,}, 
	[56664] = { id = 56664, Voice = '', Text = '做的很好，我们继续下一个任务。', Speaker = 5133, Option = '我拒绝', Action = _EmptyTable,}, 
	[56663] = { id = 56663, Voice = '', Text = '你已经有了一点风范了！', Speaker = 5133, Option = '', Action = _EmptyTable,}, 
	[56662] = { id = 56662, Voice = '', Text = '那么，请你不要打扰优舞可小姐哦。', Speaker = 5137, Option = '', Action = _EmptyTable,}, 
	[56661] = { id = 56661, Voice = '', Text = '看来，好像不太愿意啊……', Speaker = 5137, Option = '', Action = _EmptyTable,}, 
	[56660] = { id = 56660, Voice = '', Text = '要不要跟我回家呢？', Speaker = 5137, Option = '喵喵喵？', Action = _EmptyTable,}, 
	[56659] = { id = 56659, Voice = '', Text = '（慈祥地看着你）小猫咪，你找不到回家的路了吗？', Speaker = 5137, Option = '喵~喵~', Action = _EmptyTable,}, 
	[56658] = { id = 56658, Voice = '', Text = '我先走了，如果还有什么问题，我会过来的！', Speaker = 5137, Option = '', Action = _EmptyTable,}, 
	[56657] = { id = 56657, Voice = '', Text = '（大声）优舞可小姐，只是一只小猫咪，你不要害怕！', Speaker = 5137, Option = '', Action = _EmptyTable,}, 
	[56656] = { id = 56656, Voice = '', Text = '原来只是一只小猫咪呀，还以为又有变态呢！', Speaker = 5137, Option = '', Action = _EmptyTable,}, 
