	[56647] = { id = 56647, Voice = '', Text = '要不然把你变成猫咪吧，你去吸引外面人的注意力。', Speaker = 5133, Option = '', Action = _EmptyTable,}, 
	[56646] = { id = 56646, Voice = '', Text = '不要抱怨，这是我交给你的任务，如果你做到了，我就帮伊达秀明。', Speaker = 5133, Option = '', Action = _EmptyTable,}, 
