	[56638] = { id = 56638, Voice = '', Text = '嘘……你轻一点，别吵到里面了！', Speaker = 5133, Option = '', Action = _EmptyTable,}, 
	[56637] = { id = 56637, Voice = '', Text = '跟上我，我们走！', Speaker = 5133, Option = '', Action = _EmptyTable,}, 
	[56636] = { id = 56636, Voice = '', Text = '何况，优舞可是樱花盛典最热门的女孩，樱花小姐最有力的竞争者。', Speaker = 5133, Option = '', Emoji = 28, Action = _EmptyTable,}, 
	[56635] = { id = 56635, Voice = '', Text = '这就对了，求人就应该有求人的觉悟！', Speaker = 5133, Option = '', Action = _EmptyTable,}, 
	[56634] = { id = 56634, Voice = '', Text = '伊达秀明和那个樱之灵，你还想帮助他们吗？', Speaker = 5133, Option = '……好吧', Action = _EmptyTable,}, 
	[56633] = { id = 56633, Voice = '', Text = '是吗？', Speaker = 5133, Option = '', Emoji = 5, Action = _EmptyTable,}, 
	[56632] = { id = 56632, Voice = '', Text = '斐扬的美酒果然容易醉……你！既然都已经来了，就跟我一起去吧。', Speaker = 5133, Option = '我不去', Action = _EmptyTable,}, 
	[56631] = { id = 56631, Voice = '', Text = '（小声地说）好不容易一个人，可以偷看优舞可洗澡了！', Speaker = 5133, Option = '什么？！', Action = _EmptyTable,}, 
