	[56629] = { id = 56629, Voice = '', Text = '你怎么过来了！', Speaker = 5133, Option = '我帮助你', Emoji = 1, Action = _EmptyTable,}, 
	[56628] = { id = 56628, Voice = '', Text = '摩根可能是沿着这条路，去了靠近船坞的地方，往前面找一找吧。', Speaker = 5135, Option = '', Action = _EmptyTable,}, 
	[56627] = { id = 56627, Voice = '', Text = '摩根自说自话地走了，留下满地的樱花和一坛斐扬的美酒。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[56626] = { id = 56626, Voice = '', Text = '你呢，就在这里喝酒，我要去那边有点事情。', Speaker = 5133, Option = '', Action = _EmptyTable,}, 
	[56625] = { id = 56625, Voice = '', Text = '喝，多喝点，哈哈哈哈哈……', Speaker = 5133, Option = '', Action = _EmptyTable,}, 
	[56624] = { id = 56624, Voice = '', Text = '虽然有种被摩根戏耍的感觉，但坐在樱花树下，品尝着美酒让人有种时间变慢的错觉……', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[56623] = { id = 56623, Voice = '', Text = '[c][ffff00][PlayerName][-][/c]，坐在这里。', Speaker = 5133, Option = '', Action = _EmptyTable,}, 
	[56622] = { id = 56622, Voice = '', Text = '来来来……', Speaker = 5133, Option = '', Action = _EmptyTable,}, 
	[56621] = { id = 56621, Voice = '', Text = '我交给你的任务，我说了算，喝酒就是最重要的任务！', Speaker = 5133, Option = '', Action = _EmptyTable,}, 
	[56620] = { id = 56620, Voice = '', Text = '走走走，我们找个樱花树，一起去饮酒吧！', Speaker = 5133, Option = '任务！', Action = _EmptyTable,}, 
	[56619] = { id = 56619, Voice = '', Text = '不要害羞嘛，人生就应该一边喝酒，一边……嘿嘿嘿。', Speaker = 5133, Option = '', Action = _EmptyTable,}, 
	[56618] = { id = 56618, Voice = '', Text = '你，会喝酒吧。', Speaker = 5133, Option = '{我不会,2},{……,2},{不想喝,2}', Action = _EmptyTable,}, 
	[56617] = { id = 56617, Voice = '', Text = '（一脸满足）嗯，我已经闻到了酒香。', Speaker = 5133, Option = '', Action = _EmptyTable,}, 
	[56616] = { id = 56616, Voice = '', Text = '这坛酒送给你了。', Speaker = 3070, Option = '', Action = _EmptyTable,}, 
	[56615] = { id = 56615, Voice = '', Text = '（红娘摇摇头）嗯……我不会难过的，我会照顾好家丁，让妹妹不再为我们担心的。', Speaker = 3070, Option = '', Action = _EmptyTable,}, 
	[56614] = { id = 56614, Voice = '', Text = '没想到，是他的妹妹……', Speaker = 3070, Option = '别难过', Action = _EmptyTable,}, 
	[56613] = { id = 56613, Voice = '', Text = '我从没见到过他说的鬼魂，一直以为他只是想喝酒，在找借口。', Speaker = 3070, Option = '', Action = _EmptyTable,}, 
	[56612] = { id = 56612, Voice = '', Text = '原来他是因为害怕鬼魂才喝酒的，他跟我说了很多次。', Speaker = 3070, Option = '', Action = _EmptyTable,}, 
	[56611] = { id = 56611, Voice = '', Text = '我还需要值班，请你帮我告诉红娘，我以后会少喝一点酒的……', Speaker = 3056, Option = '', Action = _EmptyTable,}, 
	[56610] = { id = 56610, Voice = '', Text = '我会多去看看她的。', Speaker = 3056, Option = '', Action = _EmptyTable,}, 
	[56609] = { id = 56609, Voice = '', Text = '……是我早逝的妹妹吗？原来她这么不放心我。', Speaker = 3056, Option = '', Action = _EmptyTable,}, 
	[56608] = { id = 56608, Voice = '', Text = '我要回我自己的世界了。', Speaker = 5134, Option = '', Action = _EmptyTable,}, 
	[56607] = { id = 56607, Voice = '', Text = '看他现在过得不错，我就放心了。', Speaker = 5134, Option = '', Action = _EmptyTable,}, 
	[56606] = { id = 56606, Voice = '', Text = '这样啊……我只是想见见哥哥……', Speaker = 5134, Option = '', Action = _EmptyTable,}, 
	[56605] = { id = 56605, Voice = '', Text = '我很不放心他的生活，很想看看他现在过得怎么样。', Speaker = 5134, Option = '你吓到他了', Action = _EmptyTable,}, 
	[56604] = { id = 56604, Voice = '', Text = '家丁……是我的哥哥，我知道他结婚了。', Speaker = 5134, Option = '', Action = _EmptyTable,}, 
	[56603] = { id = 56603, Voice = '', Text = '如果，如果不喝酒，我完全睡不着呀。', Speaker = 3056, Option = '', Action = _EmptyTable,}, 
	[56602] = { id = 56602, Voice = '', Text = '我也不想饮酒，可每天晚上都有一个鬼魂在我家附近，把我吓个半死。', Speaker = 3056, Option = '', Action = _EmptyTable,}, 
	[56601] = { id = 56601, Voice = '', Text = '红娘担心我的身体吗？', Speaker = 3056, Option = '', Action = _EmptyTable,}, 
	[56600] = { id = 56600, Voice = '', Text = '他就在统帅府当差，是统帅的家丁。', Speaker = 3070, Option = '', Action = _EmptyTable,}, 
	[56599] = { id = 56599, Voice = '', Text = '平时喝点也没关系，最近他工作很累，我有点担心他的身体。', Speaker = 3070, Option = '', Action = _EmptyTable,}, 
	[56598] = { id = 56598, Voice = '', Text = '（有些犹豫）如果你能劝我丈夫不要喝酒，我也可以把家里的酒给你。', Speaker = 3070, Option = '', Action = _EmptyTable,}, 
	[56597] = { id = 56597, Voice = '', Text = '也不用这么唉声叹气吧，如果……', Speaker = 3070, Option = '', Action = _EmptyTable,}, 
