	[56592] = { id = 56592, Voice = '', Text = '不过，你可以去问问红娘，她的丈夫喜欢饮酒。', Speaker = 3061, Option = '', Action = _EmptyTable,}, 
	[56591] = { id = 56591, Voice = '', Text = '下一批酒要等到下个月了。', Speaker = 3061, Option = '头疼', Action = _EmptyTable,}, 
	[56590] = { id = 56590, Voice = '', Text = '她的婚礼非常盛大，来宾把我们的酒都喝光了。', Speaker = 3061, Option = '', Action = _EmptyTable,}, 
	[56589] = { id = 56589, Voice = '', Text = '这个月红娘结婚了，因为她促成了许多有缘人，大家都很喜欢她。', Speaker = 3061, Option = '', Action = _EmptyTable,}, 
	[56588] = { id = 56588, Voice = '', Text = '抱歉了，顾客，我们的这个月的酒已经卖完了。', Speaker = 3061, Option = '', Action = _EmptyTable,}, 
	[56587] = { id = 56587, Voice = '', Text = '快去！', Speaker = 5133, Option = '', Action = _EmptyTable,}, 
	[56586] = { id = 56586, Voice = '', Text = '去斐扬，给我买一坛美酒。', Speaker = 5133, Option = '什么？！', Action = _EmptyTable,}, 
	[56585] = { id = 56585, Voice = '', Text = '那么，第一件事情，可是个十分艰巨的任务！', Speaker = 5133, Option = '期待着', Action = _EmptyTable,}, 
