	[56582] = { id = 56582, Voice = '', Text = '如果决定了，就向我展示你的决心吧。', Speaker = 5133, Option = '{好！,2},{考虑一下,3}', Action = _EmptyTable,}, 
	[56581] = { id = 56581, Voice = '', Text = '你想清楚了吗？', Speaker = 5133, Option = '', Action = _EmptyTable,}, 
	[56580] = { id = 56580, Voice = '', Text = '去神社前的桥上见我吧，我先去换套衣服。', Speaker = 5132, Option = '', Action = _EmptyTable,}, 
	[56579] = { id = 56579, Voice = '', Text = '穿成这样，真不适合说这些。', Speaker = 5132, Option = '', Action = _EmptyTable,}, 
	[56578] = { id = 56578, Voice = '', Text = '证明给我看，不要说好听的！', Speaker = 5132, Option = '', Action = _EmptyTable,}, 
	[56577] = { id = 56577, Voice = '', Text = '无论如何，不管付出什么样的代价，都希望帮助他？', Speaker = 5132, Option = '是的', Action = _EmptyTable,}, 
	[56576] = { id = 56576, Voice = '', Text = '你有帮助伊达秀明的决心吗？', Speaker = 5132, Option = '', Action = _EmptyTable,}, 
	[56575] = { id = 56575, Voice = '', Text = '你的冒险之路，就是为了强迫其他人做你认为正确的事情吗？', Speaker = 5132, Option = '{是的,2},{……,2},{我想想,2}', Action = _EmptyTable,}, 
	[56574] = { id = 56574, Voice = '', Text = '年轻人，你又何必逼迫我呢？', Speaker = 5132, Option = '托辞', Action = _EmptyTable,}, 
	[56573] = { id = 56573, Voice = '', Text = '我说过我不想帮助伊达秀明，我遵从自己内心的选择。', Speaker = 5132, Option = '', Action = _EmptyTable,}, 
	[56572] = { id = 56572, Voice = '', Text = '你打扰我和小姐姐们玩耍了。', Speaker = 5132, Option = '', Action = _EmptyTable,}, 
	[56571] = { id = 56571, Voice = '', Text = '你，你怎么找到我的……离我远一点！', Speaker = 5132, Option = '不要胡闹', Action = _EmptyTable,}, 
	[56570] = { id = 56570, Voice = '', Text = '他好像去了旁边酒家里，好像还跟女孩子一起跳舞，真是个变态！', Speaker = 5124, Option = '', Action = _EmptyTable,}, 
	[56569] = { id = 56569, Voice = '', Text = '你们人类真让我看不懂，好端端的男人，要穿成那个样子。真可怕！', Speaker = 5124, Option = '他去了哪里？', Action = _EmptyTable,}, 
	[56568] = { id = 56568, Voice = '', Text = '我一直在这里排队，我还以为我眼花了呢。', Speaker = 5124, Option = '', Action = _EmptyTable,}, 
