	[56474] = { id = 56474, Voice = '', Text = '好了！等着！！', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[56473] = { id = 56473, Voice = '', Text = '好吧好吧，帮我去打点旁边神社池子里的水，我就帮你们做！', Speaker = 7153, Option = '交给我', Action = _EmptyTable,}, 
	[56472] = { id = 56472, Voice = '', Text = '你们两个还真的是执着…', Speaker = 7153, Option = '', Emoji = 10, Action = _EmptyTable,}, 
	[56471] = { id = 56471, Voice = '', Text = '这些看起来好像足够了。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[56470] = { id = 56470, Voice = '', Text = '去吧，去普隆德拉北部采集些糯米回来！', Speaker = 7150, Option = '', Action = _EmptyTable,}, 
	[56469] = { id = 56469, Voice = '', Text = '她想为难我们？难不倒我的！！', Speaker = 7150, Option = '', Action = _EmptyTable,}, 
	[56468] = { id = 56468, Voice = '', Text = '哈哈，我知道普隆德拉北部有这种糯米！！', Speaker = 7150, Option = '', Action = _EmptyTable,}, 
	[56467] = { id = 56467, Voice = '', Text = '不过口嚼酒需要十分细腻的糯米，天水之国是没有这种米的噢。', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[56466] = { id = 56466, Voice = '', Text = '我就勉强答应你们吧。', Speaker = 7153, Option = '谢谢', Action = _EmptyTable,}, 
	[56465] = { id = 56465, Voice = '', Text = '好吧！好吧！看在你们给我买过樱花生八桥的份上。', Speaker = 7153, Option = '这么说', Action = _EmptyTable,}, 
	[56464] = { id = 56464, Voice = '', Text = '你们只是单纯的变态而已吧！！！', Speaker = 7153, Option = '', Emoji = 12, Action = _EmptyTable,}, 
	[56463] = { id = 56463, Voice = '', Text = '我们绝对不是想自己喝一口什么的！这种想法绝对没有！', Speaker = 7150, Option = '', Action = _EmptyTable,}, 
	[56462] = { id = 56462, Voice = '', Text = '一听就很可疑好吗！', Speaker = 7153, Option = '', Emoji = 10, Action = _EmptyTable,}, 
	[56461] = { id = 56461, Voice = '', Text = '咳咳，我们绝对不是要做什么可疑的事！', Speaker = 7150, Option = '', Action = _EmptyTable,}, 
	[56460] = { id = 56460, Voice = '', Text = '你们要那个干什么…', Speaker = 7153, Option = '', Action = _EmptyTable,}, 
	[56459] = { id = 56459, Voice = '', Text = '什么…', Speaker = 7153, Option = '', Emoji = 1, Action = _EmptyTable,}, 
	[56458] = { id = 56458, Voice = '', Text = '怎么又是你们，我说了我是不会交出咒法卷轴的。', Speaker = 7153, Option = '说明来意', Action = _EmptyTable,}, 
	[56457] = { id = 56457, Voice = '', Text = '看我用我华丽的说辞说服伊达千代子~！', Speaker = 7150, Option = '', Action = _EmptyTable,}, 
	[56456] = { id = 56456, Voice = '', Text = '废话不多说，办事比较重要。', Speaker = 7150, Option = '', Action = _EmptyTable,}, 
	[56455] = { id = 56455, Voice = '', Text = '我只是把这次宝贵的锻炼机会让给你了。', Speaker = 7150, Option = '{谢谢哦,0},{呵呵,0}', Action = _EmptyTable,}, 
	[56454] = { id = 56454, Voice = '', Text = '好啦好啦，你可以明显看得出。', Speaker = 7150, Option = '', Action = _EmptyTable,}, 
	[56453] = { id = 56453, Voice = '', Text = '该死！！你小子为什么运气这么好！！！？？？', Speaker = 7150, Option = '', Action = _EmptyTable,}, 
	[56452] = { id = 56452, Voice = '', Text = '有能耐再赢我一次啊~', Speaker = 7150, Option = '{拳头,40},{剪刀,50},{布,60}', Action = _EmptyTable,}, 
	[56451] = { id = 56451, Voice = '', Text = '不错嘛…再来一局定胜负吧！', Speaker = 7150, Option = '', Action = _EmptyTable,}, 
	[56450] = { id = 56450, Voice = '', Text = '看来你还需要多修炼啊！', Speaker = 7150, Option = '', Action = _EmptyTable,}, 
	[56449] = { id = 56449, Voice = '', Text = '你一定只是运气好，我还没展现我真正的实力。', Speaker = 7150, Option = '', Action = _EmptyTable,}, 
	[56448] = { id = 56448, Voice = '', Text = '怎么样！怕了吧！！不准赖皮啊！！', Speaker = 7150, Option = '', Action = _EmptyTable,}, 
	[56447] = { id = 56447, Voice = '', Text = '石头！！剪刀！！布！！！', Speaker = 7150, Option = '', Emoji = 7, Action = _EmptyTable,}, 
	[56446] = { id = 56446, Voice = '', Text = '那就是…', Speaker = 7150, Option = '', Action = _EmptyTable,}, 
	[56445] = { id = 56445, Voice = '', Text = '那我们就来用史上最公正的方式来决定谁去要口嚼酒！', Speaker = 7150, Option = '', Action = _EmptyTable,}, 
	[56444] = { id = 56444, Voice = '', Text = '那我们就来一决胜负吧！！', Speaker = 7150, Option = '你要干嘛', Action = _EmptyTable,}, 
	[56443] = { id = 56443, Voice = '', Text = '什么！？你还不服气？？', Speaker = 7150, Option = '', Action = _EmptyTable,}, 
	[56442] = { id = 56442, Voice = '', Text = '这么勉强的吗！！？既然你不服气那也没办法。', Speaker = 7150, Option = '', Action = _EmptyTable,}, 
	[56441] = { id = 56441, Voice = '', Text = '怎么了！！不服气吗！！', Speaker = 7150, Option = '{服气,2},{不服气,3}', Action = _EmptyTable,}, 
	[56440] = { id = 56440, Voice = '', Text = '我就勉为其难的接下了！', Speaker = 7150, Option = '？！', Action = _EmptyTable,}, 
	[56439] = { id = 56439, Voice = '', Text = '冒险者，伊达秀明说的没错！如此有难度的工作…', Speaker = 7150, Option = '？', Action = _EmptyTable,}, 
	[56438] = { id = 56438, Voice = '', Text = '什么！？！？！？！小姐姐用嘴嚼过的酒！？！？', Speaker = 7150, Option = '', Emoji = 1, Action = _EmptyTable,}, 
	[56437] = { id = 56437, Voice = '', Text = '是种有灵力的酒！', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56436] = { id = 56436, Voice = '', Text = '是的，是种巫女用嘴嚼米酿出来的酒，非常特别，但用于仪式和祭祀可是非常好的。', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56435] = { id = 56435, Voice = '', Text = '我姐姐的口嚼酒！', Speaker = 7151, Option = '口嚼酒？', Action = _EmptyTable,}, 
	[56434] = { id = 56434, Voice = '', Text = '我需要…', Speaker = 7151, Option = '是什么', Action = _EmptyTable,}, 
	[56432] = { id = 56432, Voice = '', Text = '我刚才拜托摩根先生帮我准备了一些需要的东西，他办事效率实在是太高了。', Speaker = 7151, Option = '是嘛', Action = _EmptyTable,}, 
	[56431] = { id = 56431, Voice = '', Text = '噢…不，我是说，监视我的人。', Speaker = 7151, Option = '嗯', Action = _EmptyTable,}, 
	[56430] = { id = 56430, Voice = '', Text = '不过现在碍事的全部解决了，这都要感谢你，冒险者！', Speaker = 7151, Option = '碍事的？', Emoji = 9, Action = _EmptyTable,}, 
	[56429] = { id = 56429, Voice = '', Text = '这是姐姐的符咒！果然是她在跟踪我。', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56428] = { id = 56428, Voice = '', Text = '这果然和我想的没错！', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56427] = { id = 56427, Voice = '', Text = '式神“嘭”的一声变成了一张小咒符。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[56426] = { id = 56426, Voice = '', Text = '呷呷…', Speaker = 7152, Option = '', Emoji = 18, Action = _EmptyTable,}, 
	[56425] = { id = 56425, Voice = '', Text = '呷！！！！！', Speaker = 7152, Option = '', Emoji = 12, Action = _EmptyTable,}, 
	[56424] = { id = 56424, Voice = '', Text = '呷！！', Speaker = 7152, Option = '', Emoji = 1, Action = _EmptyTable,}, 
	[56423] = { id = 56423, Voice = '', Text = '在码头或酒家旁的樱花树林找找！它一定就在这其中一个地方！', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56422] = { id = 56422, Voice = '', Text = '那我再重复一遍我喜欢去两个地方！码头和酒家旁的樱花树林！', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56421] = { id = 56421, Voice = '', Text = '你认为你能找的到这只式神吗！', Speaker = 7151, Option = '{可以,6},{不确定,5}', Action = _EmptyTable,}, 
	[56420] = { id = 56420, Voice = '', Text = '我最喜欢去两个地方！码头和酒家旁的樱花树林！', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56419] = { id = 56419, Voice = '', Text = '它们一般喜欢躲在暗处观察，在我常去的地方出没！', Speaker = 7151, Option = '那是哪里', Action = _EmptyTable,}, 
	[56418] = { id = 56418, Voice = '', Text = '式神是施术者用咒符做出来的，就像是…一张会飞的小纸片！', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56417] = { id = 56417, Voice = '', Text = '太好了，那你就帮我把这个鬼鬼祟祟的式神给找出来吧！！', Speaker = 7151, Option = '怎么找', Action = _EmptyTable,}, 
	[56416] = { id = 56416, Voice = '', Text = '你同意我说的吗？', Speaker = 7151, Option = '同意', Action = _EmptyTable,}, 
	[56415] = { id = 56415, Voice = '', Text = '虽然我不知道施术者是谁，但我觉得被监视的感觉很差劲！', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56414] = { id = 56414, Voice = '', Text = '那几个式神一直在暗中监视我，让我觉得很不舒服。', Speaker = 7151, Option = '原来如此', Action = _EmptyTable,}, 
	[56413] = { id = 56413, Voice = '', Text = '没错，我毕竟是伊达家族的人，这点感知还是有的。', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56412] = { id = 56412, Voice = '', Text = '是的，那是天水之国特有的法术所召唤出来的小灵体。', Speaker = 7151, Option = '是嘛', Action = _EmptyTable,}, 
	[56411] = { id = 56411, Voice = '', Text = '我感觉到最近总是有只式神跟着我！', Speaker = 7151, Option = '式神？', Action = _EmptyTable,}, 
	[56410] = { id = 56410, Voice = '', Text = '太好了，希望是真的。', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56409] = { id = 56409, Voice = '', Text = '那我到底是为了什么找你们两个神仙帮忙啊！！！', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56408] = { id = 56408, Voice = '', Text = '但我相信你们还是有很强实力的…', Speaker = 7151, Option = '{是的,3},{不是,2}', Action = _EmptyTable,}, 
	[56407] = { id = 56407, Voice = '', Text = '虽然你们没办法骗过我姐姐千代子。', Speaker = 7151, Option = '', Action = _EmptyTable,}, 
	[56300] = { id = 56300, Voice = '', Text = '樱中鬼女被击退，她们身上飘落了许多樱花花瓣，这些花瓣看上去有些眼熟……', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[56299] = { id = 56299, Voice = '', Text = '樱花树中，落下几个美丽的樱花鬼女，拦住了去路。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[56298] = { id = 56298, Voice = '', Text = '可以请你的那位朋友一起帮忙吗？我想多一个人的力量会更容易。', Speaker = 7113, Option = '', Action = _EmptyTable,}, 
	[56297] = { id = 56297, Voice = '', Text = '你愿意帮我们吗？    ', Speaker = 7113, Option = '愿意', Action = _EmptyTable,}, 
	[56296] = { id = 56296, Voice = '', Text = '我想，永远跟小樱在一起。', Speaker = 7113, Option = '', Action = _EmptyTable,}, 
	[56295] = { id = 56295, Voice = '', Text = '我想知道发生了什么事情……', Speaker = 7113, Option = '', Action = _EmptyTable,}, 
	[56294] = { id = 56294, Voice = '', Text = '我有时会看见小樱变得越来越透明，偶尔，会完全消失……', Speaker = 7113, Option = '', Action = _EmptyTable,}, 
	[56293] = { id = 56293, Voice = '', Text = '最近，却发生了奇怪的事情……   ', Speaker = 7113, Option = '什么', Action = _EmptyTable,}, 
	[56292] = { id = 56292, Voice = '', Text = '她是我见过最善良的灵。  ', Speaker = 7113, Option = '看的出来', Action = _EmptyTable,}, 
	[56291] = { id = 56291, Voice = '', Text = '我很小的时候，就与小樱相识了。', Speaker = 7113, Option = '', Action = _EmptyTable,}, 
	[56290] = { id = 56290, Voice = '', Text = '我想请你帮忙的这件事情，是跟小樱有关。', Speaker = 7113, Option = '', Action = _EmptyTable,}, 
	[56289] = { id = 56289, Voice = '', Text = '你的朋友，很是特别…… ', Speaker = 7113, Option = '心累', Action = _EmptyTable,}, 
	[56288] = { id = 56288, Voice = '', Text = '我先走了，还有非常严肃的事情要做。', Speaker = 7111, Option = '', Action = _EmptyTable,}, 
	[56287] = { id = 56287, Voice = '', Text = '一边说着，摩根拿出了一套女士的衣服，开始化妆。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[56286] = { id = 56286, Voice = '', Text = '不用不用，这件事情，我自己就能解决了。', Speaker = 7112, Option = '', Action = _EmptyTable,}, 
	[56285] = { id = 56285, Voice = '', Text = '很重要的事情！   ', Speaker = 7112, Option = '我帮你', Action = _EmptyTable,}, 
	[56284] = { id = 56284, Voice = '', Text = '我要走了，我还有正事要做……  ', Speaker = 7112, Option = '什么事情', Emoji = 28, Action = _EmptyTable,}, 
	[56283] = { id = 56283, Voice = '', Text = '不管你说什么，我都拒绝，我是个怕麻烦的人。', Speaker = 7112, Option = '', Action = _EmptyTable,}, 
	[56282] = { id = 56282, Voice = '', Text = '我还没说出口，我请求的事情。', Speaker = 7113, Option = '', Action = _EmptyTable,}, 
	[56281] = { id = 56281, Voice = '', Text = '我拒绝!', Speaker = 7112, Option = '', Action = _EmptyTable,}, 
	[56280] = { id = 56280, Voice = '', Text = '我有件事情，请两位帮忙……   ', Speaker = 7113, Option = '你说吧', Action = _EmptyTable,}, 
	[56279] = { id = 56279, Voice = '', Text = '刚刚我见过了两位的魔法，你们不是一般人。', Speaker = 7113, Option = '', Action = _EmptyTable,}, 
	[56278] = { id = 56278, Voice = '', Text = '我第一次碰见除我以外，能见到小樱的人，你们有什么特殊之处吗？', Speaker = 7113, Option = '', Action = _EmptyTable,}, 
	[56277] = { id = 56277, Voice = '', Text = '都一样的，不要离我这么近，我是人，你是灵。', Speaker = 7112, Option = '', Action = _EmptyTable,}, 
	[56276] = { id = 56276, Voice = '', Text = '喂，我有这么可怕吗？我可不是鬼，我只是樱之灵而已……', Speaker = 7104, Option = '', Action = _EmptyTable,}, 
	[56275] = { id = 56275, Voice = '', Text = '别…别…别…鬼大人，我看得见，你不要跟着我。 ', Speaker = 7112, Option = '', Emoji = 18, Action = _EmptyTable,}, 
	[56274] = { id = 56274, Voice = '', Text = '那我只能一直跟着你，直到你看见我为止咯？', Speaker = 7104, Option = '', Action = _EmptyTable,}, 
	[56273] = { id = 56273, Voice = '', Text = '看不见，看不见，我什么都看不见。', Speaker = 7112, Option = '', Emoji = 1, Action = _EmptyTable,}, 
	[56272] = { id = 56272, Voice = '', Text = '你，看得见我？', Speaker = 7104, Option = '', Action = _EmptyTable,}, 
	[56271] = { id = 56271, Voice = '', Text = '告辞！', Speaker = 7112, Option = '', Action = _EmptyTable,}, 
	[56270] = { id = 56270, Voice = '', Text = '很好！（转头对着伊达秀明）这位小哥，打扰了，我和我的朋友准备到处逛一逛。', Speaker = 7112, Option = '', Action = _EmptyTable,}, 
	[56269] = { id = 56269, Voice = '', Text = '你明白了吗？   ', Speaker = 7112, Option = '{明白了,0},{呵呵,0}', Action = _EmptyTable,}, 
	[56268] = { id = 56268, Voice = '', Text = '要友善地对待这里的人民，天水之国是一块世外桃源，做一个正直的人，不要破坏它美丽的风情。', Speaker = 7112, Option = '', Action = _EmptyTable,}, 
	[56267] = { id = 56267, Voice = '', Text = '你也来到天水之国观看樱花盛典吗？', Speaker = 7112, Option = '', Action = _EmptyTable,}, 
	[56266] = { id = 56266, Voice = '', Text = '啊，是你啊，[c][ffff00][PlayerName][-][/c]。', Speaker = 7112, Option = '', Action = _EmptyTable,}, 
	[56265] = { id = 56265, Voice = '', Text = '他回来了。', Speaker = 7113, Option = '', Action = _EmptyTable,}, 
	[56264] = { id = 56264, Voice = '', Text = '（轻笑）我可没把他怎么样，只是带他去醒醒酒，换套衣服。', Speaker = 7113, Option = '', Action = _EmptyTable,}, 
	[56263] = { id = 56263, Voice = '', Text = '动作可真慢，这么不在乎自己同伴的性命吗？ ', Speaker = 7113, Option = '放了摩根', Action = _EmptyTable,}, 
	[56243] = { id = 56243, Voice = '', Text = '快走开！', Speaker = 7103, Option = '', Action = _EmptyTable,}, 
	[56242] = { id = 56242, Voice = '', Text = '（余怒未消地看着你）我说，外乡人，你别来教唆我儿子，这个败家子已经够蠢了。', Speaker = 7103, Option = '', Action = _EmptyTable,}, 
	[56241] = { id = 56241, Voice = '', Text = '快给我醒醒，别在外乡人面前丢脸了！', Speaker = 7103, Option = '', Action = _EmptyTable,}, 
	[56240] = { id = 56240, Voice = '', Text = '你这是又喝醉了吗？', Speaker = 7103, Option = '', Action = _EmptyTable,}, 
	[56239] = { id = 56239, Voice = '', Text = '（生气的样子）臭小子，你又在这里跟别人讨论些什么？', Speaker = 7103, Option = '', Action = _EmptyTable,}, 
	[56238] = { id = 56238, Voice = '', Text = '我们年轻的时候，只敢偷看，还从来没有当面调戏过呢！', Speaker = 7102, Option = '', Emoji = 28, Action = _EmptyTable,}, 
	[56237] = { id = 56237, Voice = '', Text = '桥前的樱花树下，有个道德沦丧的男人在调戏女孩子。', Speaker = 7102, Option = '', Action = _EmptyTable,}, 
	[56236] = { id = 56236, Voice = '', Text = '真是世风日下，人心不古，唉！', Speaker = 7102, Option = '发生了什么？', Action = _EmptyTable,}, 
	[56235] = { id = 56235, Voice = '', Text = '玩得开心，希望你喜欢我们的国家。', Speaker = 7101, Option = '', Action = _EmptyTable,}, 
	[56234] = { id = 56234, Voice = '', Text = '我国马上将举办一场樱花盛典，届时你可以参加。', Speaker = 7101, Option = '', Action = _EmptyTable,}, 
	[56233] = { id = 56233, Voice = '', Text = '外乡人，欢迎来到天水之国。', Speaker = 7101, Option = '', Action = _EmptyTable,}, 
	[56232] = { id = 56232, Voice = '', Text = '站稳扶好，卡鲁船长，带你去天水之国。', Speaker = 1147, Option = '', Action = _EmptyTable,}, 
	[56231] = { id = 56231, Voice = '', Text = '天水之国？你的消息很灵通啊，我刚开通的航线，你就知道了！', Speaker = 1147, Option = '', Action = _EmptyTable,}, 
	[56230] = { id = 56230, Voice = '', Text = '这个季节，正是樱花初放的时节。', Speaker = 7100, Option = '', Action = _EmptyTable,}, 
	[56229] = { id = 56229, Voice = '', Text = '乘坐卡鲁船长的船，就能从依斯鲁得岛前往天水之国了。', Speaker = 7100, Option = '', Action = _EmptyTable,}, 
	[56228] = { id = 56228, Voice = '', Text = '不错，如果你能亲眼见到，就明白我在说什么了。', Speaker = 7100, Option = '', Action = _EmptyTable,}, 
	[56227] = { id = 56227, Voice = '', Text = '三月，天水之国的樱花压在枝头上，层层叠叠。树下赏花的少女满面春光，满眼春情。', Speaker = 7100, Option = '这么美？', Action = _EmptyTable,}, 
	[56226] = { id = 56226, Voice = '', Text = '但我家乡的美景也不输任何地方。', Speaker = 7100, Option = '', Action = _EmptyTable,}, 
	[56225] = { id = 56225, Voice = '', Text = '普隆德拉的建筑气势恢宏，吸引全大陆的人都来到这里。', Speaker = 7100, Option = '', Action = _EmptyTable,}, 
	[55597] = { id = 55597, Voice = '', Text = '可惜其中有一片被画上了奇怪的符号，无法作为舞蹈用品使用了。不过这些符号看上去像一个藏宝图，也许有什么奇妙的用意哦。', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[55596] = { id = 55596, Voice = '', Text = '可惜因为舞蹈团受到雪熊攻击，所有大树叶都遗落在雪原上。幸好有好心人捡到了这些树叶，收好了放在雪人附近。', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[55595] = { id = 55595, Voice = '', Text = '黑天鹅舞蹈团为了在姜饼城表演节目，准备了许多精美道具，其中就有这种特殊的大树叶。', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[55594] = { id = 55594, Voice = '', Text = '这人肯定是舞蹈的忠实粉丝，为了让黑天鹅的舞蹈能够在姜饼城表演，做出了许多努力。', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[55593] = { id = 55593, Voice = '', Text = '丢失的金苹果被人放在了姜饼城东北边的森林附近，到底是谁能够在雪熊出没的雪原找到了这些金苹果？', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[55592] = { id = 55592, Voice = '', Text = '玩具大师曼斯特为黑天鹅舞蹈团制作的玩具金苹果，上面洒满了厚厚一层金色，拿在手中非常有分量。', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[55590] = { id = 55590, Voice = '', Text = '当跳舞的时候，在身上撒一些蝴蝶磷光水晶，可以让人熠熠生辉。', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[55589] = { id = 55589, Voice = '', Text = '如果将这些蝴蝶的磷粉洒在水晶上，透明的水晶也会被染上蝴蝶的荧光，在黑夜里散发出璀璨的光芒。', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[55588] = { id = 55588, Voice = '', Text = '姜饼城有一种特有的暗夜蝴蝶，它们只会在深夜里出现，散发出蓝色的荧光，照亮一片又一片的草丛，让姜饼城在夜晚也变得非常美丽。', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[55587] = { id = 55587, Voice = '', Text = '这些铃铛可以用来绑在手腕和脚腕上，跳起舞，叮当作响，让观众们可以感受到舞蹈的热情。', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[55586] = { id = 55586, Voice = '', Text = '一个陌生的冒险者陪他玩猜拳都可以让他敞开心扉，把自己心爱的铃铛送给了冒险者。', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[55585] = { id = 55585, Voice = '', Text = '胖熊是姜饼城一个可怜的孩子，因为父母在梦罗克的战役中不幸去世，性格变得非常极端，这也让他变得非常孤独。', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[55295] = { id = 55295, Voice = '', Text = '有你的姜饼城我怎么舍得离开呢。', Speaker = 5534, Option = '', Action = _EmptyTable,}, 
	[55294] = { id = 55294, Voice = '', Text = '当然喜欢，哥哥跟我说了，我学习的巫术咒语和使用的材料都是你偷偷拿给他的。我，我很感激你！你要离开姜饼城吗？你还会回来吗?', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55293] = { id = 55293, Voice = '', Text = '感谢你的包容，格蕾丝。你还喜欢我送给你的宝石吗？', Speaker = 5534, Option = '', Action = _EmptyTable,}, 
	[55292] = { id = 55292, Voice = '', Text = '格蕾丝，我从来没有怪罪过你。从我们第一次见面，你温柔的把我从地上捡起来，我就爱上你了。这三年来，我每时每刻都想向你坦白我的身份，却害怕你因此而离开我。', Speaker = 5534, Option = '', Action = _EmptyTable,}, 
	[55291] = { id = 55291, Voice = '', Text = '我听说你要离开姜饼城了，而且永远也不会再回来……我希望你不是因为我对你说的话才做出这样的决定。点点，你可以原谅我吗？', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55290] = { id = 55290, Voice = '', Text = '我来是想跟你道歉，亲自向你道歉。我听哥哥说过了，你不是邪恶的魔物，我误会你了。而且我还对你说了很过分的话……', Speaker = 5531, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[55289] = { id = 55289, Voice = '', Text = '威廉，听着，我跑过来可不是听你说这些的！', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55288] = { id = 55288, Voice = '', Text = '我马上就离开姜饼城了，以后不会再打扰你的生活。', Speaker = 5534, Option = '', Action = _EmptyTable,}, 
	[55287] = { id = 55287, Voice = '', Text = '格蕾丝！你来了……我不该欺骗你，请你原谅我。', Speaker = 5534, Option = '', Action = _EmptyTable,}, 
	[55286] = { id = 55286, Voice = '', Text = '我不能一直这样犹犹豫豫，我有话要对威廉说！', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55285] = { id = 55285, Voice = '', Text = '以后是不是点点永远不会陪伴我了呢？明明只是个恶魔，为什么眼泪还是止不住呢……冒险者，我该怎么办呢？', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55284] = { id = 55284, Voice = '', Text = '这是！海蓝宝石，我一直说想要的宝石……他送给我了吗？', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55283] = { id = 55283, Voice = '', Text = '什么事？', Speaker = 5531, Option = '威廉的礼物', Action = _EmptyTable,}, 
	[55282] = { id = 55282, Voice = '', Text = '我不太想说话，你请自便吧。', Speaker = 5531, Option = '还有一件事', Action = _EmptyTable,}, 
	[55281] = { id = 55281, Voice = '', Text = '……', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55280] = { id = 55280, Voice = '', Text = '好吧，我知道了。', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55279] = { id = 55279, Voice = '', Text = '……那，他还会回来吗？', Speaker = 5531, Option = '不会', Action = _EmptyTable,}, 
	[55278] = { id = 55278, Voice = '', Text = '他说什么了吗？', Speaker = 5531, Option = '他要离开了', Action = _EmptyTable,}, 
	[55277] = { id = 55277, Voice = '', Text = '我需要回我的世界了。', Speaker = 5534, Option = '', Action = _EmptyTable,}, 
	[55276] = { id = 55276, Voice = '', Text = '请你……帮我带给她吧。', Speaker = 5534, Option = '', Action = _EmptyTable,}, 
	[55275] = { id = 55275, Voice = '', Text = '这个是她一直想要的海蓝宝石，我原本想亲手交给她，也向她坦白我的一切。', Speaker = 5534, Option = '', Action = _EmptyTable,}, 
	[55274] = { id = 55274, Voice = '', Text = '……她都不愿意亲自来问我了吗？我明白了，请你告诉她我以后不会再出现姜饼城，不会再打扰她的生活。', Speaker = 5534, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[55273] = { id = 55273, Voice = '', Text = '那么，格蕾丝说了什么吗？', Speaker = 5534, Option = '传达格蕾丝的话', Action = _EmptyTable,}, 
	[55272] = { id = 55272, Voice = '', Text = '……为什么？', Speaker = 5534, Option = '怀兹为你解释了', Action = _EmptyTable,}, 
	[55271] = { id = 55271, Voice = '', Text = '不服气？你以为如果我真的想要你的性命，你还会活着吗，可笑！', Speaker = 5534, Option = '我是来道歉的', Action = _EmptyTable,}, 
	[55270] = { id = 55270, Voice = '', Text = '是来找麻烦的吗？格蕾丝不在，你以为我还会对你手下留情，从我眼前消失吧，愚蠢的人类。', Speaker = 5534, Option = '……', Action = _EmptyTable,}, 
	[55269] = { id = 55269, Voice = '', Text = '……', Speaker = 5534, Option = '', Action = _EmptyTable,}, 
	[55268] = { id = 55268, Voice = '', Text = '我想他也许在玩具工厂的二楼，我原来经常带点点去那里玩。', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55267] = { id = 55267, Voice = '', Text = '我想请你……帮我问问点点，或者应该称为威廉，他准备以后怎么办，回到自己的世界吗？', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55266] = { id = 55266, Voice = '', Text = '哎呀，麻烦你帮我做了这么多事情，外乡人我看你的能力很不错。我有个私人的事情想请你帮忙。', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55265] = { id = 55265, Voice = '', Text = '好好，我去找威廉，你不要着急……唉，妹妹太凶了也很可怕啊。', Speaker = 5529, Option = '', Action = _EmptyTable,}, 
	[55264] = { id = 55264, Voice = '', Text = '身为姜饼城的守卫者，我们要担负起责任！', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55263] = { id = 55263, Voice = '', Text = '那么，他现在去了哪儿呢？总不能让一个恶魔一直待在姜饼城吧，笨蛋哥哥，这么多麻烦的事情，都怪你!', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55262] = { id = 55262, Voice = '', Text = '他还是个王子，留在姜饼城是……为了我？', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55261] = { id = 55261, Voice = '', Text = '所以说，这个叫威廉的恶魔变成蝙蝠在我们家门口，不是因为被冻坏了，而是因为吃太饱了飞不动？', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55260] = { id = 55260, Voice = '', Text = '笨蛋哥哥你早就知道这件事情，居然不告诉我！', Speaker = 5531, Option = '', Emoji = 12, Action = _EmptyTable,}, 
	[55259] = { id = 55259, Voice = '', Text = '因为我被欺骗了，那个无耻肮脏下流的恶魔跟我说他只有十岁，是因为喜欢蛋糕才留在姜饼城！亲爱的格蕾丝，你千万不要被这种色迷迷的恶魔迷惑了，他们都是蠢货，让我去消灭他。', Speaker = 5529, Option = '', Action = _EmptyTable,}, 
	[55258] = { id = 55258, Voice = '', Text = '可是，哥哥明明知道这件事情，他为什么不告诉我呢？', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55257] = { id = 55257, Voice = '', Text = '他是个恶魔，又为什么要变成蝙蝠留在我家呢？', Speaker = 5531, Option = '{我不知道,0},{也许因为爱,0},{你自己想想吧,0}', Action = _EmptyTable,}, 
	[55256] = { id = 55256, Voice = '', Text = '你说真的吗？点点他没有恶意吗？', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55255] = { id = 55255, Voice = '', Text = '什么真相？我现在没心情听，让我思考一下怎么告诉哥哥这个消息……我真愚蠢，我要怎么对露比说，全世界最伟大的黑魔女连恶魔都看不出来？', Speaker = 5531, Option = '陈述事实', Emoji = 24, Action = _EmptyTable,}, 
	[55254] = { id = 55254, Voice = '', Text = '是你啊，你来这里做什么？我不知道要怎么告诉大家，是我的原因让恶魔在姜饼城待了三年……你让我静一静吧，冒险者。', Speaker = 5531, Option = '告诉你真相', Action = _EmptyTable,}, 
	[55253] = { id = 55253, Voice = '', Text = '在姜饼城的西边看雪景吧，她心情不好的时候会躲在那里偷偷的哭……我可怜的妹妹……', Speaker = 6990, Option = '', Action = _EmptyTable,}, 
	[55252] = { id = 55252, Voice = '', Text = '冒险者，我收回刚刚说出的话，恶魔就是恶魔，所有的恶魔都应该被赶尽杀绝，让我们一起携手把这个肮脏的生物消灭吧！不管是丽莎，还是我的妹妹，我都要守护他们！', Speaker = 6990, Option = '格蕾丝在哪？', Action = _EmptyTable,}, 
	[55251] = { id = 55251, Voice = '', Text = '他跟我说他只有10岁！居然欺骗了我！那么，这个猥琐的男恶魔留在我妹妹身边，是为了什么？？？', Speaker = 6990, Option = '', Action = _EmptyTable,}, 
	[55250] = { id = 55250, Voice = '', Text = '青年……他不是小男孩吗？', Speaker = 6990, Option = '是青年恶魔', Action = _EmptyTable,}, 
	[55249] = { id = 55249, Voice = '', Text = '……', Speaker = 6990, Option = '', Emoji = 1, Action = _EmptyTable,}, 
	[55248] = { id = 55248, Voice = '', Text = '什么？不是孩子，什么意思？', Speaker = 6990, Option = '是个青年', Action = _EmptyTable,}, 
	[55247] = { id = 55247, Voice = '', Text = '在我眼里，威廉他只是喜欢吃蛋糕的小孩，而且又有礼貌，还会变形为蝙蝠逗格蕾丝开心。', Speaker = 6990, Option = '他不是孩子', Action = _EmptyTable,}, 
	[55246] = { id = 55246, Voice = '', Text = '这件事情我会向格蕾丝解释的，威廉是冤枉的，他在这里待了三年了，姜饼城对他来说就跟家一样，他没有理由伤害他的家人……恶魔也是有爱的……', Speaker = 6990, Option = '', Action = _EmptyTable,}, 
	[55245] = { id = 55245, Voice = '', Text = '恶魔又怎么了？恶魔也是有血有肉有心的，也不是所有的恶魔都是邪恶的，血族离开米德加尔德大陆已经很久了，他们跟人类有免战协议，又是有思想的高等恶魔。', Speaker = 6990, Option = '这种说法，我', Action = _EmptyTable,}, 
	[55244] = { id = 55244, Voice = '', Text = '这个小王子还挺上道的，看我妹妹喜欢巫术，经常贡献一些恶魔族特有的巫术材料给我妹妹。我看他是一个喜欢蛋糕的小孩子，就收留他，让他留在姜饼城了，他平时会变回小孩子的原型，在姜饼城玩，晚上会变身为蝙蝠在我家里休息，三年来一直挺安分的。', Speaker = 6990, Option = '你收留恶魔？', Action = _EmptyTable,}, 
	[55243] = { id = 55243, Voice = '', Text = '这个恶魔叫做威廉，是某支恶魔族的小王子，他非常喜欢姜饼城的蛋糕，所以每年的冬天都会来到姜饼城度假。三年前，他在姜饼城吃蛋糕吃撑了飞不动了，倒在我家门口，被我好心的妹妹捡了回家。', Speaker = 6990, Option = '', Action = _EmptyTable,}, 
	[55242] = { id = 55242, Voice = '', Text = '这家伙是个高级恶魔，可以变身为不同的样子，蝙蝠只是方便他飞行的一种而已。', Speaker = 6990, Option = '', Action = _EmptyTable,}, 
	[55241] = { id = 55241, Voice = '', Text = '我当然知道，身为大炼金术士阿克米·曼的后人，我连这点小伎俩都看不出来，也是白混了这么多年。这蠢货蝙蝠第一次来我家的时候，我就发现他的不对劲了。', Speaker = 6990, Option = '', Action = _EmptyTable,}, 
	[55240] = { id = 55240, Voice = '', Text = '点点是不是暴露了自己的恶魔身份？', Speaker = 6990, Option = '你知道？', Action = _EmptyTable,}, 
	[55239] = { id = 55239, Voice = '', Text = '……点点！那只蝙蝠？……我就知道会出问题，那个臭小子！', Speaker = 6990, Option = '', Emoji = 12, Action = _EmptyTable,}, 
	[55238] = { id = 55238, Voice = '', Text = '不，我没有误会，摘下你的手套，亮出你的兵器，我们决一死战，为了我妹妹破碎的心灵。', Speaker = 6990, Option = '是点点！', Action = _EmptyTable,}, 
	[55237] = { id = 55237, Voice = '', Text = '我亲爱的妹妹格蕾丝，被一个无所事事的在姜饼城里转来转去的冒险者惹哭了。', Speaker = 6990, Option = '真的误会', Action = _EmptyTable,}, 
	[55236] = { id = 55236, Voice = '', Text = '站住！应该就是你吧！把我妹妹惹哭的人，我不会放过你的，跟我决斗吧！', Speaker = 6990, Option = '误会', Action = _EmptyTable,}, 
	[55235] = { id = 55235, Voice = '', Text = '格蕾丝满脸泪水的走开了……', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[55234] = { id = 55234, Voice = '', Text = '我要去问问我的哥哥怀兹，有什么补救的办法。', Speaker = 5531, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[55233] = { id = 55233, Voice = '', Text = '你是这样认为的吗？现在想一想，点点和比尔出现在姜饼城时间差不多，也许……我真的养了一个恶魔三年，我是姜饼城的罪人。', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55232] = { id = 55232, Voice = '', Text = '你对这件事情有什么看法吗？', Speaker = 5531, Option = '{点点是恶魔,0},{这很难说,0},{恶魔抓了点点,0}', Action = _EmptyTable,}, 
	[55231] = { id = 55231, Voice = '', Text = '冒险者，我的心好乱，为什么点点会在那里？怎么会这样？', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55230] = { id = 55230, Voice = '', Text = '恶魔捧起蝙蝠，释放了烟雾消失了。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[55228] = { id = 55228, Voice = '', Text = '点点！怎么回事？！', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55227] = { id = 55227, Voice = '', Text = '蝙蝠点点艰难的煽动了两下翅膀，试图向格蕾丝飞去。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[55226] = { id = 55226, Voice = '', Text = '果然露出了马脚，冒险者，我们上！把这群恶魔一网打尽！', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55225] = { id = 55225, Voice = '', Text = '王子殿下，我实在忍不住了！违背了你的意愿很抱歉，但是我不能让这群人类伤害你！', Speaker = 5532, Option = '', Action = _EmptyTable,}, 
	[55224] = { id = 55224, Voice = '', Text = '……格蕾丝，请不要用这样的表情面对我，我很伤心。', Speaker = 5534, Option = '', Action = _EmptyTable,}, 
	[55223] = { id = 55223, Voice = '', Text = '别废话，魔物！你装作人类三年，到底是为了什么？', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55222] = { id = 55222, Voice = '', Text = '不要害怕我，格蕾丝，我只是和你长得不同而已……我喜欢热闹，喜欢圣诞村静静的雪，还喜欢你烤的蛋糕，我只是喜欢呆在这样宁静的小镇。', Speaker = 5534, Option = '', Action = _EmptyTable,}, 
	[55221] = { id = 55221, Voice = '', Text = '格蕾丝，对不起，我没有恶意……我并不清楚丽莎去了哪里，我没有抓走丽莎……我在姜饼城的目的，是你。', Speaker = 5534, Option = '', Action = _EmptyTable,}, 
	[55220] = { id = 55220, Voice = '', Text = '你果然是个魔物！说！你到姜饼城有什么目的？丽莎被你抓到哪里去了？', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55219] = { id = 55219, Voice = '', Text = '啊……不要看着我！求求你了，格蕾丝。', Speaker = 5533, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[55218] = { id = 55218, Voice = '', Text = '闭嘴！魔物！冒险者，你在等些什么？他在迷惑你的心智，快点撒药水，只差最后一点，他就可以显形了。', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55217] = { id = 55217, Voice = '', Text = '格蕾丝……我不想显形啊……我不是故意要欺骗你的，请你听我说两句……啊！', Speaker = 5533, Option = '', Action = _EmptyTable,}, 
	[55216] = { id = 55216, Voice = '', Text = '真是厉害，在这种计量的药水之下，还可以保持人型，看来这个魔物真不简单啊。冒险者，继续撒药水在他身上。', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55215] = { id = 55215, Voice = '', Text = '我好痛……啊……格蕾丝，对不起……啊！', Speaker = 5533, Option = '', Action = _EmptyTable,}, 
	[55214] = { id = 55214, Voice = '', Text = '我配置了一些可以让恶魔显形的药水，你帮我一起洒在他身上。', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55213] = { id = 55213, Voice = '', Text = '居然还没有生效，这个恶魔可真是厉害。冒险者，我需要你的帮助。有些高等恶魔可以变化成人类的样子，我估计比尔就是这种恶魔。', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55212] = { id = 55212, Voice = '', Text = '格蕾丝……你做了什么，我好痛……好痛!', Speaker = 5533, Option = '', Action = _EmptyTable,}, 
	[55211] = { id = 55211, Voice = '', Text = '我亲眼见到了他拍到你跟恶魔谈话的照片，你还想要骗人吗？我可不是这么容易被你欺骗的！亮出你的原形吧！', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55210] = { id = 55210, Voice = '', Text = '格蕾丝，是谁跟你说我和恶魔有关系？这个冒险者吗？你居然相信他不相信我！', Speaker = 5533, Option = '', Action = _EmptyTable,}, 
	[55209] = { id = 55209, Voice = '', Text = '比尔，你居然跟恶魔里应外合。说！你想对姜饼城做些什么？', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55208] = { id = 55208, Voice = '', Text = '冒险者，你拖延住了恶魔吗？我来支援你了！', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55207] = { id = 55207, Voice = '', Text = '空气中有一丝尴尬的气氛。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[55206] = { id = 55206, Voice = '', Text = '姜饼城不是一直下雪吗？……你到底怎么了？', Speaker = 5533, Option = '哈哈哈哈', Action = _EmptyTable,}, 
	[55205] = { id = 55205, Voice = '', Text = '是吗？哈哈哈哈，你不去找恶魔了吗？也许在南边，你知道的，有些恶魔有翅膀飞的很快，也许就飞到南边了！', Speaker = 5533, Option = '今天天气不错', Action = _EmptyTable,}, 
	[55204] = { id = 55204, Voice = '', Text = '又是你？你你，你找到恶魔了吗？我记错了，刚刚的黑影好像去的是西边，你再去西边找找吧。', Speaker = 5533, Option = '我随便逛逛', Action = _EmptyTable,}, 
	[55203] = { id = 55203, Voice = '', Text = '冒险者，请你拖延住这些恶魔，我回家找一点有用的工具就去支援你。', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55202] = { id = 55202, Voice = '', Text = '我简直不敢相信自己的双眼，比尔真的跟恶魔认识吗？难怪他消息这么灵通，我想要的一些巫术道具他都能帮我找到！', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55201] = { id = 55201, Voice = '', Text = '你有证据了吗？如果没有，我也不能信任你，比尔已经在姜饼城待了三年，他一直是个乖孩子，我不相信他会做这样的事情。', Speaker = 5531, Option = '照片为证', Action = _EmptyTable,}, 
	[55200] = { id = 55200, Voice = '', Text = '恶魔消失了！赶快把有比尔与恶魔谈话的照片拿给格蕾丝。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[55199] = { id = 55199, Voice = '', Text = '呜呜呜呜……王子殿下。', Speaker = 5532, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[55198] = { id = 55198, Voice = '', Text = '别废话，赶快回去……', Speaker = 5533, Option = '', Emoji = 12, Action = _EmptyTable,}, 
	[55197] = { id = 55197, Voice = '', Text = '王子殿下，我不是故意的，不要赶我走啊！我太想你了，你一走就是三年，一点消息都没有，我一个人在王宫里什么事情都没有，真的很无聊。无聊的时候，我就想你，让我留下来吧，我留在你身边帮你做什么都可以！', Speaker = 5532, Option = '', Action = _EmptyTable,}, 
	[55196] = { id = 55196, Voice = '', Text = '给我闭嘴，小声一点，小心别人听到了。迪曼，我命令你滚回去，不要过来找我！要不是你今天这么鲁莽，格蕾丝也不会差点发现……蠢货，给我回去！', Speaker = 5533, Option = '', Emoji = 12, Action = _EmptyTable,}, 
	[55195] = { id = 55195, Voice = '', Text = '你一走就是三年，这三年里，发生了好多的事情，我都想讲给你听……', Speaker = 5532, Option = '', Action = _EmptyTable,}, 
	[55194] = { id = 55194, Voice = '', Text = '王子殿下，我好想你……┭┮﹏┭┮', Speaker = 5532, Option = '', Action = _EmptyTable,}, 
	[55193] = { id = 55193, Voice = '', Text = '如果没有证据，我不会相信你所说的话。', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55192] = { id = 55192, Voice = '', Text = '比尔虽然神出鬼没的经常看不见人影，但是从来没有做过危害姜饼城的事情。他怎么可能跟恶魔有联系呢？你有证据吗？', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55191] = { id = 55191, Voice = '', Text = '他说他被姜饼城外的雪熊攻击了，不小心伤到了脑袋，忘记原来发生的事情了，哥哥就帮他找个地方在姜饼城住下来了。', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55190] = { id = 55190, Voice = '', Text = '不会吧，比尔是三年前一个冬天来到姜饼城的，那个时候他一身的伤出现在我家的门口，我哥哥帮他治疗了好几天。', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55189] = { id = 55189, Voice = '', Text = '谁？谁很可疑？', Speaker = 5531, Option = '比尔', Action = _EmptyTable,}, 
	[55188] = { id = 55188, Voice = '', Text = '怎么样，冒险者，你有恶魔的消息了吗？', Speaker = 5531, Option = '有人很可疑', Action = _EmptyTable,}, 
	[55187] = { id = 55187, Voice = '', Text = '哼，我没什么好跟你说的，你走吧！', Speaker = 5533, Option = '', Action = _EmptyTable,}, 
	[55186] = { id = 55186, Voice = '', Text = '额……可能吧，哈哈哈哈哈，可能是恶魔吧，我也搞不清楚，他们刚刚往南边去了。不！也有可能在北边，你去找找看吧！', Speaker = 5533, Option = '你很慌张……', Action = _EmptyTable,}, 
	[55185] = { id = 55185, Voice = '', Text = '刚刚的黑影，冲过去的时候撞到我了，真可怕，还长着尖尖的角呢！', Speaker = 5533, Option = '尖角？恶魔？', Action = _EmptyTable,}, 
	[55184] = { id = 55184, Voice = '', Text = '那你肯定是看错了，我说没有就没有！', Speaker = 5533, Option = '刚刚的黑影', Action = _EmptyTable,}, 
	[55183] = { id = 55183, Voice = '', Text = '恶魔？什么恶魔？……你在胡说些什么，姜饼城怎么可能有恶魔呢？', Speaker = 5533, Option = '我看见了', Action = _EmptyTable,}, 
	[55182] = { id = 55182, Voice = '', Text = '这么快？！真麻烦……啊，没有，我是说谢谢你！这样我就不痛了，你有什么事情吗？', Speaker = 5533, Option = '恶魔的踪迹', Action = _EmptyTable,}, 
	[55181] = { id = 55181, Voice = '', Text = '不……不用了，如果担心我，就帮我去玩具工厂里拿一些绷带吧，那些玩具士兵有些柔软绷带，帮我拿一些。', Speaker = 5533, Option = '', Action = _EmptyTable,}, 
	[55180] = { id = 55180, Voice = '', Text = '是的，刚刚有个黑影从这边跑过去了，把我撞在地上，我被地上的石子割伤了！', Speaker = 5533, Option = '让我看看？', Action = _EmptyTable,}, 
	[55179] = { id = 55179, Voice = '', Text = '用不着你管，我不认识你！啊……好痛……', Speaker = 5533, Option = '你流血了！', Action = _EmptyTable,}, 
	[55178] = { id = 55178, Voice = '', Text = '你有什么事要找我？我可没话跟你说！啊……痛……', Speaker = 5533, Option = '你怎么了？', Action = _EmptyTable,}, 
	[55177] = { id = 55177, Voice = '', Text = '血液的踪迹消失了，也许可以问站在这里的小孩。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[55176] = { id = 55176, Voice = '', Text = '有血迹在雪地上，朝着玩具工厂方向有更多的线索！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[55175] = { id = 55175, Voice = '', Text = '有一些奇怪质地的血液，也许是刚刚那个恶魔的血。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[55174] = { id = 55174, Voice = '', Text = '我觉得也许你可以去教堂附近调查，许多魔物都喜欢偷偷在教堂附近玩耍，快去吧冒险者！', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55173] = { id = 55173, Voice = '', Text = '我等着你的好消息！', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55172] = { id = 55172, Voice = '', Text = '冒险者，可以请你帮我调查这件事情吗？', Speaker = 5531, Option = '义不容辞', Action = _EmptyTable,}, 
	[55171] = { id = 55171, Voice = '', Text = '看我的厉害，我会用黑魔法打爆这群恶魔的！', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55170] = { id = 55170, Voice = '', Text = '姜饼城居然有恶魔出没，现在的魔物都这么猖狂了吗？', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55169] = { id = 55169, Voice = '', Text = '点点，你快飞回家吧，这里太危险了，以后不要夜晚出来找我了。', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55168] = { id = 55168, Voice = '', Text = '还好今天你在这里，要不然点点就……谢谢你，冒险者，我为刚刚怀疑你道歉。', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55167] = { id = 55167, Voice = '', Text = '真的吗？谢谢你，冒险者！', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55166] = { id = 55166, Voice = '', Text = '点点……受伤了吗？怎么这么傻，那可是个恶魔呀，以后不要做这样的傻事了……', Speaker = 5531, Option = '我帮点点包扎', Emoji = 24, Action = _EmptyTable,}, 
	[55165] = { id = 55165, Voice = '', Text = '我看它可怜，就用围巾温暖它，没想到它苏醒以后好像知道我救了它一样，留在我家里不走了。', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55164] = { id = 55164, Voice = '', Text = '是我的宠物，点点，它是一只吸血蝙蝠。三年前，我在家门口碰见它掉在地上扑腾，被冻的飞不起来了。', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55163] = { id = 55163, Voice = '', Text = '我没有事，恶魔没有伤到我……', Speaker = 5531, Option = '刚刚的是？', Action = _EmptyTable,}, 
	[55162] = { id = 55162, Voice = '', Text = '小蝙蝠把恶魔撞倒，恶魔狼狈的逃走了。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[55160] = { id = 55160, Voice = '', Text = '王子殿下？！', Speaker = 5532, Option = '', Action = _EmptyTable,}, 
	[55159] = { id = 55159, Voice = '', Text = '是恶魔？居然能跑来姜饼城！', Speaker = 5531, Option = '我来帮你', Action = _EmptyTable,}, 
	[55158] = { id = 55158, Voice = '', Text = '啊……痛痛痛……王子殿下，你在哪里？王子殿下！', Speaker = 5532, Option = '', Action = _EmptyTable,}, 
	[55157] = { id = 55157, Voice = '', Text = '你是来旅游的冒险者吧，我还需要继续练习黑魔法，请你自便吧~', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55156] = { id = 55156, Voice = '', Text = '你怎么知道我的事情？露比跟你说的吗？真头疼，小朋友太容易信任陌生人了。', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55155] = { id = 55155, Voice = '', Text = '喂，你怎么说话的，我不想跟你聊天了！', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55154] = { id = 55154, Voice = '', Text = '现在还不是~我希望成为最伟大的女巫，懂得最多黑魔法的女巫！', Speaker = 5531, Option = '{祝福你,2},{黑魔法不好,3},{不可能成功,3}', Action = _EmptyTable,}, 
	[55153] = { id = 55153, Voice = '', Text = '你是谁？', Speaker = 5531, Option = '你是黑魔女？', Emoji = 1, Action = _EmptyTable,}, 
	[55152] = { id = 55152, Voice = '', Text = '今晚的温度还有月色都是适合炼制青蛙药水的时候呢……', Speaker = 5531, Option = '', Action = _EmptyTable,}, 
	[55151] = { id = 55151, Voice = '', Text = '嗯啊，姐姐说了午夜是最适合学习魔法的时候，格蕾丝姐姐就站在那边。', Speaker = 5530, Option = '', Action = _EmptyTable,}, 
	[55150] = { id = 55150, Voice = '', Text = '格蕾丝姐姐正在教我学习黑魔法呢，你就突然出现了，吓了我一跳！', Speaker = 5530, Option = '黑魔法？', Action = _EmptyTable,}, 
	[55149] = { id = 55149, Voice = '', Text = '这样啊，天色太黑了，我看不清你的脸。不好意思，误会你了，冒险者。', Speaker = 5530, Option = '', Action = _EmptyTable,}, 
	[55148] = { id = 55148, Voice = '', Text = '恩，你是谁？', Speaker = 5530, Option = '旅行的冒险者', Action = _EmptyTable,}, 
	[55147] = { id = 55147, Voice = '', Text = '你你你……你不要过来，我可是……会会会魔法的，你再过来，我我我要打你了！', Speaker = 5530, Option = '我不是坏人', Action = _EmptyTable,}, 
	[55146] = { id = 55146, Voice = '', Text = '格蕾丝姐姐！姐姐！', Speaker = 5530, Option = '', Emoji = 22, Action = _EmptyTable,}, 
	[55145] = { id = 55145, Voice = '', Text = '你是谁？！你在这里做什么？格蕾丝姐姐！这里有个可疑的人！', Speaker = 5530, Option = '', Action = _EmptyTable,}, 
	[55144] = { id = 55144, Voice = '', Text = '别怪我没提醒你，夜晚的姜饼城可是完全不一样的！', Speaker = 6990, Option = '', Action = _EmptyTable,}, 
	[55143] = { id = 55143, Voice = '', Text = '我怎么看你一脸兴奋的样子……', Speaker = 6990, Option = '', Emoji = 18, Action = _EmptyTable,}, 
	[55142] = { id = 55142, Voice = '', Text = '是的！魔物也喜欢圣诞节，没两把刷子就老老实实呆在房间里，别在晚上出门！', Speaker = 6990, Option = '唔……', Action = _EmptyTable,}, 
	[55141] = { id = 55141, Voice = '', Text = '这片土地不仅吸引普通人来旅游，还会吸引魔物前来玩耍，夜晚就是他们出没的最佳时间。', Speaker = 6990, Option = '魔物？', Action = _EmptyTable,}, 
	[55140] = { id = 55140, Voice = '', Text = '不错，就是你。我说，你是来旅游的吧，夜晚出门小心一点，姜饼城的白天和夜晚可是完全不一样的呢。', Speaker = 6990, Option = '', Action = _EmptyTable,}, 
	[55139] = { id = 55139, Voice = '', Text = '喂，冒险者……', Speaker = 6990, Option = '我？', Action = _EmptyTable,}, 
	[55138] = { id = 55138, Voice = '', Text = '又是来姜饼城旅游的人吗？每年圣诞节都有这么多人来游玩，放假了都不能好好休息一下。', Speaker = 6990, Option = '', Action = _EmptyTable,}, 
	[55137] = { id = 55137, Voice = '', Text = '哼，居然是平手，那么再来一次吧！今天非要分出胜负不可。', Speaker = 5298, Option = '', Action = _EmptyTable,}, 
	[55136] = { id = 55136, Voice = '', Text = '猜猜我会出什么！', Speaker = 5298, Option = '{拳头,40},{剪刀,50},{布,60}', Action = _EmptyTable,}, 
	[55015] = { id = 55015, Voice = '', Text = '这是最让我开心的圣诞节啦！太棒了！', Speaker = 5298, Option = '', Emoji = 17, Action = _EmptyTable,}, 
	[55014] = { id = 55014, Voice = '', Text = '希望你会永远记住我今天为你表演的舞蹈！', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[55013] = { id = 55013, Voice = '', Text = '谢谢你，好心的冒险者，这个是我们舞蹈团为这支舞给观众准备的纪念品，送给你了！', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[55012] = { id = 55012, Voice = '', Text = '如果不是你，我也没有机会在姜饼城为我最可爱的三位观众跳舞了……', Speaker = 6992, Option = '非常精彩', Emoji = 2, Action = _EmptyTable,}, 
	[55011] = { id = 55011, Voice = '', Text = '谢谢你们的夸奖～你呢，冒险者，你还喜欢我的舞蹈吗？', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[55010] = { id = 55010, Voice = '', Text = '唷唷唷唷～～唷唷唷～～', Speaker = 5299, Option = '', Action = _EmptyTable,}, 
	[55009] = { id = 55009, Voice = '', Text = '好棒好棒～～舞娘姐姐跳得真美……', Speaker = 5298, Option = '', Action = _EmptyTable,}, 
	[55008] = { id = 55008, Voice = '', Text = '你来了呀，冒险者，我们也布置好了。没想到，还有三位观众愿意看我表演，请你就位吧。', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[55007] = { id = 55007, Voice = '', Text = '胖熊快乐的跑开了……', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[55006] = { id = 55006, Voice = '', Text = '那就好！我先去看跳舞咯，你动作太慢了，不等你啦～', Speaker = 5298, Option = '', Action = _EmptyTable,}, 
	[55005] = { id = 55005, Voice = '', Text = '嗯嗯嗯，我要去看舞娘跳舞咯！……你会陪我的吧，冒险者，你也会去吗？', Speaker = 5298, Option = '当然！', Action = _EmptyTable,}, 
	[55004] = { id = 55004, Voice = '', Text = '真的吗？！！在哪里，我要去看舞蹈！！！', Speaker = 5298, Option = '冰河附近', Action = _EmptyTable,}, 
	[55003] = { id = 55003, Voice = '', Text = '什么舞蹈表演，你要表演舞蹈给我看吗？…………我觉得还是不要了吧……', Speaker = 5298, Option = '舞娘表演', Emoji = 10, Action = _EmptyTable,}, 
	[55002] = { id = 55002, Voice = '', Text = '是你，你是来陪我玩的吗？！我真是太高兴了，看来你是一个遵守承诺的大人！我们现在玩什么呢？还是猜拳吗？', Speaker = 5298, Option = '看舞蹈表演', Action = _EmptyTable,}, 
	[55001] = { id = 55001, Voice = '', Text = '……真的吗？原来这些铃铛是可怜的胖熊的，那么你快去邀请他一起来观看我的舞蹈吧！至于布置就交给我和幽灵先生了～', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[55000] = { id = 55000, Voice = '', Text = '什么？', Speaker = 6992, Option = '讲述胖熊的故事', Action = _EmptyTable,}, 
	[54999] = { id = 54999, Voice = '', Text = '没想到我的观众又多了一名，真是太棒了！！', Speaker = 6992, Option = '还可以多一个', Action = _EmptyTable,}, 
	[54998] = { id = 54998, Voice = '', Text = '看来确实是这样，估计那片树叶上的鬼画符，也是它的杰作了！可能是想提供一些信息吧，可惜我们语言不通～', Speaker = 6992, Option = '', Emoji = 2, Action = _EmptyTable,}, 
	[54997] = { id = 54997, Voice = '', Text = '（高兴的飞了起来）唷唷唷唷唷～～～～', Speaker = 5299, Option = '', Action = _EmptyTable,}, 
	[54996] = { id = 54996, Voice = '', Text = '我猜金苹果和大树叶可能是这个幽灵找到的，它可能是想拿给我吧……', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54995] = { id = 54995, Voice = '', Text = '嗯，让我猜猜看，是不是找到金苹果和大树叶的时候呢？', Speaker = 6992, Option = '确实', Action = _EmptyTable,}, 
	[54994] = { id = 54994, Voice = '', Text = '冒险者，你之前遇见过这个幽灵吗？', Speaker = 6992, Option = '是的', Action = _EmptyTable,}, 
	[54993] = { id = 54993, Voice = '', Text = '（重重的点头）唷～', Speaker = 5299, Option = '', Action = _EmptyTable,}, 
	[54992] = { id = 54992, Voice = '', Text = '那……你在这里是为了……看我跳舞吗？', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54991] = { id = 54991, Voice = '', Text = '（重重的点头）唷～唷唷～', Speaker = 5299, Option = '', Action = _EmptyTable,}, 
	[54990] = { id = 54990, Voice = '', Text = '你……不是坏蛋对吧？', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54989] = { id = 54989, Voice = '', Text = '它看上去没什么恶意，请你先别赶走它，让我跟它交流一下……', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54988] = { id = 54988, Voice = '', Text = '唷～唷唷～', Speaker = 5299, Option = '', Action = _EmptyTable,}, 
	[54987] = { id = 54987, Voice = '', Text = '魔物！冒险者，怎么会有魔物突然出现呢……', Speaker = 6992, Option = '阴魂不散', Action = _EmptyTable,}, 
	[54986] = { id = 54986, Voice = '', Text = '唷～唷唷～唷～唷～唷唷～唷～', Speaker = 5299, Option = '', Action = _EmptyTable,}, 
	[54985] = { id = 54985, Voice = '', Text = '大树叶都铺好了吗？恩，下一步，让我用这些蝴蝶磷光水晶织出一道美丽的光带！', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54984] = { id = 54984, Voice = '', Text = '来帮忙吧，冒险者！首先把这些大树叶铺到地面上！', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54983] = { id = 54983, Voice = '', Text = '我怎么没想到呢，我可以表演给你一个人看！嗯，你帮了我这么多忙，如果没有你的鼓励和帮助，也许我早就坚持不下去，跟舞蹈团的其他人一样回到梦罗克了……', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54982] = { id = 54982, Voice = '', Text = '……可是只有我一个人的舞蹈，会有人愿意观看吗？也许连场地都不会借给我了吧……', Speaker = 6992, Option = '我愿意看！', Action = _EmptyTable,}, 
	[54981] = { id = 54981, Voice = '', Text = '终于收集到所有的舞蹈用品了，我也可以为游客们表演舞蹈了！', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54980] = { id = 54980, Voice = '', Text = '好吧，真是怪事，嗯～～～这片树叶不能使用了，真可惜，我们制作这些树叶可是花了不少时间呢！到底是谁在上面乱画呢……', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54979] = { id = 54979, Voice = '', Text = '这片树叶上怎么有一些文字？好像画了一些东西，好奇怪，这个是你画上去的吗？', Speaker = 6992, Option = '不是', Action = _EmptyTable,}, 
	[54978] = { id = 54978, Voice = '', Text = '嗯…这件事情听上去很古怪…', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54977] = { id = 54977, Voice = '', Text = '难道，你不顾危险，去了城外的雪原吗？你真是太勇敢了，冒险者，我应该怎样感谢你！', Speaker = 6992, Option = '{我捡来的,4},{就在雪人附近,4},{有人收整齐了,4}', Action = _EmptyTable,}, 
	[54976] = { id = 54976, Voice = '', Text = '这不是我们舞蹈团的大树叶吗？！当时雪熊追逐着我们，情急之下我们把树叶从马车里扔了出去，扰乱雪熊的注意力，你从哪里找到的呢？', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54975] = { id = 54975, Voice = '', Text = '被击退的幽灵一边哭泣一边跑开了，看上去有点可怜……得到了一些大树叶……', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54974] = { id = 54974, Voice = '', Text = '这时突然从雪人背后冲出来一只幽灵！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54973] = { id = 54973, Voice = '', Text = '地方有一些摆放整齐的树叶，好象是有人故意摆放在那边……', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54972] = { id = 54972, Voice = '', Text = '地上有一些又宽又大的树叶，看上去不像姜饼城的树叶，要拾取吗？', Speaker = 5294, Option = '{拾取,2},{放弃,3}', Action = _EmptyTable,}, 
	[54971] = { id = 54971, Voice = '', Text = '嗯，让我挑个最大的……这个给你吧，冒险者，希望你喜欢，虽然并不能吃，但是还是挺好看的吧……', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54970] = { id = 54970, Voice = '', Text = '这么多金苹果，我也只需要一个而已……', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54969] = { id = 54969, Voice = '', Text = '……只可惜舞蹈团的其他人都离开姜饼城了，如果你早点出现就好了，这样也许其他人会选择留下来，跟我一起表演舞蹈……', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54968] = { id = 54968, Voice = '', Text = '不管如何，能够找回金苹果就好了，看来你是我的幸运儿啊，有你在一切都很顺利了！太棒了！', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54967] = { id = 54967, Voice = '', Text = '嗯？这件事情可真古怪呀，有谁会从雪原上找到我们的金苹果藏起来呢？', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54966] = { id = 54966, Voice = '', Text = '这，这就是我们舞蹈团用来表演的金苹果，因为我们的马车被雪熊追赶，所以遗落在姜饼城的雪原上……冒险者，你是从哪里找到我们的金苹果的？', Speaker = 6992, Option = '被人藏起来了', Action = _EmptyTable,}, 
	[54965] = { id = 54965, Voice = '', Text = '金苹果！让我仔细看看……', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54964] = { id = 54964, Voice = '', Text = '被击退的幽灵恋恋不舍的走了……赶快捡起地上的金苹果吧。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54963] = { id = 54963, Voice = '', Text = '这时突然从树木背后冲出来一只幽灵！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54962] = { id = 54962, Voice = '', Text = '仔细查看后发现泥土里有几颗金光闪闪的苹果，白天根本看不见！好像是有人故意藏在那边……', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54961] = { id = 54961, Voice = '', Text = '黑暗中可以看见泥土上有一些闪闪发亮的东西，仔细观察一下吗？', Speaker = 5296, Option = '{仔细观察,2},{放弃,3}', Action = _EmptyTable,}, 
	[54960] = { id = 54960, Voice = '', Text = '看，这些蝴蝶磷光水晶…有了这些水晶，就可以代替舞蹈表演的装饰品了…好像多出来了一些……这些送给你了！要好好珍藏哦！', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54959] = { id = 54959, Voice = '', Text = '你取来了碎水晶吗？让我看看水晶的成色……唔，这个通透程度和纯净度都很好。太棒了！冒险者！让我们来制作蝴蝶磷光水晶吧！', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54958] = { id = 54958, Voice = '', Text = '我听说玩具工厂的拉链熊有一些碎水晶，可以请你帮我取来一些吗？', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54957] = { id = 54957, Voice = '', Text = '如果能有一些碎水晶，我就可以把蝴蝶翅膀上的磷粉和水晶混合在一起，制作成可以散发五彩光芒的水晶。', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54956] = { id = 54956, Voice = '', Text = '让我看看你给我带了些什么？一些暗夜蝴蝶，这就是姜饼城特有的蝴蝶吗？传说中只在午夜才会出现的华丽生物，真美呢……', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54955] = { id = 54955, Voice = '', Text = '捕捉了几只闪闪发光的蝴蝶，原来是它们翅膀上的磷粉在发光。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54954] = { id = 54954, Voice = '', Text = '黑暗中有一些闪闪发光的蝴蝶，请问要捕捉几只吗？', Speaker = 5295, Option = '{捕捉,2},{放弃,3}', Action = _EmptyTable,}, 
	[54953] = { id = 54953, Voice = '', Text = '这声音真好听！这就是我想要找的铃铛！有了这个铃铛，我就可以表演舞蹈了！让我数数……一只两只三只四只五只，嗯，多了一只，留给你作为收藏吧冒险者。', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54952] = { id = 54952, Voice = '', Text = '这些铃铛……看上去真美，让我听听铃铛的响声……', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54951] = { id = 54951, Voice = '', Text = '看来要输给胖熊，才能拿到铃铛。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54950] = { id = 54950, Voice = '', Text = '你怎么赢了？！我不给你铃铛！！呜呜呜……我不想跟你玩游戏了！你让我不开心了，我不会给你铃铛的！除非你[c][ffff00]输给我[-][/c]……', Speaker = 5298, Option = '', Action = _EmptyTable,}, 
	[54949] = { id = 54949, Voice = '', Text = '我会想念你的，冒险者，铃铛给你了。', Speaker = 5298, Option = '', Action = _EmptyTable,}, 
	[54948] = { id = 54948, Voice = '', Text = '呜呜呜呜……我以后不会再调皮了，我会乖的，你不要不理我……', Speaker = 5298, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[54947] = { id = 54947, Voice = '', Text = '胖熊抱住你，哭了一会。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54946] = { id = 54946, Voice = '', Text = '铃铛你拿走吧，我是熊霸天，才不要你们陪我呢！', Speaker = 5298, Option = '{拿走铃铛,6},{安慰胖熊,7},{陪伴胖熊,7}', Action = _EmptyTable,}, 
	[54945] = { id = 54945, Voice = '', Text = '其实你也只是想要铃铛才跟我玩的吧，我就知道的，你们都是一样想要什么东西才会来找我……没有人喜欢我的……', Speaker = 5298, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[54944] = { id = 54944, Voice = '', Text = '其他人都不理我，他们总是说让我乖一点就陪我玩，实际上也没有陪我……是不是我真的让人厌烦，爸爸妈妈才不回家的呢？', Speaker = 5298, Option = '', Action = _EmptyTable,}, 
	[54943] = { id = 54943, Voice = '', Text = '嗯……爸爸妈妈去梦罗克打大魔物了，他们答应我会回家的，但是一直没有回来……', Speaker = 5298, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[54942] = { id = 54942, Voice = '', Text = '答应我的事情，要做到的！你不能爽约……我爸爸妈妈答应我会回家，就没有回家……你不能学他们！', Speaker = 5298, Option = '爸爸妈妈？', Action = _EmptyTable,}, 
	[54941] = { id = 54941, Voice = '', Text = '……不要生气嘛，那我把铃铛给你，你不要生气了！但是你要答应我，下次还要陪我玩……', Speaker = 5298, Option = '好吧', Action = _EmptyTable,}, 
	[54940] = { id = 54940, Voice = '', Text = '哈哈哈哈，我赢了，你真菜，哈哈哈～被我赢了……铃铛不能给你了，哈哈哈哈……', Speaker = 5298, Option = '', Action = _EmptyTable,}, 
	[54939] = { id = 54939, Voice = '', Text = '那好，我们猜拳，说好了，如果你赢了我就把铃铛给你！', Speaker = 5298, Option = '', Action = _EmptyTable,}, 
	[54938] = { id = 54938, Voice = '', Text = '那我相信你，你愿意陪我猜拳吗？', Speaker = 5298, Option = '当然！', Action = _EmptyTable,}, 
	[54937] = { id = 54937, Voice = '', Text = '真的愿意陪我吗？……', Speaker = 5298, Option = '', Action = _EmptyTable,}, 
	[54936] = { id = 54936, Voice = '', Text = '嗯……你愿意……陪我玩吗？……肯定是想骗我的铃铛吧…', Speaker = 5298, Option = '', Action = _EmptyTable,}, 
	[54935] = { id = 54935, Voice = '', Text = '怎么又是你？你有完没完啊，说了不会给你铃铛的，还跑过来做什么啊…去去去，我还要跟朋友玩呢！', Speaker = 5298, Option = '我陪你玩', Action = _EmptyTable,}, 
	[54934] = { id = 54934, Voice = '', Text = '哼，知道我的厉害了吧，知道了就回家玩泥巴去吧，别在这里丢人现眼的。', Speaker = 5298, Option = '', Action = _EmptyTable,}, 
	[54933] = { id = 54933, Voice = '', Text = '怎么样，你想通了吗？', Speaker = 5298, Option = '{我错了,5},{铃铛给你,5},{别闹了,5}', Action = _EmptyTable,}, 
	[54932] = { id = 54932, Voice = '', Text = '如果放任不管，可能会惹麻烦，还是跟胖熊说说好话吧。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54931] = { id = 54931, Voice = '', Text = '（大声叫喊）抓小孩了！抓小孩了！这里有个坏人抓小孩了！！呜呜呜呜……有个坏人要打我！！坏人抢我的东西！！呜呜呜呜！！快来人救救我！', Speaker = 5298, Option = '', Emoji = 5, Action = _EmptyTable,}, 
	[54930] = { id = 54930, Voice = '', Text = '哟，还来了一个有性格的，怕是没被我熊霸天教训过。', Speaker = 5298, Option = '', Emoji = 5, Action = _EmptyTable,}, 
	[54929] = { id = 54929, Voice = '', Text = '我从圣诞树下捡来的，哼！快给我还回来，你这个丑八怪！敢抢我的铃铛，我要你好看！', Speaker = 5298, Option = '{真没礼貌！,4},{再说一遍！,4},{你说什么？,4}', Action = _EmptyTable,}, 
	[54928] = { id = 54928, Voice = '', Text = '看什么看呢，你拿着我的铃铛还瞪我？', Speaker = 5298, Option = '你的铃铛？', Action = _EmptyTable,}, 
	[54927] = { id = 54927, Voice = '', Text = '喂！！！谁给你的胆子让你动我的东西？没听说我姜饼城熊霸天的名号吗？', Speaker = 5298, Option = '', Action = _EmptyTable,}, 
	[54926] = { id = 54926, Voice = '', Text = '远处跑来了一个看上去很邋遢的小孩……', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54925] = { id = 54925, Voice = '', Text = '地面上藏了一些精美的铃铛，请问是否要拾取？', Speaker = 5293, Option = '{拾取,2},{放弃,3}', Action = _EmptyTable,}, 
	[54924] = { id = 54924, Voice = '', Text = '不管如何，感谢你听我说了这么多……', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54923] = { id = 54923, Voice = '', Text = '……你怎么不说话了，是不是我的要求太过分了？如果找不到也没关系的啦，我也知道我的要求很过分，甚至有许多道具都只有夜晚才能找到……', Speaker = 6992, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[54922] = { id = 54922, Voice = '', Text = '最后就是一些树叶，要又宽又长的[c][ffff00]大树叶[-][/c]，姜饼城有很多松树却没什么长着大树叶的树木，如果你能帮我找到就好了……', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54921] = { id = 54921, Voice = '', Text = '还有绑在手腕和脚腕的[c][ffff00]铃铛[-][/c]，铃铛的响声一定要清脆！我在姜饼城找了很久，听到了各种铃铛的响声，可惜没有一个铃铛的响声足够悦耳……', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54920] = { id = 54920, Voice = '', Text = '我想如果把这些蝴蝶的磷粉撒在碎水晶上，水晶也会散发着五彩的磷光吧……', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54919] = { id = 54919, Voice = '', Text = '我听过姜饼城附近有一些[c][ffff00]荧光蝴蝶[-][/c]，它们的磷粉会在黑暗里发出美丽的华光，可惜这些蝴蝶只会在[c][ffff00]深夜里[-][/c]出没，我找了很久都没有找到！', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54918] = { id = 54918, Voice = '', Text = '我还需要一些五光十色的碎水晶，记住一定要多彩又晶莹的水晶，只有不同颜色的华光才能展现女神的优雅与美丽。', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54917] = { id = 54917, Voice = '', Text = '我们排演了的这场舞蹈叫做“女神的金苹果”，我需要一颗可以在[c][ffff00]夜晚[-][/c]散发着金色光芒的苹果，虽然姜饼城里有很多苹果，但是没有我想要的金苹果，如果没有[c][ffff00]金苹果[-][/c]我无法进行舞蹈表演。', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54916] = { id = 54916, Voice = '', Text = '真的吗？那么太感谢了！', Speaker = 6992, Option = '', Emoji = 2, Action = _EmptyTable,}, 
	[54915] = { id = 54915, Voice = '', Text = '排演了这么久的舞蹈，没有让任何人见到过，真是太遗憾了…所以我就留在姜饼城，希望可以找到一些代替道具，起码，为前来游玩的游客，表演一场……只要一场舞……', Speaker = 6992, Option = '我来帮你吧', Action = _EmptyTable,}, 
	[54914] = { id = 54914, Voice = '', Text = '因为没有道具，我们无法表演舞蹈，舞蹈团的其他成员就回梦罗克了，可是我有点不甘心！', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54913] = { id = 54913, Voice = '', Text = '虽然我们都安然无恙，但是跳舞用的一些道具却在马车颠簸中都丢失了…', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54912] = { id = 54912, Voice = '', Text = '这些雪熊追着我们的马车直到姜饼城外，幸好城外的守卫官及时的看见了我们，帮助我们驱赶了雪熊，才让我们捡回了一条命……', Speaker = 6992, Option = '', Emoji = 10, Action = _EmptyTable,}, 
	[54911] = { id = 54911, Voice = '', Text = '没想到雪熊父母就在小雪熊附近，它们看见我们跟他们的孩子在一起就开始攻击我们。', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54910] = { id = 54910, Voice = '', Text = '当我们乘坐着马车从雪原进入姜饼城时，碰见了一群正在玩闹的小雪熊，我们的小姐妹加蜜儿看着小雪熊非常可爱就去下车去逗弄它们。', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54909] = { id = 54909, Voice = '', Text = '为了这件事情，我们全体团员练习了很久，排演了一场生动的舞蹈。', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54908] = { id = 54908, Voice = '', Text = '前段时间我们舞蹈团收到了姜饼城的邀请函，请我们在圣诞节期间到姜饼城表演舞蹈。', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54907] = { id = 54907, Voice = '', Text = '啊，我的碎碎念让你听见了呀，有点难为情呢。是这样的，我叫做珂莲安，来自于梦罗克的黑天鹅舞蹈团。', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54906] = { id = 54906, Voice = '', Text = '只可惜了我的铃铛…', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54905] = { id = 54905, Voice = '', Text = '圣诞村外的雪熊真凶猛，谁能想到看上去这么可爱的动物会有这么狰狞的一面呢？', Speaker = 6992, Option = '', Action = _EmptyTable,}, 
	[54901] = { id = 54901, Voice = '', Text = '这是你应得的奖励，希望你喜欢我们的狂欢节哦~', Speaker = 5487, Option = '', Emoji = 27, Action = _EmptyTable,}, 
	[54900] = { id = 54900, Voice = '', Text = '没想到啊，你这么快就完成了挑战，看来你的观察能力和记忆力都是万中挑一的！', Speaker = 5487, Option = '', Action = _EmptyTable,}, 
	[54886] = { id = 54886, Voice = '', Text = '看来你已经胸有成竹，来吧！', Speaker = 5496, Option = '', Action = _EmptyTable,}, 
	[54885] = { id = 54885, Voice = '', Text = '你需要再听我讲述游戏规则吗？', Speaker = 5496, Option = '{需要,10},{不需要,11}', Action = _EmptyTable,}, 
	[54884] = { id = 54884, Voice = '', Text = '我就认同你的能力！', Speaker = 5496, Option = '', Action = _EmptyTable,}, 
	[54883] = { id = 54883, Voice = '', Text = '按照我的脚步和顺序重复我的路径，如果可以记住所有的路，重复我的步骤。', Speaker = 5496, Option = '', Action = _EmptyTable,}, 
	[54882] = { id = 54882, Voice = '', Text = '我会快速的在地图上移形换影，记住我所有出现的位置。', Speaker = 5496, Option = '', Action = _EmptyTable,}, 
	[54881] = { id = 54881, Voice = '', Text = '那么废话不多说，我们就开始游戏吧！', Speaker = 5496, Option = '', Action = _EmptyTable,}, 
	[54880] = { id = 54880, Voice = '', Text = '嗯……没想到你的实力如此高，很好，你这样的挑战者，就是我狂骨想要的人！', Speaker = 5496, Option = '', Action = _EmptyTable,}, 
	[54879] = { id = 54879, Voice = '', Text = '给我拿来20个小丑把戏！', Speaker = 5496, Option = '', Action = _EmptyTable,}, 
	[54878] = { id = 54878, Voice = '', Text = '那么，向我证明你的能力，我需要20个小丑把戏才能布置游戏。', Speaker = 5496, Option = '', Action = _EmptyTable,}, 
	[54877] = { id = 54877, Voice = '', Text = '我看你也是活腻了！', Speaker = 5496, Option = '', Action = _EmptyTable,}, 
	[54876] = { id = 54876, Voice = '', Text = '我的移形换影游戏可不是那么容易通过的。', Speaker = 5496, Option = '{呵呵,4},{帮帮忙吧,5},{闭嘴,6}', Action = _EmptyTable,}, 
	[54875] = { id = 54875, Voice = '', Text = '不要以为我会让你这么容易就通过狂欢节！', Speaker = 5496, Option = '', Action = _EmptyTable,}, 
	[54874] = { id = 54874, Voice = '', Text = '是来参加狂欢节最后一个项目的吗？没想到前两个游戏你都顺利通过了。', Speaker = 5496, Option = '', Action = _EmptyTable,}, 
	[54780] = { id = 54780, Voice = '', Text = '小心，狂骨可不是好惹的，请你跟他说话的时候，一定要客气一点！', Speaker = 5489, Option = '', Action = _EmptyTable,}, 
	[54779] = { id = 54779, Voice = '', Text = '快去进行下一场狂欢游戏吧，去玩具工厂2F的北边吧。狂骨在北边等着你，只要通过它的游戏，你就是第一个挑战成功的挑战者！', Speaker = 5489, Option = '', Action = _EmptyTable,}, 
	[54778] = { id = 54778, Voice = '', Text = '我已经听我们的伴侣说了，你帮助他们找到了他们心仪的玩偶。', Speaker = 5489, Option = '', Action = _EmptyTable,}, 
	[54777] = { id = 54777, Voice = '', Text = '你说有伴侣不嫌我胖，也愿意一直跟我在一起，我要跟你一起走！', Speaker = 5495, Option = '', Action = _EmptyTable,}, 
	[54776] = { id = 54776, Voice = '', Text = '肚子吃的好饱，要多走走楼梯可以减肥！', Speaker = 5495, Option = '', Action = _EmptyTable,}, 
	[54775] = { id = 54775, Voice = '', Text = '嘻嘻，藏在这里都被你找到了，是不是找了很长时间？', Speaker = 5494, Option = '', Action = _EmptyTable,}, 
	[54774] = { id = 54774, Voice = '', Text = '哎呀，被你找到了，既然被你找到了我就跟你走吧。', Speaker = 5493, Option = '', Action = _EmptyTable,}, 
	[54773] = { id = 54773, Voice = '', Text = '看来玩偶就在这附近，多找找看吧。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54772] = { id = 54772, Voice = '', Text = '[c][ffff00]他们喜欢待在玩具工厂南面的玩具堆，喜欢站在视野的盲区和高处，一定要多多使用照相机，可以更方便的找到玩偶哦！[-][/c]', Speaker = 5489, Option = '', Action = _EmptyTable,}, 
	[54771] = { id = 54771, Voice = '', Text = '你都听清了吗？嗯嗯，我觉得你很顺眼，偷偷告诉你玩偶喜欢的位置哦。', Speaker = 5489, Option = '', Action = _EmptyTable,}, 
	[54770] = { id = 54770, Voice = '', Text = '挑战者，你听好了，我想要的是拉链熊玩偶，那种抱起来超软的拉链熊哦。', Speaker = 5492, Option = '', Action = _EmptyTable,}, 
	[54769] = { id = 54769, Voice = '', Text = '挑战者，你听好了，我想要的是红帽子玩偶，不红的帽子，我不要，请你不要搞错了哦。', Speaker = 5491, Option = '', Action = _EmptyTable,}, 
	[54768] = { id = 54768, Voice = '', Text = '挑战者，你听好了，我最喜欢绿帽子玩偶，请你帮我找到我命中注定的绿帽子玩偶吧！', Speaker = 5490, Option = '', Action = _EmptyTable,}, 
	[54767] = { id = 54767, Voice = '', Text = '太棒了，那么请你快点帮我们的伴侣们找到命中注定的玩偶吧！', Speaker = 5489, Option = '', Action = _EmptyTable,}, 
	[54766] = { id = 54766, Voice = '', Text = '需要我再说一遍告诉游戏规则吗？', Speaker = 5489, Option = '{需要,2},{不需要,3}', Action = _EmptyTable,}, 
	[54765] = { id = 54765, Voice = '', Text = '[c][ffff00]寻找的时候，要多多利用自己的照相机哦！也许玩偶们都藏在你意想不到的角落！[-][/c]', Speaker = 5489, Option = '', Action = _EmptyTable,}, 
	[54764] = { id = 54764, Voice = '', Text = '请你分别与三队的伴侣沟通之后，帮助他们寻找心仪的玩偶。', Speaker = 5489, Option = '', Action = _EmptyTable,}, 
	[54763] = { id = 54763, Voice = '', Text = '仔细听清楚！我们一共有三位玩偶伴侣，分别是绿帽子伴侣、红帽子伴侣和拉链熊伴侣。', Speaker = 5489, Option = '', Action = _EmptyTable,}, 
	[54762] = { id = 54762, Voice = '', Text = '帮助玩偶找到伴侣，你会得到应得的奖励！', Speaker = 5489, Option = '', Action = _EmptyTable,}, 
	[54761] = { id = 54761, Voice = '', Text = '请去东边寻找玩偶红娘，进行玩具们的下一个游戏吧！', Speaker = 5488, Option = '', Action = _EmptyTable,}, 
	[54760] = { id = 54760, Voice = '', Text = '很好~挑战者，你是我见过眼神最敏锐的人了，恭喜你完成了红帽仪仗队的游戏。', Speaker = 5488, Option = '', Action = _EmptyTable,}, 
	[54759] = { id = 54759, Voice = '', Text = '你的观察能力有待提高，请多多练习。', Speaker = 5488, Option = '', Action = _EmptyTable,}, 
	[54758] = { id = 54758, Voice = '', Text = '那么冒险者，看清楚了吗？', Speaker = 5488, Option = '', Action = _EmptyTable,}, 
	[54757] = { id = 54757, Voice = '', Text = '准备好了，第三次挑战开始！', Speaker = 5488, Option = '', Action = _EmptyTable,}, 
	[54756] = { id = 54756, Voice = '', Text = '第三次游戏你需要观察更仔细。', Speaker = 5488, Option = '', Action = _EmptyTable,}, 
	[54755] = { id = 54755, Voice = '', Text = '没想到第二个你也清楚的观察到了，我很满意！', Speaker = 5488, Option = '', Action = _EmptyTable,}, 
	[54754] = { id = 54754, Voice = '', Text = '你的观察能力有待提高，请多多练习。', Speaker = 5488, Option = '', Action = _EmptyTable,}, 
	[54753] = { id = 54753, Voice = '', Text = '那么冒险者，看清楚了吗？', Speaker = 5488, Option = '', Action = _EmptyTable,}, 
	[54752] = { id = 54752, Voice = '', Text = '大家准备好，第二个挑战！准备！开始！', Speaker = 5488, Option = '', Action = _EmptyTable,}, 
	[54751] = { id = 54751, Voice = '', Text = '想必你已经理解了我们的玩法了，那么下一关将会有两次动作，请仔细观察！', Speaker = 5488, Option = '', Action = _EmptyTable,}, 
	[54750] = { id = 54750, Voice = '', Text = '干得漂亮！挑战者，你的观察能力很不错！', Speaker = 5488, Option = '', Action = _EmptyTable,}, 
	[54749] = { id = 54749, Voice = '', Text = '你的观察能力有待提高，请多多练习。', Speaker = 5488, Option = '', Action = _EmptyTable,}, 
	[54748] = { id = 54748, Voice = '', Text = '那么冒险者，看清楚了吗？', Speaker = 5488, Option = '', Action = _EmptyTable,}, 
	[54746] = { id = 54746, Voice = '', Text = '我们的游戏马上开始，请各位仪仗队的队员准备完毕！', Speaker = 5488, Option = '', Action = _EmptyTable,}, 
	[54745] = { id = 54745, Voice = '', Text = '即将开始挑战，请问你需要再听一遍我们规则吗？', Speaker = 5488, Option = '{需要听规则,4},{不需要,5}', Action = _EmptyTable,}, 
	[54744] = { id = 54744, Voice = '', Text = '选择正确，你将会通过我们的游戏。', Speaker = 5488, Option = '', Action = _EmptyTable,}, 
	[54743] = { id = 54743, Voice = '', Text = '仔细观察我们的动作后，选择我们摆出的动作是X还是O。', Speaker = 5488, Option = '', Action = _EmptyTable,}, 
	[54742] = { id = 54742, Voice = '', Text = '我们即将开始3次挑战，红帽仪仗队的队员会为你摆出[c][ffff00]X或O的姿势[-][/c]。', Speaker = 5488, Option = '', Action = _EmptyTable,}, 
	[54741] = { id = 54741, Voice = '', Text = '嗯嗯，看来你是我们玩具的好朋友，欢迎你参与我们的游戏。', Speaker = 5488, Option = '', Action = _EmptyTable,}, 
	[54740] = { id = 54740, Voice = '', Text = '挑战者，让我看看你的狂欢票，如果没有狂欢票不能参与我们玩具的游戏！', Speaker = 5488, Option = '', Action = _EmptyTable,}, 
	[54739] = { id = 54739, Voice = '', Text = '如果通过他的考验，你可以进入下一个游戏哦~', Speaker = 5487, Option = '', Action = _EmptyTable,}, 
	[54738] = { id = 54738, Voice = '', Text = '第一个游戏，请去玩具工厂2F找玩具仪仗队的队长。', Speaker = 5487, Option = '', Action = _EmptyTable,}, 
	[54737] = { id = 54737, Voice = '', Text = '什么你不知道玩具狂欢节吗，那就好好听我讲[c][ffff00]狂欢规则[-][/c]哦！', Speaker = 5487, Option = '', Action = _EmptyTable,}, 
	[54736] = { id = 54736, Voice = '', Text = '来来来，给你一张狂欢票，如果想要参加玩具的狂欢，请拿给我们的工作人员哦~', Speaker = 5487, Option = '{什么？,2},{谢谢,3}', Action = _EmptyTable,}, 
	[54735] = { id = 54735, Voice = '', Text = '生面孔！是听说了我们玩具狂欢节，来参加的挑战者吗？', Speaker = 5487, Option = '', Action = _EmptyTable,}, 
	[54734] = { id = 54734, Voice = '', Text = '完成所有的小游戏，你将会得到丰厚的奖励哦~', Speaker = 5487, Option = '', Action = _EmptyTable,}, 
	[54733] = { id = 54733, Voice = '', Text = '第三个游戏是大法师狂骨的移形换影小游戏。', Speaker = 5487, Option = '', Action = _EmptyTable,}, 
	[54732] = { id = 54732, Voice = '', Text = '第二个游戏是红娘小游戏，请挑战者们好好寻找，要多多利用自己的[c][ffff00]照相机[-][/c]哦！', Speaker = 5487, Option = '', Action = _EmptyTable,}, 
	[54731] = { id = 54731, Voice = '', Text = '第一个是游戏玩具士兵礼队，这个游戏会考验到大家的[c][ffff00]观察力[-][/c]，要好好观察哦~', Speaker = 5487, Option = '', Action = _EmptyTable,}, 
	[54730] = { id = 54730, Voice = '', Text = '参与狂欢节会进行[c][ffff00]3个玩具的小游戏[-][/c]哦，成功通过游戏的挑战者会得到我们丰厚的奖励哦。', Speaker = 5487, Option = '', Action = _EmptyTable,}, 
	[54718] = { id = 54718, Voice = '', Text = '祝幸运与胜利与你同在。', Speaker = 5518, Option = '', Action = _EmptyTable,}, 
	[54717] = { id = 54717, Voice = '', Text = '很遗憾年轻人，银狼卡仑如冰晶一般的羽毛会在月光下闪闪发光，那是我见过最美的动物，你没办法一睹那风采了。', Speaker = 5518, Option = '', Action = _EmptyTable,}, 
	[54716] = { id = 54716, Voice = '', Text = '雪狼喜欢夜晚迎着月光出来猎食野兔，所以去猎杀几只雪原野兔，卡仑便会被野兔的味道所吸引而出现。你可有这份勇气?', Speaker = 5518, Option = '{有,4},{没有,5}', Action = _EmptyTable,}, 
	[54715] = { id = 54715, Voice = '', Text = '是的，卡仑就这么倒在了雪地里，冰冷的雪将它埋没，仇恨使它的灵魂无法安息。从那之后，卡仑便幻化成一头巨大的冰狼，猎杀着它为之付出一切的人类。', Speaker = 5518, Option = '', Action = _EmptyTable,}, 
	[54714] = { id = 54714, Voice = '', Text = '狼群被卡仑击退了，卡仑也遍体鳞伤。但村民们被卡仑的强大吓到了，猎人也觉得卡仑很有可能成为村庄的威胁，便将冰冷的枪口对准了卡仑。', Speaker = 5518, Option = '什么！', Action = _EmptyTable,}, 
	[54713] = { id = 54713, Voice = '', Text = '雪原上的狼群攻击了村庄，卡仑为了保护村里的人用尽了气力与狼群撕咬搏斗，月光映在它孤独奋战的身影上格外凌厉。', Speaker = 5518, Option = '厉害', Action = _EmptyTable,}, 
	[54712] = { id = 54712, Voice = '', Text = '年轻猎人给这头小狼起了个名字，叫作“卡仑”。卡仑一天天茁壮的成长，确实也成为了村子忠诚的守护者！直到又是一年新月的晚上…', Speaker = 5518, Option = '发生了什么？', Action = _EmptyTable,}, 
	[54711] = { id = 54711, Voice = '', Text = '传说在一个新月的晚上，一位年轻的猎人偷走了雪原上的一只小狼。他充满私心的想着，狼长大了便能帮他对抗雪原上的其他狼。', Speaker = 5518, Option = '然后呢？', Action = _EmptyTable,}, 
	[54710] = { id = 54710, Voice = '', Text = '那你一定知道，在雪原行走可要小心呀！年轻人！', Speaker = 5518, Option = '', Action = _EmptyTable,}, 
	[54709] = { id = 54709, Voice = '', Text = '孩子，你听过新月银狼的故事吗？', Speaker = 5518, Option = '{听过,2},{没听过,3}', Action = _EmptyTable,}, 
	[54569] = { id = 54569, Voice = '', Text = '这可是代表着你，帮助赶走孩子们噩梦的最好勋章。', Speaker = 5289, Option = '谢谢', Action = _EmptyTable,}, 
	[54568] = { id = 54568, Voice = '', Text = '由于你非凡的胆量与能力，我将赠你一根坎卜斯的桦树枝。', Speaker = 5289, Option = '是什么？', Action = _EmptyTable,}, 
	[54567] = { id = 54567, Voice = '', Text = '这些玩意儿虽然邪恶，却能为我在制作孩子们的玩具时，带来许多灵感。', Speaker = 5289, Option = '', Action = _EmptyTable,}, 
	[54566] = { id = 54566, Voice = '', Text = '实在是让我刮目相看！', Speaker = 5289, Option = '过奖', Action = _EmptyTable,}, 
	[54565] = { id = 54565, Voice = '', Text = '你这非凡的年轻人，居然能将坎卜斯这恶魔的礼物全部收集到。', Speaker = 5289, Option = '', Action = _EmptyTable,}, 
	[54564] = { id = 54564, Voice = '', Text = '找玩具工厂2F的梅丽莎聊聊吧，她小时候可是查理的忠实粉丝，这孩子一直相信查理是清白的。', Speaker = 5289, Option = '', Action = _EmptyTable,}, 
	[54563] = { id = 54563, Voice = '', Text = '腹语师查理可是圣诞村最好的腹语表演者，但是因为一起抢匪案件，他被当成杀人犯，圣诞村的村民将他处死，之后他的玩偶熊们不知怎么就纷纷暴走，频繁攻击这里的人们。', Speaker = 5289, Option = '', Action = _EmptyTable,}, 
	[54562] = { id = 54562, Voice = '', Text = '如果你一无所知，就不要轻易揭开尘封的历史，年轻人。', Speaker = 5289, Option = '', Action = _EmptyTable,}, 
	[54561] = { id = 54561, Voice = '', Text = '说起查理，那可是我的老朋友了。你也听说过他吗？', Speaker = 5289, Option = '{是的,2},{没有,3}', Action = _EmptyTable,}, 
	[54560] = { id = 54560, Voice = '', Text = '我的两个学徒塞缪妮和科林正在玩具工厂1F收集关于坎卜斯的礼物，相信你能从他们那里知道很多你感兴趣的信息。当你帮助完他们，再回来找我，我一定不会让你空手而归。', Speaker = 5289, Option = '', Action = _EmptyTable,}, 
	[54559] = { id = 54559, Voice = '', Text = '坎卜斯是个专门惩戒坏孩子的圣诞恶魔！他的方式邪恶可怕令人发指！', Speaker = 5289, Option = '', Action = _EmptyTable,}, 
	[54558] = { id = 54558, Voice = '', Text = '噢…年轻人，你也对坎卜斯的传说抱有颇大的兴趣，是不是？', Speaker = 5289, Option = '{是的,2},{不是,3}', Action = _EmptyTable,}, 
	[54557] = { id = 54557, Voice = '', Text = '你是找到真相的英雄，只有你带着这块陶板，查理先生的玩偶熊们也能够放下仇恨了。', Speaker = 5292, Option = '', Action = _EmptyTable,}, 
	[54556] = { id = 54556, Voice = '', Text = '这块陶板就交给你了！', Speaker = 5292, Option = '好的', Action = _EmptyTable,}, 
	[54555] = { id = 54555, Voice = '', Text = '是的，有了这些信息，我将会把腹语师查理事件的真相告诉整个圣诞村。', Speaker = 5292, Option = '', Action = _EmptyTable,}, 
	[54554] = { id = 54554, Voice = '', Text = '就在刚才，我也找到了查理先生生前留下的遗物。一块陈述真相的陶板！', Speaker = 5292, Option = '陶板？', Action = _EmptyTable,}, 
	[54553] = { id = 54553, Voice = '', Text = '真高兴你能看清楚事实的真相！', Speaker = 5292, Option = '', Action = _EmptyTable,}, 
	[54552] = { id = 54552, Voice = '', Text = '不对，你没有读清事情的真相，再从你得到的那些物品里找找线索吧！我相信你一定可以的。', Speaker = 5292, Option = '', Action = _EmptyTable,}, 
	[54551] = { id = 54551, Voice = '', Text = '我已经从中读出了真相，你看出谁才是杀死贝尔摩的凶手了吗？', Speaker = 5292, Option = '{丹,2},{曼逊,3},{里克,4},{卡尔,5}', Action = _EmptyTable,}, 
	[54550] = { id = 54550, Voice = '', Text = '感谢你为亲爱的查理先生找齐了洗清冤屈的线索！', Speaker = 5292, Option = '不客气', Action = _EmptyTable,}, 
	[54549] = { id = 54549, Voice = '', Text = '确实是这只肩包没错！看看这群劫匪干的好事！从这些钻石的分配中，我仿佛又得到了许多线索！！', Speaker = 5292, Option = '', Action = _EmptyTable,}, 
	[54548] = { id = 54548, Voice = '', Text = '这只锈箭就是我要找的那一只，太可怕了！不过这同时也证明了，查理先生的清白，因为查理先生根本不会用弓箭。', Speaker = 5292, Option = '', Action = _EmptyTable,}, 
	[54547] = { id = 54547, Voice = '', Text = '一幅小女孩的画也许看起来平淡无奇，但里面却能读出很多事实与真相，冒险者你也看出来了吗？', Speaker = 5292, Option = '', Action = _EmptyTable,}, 
	[54546] = { id = 54546, Voice = '', Text = '有了这封密信，就能充分证明，查理先生果然如我所说是个好人！谢谢你冒险者，不是你，大家不会知道真相。', Speaker = 5292, Option = '', Action = _EmptyTable,}, 
	[54545] = { id = 54545, Voice = '', Text = '玩偶里原来隐藏着如此重要的线索，我似乎看到了些当时的景象！', Speaker = 5292, Option = '', Action = _EmptyTable,}, 
	[54544] = { id = 54544, Voice = '', Text = '太好了，从这封信我们能看出，查理先生，一定是被这四个人当替罪羔羊，给陷害了！', Speaker = 5292, Option = '', Action = _EmptyTable,}, 
	[54543] = { id = 54543, Voice = '', Text = '你没有找到我想要的线索，查理先生的案件并没有进展。', Speaker = 5292, Option = '', Action = _EmptyTable,}, 
	[54542] = { id = 54542, Voice = '', Text = '太可惜了，那肩包可价值不菲啊！', Speaker = 5292, Option = '', Action = _EmptyTable,}, 
	[54541] = { id = 54541, Voice = '', Text = '如此危险的武器，你也有你的难处不是吗？', Speaker = 5292, Option = '', Action = _EmptyTable,}, 
	[54540] = { id = 54540, Voice = '', Text = '这么重要的线索，确实不容易得到，我理解你。', Speaker = 5292, Option = '', Action = _EmptyTable,}, 
	[54539] = { id = 54539, Voice = '', Text = '真的很遗憾没有人愿意为查理先生洗清冤屈。', Speaker = 5292, Option = '', Action = _EmptyTable,}, 
	[54538] = { id = 54538, Voice = '', Text = '如果没人追问，那么真相将永远藏于地底。', Speaker = 5292, Option = '', Action = _EmptyTable,}, 
	[54537] = { id = 54537, Voice = '', Text = '哎…难道查理先生就永远洗脱不了他的冤屈…', Speaker = 5292, Option = '', Action = _EmptyTable,}, 
	[54536] = { id = 54536, Voice = '', Text = '据说这只肩包里藏着当时劫匪们的战利品，价值不菲。那里面一定隐藏着一些秘密，请务必找到这只肩包的10个碎片，让我看看好吗？', Speaker = 5292, Option = '{交予,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[54535] = { id = 54535, Voice = '', Text = '一根杀死受害者的断箭，说不定能诉说当时事件的真相，请你找到它的10个碎片后带给我，我们一起分析分析这根断箭。', Speaker = 5292, Option = '{交予,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[54534] = { id = 54534, Voice = '', Text = '一幅属于受害者小女儿的画，据说这群劫匪的头领曾是这位小姑娘的绘画老师。能不能从中找到线索，还得劳烦你找到这幅画的10个碎片，带给我看看。', Speaker = 5292, Option = '{交予,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[54533] = { id = 54533, Voice = '', Text = '一封抢匪团伙成员之间沟通的密信，一定揭示了当年他们犯下的罪行！现在这封密信已经支离破碎了，你能否带给我它的10个碎片？', Speaker = 5292, Option = '{交予,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[54532] = { id = 54532, Voice = '', Text = '一只属于受害者小女儿的玩偶，我们一定能从这只玩偶上找到一些线索。你能带给我这只玩偶的10个碎片吗？', Speaker = 5292, Option = '{交予,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[54531] = { id = 54531, Voice = '', Text = '那篇告示详细的记载了腹语师查理先生被处死的案件，这篇告示将会让我们了解当时的许多情况。你能带给我它的10个碎片吗？', Speaker = 5292, Option = '{交予,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[54530] = { id = 54530, Voice = '', Text = '有机会能见识发现真相的眼睛，真是为我的工作提供了宝贵的资料呢，谢谢你。', Speaker = 5291, Option = '', Action = _EmptyTable,}, 
	[54529] = { id = 54529, Voice = '', Text = '看似能带给人幸运的四叶草经过魔鬼之手也只能带给人不幸，实在是让人叹息！', Speaker = 5291, Option = '', Action = _EmptyTable,}, 
	[54528] = { id = 54528, Voice = '', Text = '这只心脏仿佛还能听到它的跳动声，真是个魔鬼的产物！！', Speaker = 5291, Option = '', Action = _EmptyTable,}, 
	[54527] = { id = 54527, Voice = '', Text = '这就是传说中会说话的鱼啊！我今天可算是长见识了。', Speaker = 5291, Option = '', Action = _EmptyTable,}, 
	[54526] = { id = 54526, Voice = '', Text = '哼！你身上没有我要求的东西！我可不喜欢别人跟我乱开玩笑。', Speaker = 5291, Option = '', Action = _EmptyTable,}, 
	[54525] = { id = 54525, Voice = '', Text = '没有这些资料我就没办法让塔伦老师满意，哎…', Speaker = 5291, Option = '', Action = _EmptyTable,}, 
	[54524] = { id = 54524, Voice = '', Text = '我就知道你可能没这个实力，不过这不怪你。', Speaker = 5291, Option = '', Action = _EmptyTable,}, 
	[54523] = { id = 54523, Voice = '', Text = '我懂我懂，谁听到心脏这么恐怖的东西都会吓的不知所措的。', Speaker = 5291, Option = '', Action = _EmptyTable,}, 
	[54522] = { id = 54522, Voice = '', Text = '我知道这种传奇生物不是谁都能搞的到的，哎，可真想见识一下啊…', Speaker = 5291, Option = '', Action = _EmptyTable,}, 
	[54521] = { id = 54521, Voice = '', Text = '这颗神奇的眼球能看清那些充斥着嫉妒心的孩子们的真面目，每到圣诞前夜，坎卜斯会戴着这颗眼球观察圣诞村的所有孩子。你可否带给我它的10个碎片？', Speaker = 5291, Option = '{交予,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[54520] = { id = 54520, Voice = '', Text = '这片四叶草，是圣诞恶魔给傲慢的孩子的礼物，它会吸走孩子们身上的幸运，让他们的人生惨淡无比，危险至极！你也有实力将它的10个碎片带给我看看嘛？', Speaker = 5291, Option = '{交予,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[54519] = { id = 54519, Voice = '', Text = '一颗埋在树底的心脏，已经被冰雪冻得冰冷僵硬，这是坎卜斯用来恐吓吝啬孩子的可怕礼物。你可有能力将它的10个碎片带给我见一见？', Speaker = 5291, Option = '{交予,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[54518] = { id = 54518, Voice = '', Text = '传说中可以开口说话的鱼，是坎卜斯的分身之一。给贪婪的孩子带来厄运，这样的稀奇物品，你能否带给我它的10个碎片。', Speaker = 5291, Option = '{交予,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[54517] = { id = 54517, Voice = '', Text = '这些金沙我可没勇气用，光是研究一下，就够我受的了。谢谢你提供的样品。', Speaker = 5290, Option = '', Action = _EmptyTable,}, 
	[54516] = { id = 54516, Voice = '', Text = '真是可怕，你能想象那些孩子们脱不下鞋时的痛苦表情吗！？收好它们，让它们远离这些可怜的孩子吧！', Speaker = 5290, Option = '', Action = _EmptyTable,}, 
	[54515] = { id = 54515, Voice = '', Text = '是我要找的毒糖豆，谢谢你，不过快把它收好，我已经过了吃糖的年纪了。', Speaker = 5290, Option = '', Action = _EmptyTable,}, 
	[54514] = { id = 54514, Voice = '', Text = '太好了，就是这只玩偶，光看着它的外表我就浑身起鸡皮疙瘩。你让我长见识了。', Speaker = 5290, Option = '', Action = _EmptyTable,}, 
	[54513] = { id = 54513, Voice = '', Text = '哼！你身上没有我要求的东西！我可不喜欢别人跟我乱开玩笑。', Speaker = 5290, Option = '', Action = _EmptyTable,}, 
	[54512] = { id = 54512, Voice = '', Text = '金沙你都不感兴趣，我可不知道你对什么感兴趣。', Speaker = 5290, Option = '', Action = _EmptyTable,}, 
	[54511] = { id = 54511, Voice = '', Text = '嗨，你是在害怕什么吗？这只是个童谣，放轻松！', Speaker = 5290, Option = '', Action = _EmptyTable,}, 
	[54510] = { id = 54510, Voice = '', Text = '这种危险物品果然不适合你这样的小毛孩子。', Speaker = 5290, Option = '', Action = _EmptyTable,}, 
	[54509] = { id = 54509, Voice = '', Text = '嗯…理解你的能力确实有限。', Speaker = 5290, Option = '', Action = _EmptyTable,}, 
	[54508] = { id = 54508, Voice = '', Text = '金光闪闪的恶魔金沙，充满着危险的气息。据说恶魔金沙专用来引诱那些爱说谎的孩子，如果你能带给我它的10个碎片，那会很好。', Speaker = 5290, Option = '{交予,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[54507] = { id = 54507, Voice = '', Text = '旋转红鞋是一双永不停歇的红舞鞋，据说虚荣的孩子穿上它，将永远无法将它从脚上脱下。你能带给我它的10个碎片吗？', Speaker = 5290, Option = '{交予,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[54506] = { id = 54506, Voice = '', Text = '苦涩的糖豆是一颗带着剧毒的糖果，坎卜斯用它来惩戒懒惰的孩子。你能带给我它的10个碎片让我研究一下吗？', Speaker = 5290, Option = '{交予,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[54505] = { id = 54505, Voice = '', Text = '金线兔玩偶据说是圣诞恶魔用来惩罚和告诫圣诞村自私小孩们的可怕玩偶，我需要它的一手资料，你能提供给我它的10个碎片吗？', Speaker = 5290, Option = '{交予,2},{没兴趣,3}', Action = _EmptyTable,}, 
	[54335] = { id = 54335, Voice = '', Text = '玩偶熊慌张的乱跑，口袋里掉出一些画笔和颜料！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54333] = { id = 54333, Voice = '', Text = '我相信只有你才有这个资格！', Speaker = 5288, Option = '谢谢', Action = _EmptyTable,}, 
	[54332] = { id = 54332, Voice = '', Text = '带着它不但不会受到它的任何影响，还会让你的心灵更加壮大。', Speaker = 5288, Option = '', Action = _EmptyTable,}, 
	[54331] = { id = 54331, Voice = '', Text = '因为只有心灵足够强大的人，才有能力保管。', Speaker = 5288, Option = '', Action = _EmptyTable,}, 
	[54330] = { id = 54330, Voice = '', Text = '这样危险的东西，只有你才能保管它。', Speaker = 5288, Option = '为什么？', Action = _EmptyTable,}, 
	[54329] = { id = 54329, Voice = '', Text = '我这里有一条灵偶发带，是从那些可怕的娃娃身上取下来的。', Speaker = 5288, Option = '', Action = _EmptyTable,}, 
	[54328] = { id = 54328, Voice = '', Text = '很高兴你能让三个姑娘重新找回自己的心灵，战胜魔灵娃娃的诅咒。', Speaker = 5288, Option = '这没什么', Action = _EmptyTable,}, 
	[54327] = { id = 54327, Voice = '', Text = '你也能在玩具工厂二层找到这三个姑娘，和她们聊聊吧。如果你能成功拉回她们的心灵，再回来找我…', Speaker = 5288, Option = '', Action = _EmptyTable,}, 
	[54326] = { id = 54326, Voice = '', Text = '我在这玩具工厂的二层发现了这三只到处袭击冒险者的娃娃！它们身上有锁住少女心灵的容器，找到那些容器，说不定能拯救三个失去心灵的少女！', Speaker = 5288, Option = '', Action = _EmptyTable,}, 
	[54325] = { id = 54325, Voice = '', Text = '传说有三只被诅咒的娃娃，怀揣心事的少女拥着它们入睡，心灵便会被囚禁在娃娃的身体里，陷入拒绝现实，空洞失忆的状态当中。', Speaker = 5288, Option = '', Action = _EmptyTable,}, 
	[54324] = { id = 54324, Voice = '', Text = '噢…年轻人，你听说过魔灵娃娃的诅咒吗？', Speaker = 5288, Option = '{听过,2},{没有,3}', Action = _EmptyTable,}, 
	[54323] = { id = 54323, Voice = '', Text = '你没有对方想要的东西。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[54322] = { id = 54322, Voice = '', Text = '但一切都晚了，我的生母因为我的愿望永远的走了…回到家后，我十分绝望的抱着一只娃娃睡着了…之后，不知怎么的，我就站在这里了。', Speaker = 5287, Option = '', Action = _EmptyTable,}, 
	[54321] = { id = 54321, Voice = '', Text = '我想起了一切…我许愿让虐待我的爸爸妈妈和哥哥死去，可第二天当我看到他们完好无损的站在我面前，我就想通了他们根本不是我真正的亲人，也恍然大悟我亲生母亲就是村口那位妇人。', Speaker = 5287, Option = '', Action = _EmptyTable,}, 
	[54320] = { id = 54320, Voice = '', Text = '这不是我所等的答案。', Speaker = 5287, Option = '', Action = _EmptyTable,}, 
	[54319] = { id = 54319, Voice = '', Text = '告诉我冒险者，为什么我感到如此绝望…', Speaker = 5287, Option = '{因为愿望没实现,5},{因为家人,4},{因为精灵可怕,5}', Action = _EmptyTable,}, 
	[54318] = { id = 54318, Voice = '', Text = '这…这是我的玫瑰…我…我好像犯了很可怕的错误…', Speaker = 5287, Option = '', Action = _EmptyTable,}, 
	[54317] = { id = 54317, Voice = '', Text = '呵呵…嘿嘿…再见…', Speaker = 5287, Option = '', Action = _EmptyTable,}, 
	[54316] = { id = 54316, Voice = '', Text = '你见到我的冰晶玫瑰了吗…那里面可有个精灵，呵呵…呵呵…', Speaker = 5287, Option = '{交给你,2},{没见到,3}', Action = _EmptyTable,}, 
	[54315] = { id = 54315, Voice = '', Text = '虽然我真的在葬礼上见到了他，可我怎么也开心不起来…回到家后，我便抱着一只娃娃睡着了…之后，不知怎么的，我就站在这里了。', Speaker = 5286, Option = '', Action = _EmptyTable,}, 
	[54314] = { id = 54314, Voice = '', Text = '我想起了一切…为了再次见到葬礼上的意中人，我偷偷拿了一根箭筒里的箭，乘妹妹熟睡的时候向她刺了下去…', Speaker = 5286, Option = '', Action = _EmptyTable,}, 
	[54313] = { id = 54313, Voice = '', Text = '这好像不是我在寻找的答案。', Speaker = 5286, Option = '', Action = _EmptyTable,}, 
	[54312] = { id = 54312, Voice = '', Text = '告诉我冒险者，我内心为何如此的不安…', Speaker = 5286, Option = '{因为意中人,5},{因为婶婶,5},{因为妹妹,4}', Action = _EmptyTable,}, 
	[54311] = { id = 54311, Voice = '', Text = '这…这是我意中人的箭筒…我…我好像做了很可怕的事…', Speaker = 5286, Option = '', Action = _EmptyTable,}, 
	[54310] = { id = 54310, Voice = '', Text = '那你走开…', Speaker = 5286, Option = '', Action = _EmptyTable,}, 
	[54309] = { id = 54309, Voice = '', Text = '你见到我的羊皮箭筒了吗！那是我意中人的…把我的羊皮箭筒还给我…', Speaker = 5286, Option = '{交给你,2},{没见到,3}', Action = _EmptyTable,}, 
	[54308] = { id = 54308, Voice = '', Text = '我想起了一切…我的爱人麦斯把他宝贵的眼睛给了我，他的眼睛是水绿色的，当我看到镜子的那一刻，我就知道他不在了。我悲痛欲绝，回到家后，我便抱着一只娃娃睡着了…之后，不知怎么的，我就站在这里了。', Speaker = 5285, Option = '', Action = _EmptyTable,}, 
	[54307] = { id = 54307, Voice = '', Text = '这不是我要的答案…', Speaker = 5285, Option = '', Action = _EmptyTable,}, 
	[54306] = { id = 54306, Voice = '', Text = '告诉我冒险者，我为何会这么悲伤…', Speaker = 5285, Option = '{因为爱人牺牲,4},{因为眼睛失明,5},{因为铜镜碎了,5}', Action = _EmptyTable,}, 
	[54305] = { id = 54305, Voice = '', Text = '这…这是我的铜镜…我…我好像想起来我是谁了…', Speaker = 5285, Option = '', Action = _EmptyTable,}, 
	[54304] = { id = 54304, Voice = '', Text = '…那你快帮我找找。', Speaker = 5285, Option = '', Action = _EmptyTable,}, 
	[54303] = { id = 54303, Voice = '', Text = '你见到我的铜镜了吗？那是面没有装饰的铜镜…把我的铜镜给我。', Speaker = 5285, Option = '{交给你,2},{没见到,3}', Action = _EmptyTable,}, 
	[53828] = { id = 53828, Voice = '', Text = '看，它们如此悲伤的离开了，伊莱纳花对他们来说，就像卡米耶对我一样重要。直到今天，我才对这个世界有了新的认识。年轻的冒险者，很抱歉将你牵涉到这件悲剧之中，你是个好孩子。希望你在往后的冒险生活中，不要犯我的错误。', Speaker = 5503, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[53827] = { id = 53827, Voice = '', Text = '咕咕咕咕……咕咕……', Speaker = 5500, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[53826] = { id = 53826, Voice = '', Text = '当我带着偏见去看待这个世界时，很难看清表象下的真实。当我盲目的坚持自己的正义时，也许会做出违背正义的行为。', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53825] = { id = 53825, Voice = '', Text = '原来我才是那个伤害伊莱纳花的人，那些玩偶熊是为了阻止我破坏花茎，而我……却伤害了它们。', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53824] = { id = 53824, Voice = '', Text = '你走了以后，我仔细观察过了伊莱纳花的藤蔓，那些荆棘确实是和花朵的根部很像…作为一个画家，我没有发现这样的细节，误以为那些荆棘挡住了伊莱纳花，真是失败…我原以为这朵花只是一朵娇柔的生命，没想到原来它是这么的坚强……', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53823] = { id = 53823, Voice = '', Text = '年轻人，你回来了。这些……生灵，似乎在祭奠伊莱纳花。', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53822] = { id = 53822, Voice = '', Text = '对不起冒险者，让你这么为难，请你离开吧，我现在不想说话了…', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[53821] = { id = 53821, Voice = '', Text = '为什么事情会变成这样？大师明明可以为卡米耶夫人画出伊莱纳花的……', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[53820] = { id = 53820, Voice = '', Text = '伊莱纳花凋谢了？！原来克劳德大师没能实现自己的心愿。', Speaker = 6991, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[53819] = { id = 53819, Voice = '', Text = '没喝呀，是不是我做的不够好？', Speaker = 6991, Option = '告诉巧巧事实', Action = _EmptyTable,}, 
	[53818] = { id = 53818, Voice = '', Text = '太好了！能够帮助到你们，我真的太高兴了！等克劳德大师的作品诞生以后，我会再去普隆德拉观看画展的！到时候我们可以一起去哦～', Speaker = 6991, Option = '', Emoji = 2, Action = _EmptyTable,}, 
	[53817] = { id = 53817, Voice = '', Text = '没关系，如果不好喝也没关系，只是没能帮到克劳德大师，我觉得很遗憾。', Speaker = 6991, Option = '{他说非常好喝,15},{我们没有喝…,16}', Action = _EmptyTable,}, 
	[53816] = { id = 53816, Voice = '', Text = '你们觉得不好喝吗？', Speaker = 6991, Option = '……', Action = _EmptyTable,}, 
	[53815] = { id = 53815, Voice = '', Text = '克劳德大师喝过了我专门为你们制作的巧克力吗？他觉得怎么样呢？', Speaker = 6991, Option = '……', Action = _EmptyTable,}, 
	[53814] = { id = 53814, Voice = '', Text = '诶，冒险者你回来了？！', Speaker = 6991, Option = '', Emoji = 2, Action = _EmptyTable,}, 
	[53813] = { id = 53813, Voice = '', Text = '……请你帮我谢谢巧巧莉的好意，可是我实在没有心情，冒险者我需要冷静冷静…', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53812] = { id = 53812, Voice = '', Text = '还有什么事情吗？我希望你不要打扰我了……抱歉，冒险者，我……', Speaker = 5503, Option = '热巧克力…', Action = _EmptyTable,}, 
	[53811] = { id = 53811, Voice = '', Text = '我需要冷静一下…', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53810] = { id = 53810, Voice = '', Text = '冒险者，事情为什么会变成这样？我还能做些什么？', Speaker = 5503, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[53809] = { id = 53809, Voice = '', Text = '我只是想为卡米耶画一幅画而已……', Speaker = 5503, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[53808] = { id = 53808, Voice = '', Text = '冒险者，告诉我，到底是怎么回事？', Speaker = 5503, Option = '{是我们的错,10},{是你的错,10},{是魔物捣鬼！,10}', Action = _EmptyTable,}, 
	[53807] = { id = 53807, Voice = '', Text = '还是我做错了什么？那些荆棘真的是伊莱纳花的花茎吗？是我亲手扼杀了这个脆弱的生命吗？', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53806] = { id = 53806, Voice = '', Text = '怎么会这样！是刚刚的那个邪恶的魔物施了什么法术吗？为什么？！', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53805] = { id = 53805, Voice = '', Text = '不！！！！不！！！！！伊莱纳花！', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53804] = { id = 53804, Voice = '', Text = '咕咕咕咕咕咕咕…………', Speaker = 5500, Option = '', Action = _EmptyTable,}, 
	[53803] = { id = 53803, Voice = '', Text = '伊莱纳花的花瓣从花苞上脱落，落在地上，花朵急速的枯萎了。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[53802] = { id = 53802, Voice = '', Text = '听你的吧，我只是个画家……现在的情况，是我没有料想到的…', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53801] = { id = 53801, Voice = '', Text = '可是这个魔物…守护灵…咕咕…挡在这里，我们要怎么办呢？', Speaker = 5503, Option = '{不管它,9},{不知道,9},{击退它,9}', Action = _EmptyTable,}, 
	[53800] = { id = 53800, Voice = '', Text = '不管怎么样，我的妻子希望可以看见我画出的伊莱纳花，我必须达成她的愿望！', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53799] = { id = 53799, Voice = '', Text = '潘兰小姐走了吗……她说那些荆棘是伊莱纳花的花茎，那我们刚刚不是在伤害伊莱纳花吗……你相信她说的话吗？', Speaker = 5503, Option = '{我相信…,8}{我不信,8}{不要在意她,8}', Action = _EmptyTable,}, 
	[53798] = { id = 53798, Voice = '', Text = '我想我已经找到我想要守护的东西……', Speaker = 5502, Option = '', Action = _EmptyTable,}, 
	[53797] = { id = 53797, Voice = '', Text = '伊莱纳花即将凋谢，我也不会继续呆在姜饼城了……', Speaker = 5502, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[53796] = { id = 53796, Voice = '', Text = '直到在姜饼城见到咕咕和伊莱纳花，我才明白那些等级、卡片、装备和精炼并不是我冒险所追求的一切。', Speaker = 5502, Option = '', Action = _EmptyTable,}, 
	[53795] = { id = 53795, Voice = '', Text = '曾经我也和你很像……一个无知的冒险者。', Speaker = 5502, Option = '', Action = _EmptyTable,}, 
	[53794] = { id = 53794, Voice = '', Text = '……', Speaker = 5502, Option = '', Action = _EmptyTable,}, 
	[53793] = { id = 53793, Voice = '', Text = '那些帮忙保护伊莱纳花的玩偶熊呢，你把它们怎么了？', Speaker = 5502, Option = '{我没有伤害它们,3},{我把它们杀了,3},{我不在乎它们！,3}', Action = _EmptyTable,}, 
	[53792] = { id = 53792, Voice = '', Text = '是吗？听到这种话从你口中说出来……我……', Speaker = 5502, Option = '', Action = _EmptyTable,}, 
	[53791] = { id = 53791, Voice = '', Text = '冒险者，你也是凶手之一！', Speaker = 5502, Option = '{我很难过…,7},{我不相信你说的,7},{我不在意这些！,7}', Action = _EmptyTable,}, 
	[53790] = { id = 53790, Voice = '', Text = '到底是什么让人类这么自负呢？凭借自己的武力，去践踏其他的生命？', Speaker = 5502, Option = '', Action = _EmptyTable,}, 
	[53789] = { id = 53789, Voice = '', Text = '咕咕不是魔物…大概只有人类才会把其他的生灵称为魔物吧…真可笑，有利于自己的生灵就当作宠物，稍有不逊的生灵就当作魔物！不要叫我什么小姐，我不是什么柔柔弱弱的小姑娘，我叫潘兰！', Speaker = 5502, Option = '', Action = _EmptyTable,}, 
	[53788] = { id = 53788, Voice = '', Text = '这位小姐，你能听懂这个魔物说话吗？你不要信任这些魔物，他们都想要侵占人类的土地，也许他是骗你的…你看，伊莱纳花还在盛开，怎么可能会这么快就凋谢呢？', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53787] = { id = 53787, Voice = '', Text = '真的救不活了吗…… 怎么会这样呢？', Speaker = 5502, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[53786] = { id = 53786, Voice = '', Text = '咕咕咕……呜呜呜……咕咕……', Speaker = 5500, Option = '', Action = _EmptyTable,}, 
	[53785] = { id = 53785, Voice = '', Text = '咕咕不哭，你快试试能不能救回伊莱纳花……', Speaker = 5502, Option = '', Action = _EmptyTable,}, 
	[53784] = { id = 53784, Voice = '', Text = '……呼咕咕咕咕……咕咕……呜呜呜…', Speaker = 5500, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[53783] = { id = 53783, Voice = '', Text = '你才是魔物，咕咕是森林的守护灵！他一直守护着姜饼城雪原上的松树林，还有……伊莱纳花……', Speaker = 5502, Option = '', Action = _EmptyTable,}, 
	[53782] = { id = 53782, Voice = '', Text = '有魔物！', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53781] = { id = 53781, Voice = '', Text = '……呼咕咕咕……', Speaker = 5500, Option = '', Action = _EmptyTable,}, 
	[53780] = { id = 53780, Voice = '', Text = '如果我没有松懈，就不会让你们有机会靠近伊莱纳花……怎么办？花茎都被你们破坏了……', Speaker = 5502, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[53779] = { id = 53779, Voice = '', Text = '都怪我，我以为你没办法打败玩偶熊，就没有把这件事情放在心上。', Speaker = 5502, Option = '', Action = _EmptyTable,}, 
	[53778] = { id = 53778, Voice = '', Text = '我早就听森林的守护灵说过，几天前你就在这里破坏伊莱纳花的花茎…它请玩偶熊拿走了你的东西，希望转移你的注意力，没想到你又来了！', Speaker = 5502, Option = '', Action = _EmptyTable,}, 
	[53777] = { id = 53777, Voice = '', Text = '什么？！', Speaker = 5503, Option = '', Emoji = 1, Action = _EmptyTable,}, 
	[53776] = { id = 53776, Voice = '', Text = '你以为这些荆棘是什么？杂草吗？这些荆棘是伊莱纳花花茎的一部分！你们破坏了伊莱纳的茎，它的花朵很快就会凋谢了。', Speaker = 5502, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[53775] = { id = 53775, Voice = '', Text = '…………真愚蠢啊，无知而愚昧的人类…', Speaker = 5502, Option = '', Action = _EmptyTable,}, 
	[53774] = { id = 53774, Voice = '', Text = '…………', Speaker = 5502, Option = '', Action = _EmptyTable,}, 
	[53773] = { id = 53773, Voice = '', Text = '这位小姐，我只是一个想为伊莱纳花作画的画家而已，我们不准备摘走这朵花，请你安心。', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53772] = { id = 53772, Voice = '', Text = '这副什么都不知道的嘴脸又是怎么样？你想说你根本什么都不知道吗？', Speaker = 5502, Option = '', Emoji = 12, Action = _EmptyTable,}, 
	[53771] = { id = 53771, Voice = '', Text = '……？？', Speaker = 0, Option = '', Emoji = 8, Action = _EmptyTable,}, 
	[53770] = { id = 53770, Voice = '', Text = '你们这两个卑鄙无耻的强盗！你们对伊莱纳花做了什么？！', Speaker = 5502, Option = '', Emoji = 12, Action = _EmptyTable,}, 
	[53769] = { id = 53769, Voice = '', Text = '可恶！你做了些什么！', Speaker = 5502, Option = '', Action = _EmptyTable,}, 
	[53768] = { id = 53768, Voice = '', Text = '也许明年，一切都会来不及……能够遇见你真是一件幸运的事情啊！', Speaker = 5503, Option = '', Emoji = 30, Action = _EmptyTable,}, 
	[53767] = { id = 53767, Voice = '', Text = '另一方面是因为……我的年龄越来越大了，近期眼睛一直不舒服，眼前的光景越来越模糊，我知道留给我作画的时间不多了……', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53766] = { id = 53766, Voice = '', Text = '……诶，不瞒你说，我这么心急想要快点作画一方面是因为我妻子的身体不太好。', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53765] = { id = 53765, Voice = '', Text = '完成了吗？年轻人就是有效率，连我都充满了干劲！', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53764] = { id = 53764, Voice = '', Text = '好吧，哈哈哈，真是热心的冒险者！女神保佑你的生活充满爱与美满……', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53763] = { id = 53763, Voice = '', Text = '大功告成…等我支起画架就开始作画吧…', Speaker = 5503, Option = '交给我吧！', Action = _EmptyTable,}, 
	[53762] = { id = 53762, Voice = '', Text = '为了防止发生什么意外，我们快把荆棘清理一下，开始作画吧！我清理左边的，你清理右边的！', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53761] = { id = 53761, Voice = '', Text = '做的不错，我刚刚看见你矫健的身姿，真令人安心，有你在身边我就可以全心投入作画了。', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53760] = { id = 53760, Voice = '', Text = '又是这群强盗！一次次的打扰我作画！你没事吧，冒险者？', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53759] = { id = 53759, Voice = '', Text = '太好了，伊莱纳花没有什么影响！', Speaker = 0, Option = '没事', Action = _EmptyTable,}, 
	[53758] = { id = 53758, Voice = '', Text = '伊莱纳花似乎发出一些呼唤，引起了周围的一些骚动！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[53757] = { id = 53757, Voice = '', Text = '救救我…救救我…', Speaker = 5501, Option = '', Action = _EmptyTable,}, 
	[53756] = { id = 53756, Voice = '', Text = '请别伤害我……', Speaker = 5501, Option = '{出现幻觉了…,5},{是花在说话？,5},{我听见了什么？,5}', Action = _EmptyTable,}, 
	[53755] = { id = 53755, Voice = '', Text = '尖锐的荆棘里有一只神秘而高傲的花朵，它粉嫩的花瓣仿佛在向人倾诉些什么…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[53754] = { id = 53754, Voice = '', Text = '走近点去看看吧。', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53753] = { id = 53753, Voice = '', Text = '顺着克劳德指的方向看过去，有一大片荆棘，荆棘里有一朵花。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[53752] = { id = 53752, Voice = '', Text = '哈哈哈，年轻人真有干劲，那就麻烦你帮我处理一下周围的荆棘。你看……那里就是伊莱纳花……', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53751] = { id = 53751, Voice = '', Text = '哦？是巧巧莉做的吗？她真是个好孩子！我等会再品尝热巧克力吧，等我把这片荆棘稍作处理，腾出个位置支画架！', Speaker = 5503, Option = '我来帮忙', Action = _EmptyTable,}, 
	[53750] = { id = 53750, Voice = '', Text = '冒险者，你来的速度挺快的，我还没有准备画架呢…早点来也好，免得那群玩偶熊再来打扰我作画…', Speaker = 5503, Option = '爱心热巧克力！', Action = _EmptyTable,}, 
	[53749] = { id = 53749, Voice = '', Text = '那太好了，我在这里等你告诉我，嘿嘿嘿……', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[53748] = { id = 53748, Voice = '', Text = '还有件事情……克劳德大师是我的偶像，等你们喝过了我的巧克力以后，我想请你帮我问问大师他喜不喜欢我的巧克力。我虽然画画不厉害，但是制作热巧克力还是有一手的！', Speaker = 6991, Option = '没问题！', Emoji = 6, Action = _EmptyTable,}, 
	[53747] = { id = 53747, Voice = '', Text = '好了，这是为你们两个人特别制作的爱心热巧克力，快拿给克劳德大师吧。', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[53746] = { id = 53746, Voice = '', Text = '（擤鼻涕声）……咳咳，不好意思冒险者，我有点激动……唔，让我们来做巧克力吧，这次我请客！我会为你们做出最美味的巧克力的！你也来帮忙吧～', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[53745] = { id = 53745, Voice = '', Text = '巧巧莉哭了一会……', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[53744] = { id = 53744, Voice = '', Text = '一定可以看见的……呜呜呜……我真的太高兴了…', Speaker = 6991, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[53743] = { id = 53743, Voice = '', Text = '还好有你出现了，帮助克劳德画家找回了画笔和颜料……这样他就可以为自己的夫人画画了，夫人就能再次见到伊莱纳花了！', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[53742] = { id = 53742, Voice = '', Text = '正当他准备开始作画的时候，画笔却被玩偶熊抢走了！当我听说他的画笔被抢走的时候，我真为他难过……没有人愿意帮助他拿回画笔和颜料……', Speaker = 6991, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[53741] = { id = 53741, Voice = '', Text = '克劳德大师带着夫人的愿望在世界上寻找伊莱纳花，终于在姜饼城找到了！', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[53740] = { id = 53740, Voice = '', Text = '那是她一生见过的最美妙的植物，遗憾的是她也只见过一次。夫人希望画家可以去寻找到一株伊莱纳花，为这朵花创作一幅作品，让更多的人知道这个世界上还有这么美好的生命。', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[53739] = { id = 53739, Voice = '', Text = '我跟他交谈之后得知，他的夫人猜测到自己的命不久矣，希望大师可以帮她完成一个心愿。她年少的时候曾经是卡普拉的员工，走遍了全国，帮助冒险者们四处传送。在那段时间，她见过一种美丽的花朵，叫做伊莱纳。', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[53738] = { id = 53738, Voice = '', Text = '没有参观到画展，我就回到姜饼城继续卖巧克力，没想到克劳德大师居然来到了姜饼城，前几天见到他的时候我简直不敢相信自己的眼睛。', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[53737] = { id = 53737, Voice = '', Text = '……呜呜呜，可怜的克劳德大师……', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[53736] = { id = 53736, Voice = '', Text = '卡米耶夫人陪伴克劳德大师很多年了，他们夫妻的感情一直很好，夫人一病不起对克劳德大师的打击太大了。他停办了画展，全心全意的陪伴夫人在全国寻找良医……可是，可是卡米耶夫人病的太重了，没有医生能够治疗她的疾病！', Speaker = 6991, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[53735] = { id = 53735, Voice = '', Text = '去年的时候，克劳德大师没有举办画展，我向朋友打听才知道了，大师的妻子生了重病卧床不起T T', Speaker = 6991, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[53734] = { id = 53734, Voice = '', Text = '不瞒你说，冒险者，我很喜欢画画，业余的时间也会自己随便涂涂抹抹的，克劳德大师是我最喜欢的画家……每年普隆德拉的克劳德画展我都会观看……', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[53733] = { id = 53733, Voice = '', Text = '你这么热心帮他取回来，我就放心了……！这样真是太好了……T T', Speaker = 6991, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[53732] = { id = 53732, Voice = '', Text = '我……只是个不知名的小甜点师，做一点巧克力，没有能力帮助他……', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[53731] = { id = 53731, Voice = '', Text = '太好了！因为画家请人帮他找画笔已经有段时间了，可是没有武力高强的冒险者愿意去玩具工厂帮他拿回来。', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[53730] = { id = 53730, Voice = '', Text = '……是画家想喝巧克力吗？唔……那么我猜你一定帮他取回了画笔和颜料吧！', Speaker = 6991, Option = '你怎么知道？', Action = _EmptyTable,}, 
	[53729] = { id = 53729, Voice = '', Text = '……是大画家克劳德·莫吗？', Speaker = 6991, Option = '是！', Action = _EmptyTable,}, 
	[53728] = { id = 53728, Voice = '', Text = '唔！～～两杯呀，还有一杯是准备送给你的恋人吗？莫非是来姜饼城举行白色婚礼的有情人吗？', Speaker = 6991, Option = '给克劳德大师', Action = _EmptyTable,}, 
	[53727] = { id = 53727, Voice = '', Text = '冒险者，你要买一杯巧克力吗？', Speaker = 6991, Option = '来两杯', Action = _EmptyTable,}, 
	[53726] = { id = 53726, Voice = '', Text = '嘿～走一走看一看，各种美味的热巧克力～～榛果、杏仁、香草、酒心～～什么都有，来看看，来瞧瞧～～', Speaker = 6991, Option = '', Action = _EmptyTable,}, 
	[53725] = { id = 53725, Voice = '', Text = '这么冷的天气，需要一点温暖人心的饮品……等我画完这幅画，我还可以请你吃顿热气腾腾的美餐。', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53724] = { id = 53724, Voice = '', Text = '我需要回去准备准备，你可以找圣诞树附近的热饮商人买一杯浓浓的巧克力，一边喝着美味的巧克力一边欣赏伊莱纳花。', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53723] = { id = 53723, Voice = '', Text = '冒险者，我忘记告诉你了，伊莱纳花在姜饼城的冰河附近，姜饼城外靠近雪原的边缘。你准备好了就去找我吧。', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53722] = { id = 53722, Voice = '', Text = '冒……呼呼……冒险者……呼呼，我忘记告诉你了……呼呼，呼～～～', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53721] = { id = 53721, Voice = '', Text = '克劳德匆匆忙忙的跑走了，并没有告诉你伊莱纳花的位置……！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[53720] = { id = 53720, Voice = '', Text = '好的，我先回旅馆收拾一下画架，我在伊莱纳花附近等你！', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53719] = { id = 53719, Voice = '', Text = '我还有一件事情要拜托你……我害怕那些玩偶熊再来抢走我的画笔，可以请你陪我一起作画吗？', Speaker = 5503, Option = '荣幸之至', Action = _EmptyTable,}, 
	[53718] = { id = 53718, Voice = '', Text = '我的画笔……感谢幸运女神的眷顾！请接受本人最真挚的谢意！', Speaker = 5503, Option = '', Emoji = 30, Action = _EmptyTable,}, 
	[53717] = { id = 53717, Voice = '', Text = '冒险者，你拿回了我的画笔和颜料了吗？快给我吧！', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53716] = { id = 53716, Voice = '', Text = '那些可恶的玩偶熊在玩具工厂的二楼，小心行事！', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53715] = { id = 53715, Voice = '', Text = '……谢谢你，冒险者，女神会保佑你的！', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53714] = { id = 53714, Voice = '', Text = '年轻人……我在这里等你，请你务必多考虑考虑，什么时候来找我都可以……', Speaker = 5503, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[53713] = { id = 53713, Voice = '', Text = '……这样啊，真遗憾啊，我的视力越来越不好，眼前的光线越来越模糊，也许明年伊莱纳花开的时候，我就没有能力画画了吧。', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53712] = { id = 53712, Voice = '', Text = '年轻人，你愿意为我进入玩具工厂，帮我取回我被抢走的画笔和颜料吗？', Speaker = 5503, Option = '{当然！,4},{我没时间,5}', Action = _EmptyTable,}, 
	[53711] = { id = 53711, Voice = '', Text = '我本想雇佣一些冒险者帮我找回画笔和颜料，但是没有人愿意为了这点小事情进入危险的玩具工厂。伊莱纳花的花期就在这几天，等到我的新颜料运过来的时候，可能……没办法画出伊莱纳花了。也许，这此生我都没机会画出伊莱纳花了……', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53710] = { id = 53710, Voice = '', Text = '我在姜饼城里打听消息，听说那些邪恶的玩偶熊都盘踞在玩具工厂的角落里……可是，正如你所见，我只是一个会画画的老人而已……咳咳咳……我没有力气进入玩具工厂与那些没有灵魂的魔物战斗。', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53709] = { id = 53709, Voice = '', Text = '正当我准备清理伊莱纳花附近的荆棘，将画架支在伊莱纳花旁边作画的时候，一群可恶的玩偶熊从背后偷袭我！它们抢走了我所有的画笔还有颜料！然后就跑开了！', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53708] = { id = 53708, Voice = '', Text = '它的花瓣在微风之中颤抖，浓郁的香味从它的花苞中飘向我，让我回忆起多年前我还年轻的那个夏天，我第一次见到我的妻子卡米耶。她也还年轻，笑容熠熠的走在普隆德拉郊外的田间，洁白的花朵在她的脚边绽放，她穿着卡普拉公司的蓝裙子，从我眼前走过……那一幕我永远也忘不了……', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53707] = { id = 53707, Voice = '', Text = '我请卡普拉公司帮我传送到艾尔帕兰，又找了冒险家带我进入姜饼城，花了很多的功夫才进入姜饼城，见到那朵盛放的花。', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53706] = { id = 53706, Voice = '', Text = '咳咳咳……我从千里之外的普隆德拉赶到姜饼城就是为了一睹这株传说中的伊莱纳花，用我手中的画笔描绘出这花的容姿。', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53705] = { id = 53705, Voice = '', Text = '咳咳咳……年轻人态度很好……真不错……咳咳咳，我听闻姜饼城有一种非常美丽的花，叫做伊莱纳。伊莱纳是精灵语之中“仙境”的意思，传说中伊莱纳花是精灵之国的神花，它的香味具有安抚心灵的作用。', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53704] = { id = 53704, Voice = '', Text = '是普隆德拉来的冒险者吧，我确实是克劳德·莫，既然在他乡相遇，不如帮我做一点事情吧。', Speaker = 5503, Option = '请吩咐', Action = _EmptyTable,}, 
	[53703] = { id = 53703, Voice = '', Text = '是的，被认出来了吗？', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53702] = { id = 53702, Voice = '', Text = '年轻人，姜饼城有大把美丽的风光，你不去看这些美景，在我这个老头子面前晃悠什么？', Speaker = 5503, Option = '克劳德大师？', Action = _EmptyTable,}, 
	[53701] = { id = 53701, Voice = '', Text = '……', Speaker = 5503, Option = '', Action = _EmptyTable,}, 
	[53166] = { id = 53166, Voice = '', Text = '冒险者，我即将把你送到姜饼城，你准备好了吗？', Speaker = 6943, Option = '', Action = _EmptyTable,}, 
	[52767] = { id = 52767, Voice = '', Text = '谢谢你，为这些孩子做了如此高尚的榜样。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52766] = { id = 52766, Voice = '', Text = '我们长大了也要变成你这样勇敢的冒险者！', Speaker = 5336, Option = '', Emoji = 27, Action = _EmptyTable,}, 
	[52765] = { id = 52765, Voice = '', Text = '谢谢你，冒险者！', Speaker = 5320, Option = '', Emoji = 2, Action = _EmptyTable,}, 
	[52764] = { id = 52764, Voice = '', Text = '谢谢你，冒险者！', Speaker = 5310, Option = '', Emoji = 2, Action = _EmptyTable,}, 
	[52763] = { id = 52763, Voice = '', Text = '谢谢你，冒险者~~！', Speaker = 5336, Option = '', Emoji = 6, Action = _EmptyTable,}, 
	[52762] = { id = 52762, Voice = '', Text = '还有人要向你道谢呢！你对姜饼城的贡献可不是我一个人能谢的完的~你看~', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52761] = { id = 52761, Voice = '', Text = '我真的为你感到骄傲。', Speaker = 6944, Option = '谢谢', Action = _EmptyTable,}, 
	[52760] = { id = 52760, Voice = '', Text = '驯鹿和雪橇告诉我，毕尤被你们打的落荒而逃，看样子是一阵时间不敢回来姜饼城作恶了。', Speaker = 6944, Option = '是的', Action = _EmptyTable,}, 
	[52759] = { id = 52759, Voice = '', Text = '虽然加尔曾经做过许多坏事，但有贝蒂陪在他身边，我很放心。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52758] = { id = 52758, Voice = '', Text = '嗯…这对他们来说，可能是最好的结局。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52757] = { id = 52757, Voice = '', Text = '加尔和贝蒂呢？', Speaker = 6944, Option = '走了', Action = _EmptyTable,}, 
	[52756] = { id = 52756, Voice = '', Text = '年轻人！你成功的解除了姜饼城的危机，解救了这么多无辜的孩子。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52755] = { id = 52755, Voice = '', Text = '好啦！一切都结束了！该是回去向圣诞老人报平安的时候啦~~', Speaker = 5338, Option = '', Action = _EmptyTable,}, 
	[52738] = { id = 52738, Voice = '', Text = '冒险者你看！', Speaker = 5338, Option = '', Action = _EmptyTable,}, 
	[52737] = { id = 52737, Voice = '', Text = '哈哈哈，我们把毕尤合力打败了呢~~太好啦！！！冒险者你是最棒的。', Speaker = 5338, Option = '你才厉害…', Action = _EmptyTable,}, 
	[52734] = { id = 52734, Voice = '', Text = '怎么了冒险者，这么快就不认得我了？我是雪橇啊~！', Speaker = 5338, Option = '', Action = _EmptyTable,}, 
	[52709] = { id = 52709, Voice = '', Text = '是不是啊~老~太~婆~', Speaker = 5337, Option = '', Action = _EmptyTable,}, 
	[52708] = { id = 52708, Voice = '', Text = '我亲爱的小加尔，你当然不认识她，她只是个路过的老太婆而已。', Speaker = 5337, Option = '', Action = _EmptyTable,}, 
	[52707] = { id = 52707, Voice = '', Text = '你是谁！我不认识你这个老太婆！', Speaker = 5335, Option = '', Action = _EmptyTable,}, 
	[52706] = { id = 52706, Voice = '', Text = '加尔！我终于找到你了！', Speaker = 5325, Option = '', Action = _EmptyTable,}, 
	[52704] = { id = 52704, Voice = '', Text = '你们…你们到底是什么人…', Speaker = 5335, Option = '', Action = _EmptyTable,}, 
	[52687] = { id = 52687, Voice = '', Text = '闭嘴！', Speaker = 5333, Option = '', Action = _EmptyTable,}, 
	[52686] = { id = 52686, Voice = '', Text = '看来我们是需要撬开它，可是我们这样私自拆人家的舞台，这样会不会不太好呀？', Speaker = 5334, Option = '', Action = _EmptyTable,}, 
	[52685] = { id = 52685, Voice = '', Text = '好吧，为了你们的安全，带上驯鹿和雪橇，你们先去此处探查一下！', Speaker = 6944, Option = '好的！', Action = _EmptyTable,}, 
	[52684] = { id = 52684, Voice = '', Text = '可是如果她执意要帮助我们，说不定真的能打开加尔的心门，阻止这一切。', Speaker = 6944, Option = '是的', Action = _EmptyTable,}, 
	[52683] = { id = 52683, Voice = '', Text = '考虑到贝蒂的年纪，我是不建议她跟随你们去这么危险的地方。', Speaker = 6944, Option = '但是', Action = _EmptyTable,}, 
	[52682] = { id = 52682, Voice = '', Text = '孩子，根据雪橇和驯鹿的调查，加尔将马戏团的后台改造成了一个深不可测的异时空！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52678] = { id = 52678, Voice = '', Text = '看来你们已经制定了自己的计划…', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52677] = { id = 52677, Voice = '', Text = '帮我说服圣诞老人，冒险者，我一定要和你一起去。', Speaker = 5325, Option = '', Action = _EmptyTable,}, 
	[52676] = { id = 52676, Voice = '', Text = '我们得顺着通道寻过去。', Speaker = 5325, Option = '', Action = _EmptyTable,}, 
	[52675] = { id = 52675, Voice = '', Text = '只不过这一次，他一定不会因为糖果杖的敲击而出现。', Speaker = 5325, Option = '', Action = _EmptyTable,}, 
	[52674] = { id = 52674, Voice = '', Text = '相信我，加尔一定就藏在表演人员使用的后台中…', Speaker = 5325, Option = '有道理', Action = _EmptyTable,}, 
	[52673] = { id = 52673, Voice = '', Text = '还记得上次我和你说的，我们约定的秘密吗…敲三下舞台，小加尔就会跑出来。', Speaker = 5325, Option = '记得！', Action = _EmptyTable,}, 
	[52672] = { id = 52672, Voice = '', Text = '但我相信加尔纯真的本质，我一定能让他回心转意！', Speaker = 5325, Option = '加尔下落不明', Action = _EmptyTable,}, 
	[52671] = { id = 52671, Voice = '', Text = '我知道我年纪大了，帮不上什么忙，可能还会拖你们的后腿。', Speaker = 5325, Option = '', Action = _EmptyTable,}, 
	[52670] = { id = 52670, Voice = '', Text = '我更不能就这样不管不顾。', Speaker = 5325, Option = '你的身体…', Action = _EmptyTable,}, 
	[52669] = { id = 52669, Voice = '', Text = '还有，我始终无法相信加尔是个坏人，如果他被毕尤那样邪恶的人操控了。', Speaker = 5325, Option = '', Action = _EmptyTable,}, 
	[52668] = { id = 52668, Voice = '', Text = '是的，他带走了我的孙女，我一定要去救她。', Speaker = 5325, Option = '', Action = _EmptyTable,}, 
	[52667] = { id = 52667, Voice = '', Text = '相信我只有见到他，才会找到答案…', Speaker = 5325, Option = '你要见他？', Action = _EmptyTable,}, 
	[52666] = { id = 52666, Voice = '', Text = '这和加尔有关。', Speaker = 5325, Option = '', Action = _EmptyTable,}, 
	[52665] = { id = 52665, Voice = '', Text = '我还是没有想起来，失去的记忆是什么，但是我脑子里有个声音告诉我。', Speaker = 5325, Option = '什么？', Action = _EmptyTable,}, 
	[52664] = { id = 52664, Voice = '', Text = '谢谢你，冒险者，我感觉好多了…', Speaker = 5325, Option = '', Action = _EmptyTable,}, 
	[52663] = { id = 52663, Voice = '', Text = '快快快，把特效药给贝蒂奶奶喂下！', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52662] = { id = 52662, Voice = '', Text = '干的好冒险者，贝蒂奶奶喝了这个特效药就会好很多的，你放心吧。', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52661] = { id = 52661, Voice = '', Text = '带种子的魔法风藤会发光噢，你要仔细观察，就一定能发现。', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52660] = { id = 52660, Voice = '', Text = '还有，种子才是治病的秘方，所以只挖到叶子，是不行的！但不是每一株魔法风藤的根部都带种子的哦！', Speaker = 5201, Option = '如何辨别？', Action = _EmptyTable,}, 
	[52659] = { id = 52659, Voice = '', Text = '哦对了，可要小心那些魔法风藤在拔起来的时候可是不好惹的，风藤的种子可能会不听话的咬人，被咬到了也是很痛的呢！', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52658] = { id = 52658, Voice = '', Text = '就在雪人的院子里，快去吧！！', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52656] = { id = 52656, Voice = '', Text = '魔法风藤是一种治疗呼吸疾病的通用药，是只有在姜饼城能找到的神奇植物！它曾经帮助了不少患有呼吸疾病的人们。', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52655] = { id = 52655, Voice = '', Text = '快帮贝蒂奶奶弄些魔法风藤来！这个病可是很危险的，不管对大人还是小孩都是很危险的，你知道吗！！', Speaker = 5201, Option = '魔法风藤？', Action = _EmptyTable,}, 
	[52654] = { id = 52654, Voice = '', Text = '不好，贝蒂奶奶看着像是家族哮喘发作！', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52653] = { id = 52653, Voice = '', Text = '到底…到底是什么…', Speaker = 5325, Option = '', Action = _EmptyTable,}, 
	[52652] = { id = 52652, Voice = '', Text = '贝蒂因为怎么也回想不起来的记忆变得异常激动，呼吸也变得十分急促…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[52651] = { id = 52651, Voice = '', Text = '我也不清楚，我只记得，那件事很重要很重要…', Speaker = 5325, Option = '', Action = _EmptyTable,}, 
	[52650] = { id = 52650, Voice = '', Text = '冒险者，虽然这段记忆很痛苦，但我是记得的。', Speaker = 5325, Option = '那忘记的是…', Action = _EmptyTable,}, 
	[52649] = { id = 52649, Voice = '', Text = '她这么做！！到底是为了什么。', Speaker = 5325, Option = '安慰贝蒂', Action = _EmptyTable,}, 
	[52648] = { id = 52648, Voice = '', Text = '我的父母当年，是被毕尤杀死的…', Speaker = 5325, Option = '看到了', Action = _EmptyTable,}, 
	[52647] = { id = 52647, Voice = '', Text = '我…我的父母…', Speaker = 5325, Option = '', Action = _EmptyTable,}, 
	[52646] = { id = 52646, Voice = '', Text = '妈妈爱你…', Speaker = 5328, Option = '', Action = _EmptyTable,}, 
	[52645] = { id = 52645, Voice = '', Text = '嘘，别出声…', Speaker = 5328, Option = '', Action = _EmptyTable,}, 
	[52644] = { id = 52644, Voice = '', Text = '妈妈…呜呜呜，你怎么办…', Speaker = 5326, Option = '', Action = _EmptyTable,}, 
	[52643] = { id = 52643, Voice = '', Text = '贝蒂…快跑…', Speaker = 5328, Option = '', Action = _EmptyTable,}, 
	[52642] = { id = 52642, Voice = '', Text = '我的天啊！', Speaker = 5328, Option = '', Action = _EmptyTable,}, 
	[52641] = { id = 52641, Voice = '', Text = '带…带着女儿跑！！', Speaker = 5327, Option = '', Action = _EmptyTable,}, 
	[52640] = { id = 52640, Voice = '', Text = '你是什么人！！', Speaker = 5327, Option = '', Action = _EmptyTable,}, 
	[52639] = { id = 52639, Voice = '', Text = '嘿嘿妈妈真好。', Speaker = 5326, Option = '', Action = _EmptyTable,}, 
	[52638] = { id = 52638, Voice = '', Text = '当然是等到今年圣诞啦~我的小宝贝~', Speaker = 5328, Option = '', Action = _EmptyTable,}, 
	[52637] = { id = 52637, Voice = '', Text = '妈妈~~我们什么时候再去姜饼城呀？', Speaker = 5326, Option = '', Action = _EmptyTable,}, 
	[52636] = { id = 52636, Voice = '', Text = '是啊！日记，看我这老糊涂，让我看看。', Speaker = 5325, Option = '', Action = _EmptyTable,}, 
	[52635] = { id = 52635, Voice = '', Text = '我真的记不起来了…但我觉得我似乎忘记了很重要的事情。', Speaker = 5325, Option = '日记！', Action = _EmptyTable,}, 
	[52634] = { id = 52634, Voice = '', Text = '冒险者，我好像有一段记忆，被抹去了… ', Speaker = 5325, Option = '什么？！', Action = _EmptyTable,}, 
	[52633] = { id = 52633, Voice = '', Text = '雪橇说到这里一旁的贝蒂奶奶终于忍不住一些激动的情绪，用颤抖的手扶了扶眼镜。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[52632] = { id = 52632, Voice = '', Text = '加尔又是为什么这么迫切的想找到贝蒂奶奶？', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52631] = { id = 52631, Voice = '', Text = '既然贝蒂奶奶与小加尔的关系这么好，为什么两人后来就没有再见面了？', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52630] = { id = 52630, Voice = '', Text = '可...我有件事想不通。', Speaker = 5201, Option = '什么？', Action = _EmptyTable,}, 
	[52629] = { id = 52629, Voice = '', Text = '一定是这样！贝蒂因为是小加尔悲惨童年中唯一美好的回忆，所以应该只有她能阻止加尔！！', Speaker = 5201, Option = '嗯', Action = _EmptyTable,}, 
	[52628] = { id = 52628, Voice = '', Text = '我的直觉告诉我好像应该不是这样子的…【喷汗】你要不要再想一下，我相信你…', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52627] = { id = 52627, Voice = '', Text = '那么她不想让贝蒂待在这里到底是为了什么呢…', Speaker = 5201, Option = '{能阻止加尔,3},{有伊米尔之心,4},{贝蒂魔力强大,4}', Action = _EmptyTable,}, 
	[52626] = { id = 52626, Voice = '', Text = '这样她就可以等我们和加尔斗争得你死我活之际，渔翁得利！一定是这样。', Speaker = 5201, Option = '是的', Action = _EmptyTable,}, 
	[52625] = { id = 52625, Voice = '', Text = '她一定是以为，只要在这么和平的城镇闹事，我们就会因为应对不来而借助伊米尔之心的力量。', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52624] = { id = 52624, Voice = '', Text = '毕尤的目的，无非就是制造混乱来，更是为了寻找这里的伊米尔之心。', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52623] = { id = 52623, Voice = '', Text = '毕尤这么不想让贝蒂待在这里，说明背后一定隐藏着某种真实！', Speaker = 5201, Option = '同意', Action = _EmptyTable,}, 
	[52622] = { id = 52622, Voice = '', Text = '原来是这样，那些可怜的孩子们，究竟在哪里啊…', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52621] = { id = 52621, Voice = '', Text = '至少不算空手而归，我们回去将情报汇总给圣诞老人吧！', Speaker = 5226, Option = '好', Action = _EmptyTable,}, 
	[52620] = { id = 52620, Voice = '', Text = '我们刚才也在这周围找到了一些加尔的个人物品。', Speaker = 5226, Option = '', Action = _EmptyTable,}, 
	[52619] = { id = 52619, Voice = '', Text = '皮箱里是一枚晶莹剔透的红色弹珠和一只冒着线头的兔子玩偶。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[52618] = { id = 52618, Voice = '', Text = '似乎有只可疑的皮箱，皮箱模糊的印着加尔两个字。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[52617] = { id = 52617, Voice = '', Text = '找找这帐篷里还有没有留下来的线索！', Speaker = 5226, Option = '好', Action = _EmptyTable,}, 
	[52616] = { id = 52616, Voice = '', Text = '这次营救行动我们不能空手而归。', Speaker = 5226, Option = '', Action = _EmptyTable,}, 
	[52615] = { id = 52615, Voice = '', Text = '狡猾的毕尤！下次绝不能轻饶她！', Speaker = 5226, Option = '是啊', Action = _EmptyTable,}, 
	[52587] = { id = 52587, Voice = '', Text = '我们为你带来了糖果杖！', Speaker = 5226, Option = '收下糖杖', Action = _EmptyTable,}, 
	[52586] = { id = 52586, Voice = '', Text = '拿糖果杖敲击舞台三下就能把加尔叫出来。', Speaker = 5227, Option = '', Action = _EmptyTable,}, 
	[52585] = { id = 52585, Voice = '', Text = '记得贝蒂奶奶的情报说……', Speaker = 5227, Option = '', Action = _EmptyTable,}, 
	[52584] = { id = 52584, Voice = '', Text = '准备好就出发吧！等你们的好消息！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52583] = { id = 52583, Voice = '', Text = '你需要有人慷慨的帮助，相信他们一定会帮到你！', Speaker = 6944, Option = '谢谢', Action = _EmptyTable,}, 
	[52582] = { id = 52582, Voice = '', Text = '他们都是自愿来到姜饼城并且守护姜饼城的善良孩子们！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52581] = { id = 52581, Voice = '', Text = '这是凯拉和卡莱娜。', Speaker = 6944, Option = '幸会', Action = _EmptyTable,}, 
	[52580] = { id = 52580, Voice = '', Text = '冒险者！我派了两位帮手给你。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52579] = { id = 52579, Voice = '', Text = '你可以回去找圣诞老人啦！', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52578] = { id = 52578, Voice = '', Text = '你通过了我的考验！加油冒险者。', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52577] = { id = 52577, Voice = '', Text = '太棒了，你真是超乎我的想象，看来你的观察力已经十分强了。', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52576] = { id = 52576, Voice = '', Text = '太可惜了，回答错误，但是我还是愿意再给你一次回答的机会呢！', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52575] = { id = 52575, Voice = '', Text = '玩具工厂里的小熊扑克牌是什么花色的呢？', Speaker = 5201, Option = '{黑桃,12},{红心,12},{方块,12},{梅花,13}', Action = _EmptyTable,}, 
	[52574] = { id = 52574, Voice = '', Text = '这些字下方的黄色地砖上拼成了什么图案呢？', Speaker = 5201, Option = '{笑容,10},{波利,11},{筛子,10},{礼物,10}', Action = _EmptyTable,}, 
	[52573] = { id = 52573, Voice = '', Text = '下列哪些不是玩具工厂1F的像素大字呢？', Speaker = 5201, Option = '{笑容,8},{狂欢,9},{圣诞,8},{愉快,8}', Action = _EmptyTable,}, 
	[52572] = { id = 52572, Voice = '', Text = '好，那我就来考考你~', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52571] = { id = 52571, Voice = '', Text = '看来你还需要磨练自己呀！快去玩具工厂仔细观察一下。', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52570] = { id = 52570, Voice = '', Text = '我现在就来考考你，关于玩具工厂的一些细节信息。', Speaker = 5201, Option = '{准备好了,6},{没准备好,7}', Action = _EmptyTable,}, 
	[52569] = { id = 52569, Voice = '', Text = '出入玩具工厂如此危险的地方，要时刻保持洞察力。', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52568] = { id = 52568, Voice = '', Text = '你真是个天才！你真棒！我就知道你会答出来的，我就知道你和别人不一样！', Speaker = 5201, Option = '终于…', Action = _EmptyTable,}, 
	[52567] = { id = 52567, Voice = '', Text = '不对呀，你再好好想想，分身多，可不是速度快就能打败的。', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52566] = { id = 52566, Voice = '', Text = '不对哦！我聪明的冒险者，和这种狡猾的家伙只有力量是打不倒她的呢！', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52565] = { id = 52565, Voice = '', Text = '不对哦，我的好伙伴，再想想，和这种狡猾的家伙光靠勇气可是不够的呀。', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52564] = { id = 52564, Voice = '', Text = '所以我们需要什么呢？', Speaker = 5201, Option = '{勇气,2},{速度,3},{力量,4},{洞察力,5}', Action = _EmptyTable,}, 
	[52563] = { id = 52563, Voice = '', Text = '毕尤这个家伙可是十分狡猾的，到处都是她的分身傀儡！', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52562] = { id = 52562, Voice = '', Text = '没关系，通过我的考验会让你信心大增的！', Speaker = 5201, Option = '谢谢…', Action = _EmptyTable,}, 
