	[52547] = { id = 52547, Voice = '', Text = '嗬嗬嗬，冒险者你果然没有辜负我的期待！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52546] = { id = 52546, Voice = '', Text = '完成了他们两的考验，就回来告诉我！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52545] = { id = 52545, Voice = '', Text = '好孩子！如果你能通过驯鹿和雪橇的考验，就证明你有能力与恶势力对抗！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52544] = { id = 52544, Voice = '', Text = '你必须证明有保护自己的能力啊！好孩子！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52543] = { id = 52543, Voice = '', Text = '在去寻找小珍妮之前，你必须接受驯鹿和雪橇的考验！', Speaker = 6944, Option = '{接受,2},{不接受,3}', Action = _EmptyTable,}, 
	[52542] = { id = 52542, Voice = '', Text = '那么我就不能，让你这样善良的年轻人贸然前去而受到伤害。', Speaker = 6944, Option = '没事', Action = _EmptyTable,}, 
	[52541] = { id = 52541, Voice = '', Text = '如果加尔真的听命于毕尤。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
