	[52533] = { id = 52533, Voice = '', Text = '既然这两次营救行动都没有找到珍妮，我认为我们有必要去找贝蒂说明情况。', Speaker = 6944, Option = '同意！', Action = _EmptyTable,}, 
	[52532] = { id = 52532, Voice = '', Text = '我认为，加尔是把珍妮当成了当年的贝蒂。', Speaker = 6944, Option = '原来如此', Action = _EmptyTable,}, 
