	[52472] = { id = 52472, Voice = '', Text = '找不到雪球？去圣诞老人的房间找找，那里特别暖和，雪球明明叫雪球却很怕冷，真是有意思的小东西，不是吗？哈哈哈哈！', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
