	[52468] = { id = 52468, Voice = '', Text = '它是圣诞老人的小精灵，叫雪球，胆子特别小，你可不要吓到它。', Speaker = 5201, Option = '雪球？', Action = _EmptyTable,}, 
	[52467] = { id = 52467, Voice = '', Text = '带着这些柠檬汁，这可是你这位帮手的最爱。', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
