	[52465] = { id = 52465, Voice = '', Text = '我知道你急着去解救更多的孩子，可圣诞老人他专门提醒我，让我告诉你。', Speaker = 5201, Option = '什么', Action = _EmptyTable,}, 
	[52464] = { id = 52464, Voice = '', Text = '怎么说着说着都忘记正事儿了，瞧我这脑子。', Speaker = 5201, Option = '你说吧', Action = _EmptyTable,}, 
	[52463] = { id = 52463, Voice = '', Text = '冒险者，没关系，你今天不想和我拥抱，或许明天会改变主意吧，我当然是希望你改变主意的！因为我真的很欣赏你，拥抱可以增加两个人之间的友情！你觉得呢？', Speaker = 5201, Option = '好的…', Action = _EmptyTable,}, 
	[52462] = { id = 52462, Voice = '', Text = '太感动了，你是第一个愿意和我拥抱的人，我也不懂为什么姜饼城里的人总对我有些不耐烦…', Speaker = 5201, Option = '好的…', Emoji = 2, Action = _EmptyTable,}, 
	[52461] = { id = 52461, Voice = '', Text = '冒险者，你干的真的不错。我真想猛烈的拥抱你！！', Speaker = 5201, Option = '{好吧,2},{算了,3}', Action = _EmptyTable,}, 
