	[52455] = { id = 52455, Voice = '', Text = '冒险者，好可怕呀，我们要去哪？', Speaker = 5312, Option = '找雪橇', Action = _EmptyTable,}, 
	[52435] = { id = 52435, Voice = '', Text = '冒险者，行动吧！拯救珍妮和其他的孩子们。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52434] = { id = 52434, Voice = '', Text = '透过三角形的草莓糖果和三角形的柠檬糖果查看马戏团的帐篷就可以进入小丑的邪恶空间。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52433] = { id = 52433, Voice = '', Text = '等你解救了孩子，让雪橇保护他们，送他们回家。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
