	[52420] = { id = 52420, Voice = '', Text = '你就是我最亲爱的妹妹，别让我忘记！', Speaker = 6960, Option = '', Action = _EmptyTable,}, 
	[52419] = { id = 52419, Voice = '', Text = '不要！我不要！丽莎，我永远都不要忘记你。', Speaker = 6960, Option = '', Action = _EmptyTable,}, 
	[52418] = { id = 52418, Voice = '', Text = '我希望他们跟唐尼爷爷一样，开开心心的！', Speaker = 5308, Option = '', Action = _EmptyTable,}, 
	[52417] = { id = 52417, Voice = '', Text = '冒险者，你可以让凯拉帮我的姐姐和爸爸也制作遗忘糖果吗？', Speaker = 5308, Option = '', Action = _EmptyTable,}, 
	[52416] = { id = 52416, Voice = '', Text = '别骗人啦！姐姐，你演的一点也不好。', Speaker = 5308, Option = '', Action = _EmptyTable,}, 
	[52415] = { id = 52415, Voice = '', Text = '不…不要，我…我…我还没有好，这个药丸没有用…没有用…', Speaker = 6960, Option = '', Action = _EmptyTable,}, 
	[52414] = { id = 52414, Voice = '', Text = '姐姐，我要走了…', Speaker = 5308, Option = '', Action = _EmptyTable,}, 
