	[52411] = { id = 52411, Voice = '', Text = '我不知道，我好了吗？', Speaker = 6960, Option = '没问题了！', Action = _EmptyTable,}, 
	[52410] = { id = 52410, Voice = '', Text = '姐姐，你的病好了吗？', Speaker = 5308, Option = '', Action = _EmptyTable,}, 
	[52409] = { id = 52409, Voice = '', Text = '你快把药丸拿给我的姐姐吧！', Speaker = 5308, Option = '', Action = _EmptyTable,}, 
	[52408] = { id = 52408, Voice = '', Text = '这个三角形的柠檬糖果我也见过！我看见毕尤在帐篷里透过这个糖果看些什么。', Speaker = 5308, Option = '', Action = _EmptyTable,}, 
