	[52406] = { id = 52406, Voice = '', Text = '打开布袋，发现了奇怪的药丸和一颗三角形的糖果，拿给丽莎看看。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[52405] = { id = 52405, Voice = '', Text = '发现一个可疑的布袋！不好，有傀儡偷袭！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[52404] = { id = 52404, Voice = '', Text = '地面堆积着很多杂物，上面有一些兽爪的痕迹，没有药丸…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[52403] = { id = 52403, Voice = '', Text = '地上堆积着许多表演用具，没有什么特殊的地方。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[52402] = { id = 52402, Voice = '', Text = '那个孩子…也被变成了傀儡…', Speaker = 5308, Option = '', Action = _EmptyTable,}, 
	[52401] = { id = 52401, Voice = '', Text = '在马戏团的帐篷里，我看见他拿给其他的孩子吃了。', Speaker = 5308, Option = '', Action = _EmptyTable,}, 
	[52400] = { id = 52400, Voice = '', Text = '姐姐最好了，你可以帮帮我吗？', Speaker = 5308, Option = '在哪里？', Action = _EmptyTable,}, 
	[52399] = { id = 52399, Voice = '', Text = '从小，其他的小朋友都笑话她，我不开心。', Speaker = 5308, Option = '', Action = _EmptyTable,}, 
	[52398] = { id = 52398, Voice = '', Text = '我想帮助姐姐治好她的病…', Speaker = 5308, Option = '好', Action = _EmptyTable,}, 
	[52397] = { id = 52397, Voice = '', Text = '冒险者，我想请你帮我在小丑的帐篷里找到治疗口吃的药丸。', Speaker = 5308, Option = '', Action = _EmptyTable,}, 
	[52396] = { id = 52396, Voice = '', Text = '你也能看见他，也许，你是天神派来帮我们的！', Speaker = 5308, Option = '', Action = _EmptyTable,}, 
	[52395] = { id = 52395, Voice = '', Text = '我也不知道你为什么能看见我，我之前见到你帮助内特了…', Speaker = 5308, Option = '', Action = _EmptyTable,}, 
	[52394] = { id = 52394, Voice = '', Text = '姐姐，爸爸看不见我了…只有小孩子才能看见我…', Speaker = 5308, Option = '那我…', Action = _EmptyTable,}, 
	[52393] = { id = 52393, Voice = '', Text = '爸爸…爸…爸爸，丽莎…丽莎…', Speaker = 6960, Option = '', Action = _EmptyTable,}, 
	[52392] = { id = 52392, Voice = '', Text = '你…你怎么…这么傻…', Speaker = 6960, Option = '', Action = _EmptyTable,}, 
	[52391] = { id = 52391, Voice = '', Text = '我想从小丑那里要那种药丸。', Speaker = 5308, Option = '', Action = _EmptyTable,}, 
	[52390] = { id = 52390, Voice = '', Text = '小丑有药丸可以治疗口吃，我看见他给其他的孩子吃了。', Speaker = 5308, Option = '', Action = _EmptyTable,}, 
	[52389] = { id = 52389, Voice = '', Text = '丽…丽莎，你…你怎么会…都…都跟你说了…不要找小…小丑。', Speaker = 6960, Option = '', Action = _EmptyTable,}, 
	[52388] = { id = 52388, Voice = '', Text = '姐姐…', Speaker = 5308, Option = '', Action = _EmptyTable,}, 
	[52387] = { id = 52387, Voice = '', Text = '丽莎…', Speaker = 6960, Option = '', Action = _EmptyTable,}, 
