	[52385] = { id = 52385, Voice = '', Text = '请你把兔子拿给我的大女儿吧，她就在那边……', Speaker = 6961, Option = '', Action = _EmptyTable,}, 
	[52384] = { id = 52384, Voice = '', Text = '……我不想再看见这个兔子，我没办法面对它。', Speaker = 6961, Option = '', Action = _EmptyTable,}, 
	[52383] = { id = 52383, Voice = '', Text = '他可以让我的丽莎回家吗？', Speaker = 6961, Option = '恐怕不能', Action = _EmptyTable,}, 
	[52382] = { id = 52382, Voice = '', Text = '圣诞老人…圣诞老人会一些神奇的魔法！', Speaker = 6961, Option = '', Action = _EmptyTable,}, 
