	[52374] = { id = 52374, Voice = '', Text = '可以帮我最后一个忙吗？帮我把我的小弹珠埋到我家门口吗？', Speaker = 5304, Option = '', Action = _EmptyTable,}, 
	[52373] = { id = 52373, Voice = '', Text = '他看不见我…', Speaker = 5304, Option = '', Action = _EmptyTable,}, 
	[52372] = { id = 52372, Voice = '', Text = '我不难过，我是大孩子……', Speaker = 5304, Option = '你的爷爷', Emoji = 24, Action = _EmptyTable,}, 
	[52371] = { id = 52371, Voice = '', Text = '冒险者，让你为难了…', Speaker = 5304, Option = '别难过', Action = _EmptyTable,}, 
	[52370] = { id = 52370, Voice = '', Text = '哦～我们姜饼城最适合小孩子来玩了，你要带他过来玩哦。', Speaker = 6959, Option = '', Emoji = 17, Action = _EmptyTable,}, 
	[52369] = { id = 52369, Voice = '', Text = '嗯？是谁？', Speaker = 6959, Option = '可爱的孩子', Action = _EmptyTable,}, 
	[52368] = { id = 52368, Voice = '', Text = '好孩子，谢谢你拿给我这么好吃的糖果。', Speaker = 6959, Option = '记得内特吗？', Emoji = 6, Action = _EmptyTable,}, 
	[52367] = { id = 52367, Voice = '', Text = '哇，这颗糖果可真好吃。', Speaker = 6959, Option = '', Action = _EmptyTable,}, 
