	[52365] = { id = 52365, Voice = '', Text = '……孩子，你遇见什么困难了吗？', Speaker = 6959, Option = '是', Action = _EmptyTable,}, 
	[52364] = { id = 52364, Voice = '', Text = '给我吃的吗？可是我今天已经吃了一颗糖果了…', Speaker = 6959, Option = '求求你', Action = _EmptyTable,}, 
