	[52361] = { id = 52361, Voice = '', Text = '冒险者，你走吧，不要再打扰我了。', Speaker = 5303, Option = '', Action = _EmptyTable,}, 
	[52360] = { id = 52360, Voice = '', Text = '好了，这是给唐尼爷爷的遗忘糖果，剩下两颗我等内特爸爸妈妈回家的时候……再给他们…', Speaker = 5303, Option = '', Action = _EmptyTable,}, 
	[52359] = { id = 52359, Voice = '', Text = '你回来了啊……给我吧。', Speaker = 5303, Option = '', Action = _EmptyTable,}, 
