	[52357] = { id = 52357, Voice = '', Text = '我将后悔我今天的所作所为，然后在罪恶中死亡，没有神明会为我打开天堂之门，没有地方能收容我的灵魂……', Speaker = 5303, Option = '…', Action = _EmptyTable,}, 
	[52356] = { id = 52356, Voice = '', Text = '以后每个夜晚我都不得安眠…', Speaker = 5303, Option = '', Action = _EmptyTable,}, 
	[52355] = { id = 52355, Voice = '', Text = '……冒险者，我感觉我的余生都将在忏悔中度过…', Speaker = 5303, Option = '', Action = _EmptyTable,}, 
	[52354] = { id = 52354, Voice = '', Text = '这是我们应该做的吗？', Speaker = 5303, Option = '{是的,0},{我不知道,0},{没有选择,0}', Action = _EmptyTable,}, 
	[52353] = { id = 52353, Voice = '', Text = '真的要这样吗？给唐尼爷爷和内特的爸爸妈妈一个被药物麻痹的世界？', Speaker = 5303, Option = '', Action = _EmptyTable,}, 
	[52352] = { id = 52352, Voice = '', Text = '……', Speaker = 5303, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[52351] = { id = 52351, Voice = '', Text = '这几天，我为了哄唐尼爷爷吃忘忧糖果，已经骗了他很多次了。', Speaker = 5303, Option = '…我来', Action = _EmptyTable,}, 
	[52350] = { id = 52350, Voice = '', Text = '可是，我做不出来，真的做不出来……', Speaker = 5303, Option = '', Action = _EmptyTable,}, 
	[52349] = { id = 52349, Voice = '', Text = '……你见到内特灵魂，他听到我说的话了？他真的这样要求吗？', Speaker = 5303, Option = '', Action = _EmptyTable,}, 
	[52348] = { id = 52348, Voice = '', Text = '什么？！你怎么会知道这件事情，我从来没跟别人提起过！', Speaker = 5303, Option = '转述内特请求', Action = _EmptyTable,}, 
	[52347] = { id = 52347, Voice = '', Text = '什么事情…？', Speaker = 5303, Option = '制作遗忘糖果', Action = _EmptyTable,}, 
