	[52345] = { id = 52345, Voice = '', Text = '唐尼爷爷要怎么办呢？……他明明是这么好的人，为什么会有这么悲惨的事情发生在他身上……', Speaker = 5303, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[52344] = { id = 52344, Voice = '', Text = '我以前一直都不乖，你就让我乖一次吧……', Speaker = 5304, Option = '', Action = _EmptyTable,}, 
	[52343] = { id = 52343, Voice = '', Text = '求求你了…', Speaker = 5304, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[52342] = { id = 52342, Voice = '', Text = '还有爸爸妈妈，等他们回家的时候，让凯拉哥哥给他们制作糖果…', Speaker = 5304, Option = '', Action = _EmptyTable,}, 
	[52341] = { id = 52341, Voice = '', Text = '求求你了，我灵魂能支撑的时间不多了，我想最后看见爷爷开开心心地笑…', Speaker = 5304, Option = '', Action = _EmptyTable,}, 
	[52340] = { id = 52340, Voice = '', Text = '你…可不可以跟凯拉叔叔说…让他帮爷爷制作这种糖果？', Speaker = 5304, Option = '不', Emoji = 24, Action = _EmptyTable,}, 
	[52339] = { id = 52339, Voice = '', Text = '凯拉叔叔不愿意给爷爷吃这种糖果…', Speaker = 5304, Option = '', Action = _EmptyTable,}, 
	[52338] = { id = 52338, Voice = '', Text = '只是他会忘记我…', Speaker = 5304, Option = '', Action = _EmptyTable,}, 
	[52337] = { id = 52337, Voice = '', Text = '凯拉叔叔咕咕叨叨的说有一种效果更好的遗忘糖果，只要吃下去，爷爷就能永远快乐了。', Speaker = 5304, Option = '是吗…', Action = _EmptyTable,}, 
	[52336] = { id = 52336, Voice = '', Text = '我这几天看见凯拉叔叔给爷爷吃忘忧糖果，我还听见了……', Speaker = 5304, Option = '', Action = _EmptyTable,}, 
	[52335] = { id = 52335, Voice = '', Text = '…关于我的爷爷，你可以帮我一个忙吗？', Speaker = 5304, Option = '', Action = _EmptyTable,}, 
	[52334] = { id = 52334, Voice = '', Text = '在马戏团的帐篷里，透过这个就可以看见小丑的邪恶空间。', Speaker = 5304, Option = '知道了', Action = _EmptyTable,}, 
	[52333] = { id = 52333, Voice = '', Text = '三角形的草莓糖果…我偷偷从毕尤那里拿走的！', Speaker = 5304, Option = '', Action = _EmptyTable,}, 
	[52332] = { id = 52332, Voice = '', Text = '好受一点了……呼呼…冒险者，这个你拿好！', Speaker = 5304, Option = '', Action = _EmptyTable,}, 
