	[52329] = { id = 52329, Voice = '', Text = '你需要……需要…我撑不住了…', Speaker = 5304, Option = '', Action = _EmptyTable,}, 
	[52328] = { id = 52328, Voice = '', Text = '你必须进入小丑的邪恶空间…', Speaker = 5304, Option = '怎么进入？', Action = _EmptyTable,}, 
	[52327] = { id = 52327, Voice = '', Text = '那里整日进行着恐怖的表演…她还强迫大家看…', Speaker = 5304, Option = '', Action = _EmptyTable,}, 
	[52326] = { id = 52326, Voice = '', Text = '有个奇怪的女人，她帮助小丑在马戏团里建立了一个空间。', Speaker = 5304, Option = '', Action = _EmptyTable,}, 
	[52325] = { id = 52325, Voice = '', Text = '我的朋友被困在小丑的空间里，你要救救他们！', Speaker = 5304, Option = '空间？', Action = _EmptyTable,}, 
	[52324] = { id = 52324, Voice = '', Text = '我的灵魂被困在玩偶里，偶尔清醒的时候看见小丑还在不停的引诱我的朋友们！', Speaker = 5304, Option = '', Action = _EmptyTable,}, 
	[52323] = { id = 52323, Voice = '', Text = '别自责，我早就……你只是解救了我的灵魂！', Speaker = 5304, Option = '', Action = _EmptyTable,}, 
