	[52321] = { id = 52321, Voice = '', Text = '是，小丑把我变成了玩具熊。我忘记了所有的事情……在马戏团一天又一天的表演…', Speaker = 5304, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[52320] = { id = 52320, Voice = '', Text = '冒险者，我害怕，我不要变成玩偶，不要变成小花生！', Speaker = 5304, Option = '小花生…', Action = _EmptyTable,}, 
	[52319] = { id = 52319, Voice = '', Text = '冒险者，救救我！', Speaker = 5304, Option = '', Action = _EmptyTable,}, 
	[52318] = { id = 52318, Voice = '', Text = '啊……这个邪恶的小丑，把我变成了玩偶！', Speaker = 5304, Option = '', Action = _EmptyTable,}, 
	[52317] = { id = 52317, Voice = '', Text = '啊啊啊！！！', Speaker = 5307, Option = '', Action = _EmptyTable,}, 
	[52316] = { id = 52316, Voice = '', Text = '跟我回马戏团！', Speaker = 5306, Option = '', Action = _EmptyTable,}, 
	[52315] = { id = 52315, Voice = '', Text = '永远在一起，永远在一起，我们永远在一起！', Speaker = 5306, Option = '', Action = _EmptyTable,}, 
	[52314] = { id = 52314, Voice = '', Text = '小丑叔叔，我不去马戏团看表演了，我要回家，你让我变回来！', Speaker = 5307, Option = '', Action = _EmptyTable,}, 
	[52313] = { id = 52313, Voice = '', Text = '我怎么了，我怎么变成这个样子了！', Speaker = 5307, Option = '', Action = _EmptyTable,}, 
