	[52310] = { id = 52310, Voice = '', Text = '不要！小丑叔叔，我跟你去马戏团，快把我的弹珠还给我！', Speaker = 5305, Option = '', Action = _EmptyTable,}, 
	[52309] = { id = 52309, Voice = '', Text = '不跟我走，弹珠我就拿走了哦～', Speaker = 5306, Option = '', Action = _EmptyTable,}, 
