	[52299] = { id = 52299, Voice = '', Text = '冒险者，你看吧……', Speaker = 5304, Option = '', Action = _EmptyTable,}, 
	[52298] = { id = 52298, Voice = '', Text = '内特飞快的跑走了，快跟上去！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[52297] = { id = 52297, Voice = '', Text = '别说了，先跟我来，我没有时间了……', Speaker = 5304, Option = '', Action = _EmptyTable,}, 
	[52296] = { id = 52296, Voice = '', Text = '我实在太痛苦了，对不起……', Speaker = 5304, Option = '你怎么…', Emoji = 24, Action = _EmptyTable,}, 
	[52295] = { id = 52295, Voice = '', Text = '不好意思，之前神志不清的时候攻击你和珍妮了……', Speaker = 5304, Option = '', Action = _EmptyTable,}, 
	[52294] = { id = 52294, Voice = '', Text = '我就是内特…也是那只玩具熊……', Speaker = 5304, Option = '', Action = _EmptyTable,}, 
	[52293] = { id = 52293, Voice = '', Text = '冒险者，请你帮帮我的爷爷……求求你了！', Speaker = 5304, Option = '你是？', Action = _EmptyTable,}, 
	[52292] = { id = 52292, Voice = '', Text = '红色弹珠从手心中脱落，滚到地上…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[52291] = { id = 52291, Voice = '', Text = '唐尼爷爷不接受弹珠，找凯拉想想办法吧！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[52290] = { id = 52290, Voice = '', Text = '我已经老了，玩不动了，你拿给那些小孩子们玩吧。', Speaker = 6959, Option = '', Action = _EmptyTable,}, 
	[52289] = { id = 52289, Voice = '', Text = '你真是个好孩子， 但是这些是孩子们喜欢的小玩意。', Speaker = 6959, Option = '', Action = _EmptyTable,}, 
	[52288] = { id = 52288, Voice = '', Text = '你要送给我吗？', Speaker = 6959, Option = '是的', Action = _EmptyTable,}, 
	[52287] = { id = 52287, Voice = '', Text = '给我的吗？让我看看，这红色弹珠看上去挺熟悉的呀，真漂亮。', Speaker = 6959, Option = '', Action = _EmptyTable,}, 
	[52286] = { id = 52286, Voice = '', Text = '哈哈哈，人老啦，记性不好啦。', Speaker = 6959, Option = '有一颗弹珠', Action = _EmptyTable,}, 
	[52285] = { id = 52285, Voice = '', Text = '哎呀，冒险者，我刚刚要跟你说什么事情来着，吃了一颗糖的功夫就忘记了。', Speaker = 6959, Option = '', Action = _EmptyTable,}, 
	[52284] = { id = 52284, Voice = '', Text = '对不起，唐尼爷爷……我不能让你去雪原…', Speaker = 5303, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[52283] = { id = 52283, Voice = '', Text = '唐尼吃下了忘忧糖果，看上去神情有些恍惚……', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[52282] = { id = 52282, Voice = '', Text = '那好吧，我再相信你一次…', Speaker = 6959, Option = '', Action = _EmptyTable,}, 
	[52281] = { id = 52281, Voice = '', Text = '我不骗你，我一定会带你去找内特的！', Speaker = 5303, Option = '', Emoji = 24, Action = _EmptyTable,}, 
	[52280] = { id = 52280, Voice = '', Text = '你昨天也是这么说的！你没有带我去，你骗我，你又骗我！', Speaker = 6959, Option = '', Action = _EmptyTable,}, 
	[52279] = { id = 52279, Voice = '', Text = '爷爷……我有一颗好吃的糖果，你吃了我就带你找内特。', Speaker = 5303, Option = '', Action = _EmptyTable,}, 
	[52277] = { id = 52277, Voice = '', Text = '我看见他在雪原上玩红色弹珠…', Speaker = 6959, Option = '', Action = _EmptyTable,}, 
	[52276] = { id = 52276, Voice = '', Text = '又是你！你又想说什么？我的孙子还活着！', Speaker = 6959, Option = '', Emoji = 12, Action = _EmptyTable,}, 
	[52275] = { id = 52275, Voice = '', Text = '内特…他已经……', Speaker = 5303, Option = '', Action = _EmptyTable,}, 
	[52274] = { id = 52274, Voice = '', Text = '唐尼爷爷，请别去雪原，那里太危险了。', Speaker = 5303, Option = '', Action = _EmptyTable,}, 
	[52273] = { id = 52273, Voice = '', Text = '我要去雪原找他！', Speaker = 6959, Option = '', Action = _EmptyTable,}, 
	[52272] = { id = 52272, Voice = '', Text = '内特就不会走丢了，他在外面肯定饿坏了。', Speaker = 6959, Option = '', Action = _EmptyTable,}, 
	[52271] = { id = 52271, Voice = '', Text = '如果不是我，如果我儿子和儿媳在，也许他们可以把内特照顾的更好。', Speaker = 6959, Option = '', Action = _EmptyTable,}, 
	[52270] = { id = 52270, Voice = '', Text = '都怪我，没照顾好他……', Speaker = 6959, Option = '我很抱歉', Action = _EmptyTable,}, 
	[52269] = { id = 52269, Voice = '', Text = '我的孙子内特，他在雪原一个人玩弹珠走失了。', Speaker = 6959, Option = '', Action = _EmptyTable,}, 
	[52268] = { id = 52268, Voice = '', Text = '他喜欢在雪原上玩红色弹珠，一个人玩……', Speaker = 6959, Option = '……', Action = _EmptyTable,}, 
	[52267] = { id = 52267, Voice = '', Text = '外乡人，你从雪原路过的时候，有没有看见一个孩子。', Speaker = 6959, Option = '', Action = _EmptyTable,}, 
	[52266] = { id = 52266, Voice = '', Text = '你是不是外来旅游的冒险者？', Speaker = 6959, Option = '', Action = _EmptyTable,}, 
	[52265] = { id = 52265, Voice = '', Text = '红色弹珠是内特最喜欢的玩具，兔子玩偶是丽莎的小伙伴…', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52264] = { id = 52264, Voice = '', Text = '冒险者，请你把这两个孩子的遗物还给他们的亲人吧！', Speaker = 6944, Option = '好的', Action = _EmptyTable,}, 
	[52263] = { id = 52263, Voice = '', Text = '这两个孩子的亲人…沉浸在失去孩子的悲伤之中…', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52262] = { id = 52262, Voice = '', Text = '……说来让人难过，是有关内特和丽莎的亲人的。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52261] = { id = 52261, Voice = '', Text = '好孩子，在决战之前，我有一件事情需要你去处理。', Speaker = 6944, Option = '什么', Action = _EmptyTable,}, 
	[52256] = { id = 52256, Voice = '', Text = '也不知道给唐尼爷爷吃忘忧糖果，是好事还是坏事呢？', Speaker = 5301, Option = '', Action = _EmptyTable,}, 
	[52255] = { id = 52255, Voice = '', Text = '……忧愁可以忘记一天，可失去的亲人却再也不会回来……', Speaker = 5301, Option = '', Action = _EmptyTable,}, 
	[52254] = { id = 52254, Voice = '', Text = '有了这些，我就可以为唐尼爷爷制作忘忧糖果了！', Speaker = 5301, Option = '', Action = _EmptyTable,}, 
	[52253] = { id = 52253, Voice = '', Text = '……你拿回来冰河草了吗，嗯，让我看看，确实很新鲜。', Speaker = 5301, Option = '', Action = _EmptyTable,}, 
	[52252] = { id = 52252, Voice = '', Text = '是的，请你去帮我收集一点冰河草吧。', Speaker = 5301, Option = '', Action = _EmptyTable,}, 
	[52251] = { id = 52251, Voice = '', Text = '吃一枚忘忧糖果，可以让人快乐地度过一整天。', Speaker = 5301, Option = '一整天？', Action = _EmptyTable,}, 
	[52250] = { id = 52250, Voice = '', Text = '这种特殊的糖果是使用冰河旁的冰河草制作的。', Speaker = 5301, Option = '', Action = _EmptyTable,}, 
	[52249] = { id = 52249, Voice = '', Text = '好吧，那只有继续使用忘忧糖果，让他好好休息一下了。', Speaker = 5301, Option = '', Action = _EmptyTable,}, 
	[52248] = { id = 52248, Voice = '', Text = '我实在没有办法，冒险者，你有什么好方法可以安慰唐尼爷爷吗？', Speaker = 5301, Option = '没有', Action = _EmptyTable,}, 
	[52247] = { id = 52247, Voice = '', Text = '我们不能让他去，雪原上非常危险…', Speaker = 5301, Option = '真可怜', Action = _EmptyTable,}, 
	[52246] = { id = 52246, Voice = '', Text = '好几次他都想跑到雪原上找孙子。', Speaker = 5301, Option = '', Action = _EmptyTable,}, 
	[52245] = { id = 52245, Voice = '', Text = '他现在变得有些疯疯癫癫的，总是说什么听见他的孙子在雪原上召唤他…', Speaker = 5301, Option = '……', Action = _EmptyTable,}, 
	[52244] = { id = 52244, Voice = '', Text = '当他知道内特已经被小丑……', Speaker = 5301, Option = '', Action = _EmptyTable,}, 
	[52243] = { id = 52243, Voice = '', Text = '是内特的爷爷唐尼爷爷，因为内特失踪了，他的爷爷找了他很久。', Speaker = 5301, Option = '', Action = _EmptyTable,}, 
	[52241] = { id = 52241, Voice = '', Text = '太棒了，冒险者！多谢你的帮助。', Speaker = 5300, Option = '', Action = _EmptyTable,}, 
	[52240] = { id = 52240, Voice = '', Text = '让我看看照片，嗯，角度合适，效果清晰。', Speaker = 5300, Option = '', Action = _EmptyTable,}, 
	[52239] = { id = 52239, Voice = '', Text = '那么就拜托你了！', Speaker = 5300, Option = '', Action = _EmptyTable,}, 
	[52238] = { id = 52238, Voice = '', Text = '多拍一点，角度全面一些！', Speaker = 5300, Option = '好的', Action = _EmptyTable,}, 
	[52237] = { id = 52237, Voice = '', Text = '冒险者，可以请你帮我去马戏团附近拍些照片吗？', Speaker = 5300, Option = '', Action = _EmptyTable,}, 
	[52236] = { id = 52236, Voice = '', Text = '可是我的照相机坏了！', Speaker = 5300, Option = '', Action = _EmptyTable,}, 
	[52235] = { id = 52235, Voice = '', Text = '这样，大家可以研究怎么突袭马戏团！', Speaker = 5300, Option = '很好', Action = _EmptyTable,}, 
	[52234] = { id = 52234, Voice = '', Text = '是这样的，圣诞老人吩咐我去调查马戏团内部情况，让我用照相机拍下照片。', Speaker = 5300, Option = '', Action = _EmptyTable,}, 
	[52233] = { id = 52233, Voice = '', Text = '哦，冒险者你好，我有点心烦没看见你走过来了。', Speaker = 5300, Option = '', Action = _EmptyTable,}, 
	[52232] = { id = 52232, Voice = '', Text = '照相机怎么坏了……真可恶，这种时候发生这种意外！', Speaker = 5300, Option = '怎么了', Action = _EmptyTable,}, 
	[52231] = { id = 52231, Voice = '', Text = '快把魔尘给我，让我制作饼干吧！', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52230] = { id = 52230, Voice = '', Text = '啊，我已经闻到你身上香甜的味道了。', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52229] = { id = 52229, Voice = '', Text = '所以冒险者，请你再帮我收集一些香甜魔尘吧。', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52228] = { id = 52228, Voice = '', Text = '让我们的战士在面对毕尤时不受寒冷的影响。', Speaker = 5201, Option = '这样啊', Action = _EmptyTable,}, 
	[52227] = { id = 52227, Voice = '', Text = '这次我还需要一些香甜魔尘制作成饼干。', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52226] = { id = 52226, Voice = '', Text = '上次你帮我收集的香甜魔尘，我已经全部都制作成香甜饼干送给孩子们吃了。', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52225] = { id = 52225, Voice = '', Text = '……你想多了，冒险者，我并不准备给你姜饼。', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52224] = { id = 52224, Voice = '', Text = '那些粉末是用来制作香甜姜饼的，这种姜饼吃了以后就可以保持身体温暖。', Speaker = 5201, Option = '谢谢我不需要', Action = _EmptyTable,}, 
	[52223] = { id = 52223, Voice = '', Text = '你还记得曾经帮我收集的香甜魔尘吗？', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52222] = { id = 52222, Voice = '', Text = '别…别…等等，我想起来了，想起来了！', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52221] = { id = 52221, Voice = '', Text = '什么？', Speaker = 5201, Option = '告辞', Action = _EmptyTable,}, 
	[52220] = { id = 52220, Voice = '', Text = '……刚刚说了这么多忘记了，都怪你穿这么少，让我担心你的身体。', Speaker = 5201, Option = '打扰了', Action = _EmptyTable,}, 
	[52219] = { id = 52219, Voice = '', Text = '哦…我有件事情想请你帮忙…', Speaker = 5201, Option = '什么？', Action = _EmptyTable,}, 
	[52218] = { id = 52218, Voice = '', Text = '这样，你在外面旅行的时候，爸爸妈妈才不会担心。', Speaker = 5201, Option = '有别的事吗？', Action = _EmptyTable,}, 
	[52217] = { id = 52217, Voice = '', Text = '去炎热的地方要穿少一点，去寒冷的地方要多穿一点。', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52216] = { id = 52216, Voice = '', Text = '以后去像姜饼城这么寒冷的地方要记住多穿的衣服啊…', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52215] = { id = 52215, Voice = '', Text = '诶？冒险者，你穿这么少的衣服冷不冷啊？', Speaker = 5201, Option = '什么？', Action = _EmptyTable,}, 
	[52214] = { id = 52214, Voice = '', Text = '这点[c][ffff00]雪花奖章[-][/c]还远远不够呢，孩子，你还是再收集一些来吧。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52213] = { id = 52213, Voice = '', Text = '很好，那么我们是时候与女神之手决战了！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52212] = { id = 52212, Voice = '', Text = '干的很好，现在你愿意把12个[c][ffff00]雪花奖章[-][/c]交给我吗？', Speaker = 6944, Option = '{愿意,7},{不愿意,6}', Action = _EmptyTable,}, 
	[52211] = { id = 52211, Voice = '', Text = '好孩子，你已经收集到12个[c][ffff00]雪花奖章[-][/c]，做好了万全的准备吗？', Speaker = 6944, Option = '是的', Action = _EmptyTable,}, 
	[52210] = { id = 52210, Voice = '', Text = '我们就可以正面迎战小丑和毕尤了。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52209] = { id = 52209, Voice = '', Text = '等你收集齐12个雪花奖章，就说明我们的准备工作已经完成。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52208] = { id = 52208, Voice = '', Text = '你完成工作以后他们会给你一个[c][ffff00]雪花奖章[-][/c]。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52207] = { id = 52207, Voice = '', Text = '[c][ffff00]雪橇[-][/c]、[c][ffff00]凯拉[-][/c]和[c][ffff00]卡莱娜[-][/c]有话想跟你说，你快去找他们吧。', Speaker = 6944, Option = '好', Action = _EmptyTable,}, 
	[52206] = { id = 52206, Voice = '', Text = '我的助手们似乎遇见了一些困难，请你去帮助他们吧。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52205] = { id = 52205, Voice = '', Text = '我们必须要做万全的准备！', Speaker = 6944, Option = '是的', Action = _EmptyTable,}, 
	[52204] = { id = 52204, Voice = '', Text = '而是女神之手的大祭司毕尤。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52203] = { id = 52203, Voice = '', Text = '这次我们面对的敌人不是普通人。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52202] = { id = 52202, Voice = '', Text = '但是也不要松懈，接下来的战斗肯定会更加辛苦！', Speaker = 6944, Option = '嗯', Action = _EmptyTable,}, 
	[52201] = { id = 52201, Voice = '', Text = '冒险者，这段时间你辛苦了！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52196] = { id = 52196, Voice = '', Text = '天哪！小珍妮也被加尔带走了！？', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52195] = { id = 52195, Voice = '', Text = '我们常结伴去那里捉迷藏！加尔一定是带珍妮去那里玩儿了！', Speaker = 5220, Option = '谢谢你！', Action = _EmptyTable,}, 
	[52194] = { id = 52194, Voice = '', Text = '只要我拿糖果杖敲三下舞台，加尔就会跑出来和我玩！', Speaker = 5220, Option = '', Action = _EmptyTable,}, 
	[52193] = { id = 52193, Voice = '', Text = '是啊，马戏团舞台的底下有个表演人员使用的后台。', Speaker = 5220, Option = '舞台？', Action = _EmptyTable,}, 
	[52192] = { id = 52192, Voice = '', Text = '在我们两还小的时候，有个秘密基地。', Speaker = 5220, Option = '秘密基地？', Action = _EmptyTable,}, 
	[52191] = { id = 52191, Voice = '', Text = '我还是不相信加尔会绑架我的孙女珍妮，他一定是带着小珍妮去玩耍了。', Speaker = 5220, Option = '劝说', Action = _EmptyTable,}, 
	[52190] = { id = 52190, Voice = '', Text = '不会的，加尔不会加入那种组织的。', Speaker = 5220, Option = '', Action = _EmptyTable,}, 
	[52189] = { id = 52189, Voice = '', Text = '就是个专门制造混乱的组织！是坏人！', Speaker = 6958, Option = '', Action = _EmptyTable,}, 
	[52188] = { id = 52188, Voice = '', Text = '女神之手？', Speaker = 5220, Option = '', Action = _EmptyTable,}, 
	[52187] = { id = 52187, Voice = '', Text = '妈妈！听城里的守卫说，加尔为女神之手的毕尤工作。', Speaker = 6958, Option = '', Action = _EmptyTable,}, 
	[52186] = { id = 52186, Voice = '', Text = '我的印象里，他是个害羞，有些小笨拙的孩子。', Speaker = 5220, Option = '人都会变', Action = _EmptyTable,}, 
	[52185] = { id = 52185, Voice = '', Text = '所以加尔他是个好人，是一定不会做坏事的。', Speaker = 5220, Option = '', Action = _EmptyTable,}, 
	[52184] = { id = 52184, Voice = '', Text = '但我们还是在这里留下了许多美好的回忆。', Speaker = 5220, Option = '嗯', Action = _EmptyTable,}, 
	[52183] = { id = 52183, Voice = '', Text = '当时我的父母在姜饼城停留的时间只有短短的一个月。', Speaker = 5220, Option = '', Action = _EmptyTable,}, 
	[52182] = { id = 52182, Voice = '', Text = '之后我们就成了最好的朋友，经常一起玩耍。', Speaker = 5220, Option = '后来呢？', Action = _EmptyTable,}, 
	[52181] = { id = 52181, Voice = '', Text = '我结识了世界上最可爱的小丑，加尔。', Speaker = 5220, Option = '原来如此', Action = _EmptyTable,}, 
	[52180] = { id = 52180, Voice = '', Text = '在我很小的时候，我父母带我来姜饼城玩耍。', Speaker = 5220, Option = '', Action = _EmptyTable,}, 
	[52179] = { id = 52179, Voice = '', Text = '你都看到了吧…', Speaker = 5220, Option = '', Action = _EmptyTable,}, 
	[52178] = { id = 52178, Voice = '', Text = '我叫加尔…很高兴认识你…', Speaker = 5222, Option = '', Action = _EmptyTable,}, 
	[52177] = { id = 52177, Voice = '', Text = '嗯？', Speaker = 5221, Option = '', Action = _EmptyTable,}, 
	[52176] = { id = 52176, Voice = '', Text = '我…', Speaker = 5222, Option = '', Action = _EmptyTable,}, 
	[52175] = { id = 52175, Voice = '', Text = '甜…', Speaker = 5222, Option = '', Emoji = 26, Action = _EmptyTable,}, 
	[52174] = { id = 52174, Voice = '', Text = '甜吗？', Speaker = 5221, Option = '', Action = _EmptyTable,}, 
	[52173] = { id = 52173, Voice = '', Text = '嗯…', Speaker = 5222, Option = '', Action = _EmptyTable,}, 
	[52172] = { id = 52172, Voice = '', Text = '我最喜欢吃的糖果，爸爸的糖果店制作的[c][ffff00]奶油夹心巧克力[-][/c]！', Speaker = 5221, Option = '', Action = _EmptyTable,}, 
	[52171] = { id = 52171, Voice = '', Text = '这是什么…', Speaker = 5222, Option = '', Action = _EmptyTable,}, 
	[52170] = { id = 52170, Voice = '', Text = '别听你师傅的，你是最棒的，给。', Speaker = 5221, Option = '', Action = _EmptyTable,}, 
	[52169] = { id = 52169, Voice = '', Text = '……', Speaker = 5223, Option = '', Action = _EmptyTable,}, 
	[52168] = { id = 52168, Voice = '', Text = '对小孩子这么凶，小心我去和团长告状！', Speaker = 5221, Option = '', Action = _EmptyTable,}, 
	[52167] = { id = 52167, Voice = '', Text = '不要再责备他了！', Speaker = 5221, Option = '', Action = _EmptyTable,}, 
	[52166] = { id = 52166, Voice = '', Text = '臭小子，刚才的表演失误这么多次，还有资格在这里和客人玩？！', Speaker = 5223, Option = '', Action = _EmptyTable,}, 
	[52162] = { id = 52162, Voice = '', Text = '不，我是真的喜欢你的表演，很有趣！', Speaker = 5221, Option = '', Action = _EmptyTable,}, 
	[52161] = { id = 52161, Voice = '', Text = '你是来嘲笑我的吧！', Speaker = 5222, Option = '', Action = _EmptyTable,}, 
	[52160] = { id = 52160, Voice = '', Text = '我刚才都没发挥好…', Speaker = 5222, Option = '', Action = _EmptyTable,}, 
	[52159] = { id = 52159, Voice = '', Text = '我叫贝蒂，我特别喜欢你的表演，我们交个朋友吧！', Speaker = 5221, Option = '', Action = _EmptyTable,}, 
	[52158] = { id = 52158, Voice = '', Text = '真怀念啊，这是我们初次相识的地方。', Speaker = 5220, Option = '', Action = _EmptyTable,}, 
	[52157] = { id = 52157, Voice = '', Text = '寻找贝蒂的记忆。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[52156] = { id = 52156, Voice = '', Text = '轻轻的打开它。', Speaker = 5220, Option = '', Action = _EmptyTable,}, 
	[52155] = { id = 52155, Voice = '', Text = '来吧，握住这本日记。', Speaker = 5220, Option = '握住', Action = _EmptyTable,}, 
	[52154] = { id = 52154, Voice = '', Text = '比起听我讲段老掉牙的陈年往事，还不如你们自己去看看好！', Speaker = 5220, Option = '', Action = _EmptyTable,}, 
	[52153] = { id = 52153, Voice = '', Text = '好了！找到了，是这片儿三角形的~', Speaker = 5220, Option = '太好了', Action = _EmptyTable,}, 
	[52152] = { id = 52152, Voice = '', Text = '让我戴上眼镜仔细瞧瞧…', Speaker = 5220, Option = '', Action = _EmptyTable,}, 
	[52151] = { id = 52151, Voice = '', Text = '不如你去收集一些，看看那些尺寸正好的，就可以用上了。', Speaker = 5220, Option = '这就去', Action = _EmptyTable,}, 
	[52150] = { id = 52150, Voice = '', Text = '玩具工厂里那些看起来笨笨的玩具熊，它们身上就有小拉链，什么形状的都有！', Speaker = 5220, Option = '', Action = _EmptyTable,}, 
	[52149] = { id = 52149, Voice = '', Text = '可是这日记已经是五十多年前的东西了，上面的拉环松动，拉链也老旧了。', Speaker = 5220, Option = '那怎么办？', Action = _EmptyTable,}, 
	[52148] = { id = 52148, Voice = '', Text = '这儿有个别致的拉链，拉开它便能打开日记。', Speaker = 5220, Option = '原来如此', Action = _EmptyTable,}, 
	[52147] = { id = 52147, Voice = '', Text = '这本日记可以让你看到以前发生的事。', Speaker = 5220, Option = '', Action = _EmptyTable,}, 
	[52146] = { id = 52146, Voice = '', Text = '我的父亲曾留给我一本冥想日记。', Speaker = 5220, Option = '冥想日记？', Action = _EmptyTable,}, 
	[52145] = { id = 52145, Voice = '', Text = '我和这位小丑加尔可有一段不得不说的故事。', Speaker = 5220, Option = '什么故事？', Action = _EmptyTable,}, 
	[52144] = { id = 52144, Voice = '', Text = '我来姜饼城之前就一直在寻找他！', Speaker = 5220, Option = '为什么？', Action = _EmptyTable,}, 
	[52143] = { id = 52143, Voice = '', Text = '什么！？小丑加尔！他在这里？', Speaker = 5220, Option = '', Action = _EmptyTable,}, 
	[52142] = { id = 52142, Voice = '', Text = '你们说的小丑不会是小丑…加尔吧？', Speaker = 5220, Option = '是的', Action = _EmptyTable,}, 
	[52141] = { id = 52141, Voice = '', Text = '噢不！你的意思是，她已经被红鼻子小丑给带走了！？', Speaker = 6957, Option = '是的', Action = _EmptyTable,}, 
	[52140] = { id = 52140, Voice = '', Text = '冒险者…怎么样！找到珍妮了吗？', Speaker = 6957, Option = '', Action = _EmptyTable,}, 
	[52139] = { id = 52139, Voice = '', Text = '是珍妮头上戴的花环！', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[52138] = { id = 52138, Voice = '', Text = '真不懂为什么有这么多小孩子愿意去那儿。', Speaker = 6966, Option = '', Emoji = 10, Action = _EmptyTable,}, 
	[52137] = { id = 52137, Voice = '', Text = '那地方在我看来特别阴森恐怖，一点都不好玩，我从不去那儿。', Speaker = 6966, Option = '', Action = _EmptyTable,}, 
	[52136] = { id = 52136, Voice = '', Text = '[c][ffff00]她往红鼻子马戏团帐篷那边去了~[-][/c]', Speaker = 6966, Option = '什么！', Action = _EmptyTable,}, 
	[52135] = { id = 52135, Voice = '', Text = '看你这么着急，我就告诉你吧。', Speaker = 6966, Option = '', Action = _EmptyTable,}, 
	[52134] = { id = 52134, Voice = '', Text = '好吧！我心满意足啦！', Speaker = 6966, Option = '珍妮！', Emoji = 9, Action = _EmptyTable,}, 
	[52133] = { id = 52133, Voice = '', Text = '你果然没有认真看过我们的雪人，不要总是糊弄小孩子！我们也是很聪明的！', Speaker = 6966, Option = '', Action = _EmptyTable,}, 
	[52132] = { id = 52132, Voice = '', Text = '所以是我的雪人堆的比较好对不对~？', Speaker = 6966, Option = '{对,11},{不对,12}', Action = _EmptyTable,}, 
	[52131] = { id = 52131, Voice = '', Text = '我又在圣诞村堆了几只雪人呢~？', Speaker = 6966, Option = '{8只,8},{9只,8},{10只,9},{11只,8}', Action = _EmptyTable,}, 
	[52130] = { id = 52130, Voice = '', Text = '闪电一共在圣诞村堆了几只雪人？', Speaker = 6966, Option = '{8只,6},{9只,7},{10只,6},{11只,6}', Action = _EmptyTable,}, 
	[52129] = { id = 52129, Voice = '', Text = '嘿嘿！我的雪人头上戴的是什么帽子？', Speaker = 6966, Option = '{黑色高礼帽,4},{红色高礼帽,4},{红色圣诞帽,5},{黑色圣诞帽,4}', Action = _EmptyTable,}, 
	[52128] = { id = 52128, Voice = '', Text = '如果你不清楚我堆的雪人，问问闪电就知道了。', Speaker = 6966, Option = '', Action = _EmptyTable,}, 
	[52127] = { id = 52127, Voice = '', Text = '你准备好回答我的问题了吗？', Speaker = 6966, Option = '{准备好了,2},{还没有,3}', Action = _EmptyTable,}, 
	[52126] = { id = 52126, Voice = '', Text = '[c][ffff00]当然是关于我们俩堆的雪人！证明你真的有在注意我们堆的雪人[-][/c]。', Speaker = 6966, Option = '', Action = _EmptyTable,}, 
	[52125] = { id = 52125, Voice = '', Text = '只要你能答上我的问题，我就告诉你珍妮去了哪里！', Speaker = 6966, Option = '什么问题？', Action = _EmptyTable,}, 
	[52124] = { id = 52124, Voice = '', Text = '你不是真心诚意说的，我可以看的出来！', Speaker = 6966, Option = '那…', Emoji = 12, Action = _EmptyTable,}, 
	[52123] = { id = 52123, Voice = '', Text = '他们说闪电堆的雪人比我的好看，我很不服气，这对我来说很重要！', Speaker = 6966, Option = '好看', Action = _EmptyTable,}, 
	[52122] = { id = 52122, Voice = '', Text = '比起这个，你看我堆的雪人好看吗！', Speaker = 6966, Option = '', Action = _EmptyTable,}, 
	[52121] = { id = 52121, Voice = '', Text = '看到了噢！', Speaker = 6966, Option = '她去哪儿了？', Action = _EmptyTable,}, 
	[52120] = { id = 52120, Voice = '', Text = '珍妮？你说那个戴着花环的小女孩吗？', Speaker = 6966, Option = '就是她', Action = _EmptyTable,}, 
	[52119] = { id = 52119, Voice = '', Text = '剩下的都是苹果堆的呀，冒险者你真笨。', Speaker = 6964, Option = '', Action = _EmptyTable,}, 
	[52118] = { id = 52118, Voice = '', Text = '我堆的雪人都带着高高的黑色礼帽~', Speaker = 6964, Option = '苹果的呢？', Action = _EmptyTable,}, 
	[52117] = { id = 52117, Voice = '', Text = '嘿！看你一副困扰的样子，是在找什么吗？', Speaker = 6966, Option = '', Action = _EmptyTable,}, 
	[52116] = { id = 52116, Voice = '', Text = '又一颗掉落在地上的糖球。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[52115] = { id = 52115, Voice = '', Text = '这颗糖球看着眼熟，好像是属于珍妮的。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[52114] = { id = 52114, Voice = '', Text = '我们可只有这一个女儿啊！', Speaker = 6957, Option = '交给我吧', Action = _EmptyTable,}, 
	[52113] = { id = 52113, Voice = '', Text = '冒险者！拜托了，帮我找找她吧！', Speaker = 6957, Option = '', Action = _EmptyTable,}, 
	[52112] = { id = 52112, Voice = '', Text = '你说她会不会！？', Speaker = 6957, Option = '糟糕！', Action = _EmptyTable,}, 
	[52111] = { id = 52111, Voice = '', Text = '珍妮说要去提醒他将会有危险！', Speaker = 6957, Option = '', Action = _EmptyTable,}, 
	[52110] = { id = 52110, Voice = '', Text = '珍妮这孩子之前一直在闹着要去找[c][ffff00]红鼻子小丑[-][/c]。', Speaker = 6957, Option = '找他做什么？', Action = _EmptyTable,}, 
	[52109] = { id = 52109, Voice = '', Text = '珍妮不见了！', Speaker = 6957, Option = '什么！？', Emoji = 18, Action = _EmptyTable,}, 
	[52108] = { id = 52108, Voice = '', Text = '冒险者！！！', Speaker = 6957, Option = '发生什么事？', Action = _EmptyTable,}, 
	[52106] = { id = 52106, Voice = '', Text = '她认为给孩子们带来欢乐的小丑一定不会是坏人。', Speaker = 6944, Option = '我这就去', Action = _EmptyTable,}, 
	[52105] = { id = 52105, Voice = '', Text = '这孩子只有八岁。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52104] = { id = 52104, Voice = '', Text = '我刚才观察到小珍妮情绪有些激动。', Speaker = 6944, Option = '是吗？', Action = _EmptyTable,}, 
	[52103] = { id = 52103, Voice = '', Text = '真是个善良的年轻人，先去小珍妮的父母那儿看看吧。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52102] = { id = 52102, Voice = '', Text = '我相信你善良的心会使你回头。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52101] = { id = 52101, Voice = '', Text = '好孩子，帮我确保一下姜饼城孩子们的安全吧。', Speaker = 6944, Option = '{接受,2},{不接受,3}', Action = _EmptyTable,}, 
	[52100] = { id = 52100, Voice = '', Text = '红鼻子小丑的骇人事实，使整个姜饼城都变得十分动荡。', Speaker = 6944, Option = '是啊', Action = _EmptyTable,}, 
	[52052] = { id = 52052, Voice = '', Text = '冒险者，好好准备一下，这将是一场恶战。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52039] = { id = 52039, Voice = '', Text = '姜饼城的居民们！应该团结起来了！！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52038] = { id = 52038, Voice = '', Text = '这个小丑将我们的孩子做成了傀儡！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52037] = { id = 52037, Voice = '', Text = '如果这样说的话，上下连在一起，就应该是…[c][ffff00]我们是失踪的孩子[-][/c]！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52036] = { id = 52036, Voice = '', Text = '那么后半句应该是…', Speaker = 6944, Option = '{踪的孩子,4},{子的踪孩,5},{的孩踪子,5}', Action = _EmptyTable,}, 
	[52035] = { id = 52035, Voice = '', Text = '我觉得应该不是这样理解的，孩子你再想想吧……', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52034] = { id = 52034, Voice = '', Text = '也许应该这个线索应该合在一起研究…那么前半句应该是……', Speaker = 6944, Option = '{我们是失,2},{失们我是,3},{是我们失,3}', Action = _EmptyTable,}, 
	[52033] = { id = 52033, Voice = '', Text = '怎么会说出这样的话，他们还说了什么吗？', Speaker = 6944, Option = '们…失…的…子…', Action = _EmptyTable,}, 
	[52032] = { id = 52032, Voice = '', Text = '是什么？', Speaker = 6944, Option = '我…是…踪…孩…', Action = _EmptyTable,}, 
	[52031] = { id = 52031, Voice = '', Text = '哪里很奇怪呢？', Speaker = 6944, Option = '说奇怪的话', Action = _EmptyTable,}, 
	[52030] = { id = 52030, Voice = '', Text = '你在马戏团调查还发现了什么吗？', Speaker = 6944, Option = '奇怪的玩偶', Action = _EmptyTable,}, 
	[52029] = { id = 52029, Voice = '', Text = '孩子，我们等你好久了……', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52028] = { id = 52028, Voice = '', Text = '你先去找圣诞老人吧，我要准备准备，嘿嘿……', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52027] = { id = 52027, Voice = '', Text = '对！就是这些香甜魔尘。', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52026] = { id = 52026, Voice = '', Text = '有了这个，我就有法子对抗傀儡了。', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52025] = { id = 52025, Voice = '', Text = '玩具工厂2F的甜饼人有许多魔尘，帮我收集20个香甜魔尘。', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52024] = { id = 52024, Voice = '', Text = '我知道了，我会立马赶过去的，但是我需要一些香甜魔尘，你可以帮我收集一点吗？', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52023] = { id = 52023, Voice = '', Text = '怎么会这样！', Speaker = 5201, Option = '', Action = _EmptyTable,}, 
	[52022] = { id = 52022, Voice = '', Text = '冒险者，你把事情都告诉圣诞老人了吗？', Speaker = 5201, Option = '陈述事情', Action = _EmptyTable,}, 
	[52021] = { id = 52021, Voice = '', Text = '找雪橇。', Speaker = 5200, Option = '', Action = _EmptyTable,}, 
	[52020] = { id = 52020, Voice = '', Text = '驯鹿没有攻击，反而低下头蹭了一下你的手心。', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[52019] = { id = 52019, Voice = '', Text = '不用说了。', Speaker = 5200, Option = '', Action = _EmptyTable,}, 
	[52018] = { id = 52018, Voice = '', Text = '我知道。', Speaker = 5200, Option = '', Action = _EmptyTable,}, 
	[52017] = { id = 52017, Voice = '', Text = '……', Speaker = 5200, Option = '让我说完', Action = _EmptyTable,}, 
	[52016] = { id = 52016, Voice = '', Text = '……你！', Speaker = 5200, Option = '不要！', Action = _EmptyTable,}, 
	[52015] = { id = 52015, Voice = '', Text = '我需要他们的帮助。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52014] = { id = 52014, Voice = '', Text = '现在还不能多说，请你帮我通知驯鹿和雪橇。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52013] = { id = 52013, Voice = '', Text = '不瞒你说，我有一枚伊米尔之心的碎片…', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52012] = { id = 52012, Voice = '', Text = '因为伊米尔之心的碎片！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52011] = { id = 52011, Voice = '', Text = '太可怕了，我大概知道她为什么要这样做。', Speaker = 6944, Option = '为什么？', Action = _EmptyTable,}, 
	[52010] = { id = 52010, Voice = '', Text = '原来那个小丑叫做加尔…而且六十年前毕尤就利用加尔在做些什么……', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52009] = { id = 52009, Voice = '', Text = '了解到什么情况了吗？怎么会有魔物突然袭击老马特呢？', Speaker = 6944, Option = '陈述情况', Action = _EmptyTable,}, 
	[52008] = { id = 52008, Voice = '', Text = '我已经听说了，你帮助老马特击退了那些傀儡，干的不错！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[52007] = { id = 52007, Voice = '', Text = '既然毕尤已经派遣傀儡来攻击我了，她肯定有下一步的行动！', Speaker = 5202, Option = '', Action = _EmptyTable,}, 
	[52006] = { id = 52006, Voice = '', Text = '这件事情要告诉圣诞老人，请他做好准备。', Speaker = 5202, Option = '', Action = _EmptyTable,}, 
	[52005] = { id = 52005, Voice = '', Text = '那么加尔……加尔，他也知道毕尤的事情吗？', Speaker = 5202, Option = '不清楚', Action = _EmptyTable,}, 
	[52004] = { id = 52004, Voice = '', Text = '她…是她给我下了诅咒，让我变成丑陋的模样吗？', Speaker = 5202, Option = '', Action = _EmptyTable,}, 
	[52003] = { id = 52003, Voice = '', Text = '她是坏人？她……做了什么吗？', Speaker = 5202, Option = '陈述详情', Action = _EmptyTable,}, 
	[52002] = { id = 52002, Voice = '', Text = '你说什么？', Speaker = 5202, Option = '毕尤是坏人', Action = _EmptyTable,}, 
	[52001] = { id = 52001, Voice = '', Text = '毕尤出现了，是要惩罚我吗？', Speaker = 5202, Option = '不', Action = _EmptyTable,}, 
	[52000] = { id = 52000, Voice = '', Text = '我没能好好照顾加尔。', Speaker = 5202, Option = '', Action = _EmptyTable,}, 
	[51999] = { id = 51999, Voice = '', Text = '可是毕尤已经……怎么出现在这里，莫非是她的灵魂吗？', Speaker = 5202, Option = '', Action = _EmptyTable,}, 
	[51998] = { id = 51998, Voice = '', Text = '我们分开了……', Speaker = 5202, Option = '原来是这样', Action = _EmptyTable,}, 
	[51997] = { id = 51997, Voice = '', Text = '之后……马戏团渐渐的落魄，我没有钱…加尔，他去了别的马戏团。', Speaker = 5202, Option = '', Action = _EmptyTable,}, 
	[51996] = { id = 51996, Voice = '', Text = '我没什么能给加尔的，只能教他小丑的表演。', Speaker = 5202, Option = '', Action = _EmptyTable,}, 
	[51995] = { id = 51995, Voice = '', Text = '我变成了这副模样…对，就是你现在看见的丑陋模样。', Speaker = 5202, Option = '', Action = _EmptyTable,}, 
	[51994] = { id = 51994, Voice = '', Text = '我收养了加尔……奇怪的事情发生了，一夜之间……', Speaker = 5202, Option = '', Action = _EmptyTable,}, 
	[51993] = { id = 51993, Voice = '', Text = '可怜的小加尔，从此就成为了一个孤儿……', Speaker = 5202, Option = '', Action = _EmptyTable,}, 
	[51992] = { id = 51992, Voice = '', Text = '没过多久，她就发生意外，跳舞的时候从高楼跌落……去世了…', Speaker = 5202, Option = '', Action = _EmptyTable,}, 
	[51991] = { id = 51991, Voice = '', Text = '希望得到团长的帮助，团长救下了她。', Speaker = 5202, Option = '然后呢？', Action = _EmptyTable,}, 
	[51990] = { id = 51990, Voice = '', Text = '她说自己被贵族欺骗，怀下了私生子又被抛弃。', Speaker = 5202, Option = '', Action = _EmptyTable,}, 
	[51989] = { id = 51989, Voice = '', Text = '那年她带着小加尔来到我们舞蹈团，请求团长的帮助。', Speaker = 5202, Option = '', Action = _EmptyTable,}, 
	[51988] = { id = 51988, Voice = '', Text = '是毕尤…她曾经是我们马戏团的舞蹈家。', Speaker = 5202, Option = '舞蹈家…', Action = _EmptyTable,}, 
	[51987] = { id = 51987, Voice = '', Text = '我认识这个人，她是……她是……', Speaker = 5202, Option = '是谁？', Action = _EmptyTable,}, 
	[51986] = { id = 51986, Voice = '', Text = '哼，一群虫子。', Speaker = 5203, Option = '', Action = _EmptyTable,}, 
	[51985] = { id = 51985, Voice = '', Text = '是你…怎么会？你不是……', Speaker = 5202, Option = '', Action = _EmptyTable,}, 
	[51984] = { id = 51984, Voice = '', Text = '马特，你想说些什么？', Speaker = 5203, Option = '', Action = _EmptyTable,}, 
	[51983] = { id = 51983, Voice = '', Text = '我们收留了一位可怜的舞娘，还有她的孩子……', Speaker = 5202, Option = '详细说', Action = _EmptyTable,}, 
	[51982] = { id = 51982, Voice = '', Text = '我们的马戏团曾经在大陆红极一时，直到……发生了那件事情……', Speaker = 5202, Option = '哪件事？', Action = _EmptyTable,}, 
	[51981] = { id = 51981, Voice = '', Text = '咳咳，那个时候你还没出生呢。', Speaker = 5202, Option = '', Action = _EmptyTable,}, 
	[51980] = { id = 51980, Voice = '', Text = '怎么了，看不出来吗，我曾经是大陆上最出名的巡游马戏团——欢乐马戏团的首席小丑。', Speaker = 5202, Option = '没听过', Action = _EmptyTable,}, 
	[51979] = { id = 51979, Voice = '', Text = '谁知道呢？……我也只是个年老的小丑而已……', Speaker = 5202, Option = '小丑！', Action = _EmptyTable,}, 
	[51978] = { id = 51978, Voice = '', Text = '没事，这些傀儡真可怕，老人家的身子受不住啊……', Speaker = 5202, Option = '怎么会攻击你？', Action = _EmptyTable,}, 
	[51977] = { id = 51977, Voice = '', Text = '呼，好险，还好你及时赶到了……', Speaker = 5202, Option = '你没事吧', Action = _EmptyTable,}, 
	[51976] = { id = 51976, Voice = '', Text = '冒险者，救救我！', Speaker = 5202, Option = '', Action = _EmptyTable,}, 
	[51975] = { id = 51975, Voice = '', Text = '不管怎么说，你先去帮助老马特解围吧。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51974] = { id = 51974, Voice = '', Text = '什么？居然是女神之手的人？她们到姜饼城到底有什么目的？', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51973] = { id = 51973, Voice = '', Text = '这到底是怎么回事？', Speaker = 6944, Option = '汇报结果', Action = _EmptyTable,}, 
