	[51345] = { id = 51345, Voice = '', Text = '珍妮非常生气，这几天一直在跟我们赌气。', Speaker = 6957, Option = '嗯', Action = _EmptyTable,}, 
	[51344] = { id = 51344, Voice = '', Text = '因为这种情况，我的妻子帕茜尔不让珍妮出门玩……', Speaker = 6957, Option = '', Action = _EmptyTable,}, 
	[51343] = { id = 51343, Voice = '', Text = '恩，我们带她到姜饼城度假，没想到刚来就听说有小孩子失踪的消息。', Speaker = 6957, Option = '', Action = _EmptyTable,}, 
	[51342] = { id = 51342, Voice = '', Text = '让你见笑了，冒险者……珍妮平时都挺乖的……', Speaker = 6957, Option = '我相信…', Action = _EmptyTable,}, 
	[51341] = { id = 51341, Voice = '', Text = '珍妮一边哭一边跑走了…', Speaker = 0, Option = '', Action = _EmptyTable,}, 
	[51340] = { id = 51340, Voice = '', Text = '我讨厌你们……呜呜呜，我不理你啦！臭爸爸，臭爸爸！', Speaker = 6973, Option = '', Action = _EmptyTable,}, 
	[51339] = { id = 51339, Voice = '', Text = '我都听见啦！你跟这个穿的奇奇怪怪的人说不想让我去看小丑！你们都是坏人！', Speaker = 6973, Option = '', Action = _EmptyTable,}, 
	[51338] = { id = 51338, Voice = '', Text = '你答应过我让我去看马戏的，你又食言了！', Speaker = 6973, Option = '', Action = _EmptyTable,}, 
	[51337] = { id = 51337, Voice = '', Text = '臭爸爸，我讨厌你！', Speaker = 6973, Option = '', Action = _EmptyTable,}, 
	[51336] = { id = 51336, Voice = '', Text = '可是我听圣诞老人说，近期有许多小孩失踪了，我不想让珍妮观看表演……', Speaker = 6957, Option = '', Action = _EmptyTable,}, 
	[51335] = { id = 51335, Voice = '', Text = '珍妮想去看马戏团的小丑表演。', Speaker = 6957, Option = '', Action = _EmptyTable,}, 
	[51334] = { id = 51334, Voice = '', Text = '最近我带着我的女儿、妻子和母亲到姜饼城度假。', Speaker = 6957, Option = '', Action = _EmptyTable,}, 
	[51333] = { id = 51333, Voice = '', Text = '我是一名普隆德拉的糖果商人，经营着一家糖果商店。', Speaker = 6957, Option = '真棒', Action = _EmptyTable,}, 
	[51332] = { id = 51332, Voice = '', Text = '太棒了，我有一件事情想请你帮忙！', Speaker = 6957, Option = '', Action = _EmptyTable,}, 
	[51331] = { id = 51331, Voice = '', Text = '嗯？你是圣诞老人推荐的冒险者吗？', Speaker = 6957, Option = '是的', Action = _EmptyTable,}, 
	[51330] = { id = 51330, Voice = '', Text = '这么多小孩都失踪了，珍妮还吵着要去看马戏，真令人担忧！', Speaker = 6957, Option = '', Action = _EmptyTable,}, 
	[51329] = { id = 51329, Voice = '', Text = '请你务必保证她的安全。她的父母应该在东边的旅馆附近，请你去向他们了解具体情况！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51328] = { id = 51328, Voice = '', Text = '你可以陪伴她一起观看表演，借此机会调查一下马戏团的情况。', Speaker = 6944, Option = '没问题', Action = _EmptyTable,}, 
	[51327] = { id = 51327, Voice = '', Text = '我知道一位叫珍妮的小女孩准备去马戏团看表演。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51326] = { id = 51326, Voice = '', Text = '那块地方明明应该是一个充满笑声的乐园，却总给我一种阴森森的感觉。', Speaker = 6944, Option = '这样啊', Action = _EmptyTable,}, 
	[51325] = { id = 51325, Voice = '', Text = '去马戏团调查一下吧，冒险者！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51324] = { id = 51324, Voice = '', Text = '我感觉去马戏团调查比较合适。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51323] = { id = 51323, Voice = '', Text = '乖孩子，我觉得你的洞察能力还需要提高！', Speaker = 6944, Option = '什么？', Action = _EmptyTable,}, 
	[51322] = { id = 51322, Voice = '', Text = '恩…这是你的看法啊。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51321] = { id = 51321, Voice = '', Text = '你对这件事情有什么想法吗？', Speaker = 6944, Option = '{没想法,4},{去玩具工厂调查,4},{去马戏团调查,5}', Action = _EmptyTable,}, 
	[51320] = { id = 51320, Voice = '', Text = '而且是去城中的马戏团看完表演后失踪的！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51319] = { id = 51319, Voice = '', Text = '嗯，我知道一条线索，小孩子们都是在晚上失踪的。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51318] = { id = 51318, Voice = '', Text = '冒险者，希望你可以再考虑考虑……姜饼城的孩子们很需要你！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51317] = { id = 51317, Voice = '', Text = '如果你不愿意帮我们处理这件事情，我无法想象会有什么样的后果！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51316] = { id = 51316, Voice = '', Text = '……这样啊，真是可惜……我好不容易遇见一个能力这么强的冒险者。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51315] = { id = 51315, Voice = '', Text = '请你帮助我们寻找回小丽莎和小内特，还有其他在姜饼城里失踪的孩子。', Speaker = 6944, Option = '有线索吗？', Action = _EmptyTable,}, 
	[51314] = { id = 51314, Voice = '', Text = '我希望你可以帮忙调查这件事情的真相。', Speaker = 6944, Option = '好的', Action = _EmptyTable,}, 
	[51313] = { id = 51313, Voice = '', Text = '我感觉到有什么不得了的“东西”进入了姜饼城，严重威胁着姜饼城里孩子们的安全。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51312] = { id = 51312, Voice = '', Text = '没想到，在这些守卫官的眼皮子底下又有小孩失踪了。', Speaker = 6944, Option = '发生什么了？', Action = _EmptyTable,}, 
	[51311] = { id = 51311, Voice = '', Text = '城中的守卫官们日夜巡逻，守护着我们的孩子。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51310] = { id = 51310, Voice = '', Text = '这件事情让全城的人都重视起来。', Speaker = 6944, Option = '确实应该', Action = _EmptyTable,}, 
	[51309] = { id = 51309, Voice = '', Text = '但紧接着，我们的小男孩内特也失踪了！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51308] = { id = 51308, Voice = '', Text = '原本，我们以为她自己淘气跑出去玩，没按时回家。', Speaker = 6944, Option = '然后呢？', Action = _EmptyTable,}, 
	[51307] = { id = 51307, Voice = '', Text = '几天前，我们姜饼城的小女孩丽莎晚上失踪了。', Speaker = 6944, Option = '嗯', Action = _EmptyTable,}, 
	[51306] = { id = 51306, Voice = '', Text = '让我为你简单的解释一下目前的情况…', Speaker = 6944, Option = '好的', Action = _EmptyTable,}, 
	[51305] = { id = 51305, Voice = '', Text = '我就知道你会答应！嗬嗬嗬嗬～', Speaker = 6944, Option = '', Emoji = 6, Action = _EmptyTable,}, 
	[51304] = { id = 51304, Voice = '', Text = '乖孩子，你愿意帮助我查明真相吗？', Speaker = 6944, Option = '{义不容辞,2},{暂时没空,3}', Action = _EmptyTable,}, 
	[51303] = { id = 51303, Voice = '', Text = '出现这样的情况一定有什么阴谋在酝酿！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51302] = { id = 51302, Voice = '', Text = '要知道，姜饼城对小孩子来说可是最安全的地方。', Speaker = 6944, Option = '是的', Action = _EmptyTable,}, 
	[51301] = { id = 51301, Voice = '', Text = '许多姜饼城的小孩，还有外乡来姜饼城游玩的小孩都失踪了！', Speaker = 6944, Option = '怎么会？', Action = _EmptyTable,}, 
	[51300] = { id = 51300, Voice = '', Text = '是这样的，最近有一些奇怪的事情发生了！', Speaker = 6944, Option = '是什么？', Action = _EmptyTable,}, 
	[51299] = { id = 51299, Voice = '', Text = '既然你这么厉害，我能放心的把事情交给你解决了！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51298] = { id = 51298, Voice = '', Text = '嗬嗬嗬嗬～～', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51297] = { id = 51297, Voice = '', Text = '谢谢你冒险者！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51296] = { id = 51296, Voice = '', Text = '有了这些我就可以为姜饼城的小朋友们准备礼物了！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51295] = { id = 51295, Voice = '', Text = '就是这些礼品盒！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51294] = { id = 51294, Voice = '', Text = '记住这些邪恶礼盒可不容小觑！', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51293] = { id = 51293, Voice = '', Text = '如果你能从玩具工厂1F的邪恶礼盒魔物身上帮我收集一些礼品盒，我才能相信你的能力。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51292] = { id = 51292, Voice = '', Text = '否则我不能将这件麻烦事交给你。', Speaker = 6944, Option = '好！', Action = _EmptyTable,}, 
	[51291] = { id = 51291, Voice = '', Text = '除非你能向我证明你的能力。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51290] = { id = 51290, Voice = '', Text = '现在我也的确需要帮手，可是这么危险的事情……', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51289] = { id = 51289, Voice = '', Text = '看来你还是个勇敢的冒险者呢！嗬嗬嗬嗬～', Speaker = 6944, Option = '是的', Action = _EmptyTable,}, 
	[51288] = { id = 51288, Voice = '', Text = '夜晚的姜饼城不太安全，建议你留在房间里不要随意出门。', Speaker = 6944, Option = '什么事情？', Action = _EmptyTable,}, 
	[51287] = { id = 51287, Voice = '', Text = '嗯，乖孩子，你是来旅游的冒险者吧？', Speaker = 6944, Option = '是的', Action = _EmptyTable,}, 
	[51286] = { id = 51286, Voice = '', Text = '好像有某种神秘的力量在引导灾难降临……', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51285] = { id = 51285, Voice = '', Text = '今年的姜饼城，真让人不安心啊。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51284] = { id = 51284, Voice = '', Text = '你可能要去其他的地方冒险才能找到你心目中的地方了…', Speaker = 6943, Option = '', Action = _EmptyTable,}, 
	[51283] = { id = 51283, Voice = '', Text = '抱歉，恐怕我们的姜饼城不能满足你的冒险需求。', Speaker = 6943, Option = '', Action = _EmptyTable,}, 
	[51282] = { id = 51282, Voice = '', Text = '欢迎来到姜饼城，我的孩子。', Speaker = 6944, Option = '', Action = _EmptyTable,}, 
	[51281] = { id = 51281, Voice = '', Text = '你通过了我的考验，欢迎你进入我们的姜饼城！', Speaker = 6943, Option = '', Action = _EmptyTable,}, 
	[51280] = { id = 51280, Voice = '', Text = '确实，你想的没错，我们姜饼城就是传说中的白雪村落……', Speaker = 6943, Option = '', Action = _EmptyTable,}, 
	[51279] = { id = 51279, Voice = '', Text = '你心目中的姜饼城是什么样的？', Speaker = 6943, Option = '{童话白雪世界,8},{大型地下赌场,9},{无人偏僻荒岛,9}', Action = _EmptyTable,}, 
	[51278] = { id = 51278, Voice = '', Text = '恩，你是个好孩子，要一直保持下去哦～', Speaker = 6943, Option = '', Action = _EmptyTable,}, 
	[51277] = { id = 51277, Voice = '', Text = '可怕，这个世界上居然还有你这样的冒险者。看上去人模人样，居然是这样的人……', Speaker = 6943, Option = '', Action = _EmptyTable,}, 
	[51276] = { id = 51276, Voice = '', Text = '如果我让你进入姜饼城，你会好好爱护我们姜饼城的环境吗？你会怎么做呢？', Speaker = 6943, Option = '{随地大小便,6},{随手扔垃圾,6},{好好收捡垃圾,7}', Action = _EmptyTable,}, 
	[51275] = { id = 51275, Voice = '', Text = '你真是有爱心的冒险者，姜饼城就是需要你这样的人。', Speaker = 6943, Option = '', Action = _EmptyTable,}, 
	[51274] = { id = 51274, Voice = '', Text = '我们姜饼城不欢迎你这么残忍的人！', Speaker = 6943, Option = '', Action = _EmptyTable,}, 
	[51273] = { id = 51273, Voice = '', Text = '那么，冒险者哟，请你告诉我如果你看见迷路的小猫咪，你会怎么做呢？', Speaker = 6943, Option = '{虐待它,4},{收养它,5},{玩弄它,4}', Action = _EmptyTable,}, 
	[51272] = { id = 51272, Voice = '', Text = '我会问你几个问题，准备好了就来找我吧！', Speaker = 6943, Option = '', Action = _EmptyTable,}, 
	[51271] = { id = 51271, Voice = '', Text = '嗬嗬嗬嗬～～想要去姜饼城啊？必须要经过我的同意哦～～', Speaker = 6943, Option = '', Action = _EmptyTable,}, 
	[51270] = { id = 51270, Voice = '', Text = '年轻的冒险者，你想去姜饼城玩一玩吗？', Speaker = 6943, Option = '我想去', Action = _EmptyTable,}, 
	[51269] = { id = 51269, Voice = '', Text = '嗬嗬嗬嗬～～又是一年圣诞节~~', Speaker = 6943, Option = '', Action = _EmptyTable,}, 
	[51268] = { id = 51268, Voice = '', Text = '这是你命运中必经的阶段……', Speaker = 6942, Option = '', Action = _EmptyTable,}, 
	[51267] = { id = 51267, Voice = '', Text = '去吧，在艾尔帕兰找到[c][ffff00]圣诞老公公[-][/c]，让他带你进入姜饼城。', Speaker = 6942, Option = '', Action = _EmptyTable,}, 
	[51266] = { id = 51266, Voice = '', Text = '恒星之人，请你用你的光芒驱赶那些黑暗，那些笼罩在姜饼城的恶意。', Speaker = 6942, Option = '', Action = _EmptyTable,}, 
	[51265] = { id = 51265, Voice = '', Text = '……你！', Speaker = 6942, Option = '', Action = _EmptyTable,}, 
	[51264] = { id = 51264, Voice = '', Text = '我看见因果交织在一起，一端连系着过去，一端连系着……', Speaker = 6942, Option = '', Action = _EmptyTable,}, 
	[51263] = { id = 51263, Voice = '', Text = '我看见了黑暗在侵蚀那块白雪之地，孩子们的灵魂在颤抖着哭泣…', Speaker = 6942, Option = '', Action = _EmptyTable,}, 
	[51262] = { id = 51262, Voice = '', Text = '前几日，命运引导我做了一个梦，我梦见了一个地方，那里终年落雪，是孩子们的天堂。', Speaker = 6942, Option = '', Action = _EmptyTable,}, 
	[51261] = { id = 51261, Voice = '', Text = '我等待你的光芒再次在我面前亮起，希望你不要让我等太久……', Speaker = 6942, Option = '', Action = _EmptyTable,}, 
	[51260] = { id = 51260, Voice = '', Text = '恒星之人，有一件事情困扰我多时，希望你可以听我述说。', Speaker = 6942, Option = '{请说,2},{暂时没空,3}', Action = _EmptyTable,}, 
	[51259] = { id = 51259, Voice = '', Text = '当你从我面前走过，这道光芒扫过我干涸的双眼，为我点亮了世界。', Speaker = 6942, Option = '', Action = _EmptyTable,}, 
	[51258] = { id = 51258, Voice = '', Text = '但是你灵魂的光芒像恒星一样炙热地燃烧着…', Speaker = 6942, Option = '', Action = _EmptyTable,}, 
	[51257] = { id = 51257, Voice = '', Text = '是的，虽然我的双眼无法看见你的脸庞…', Speaker = 6942, Option = '', Action = _EmptyTable,}, 
	[51256] = { id = 51256, Voice = '', Text = '我感受到你向我靠近了，[c][ffff00][PlayerName][-][/c]！', Speaker = 6942, Option = '你知道我的名字？', Action = _EmptyTable,}, 
	[50951] = { id = 50951, Voice = '', Text = '那么请收好金蛇之瞳，你现在可以在普瑞托大人那里挑选武器了，希望你可以收获惊喜哦。', Speaker = 6924, Option = '', Action = _EmptyTable,}, 
	[50950] = { id = 50950, Voice = '', Text = '那可就遗憾了。', Speaker = 6924, Option = '', Action = _EmptyTable,}, 
	[50949] = { id = 50949, Voice = '', Text = '我觉得你是在和我开玩笑，你没带够钱！', Speaker = 6924, Option = '', Emoji = 12, Action = _EmptyTable,}, 
	[50948] = { id = 50948, Voice = '', Text = '那么冒险者，你是否愿意用[c][ffff00]1,990,000Zeny[-][/c]来购买一个金蛇之瞳呢？', Speaker = 6924, Option = '{确认购买,4},{没有兴趣,5}', Action = _EmptyTable,}, 
	[50947] = { id = 50947, Voice = '', Text = '传说中的金蛇之瞳可是历来弑蛇英雄的象征！珍奇瑰宝！你想拥有吗？', Speaker = 6924, Option = '{是的,2},{考虑考虑,3}', Action = _EmptyTable,}, 
	[50931] = { id = 50931, Voice = '', Text = '艾尔帕兰，将永远是你的家。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[50930] = { id = 50930, Voice = '', Text = '你为黄金时钟所做的一切。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[50929] = { id = 50929, Voice = '', Text = '冒险者，我们永远都不会忘记，', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[50874] = { id = 50874, Voice = '', Text = '时间不等人，每分每秒都意味着有新的牺牲。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[50873] = { id = 50873, Voice = '', Text = '保护黄金时钟！', Speaker = 6461, Option = '{好,4},{没准备好,3}', Action = _EmptyTable,}, 
	[50872] = { id = 50872, Voice = '', Text = '在这之前，你一定要拖住轮回梦魇。', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[50871] = { id = 50871, Voice = '', Text = '我会前往炼金术协会，寻求威克斯的帮助。', Speaker = 6461, Option = '好主意', Action = _EmptyTable,}, 
	[50870] = { id = 50870, Voice = '', Text = '用来填补奇点球造成的黑洞！', Speaker = 6461, Option = '收下', Action = _EmptyTable,}, 
	[50869] = { id = 50869, Voice = '', Text = '这些是时空碎片，你收下。', Speaker = 6461, Option = '时空碎片', Action = _EmptyTable,}, 
	[50868] = { id = 50868, Voice = '', Text = '好吧。', Speaker = 6645, Option = '', Action = _EmptyTable,}, 
	[50867] = { id = 50867, Voice = '', Text = '…', Speaker = 6645, Option = '', Action = _EmptyTable,}, 
