	[50520] = { id = 50520, Voice = '', Text = '你回来得可真快！', Speaker = 6625, Option = '', Action = _EmptyTable,}, 
	[50519] = { id = 50519, Voice = '', Text = '对，码头到处都是这样的补给箱，运河之都就是这点方便。', Speaker = 6755, Option = '', Action = _EmptyTable,}, 
	[50518] = { id = 50518, Voice = '', Text = '一起去找一些补给箱，里面有必须的食物和药品。', Speaker = 6755, Option = '补给箱？', Action = _EmptyTable,}, 
