	[50512] = { id = 50512, Voice = '', Text = '好吧，我承认，我错怪了他，蛇洞是存在的。', Speaker = 6755, Option = '好吧', Action = _EmptyTable,}, 
	[50511] = { id = 50511, Voice = '', Text = '我在钟楼发现了詹金斯所说的那个蛇洞，典籍应该就在那里。', Speaker = 6755, Option = '你不是说？', Action = _EmptyTable,}, 
