	[50505] = { id = 50505, Voice = '', Text = '它的威力实在太大了，万一耶梦加得利用它来对付我们。', Speaker = 6493, Option = '', Action = _EmptyTable,}, 
	[50504] = { id = 50504, Voice = '', Text = '我认为现在首要得先找回奇点球。', Speaker = 6493, Option = '', Action = _EmptyTable,}, 
