	[50501] = { id = 50501, Voice = '', Text = '另一个世界线的耶梦加得吸收了黄金时钟的力量，', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[50500] = { id = 50500, Voice = '', Text = '你说的都是真的！？', Speaker = 6461, Option = '', Action = _EmptyTable,}, 
	[50499] = { id = 50499, Voice = '', Text = '更何况，我答应过诺曼会放过艾尔帕兰。', Speaker = 6645, Option = '我支持你', Action = _EmptyTable,}, 
	[50498] = { id = 50498, Voice = '', Text = '呵，我不允许这世界上有两个我的存在。', Speaker = 6645, Option = '', Action = _EmptyTable,}, 
