	[50462] = { id = 50462, Voice = '', Text = '你准备好了吗！', Speaker = 6645, Option = '{准备好了,3},{还没有,2}', Action = _EmptyTable,}, 
	[50461] = { id = 50461, Voice = '', Text = '看来真相只有进入蛇洞后才能弄清楚了！', Speaker = 6645, Option = '是啊', Action = _EmptyTable,}, 
	[50460] = { id = 50460, Voice = '', Text = '我不喜欢这种蹊跷的感觉。', Speaker = 6645, Option = '诡异', Action = _EmptyTable,}, 
	[50459] = { id = 50459, Voice = '', Text = '我刚才在这周围发现了一些金色的蛇鳞片。', Speaker = 6645, Option = '我也发现了', Action = _EmptyTable,}, 
	[50458] = { id = 50458, Voice = '', Text = '有必要进入那个蛇洞再次查看一下。', Speaker = 6645, Option = '好！', Action = _EmptyTable,}, 
